INSERT INTO "Subject" (id, title, description, semester, tags, "totalQCM", "createdAt", views) VALUES ('cmhi0de930138k498s1t79ku1', $$Sémiologie Respiratoire$$, $$Cours de Sémiologie Respiratoire$$, 'DCEM1', ARRAY[$$DCEM1$$, $$Sémiologie$$, $$Sémiologie Respiratoire$$]::text[], 95, '2025-11-02T17:52:05.992Z', 0);
INSERT INTO "Chapter" (id, "subjectId", title, description, "orderIndex", "pdfUrl", "createdAt") VALUES ('cmhi0de99013ak498fg8rb6y8', 'cmhi0de930138k498s1t79ku1', $$Chapitre 1 – La Dyspnée$$, NULL, 1, NULL, '2025-11-02T17:52:05.997Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0de9b013ck498nk6wbw67', 'cmhi0de99013ak498fg8rb6y8', $$Définition$$, $$[{"text":"La dyspnée est une sensation consciente de gêne respiratoire.","isCorrect":true},{"text":"Elle traduit toujours une acidose métabolique.","isCorrect":false,"justification":"Peut aussi être d’origine cardiaque, pulmonaire ou psychogène."},{"text":"Elle correspond à un trouble de la ventilation alvéolaire.","isCorrect":true},{"text":"Elle n’a pas de valeur sémiologique.","isCorrect":false,"justification":"Symptôme majeur respiratoire."},{"text":"Elle est toujours objectivée par l’examinateur.","isCorrect":false,"justification":"Parfois subjective."}]$$::jsonb, $$La dyspnée est subjective mais reflète une gêne ventilatoire réelle.$$, 0, '2025-11-02T17:52:05.999Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0de9d013ek49828d5rouo', 'cmhi0de99013ak498fg8rb6y8', $$Types de dyspnée$$, $$[{"text":"La dyspnée inspiratoire traduit une atteinte bronchique.","isCorrect":false,"justification":"Atteinte laryngée/trachéale."},{"text":"La dyspnée expiratoire traduit une obstruction bronchique.","isCorrect":true},{"text":"La dyspnée mixte combine trouble inspiratoire et expiratoire.","isCorrect":true},{"text":"La dyspnée centrale se voit dans les atteintes du tronc cérébral.","isCorrect":true},{"text":"La dyspnée de Cheyne-Stokes est un trouble obstructif.","isCorrect":false,"justification":"Trouble central cyclique."}]$$::jsonb, $$Type → inspiratoire (haut), expiratoire (bas), mixte (diffuse), centrale (neurologique).$$, 1, '2025-11-02T17:52:06.001Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0de9f013gk498zfcrwt7w', 'cmhi0de99013ak498fg8rb6y8', $$Dyspnée inspiratoire$$, $$[{"text":"Survient lors d’obstruction bronchique distale.","isCorrect":false,"justification":"Obstruction haute."},{"text":"S’accompagne d’un tirage sus-sternal.","isCorrect":true},{"text":"Le stridor est un signe caractéristique.","isCorrect":true},{"text":"S’observe dans l’asthme.","isCorrect":false,"justification":"Expiratoire."},{"text":"Peut indiquer une laryngite aiguë.","isCorrect":true}]$$::jsonb, $$Inspiratoire = voies hautes (larynx, trachée) + tirage + stridor.$$, 2, '2025-11-02T17:52:06.003Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0de9h013ik49894fvvdwt', 'cmhi0de99013ak498fg8rb6y8', $$Dyspnée expiratoire$$, $$[{"text":"Liée à une obstruction des bronches fines.","isCorrect":true},{"text":"S’accompagne de sifflements (sibilants).","isCorrect":true},{"text":"Typique de l’asthme et de la BPCO.","isCorrect":true},{"text":"L’expiration est abrégée.","isCorrect":false,"justification":"Prolongée."},{"text":"Le tirage est absent.","isCorrect":false,"justification":"Peut exister."}]$$::jsonb, $$Expiratoire = sibilants + expiration prolongée (asthme/BPCO).$$, 3, '2025-11-02T17:52:06.005Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0de9j013kk498xuxkkdcx', 'cmhi0de99013ak498fg8rb6y8', $$Dyspnée mixte$$, $$[{"text":"Combine signes inspiratoires et expiratoires.","isCorrect":true},{"text":"Peut s’observer dans les œdèmes pulmonaires.","isCorrect":true},{"text":"S’observe dans les pneumonies étendues.","isCorrect":true},{"text":"N’existe jamais dans l’embolie pulmonaire.","isCorrect":false,"justification":"Fréquente."},{"text":"Évoque souvent une atteinte cardiorespiratoire globale.","isCorrect":true}]$$::jsonb, $$Mixte = origine plurifactorielle (parenchyme + circulation).$$, 4, '2025-11-02T17:52:06.007Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0de9l013mk49890pbtb4j', 'cmhi0de99013ak498fg8rb6y8', $$Dyspnée de Cheyne-Stokes$$, $$[{"text":"Respiration régulière et profonde.","isCorrect":false,"justification":"Cyclique avec pauses."},{"text":"Due à une atteinte du tronc cérébral ou IC sévère.","isCorrect":true},{"text":"Présente une alternance apnée-hyperpnée.","isCorrect":true},{"text":"Est typiquement observée dans l’asthme.","isCorrect":false,"justification":"Neurologique/Cardiaque."},{"text":"Peut apparaître pendant le sommeil.","isCorrect":true}]$$::jsonb, $$Cheyne-Stokes = oscillation périodique respiration–pause (IC/atteinte bulbaire).$$, 5, '2025-11-02T17:52:06.010Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0de9o013ok4986f1afc0w', 'cmhi0de99013ak498fg8rb6y8', $$Dyspnée de Kussmaul$$, $$[{"text":"Respiration rapide, ample et régulière.","isCorrect":true},{"text":"Typique de l’acidose métabolique.","isCorrect":true},{"text":"Se voit dans le diabète acidocétosique.","isCorrect":true},{"text":"S’accompagne d’une cyanose centrale.","isCorrect":true},{"text":"Est d’origine psychogène.","isCorrect":false,"justification":"Métabolique."}]$$::jsonb, $$Kussmaul = acidose métabolique compensée par hyperventilation.$$, 6, '2025-11-02T17:52:06.012Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0de9q013qk498p2q4k763', 'cmhi0de99013ak498fg8rb6y8', $$Dyspnée d’effort$$, $$[{"text":"Toujours pathologique.","isCorrect":false,"justification":"Normale chez le sujet sain."},{"text":"Évolue selon le stade NYHA en cardiologie.","isCorrect":true},{"text":"Peut révéler une insuffisance cardiaque gauche.","isCorrect":true},{"text":"Disparaît immédiatement au repos.","isCorrect":true},{"text":"S’accompagne toujours d’une polypnée.","isCorrect":true}]$$::jsonb, $$D’effort = physio ou cardiaque, stade NYHA utile au suivi.$$, 7, '2025-11-02T17:52:06.014Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0de9s013sk498afvkobrn', 'cmhi0de99013ak498fg8rb6y8', $$Dyspnée de repos$$, $$[{"text":"Témoigne d’une pathologie avancée.","isCorrect":true},{"text":"Peut survenir dans les embolies pulmonaires massives.","isCorrect":true},{"text":"Se voit dans les OAP graves.","isCorrect":true},{"text":"Est sans valeur pronostique.","isCorrect":false,"justification":"Mauvais signe de gravité."},{"text":"Peut s’associer à une orthopnée.","isCorrect":true}]$$::jsonb, $$De repos = stade sévère d’atteinte respiratoire ou cardiaque.$$, 8, '2025-11-02T17:52:06.017Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0de9v013uk498dwfvokij', 'cmhi0de99013ak498fg8rb6y8', $$Dyspnée aiguë$$, $$[{"text":"Installation brutale en quelques secondes ou minutes.","isCorrect":true},{"text":"Typique d’un asthme aigu grave.","isCorrect":true},{"text":"Peut résulter d’une embolie pulmonaire.","isCorrect":true},{"text":"Indique toujours une cause chronique.","isCorrect":false,"justification":"Cause aiguë souvent vitale."},{"text":"Peut apparaître lors d’un pneumothorax.","isCorrect":true}]$$::jsonb, $$Dyspnée aiguë = urgence cardio-pulmonaire.$$, 9, '2025-11-02T17:52:06.019Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0de9x013wk49809j20g6g', 'cmhi0de99013ak498fg8rb6y8', $$Dyspnée chronique$$, $$[{"text":"Persiste depuis plus de 8 semaines.","isCorrect":true},{"text":"Évoque une atteinte progressive (BPCO, fibrose).","isCorrect":true},{"text":"S’accompagne souvent d’amaigrissement.","isCorrect":true},{"text":"Est sans impact sur la qualité de vie.","isCorrect":false,"justification":"Altération majeure."},{"text":"Peut être fonctionnelle (anxiété).","isCorrect":true}]$$::jsonb, $$Chronique = atteinte pulmonaire durable ou fonctionnelle.$$, 10, '2025-11-02T17:52:06.021Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0de9z013yk4983ty47fsz', 'cmhi0de99013ak498fg8rb6y8', $$Dyspnée paroxystique nocturne$$, $$[{"text":"Survient la nuit, réveille le patient.","isCorrect":true},{"text":"Typique de l’insuffisance cardiaque gauche.","isCorrect":true},{"text":"S’améliore en position assise.","isCorrect":true},{"text":"S’accompagne d’une orthopnée.","isCorrect":true},{"text":"Se voit dans l’asthme uniquement.","isCorrect":false,"justification":"Cardiaque surtout."}]$$::jsonb, $$DPN = insuffisance cardiaque gauche sévère.$$, 11, '2025-11-02T17:52:06.023Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0dea10140k4982o426exs', 'cmhi0de99013ak498fg8rb6y8', $$Orthopnée$$, $$[{"text":"Dyspnée en position couchée.","isCorrect":true},{"text":"Soulagée en position assise.","isCorrect":true},{"text":"Typique de l’asthme nocturne.","isCorrect":false,"justification":"Insuffisance cardiaque."},{"text":"Liée à la redistribution du sang pulmonaire.","isCorrect":true},{"text":"Ne s’accompagne jamais d’œdèmes.","isCorrect":false,"justification":"Souvent associés."}]$$::jsonb, $$Orthopnée = IC gauche décompensée.$$, 12, '2025-11-02T17:52:06.025Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0dea30142k498lo4jprfj', 'cmhi0de99013ak498fg8rb6y8', $$Polypnée$$, $$[{"text":"Augmentation de la fréquence respiratoire.","isCorrect":true},{"text":"Peut être physiologique (fièvre, effort).","isCorrect":true},{"text":"Signe un trouble ventilatoire sévère.","isCorrect":true},{"text":"Se définit par une fréquence < 12/min.","isCorrect":false,"justification":"> 20/min."},{"text":"Peut précéder l’insuffisance respiratoire.","isCorrect":true}]$$::jsonb, $$Polypnée = accélération du rythme respiratoire.$$, 13, '2025-11-02T17:52:06.027Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0dea50144k498rd4lc5ko', 'cmhi0de99013ak498fg8rb6y8', $$Bradypnée$$, $$[{"text":"Fréquence respiratoire > 30/min.","isCorrect":false,"justification":"< 10/min."},{"text":"Peut s’observer dans les intoxications médicamenteuses.","isCorrect":true},{"text":"Signale une dépression du centre respiratoire.","isCorrect":true},{"text":"Se voit dans les acidoses métaboliques.","isCorrect":false,"justification":"Hyperventilation plutôt."},{"text":"Peut précéder un arrêt respiratoire.","isCorrect":true}]$$::jsonb, $$Bradypnée = atteinte centrale grave.$$, 14, '2025-11-02T17:52:06.030Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0dea70146k498ficy206y', 'cmhi0de99013ak498fg8rb6y8', $$Signes de gravité respiratoire$$, $$[{"text":"Tirage sus-sternal et battement des ailes du nez.","isCorrect":true},{"text":"Cyanose périphérique.","isCorrect":true},{"text":"Silence auscultatoire unilatéral.","isCorrect":true},{"text":"Polypnée > 40/min à l’effort.","isCorrect":false,"justification":"Au repos inquiétante."},{"text":"Agitation, troubles de conscience.","isCorrect":true}]$$::jsonb, $$Signes de gravité = tirage + cyanose + troubles conscience.$$, 15, '2025-11-02T17:52:06.031Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0dea90148k498ggywl951', 'cmhi0de99013ak498fg8rb6y8', $$Causes pulmonaires$$, $$[{"text":"Asthme, BPCO, embolie pulmonaire.","isCorrect":true},{"text":"Pneumopathies infectieuses.","isCorrect":true},{"text":"Œdème aigu du poumon.","isCorrect":true},{"text":"Scoliose sévère.","isCorrect":true},{"text":"Cardiopathie ischémique.","isCorrect":false,"justification":"Cardiaque non pulmonaire."}]$$::jsonb, $$Causes pulmonaires = obstruction, infection, restriction.$$, 16, '2025-11-02T17:52:06.033Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0deab014ak498feiagxa8', 'cmhi0de99013ak498fg8rb6y8', $$Causes cardiaques$$, $$[{"text":"Insuffisance cardiaque gauche.","isCorrect":true},{"text":"Rétrécissement mitral.","isCorrect":true},{"text":"Œdème pulmonaire aigu.","isCorrect":true},{"text":"Embolie pulmonaire.","isCorrect":true},{"text":"Péricardite constrictive.","isCorrect":true}]$$::jsonb, $$Dyspnée cardiaque = stase, pression veineuse, congestion.$$, 17, '2025-11-02T17:52:06.035Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0dead014ck498vu5nck8i', 'cmhi0de99013ak498fg8rb6y8', $$Causes extra-respiratoires$$, $$[{"text":"Anémie sévère.","isCorrect":true},{"text":"Acidose métabolique.","isCorrect":true},{"text":"Obésité massive.","isCorrect":true},{"text":"Anxiété.","isCorrect":true},{"text":"Hypothyroïdie.","isCorrect":false,"justification":"Plutôt bradypnée."}]$$::jsonb, $$Dyspnée extra-respiratoire = anémie, acidose, psychogène.$$, 18, '2025-11-02T17:52:06.037Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0deaf014ek498ul2fqsn7', 'cmhi0de99013ak498fg8rb6y8', $$Synthèse$$, $$[{"text":"La dyspnée est toujours d’origine pulmonaire.","isCorrect":false,"justification":"Cardiaque ou métabolique aussi."},{"text":"Elle peut être aiguë ou chronique.","isCorrect":true},{"text":"Les signes de gravité nécessitent hospitalisation urgente.","isCorrect":true},{"text":"La cyanose traduit toujours un excès d’oxygène.","isCorrect":false,"justification":"Hypoxémie."},{"text":"Le traitement dépend de la cause.","isCorrect":true}]$$::jsonb, $$Dyspnée = symptôme transversal, cause plurifactorielle, gravité à évaluer d’emblée.$$, 19, '2025-11-02T17:52:06.039Z');
INSERT INTO "Chapter" (id, "subjectId", title, description, "orderIndex", "pdfUrl", "createdAt") VALUES ('cmhi0deai014gk49816gpyxt9', 'cmhi0de930138k498s1t79ku1', $$Chapitre 2 – L’Hémoptysie$$, NULL, 2, NULL, '2025-11-02T17:52:06.042Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0deal014ik498a7f03f7d', 'cmhi0deai014gk49816gpyxt9', $$Définition$$, $$[{"text":"Rejet de sang par vomissement.","isCorrect":false,"justification":"C’est une hématémèse."},{"text":"Rejet de sang par la bouche, d’origine bronchique.","isCorrect":true},{"text":"S’accompagne toujours de douleurs abdominales.","isCorrect":false,"justification":"Souvent toux et brûlure thoracique."},{"text":"La coloration du sang est rouge vif, aéré.","isCorrect":true},{"text":"N’a aucune valeur sémiologique.","isCorrect":false,"justification":"Symptôme majeur respiratoire."}]$$::jsonb, $$Hémoptysie = rejet de sang aéré par toux → origine bronchique/pulmonaire.$$, 0, '2025-11-02T17:52:06.045Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0dean014kk498wbz0bubb', 'cmhi0deai014gk49816gpyxt9', $$Diagnostic différentiel$$, $$[{"text":"L’hématémèse correspond à du sang digéré.","isCorrect":true},{"text":"L’épistaxis postérieure peut simuler une hémoptysie.","isCorrect":true},{"text":"L’hémoptysie se produit sans toux.","isCorrect":false,"justification":"Avec toux bruyante."},{"text":"Le sang hémoptysique est mousseux.","isCorrect":true},{"text":"L’hématémèse donne un sang noirâtre.","isCorrect":true}]$$::jsonb, $$Différencier par couleur, toux et caractère aéré.$$, 1, '2025-11-02T17:52:06.047Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0deap014mk4983k7ftk75', 'cmhi0deai014gk49816gpyxt9', $$Volume de sang$$, $$[{"text":"Faible < 50 mL → minime.","isCorrect":true},{"text":"Moyenne = 100–200 mL.","isCorrect":true},{"text":"Massive > 500 mL → urgence.","isCorrect":true},{"text":"Le volume n’influence pas la gravité.","isCorrect":false,"justification":"Corrélé au risque vital."},{"text":"La quantification est inutile.","isCorrect":false,"justification":"Indispensable."}]$$::jsonb, $$Évaluer le volume → critère majeur de gravité.$$, 2, '2025-11-02T17:52:06.049Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0dear014ok498o69ixcz3', 'cmhi0deai014gk49816gpyxt9', $$Origine bronchique$$, $$[{"text":"Lésions inflammatoires (bronchite) possibles.","isCorrect":true},{"text":"Tumeurs bronchiques → hémoptysies répétées.","isCorrect":true},{"text":"Tuberculose → cause classique.","isCorrect":true},{"text":"Embolie pulmonaire → cause digestive.","isCorrect":false,"justification":"Pulmonaire."},{"text":"Dilatations des bronches → source chronique.","isCorrect":true}]$$::jsonb, $$Causes bronchiques fréquentes : TBC, cancer, bronchiectasies.$$, 3, '2025-11-02T17:52:06.051Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0deat014qk498xsok08sx', 'cmhi0deai014gk49816gpyxt9', $$Origine vasculaire$$, $$[{"text":"Rupture artérielle bronchique = hémoptysie massive.","isCorrect":true},{"text":"Embolie pulmonaire → cause possible.","isCorrect":true},{"text":"Rétrécissement mitral → stase veineuse.","isCorrect":true},{"text":"Hypertension pulmonaire jamais impliquée.","isCorrect":false,"justification":"Facteur favorisant."},{"text":"Traumatisme thoracique mineur → cause rare mais possible.","isCorrect":true}]$$::jsonb, $$Origine vasculaire = rupture, stase, embolie.$$, 4, '2025-11-02T17:52:06.053Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0deav014sk498321rs5do', 'cmhi0deai014gk49816gpyxt9', $$Hémoptysie massive$$, $$[{"text":"Volume > 600 mL/24 h.","isCorrect":true},{"text":"Urgence vitale.","isCorrect":true},{"text":"Nécessite intubation et aspiration.","isCorrect":true},{"text":"Se traite par repos simple.","isCorrect":false,"justification":"Réanimation."},{"text":"Embolisation artérielle possible.","isCorrect":true}]$$::jsonb, $$Massive = urgence vitale → intubation + embolisation.$$, 5, '2025-11-02T17:52:06.055Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0deaw014uk498rnk6ljne', 'cmhi0deai014gk49816gpyxt9', $$Aspect du sang$$, $$[{"text":"Rouge vif et mousseux → hémoptysie.","isCorrect":true},{"text":"Noir et liquide → digestif.","isCorrect":true},{"text":"Mélangé à des aliments.","isCorrect":false,"justification":"Hématémèse."},{"text":"Sang clair et aéré → alvéolaire.","isCorrect":true},{"text":"Hémoptysie récente → souvent matinale.","isCorrect":true}]$$::jsonb, $$Caractère aéré = preuve d’origine bronchique.$$, 6, '2025-11-02T17:52:06.057Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0deay014wk498cfufzrbh', 'cmhi0deai014gk49816gpyxt9', $$Examens complémentaires$$, $$[{"text":"Radiographie thoracique → examen de première intention.","isCorrect":true},{"text":"Scanner thoracique → étiologie inconnue.","isCorrect":true},{"text":"Fibroscopie bronchique utile au diagnostic et au traitement.","isCorrect":true},{"text":"Le bilan biologique est inutilisable.","isCorrect":false,"justification":"Bilan inflammatoire, coagulant."},{"text":"L’examen des crachats peut identifier Mycobacterium tuberculosis.","isCorrect":true}]$$::jsonb, $$Rx + scanner + fibroscopie → triade diagnostique.$$, 7, '2025-11-02T17:52:06.059Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0deb1014yk49891rzqvcs', 'cmhi0deai014gk49816gpyxt9', $$Hémoptysie et TBC$$, $$[{"text":"Signe précoce et caractéristique de la tuberculose pulmonaire.","isCorrect":true},{"text":"Survient toujours avec fièvre.","isCorrect":false,"justification":"Possible isolée."},{"text":"Associée à cavernes radiologiques.","isCorrect":true},{"text":"Nécessite isolement du patient.","isCorrect":true},{"text":"Ne nécessite aucun traitement spécifique.","isCorrect":false,"justification":"Antituberculeux."}]$$::jsonb, $$TBC + hémoptysie = événement classique à haut risque.$$, 8, '2025-11-02T17:52:06.061Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0deb30150k498w63efbbk', 'cmhi0deai014gk49816gpyxt9', $$Hémoptysie et cancer$$, $$[{"text":"Cause fréquente chez le fumeur.","isCorrect":true},{"text":"Souvent chronique et peu abondante.","isCorrect":true},{"text":"Nécessite fibroscopie systématique.","isCorrect":true},{"text":"Indique une infection bénigne.","isCorrect":false,"justification":"Cause maligne possible."},{"text":"Toujours massive d’emblée.","isCorrect":false,"justification":"Souvent modérée."}]$$::jsonb, $$Cancer bronchique = hémoptysie chronique signal d’alarme.$$, 9, '2025-11-02T17:52:06.064Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0deb50152k498ix8dvl7h', 'cmhi0deai014gk49816gpyxt9', $$Hémoptysie et cardiopathie$$, $$[{"text":"Rétrécissement mitral → stase pulmonaire chronique.","isCorrect":true},{"text":"Insuffisance cardiaque droite → cause majeure.","isCorrect":false,"justification":"Gauche."},{"text":"Signe de mauvais pronostic.","isCorrect":true},{"text":"Survient sans toux.","isCorrect":false,"justification":"Avec toux."},{"text":"Souvent liée à HT pulmonaire.","isCorrect":true}]$$::jsonb, $$Hémoptysie cardiaque = stase pulmonaire mitrale.$$, 10, '2025-11-02T17:52:06.066Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0deb80154k498imef7ecv', 'cmhi0deai014gk49816gpyxt9', $$Causes inflammatoires$$, $$[{"text":"Bronchite aiguë → cause mineure fréquente.","isCorrect":true},{"text":"Bronchiectasie chronique → cause récurrente.","isCorrect":true},{"text":"Pneumonie → peut donner du sang rouillé.","isCorrect":true},{"text":"Asthme → cause majeure d’hémoptysie.","isCorrect":false,"justification":"Exceptionnelle."},{"text":"Tuberculose → cause infectieuse principale.","isCorrect":true}]$$::jsonb, $$Hémoptysie inflammatoire = bronches, pneumonie, TBC.$$, 11, '2025-11-02T17:52:06.068Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0deba0156k498ld47nhsz', 'cmhi0deai014gk49816gpyxt9', $$Signes de gravité$$, $$[{"text":"Dyspnée associée → risque d’étouffement.","isCorrect":true},{"text":"Cyanose centrale → urgence.","isCorrect":true},{"text":"Chute de tension → choc hémorragique.","isCorrect":true},{"text":"Polypnée modérée sans toux = bénin.","isCorrect":false,"justification":"Toujours évaluer."},{"text":"Altération de conscience → hypoxie.","isCorrect":true}]$$::jsonb, $$Gravité = hémorragie + asphyxie potentielle.$$, 12, '2025-11-02T17:52:06.070Z');
