INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0dedf0176k4986ps3mj6m', 'cmhi0decq016ik498gaxtomse', $$Scanner thoracique$$, $$[{"text":"Confirme le diagnostic douteux.","isCorrect":true},{"text":"Visualise bulles ou blebs apicaux.","isCorrect":true},{"text":"Toujours indispensable.","isCorrect":false,"justification":"Pas si typique."},{"text":"Précise la topographie et la taille.","isCorrect":true},{"text":"Nécessite injection de produit iodé.","isCorrect":false,"justification":"Pas forcément."}]$$::jsonb, $$Scanner = utile pour bilan étiologique et quantitatif.$$, 11, '2025-11-02T17:52:06.147Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0dedh0178k498taqg9hn5', 'cmhi0decq016ik498gaxtomse', $$Ponction évacuatrice$$, $$[{"text":"Réalisée d’emblée dans tous les cas.","isCorrect":false,"justification":"Si volumineux ou compressif."},{"text":"Se fait en 2ᵉ espace intercostal antérieur.","isCorrect":true},{"text":"En position demi-assise.","isCorrect":true},{"text":"Toujours suivie d’un drainage.","isCorrect":false,"justification":"Si reconstitution."},{"text":"Réalisée sous asepsie stricte.","isCorrect":true}]$$::jsonb, $$Ponction = geste d’urgence guidé.$$, 12, '2025-11-02T17:52:06.149Z');
