INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0dedn017ek498xngv72b4', 'cmhi0decq016ik498gaxtomse', $$Pneumothorax bilatéral$$, $$[{"text":"Toujours létal.","isCorrect":false,"justification":"Grave, mais réversible."},{"text":"Dyspnée aiguë extrême.","isCorrect":true},{"text":"Hypotension rapide.","isCorrect":true},{"text":"Cyanose marquée.","isCorrect":true},{"text":"Nécessite drainage bilatéral.","isCorrect":true}]$$::jsonb, $$Bilatéral = urgence majeure → double drainage.$$, 15, '2025-11-02T17:52:06.155Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0dedp017gk498ma8w2hip', 'cmhi0decq016ik498gaxtomse', $$Pneumothorax iatrogène$$, $$[{"text":"Suite à ponction pleurale.","isCorrect":true},{"text":"Peut suivre la ventilation mécanique.","isCorrect":true},{"text":"Est toujours bénin.","isCorrect":false,"justification":"Peut être compressif."},{"text":"Prévenu par contrôle échographique.","isCorrect":true},{"text":"Se traite toujours par chirurgie.","isCorrect":false,"justification":"Drainage souvent suffisant."}]$$::jsonb, $$Iatrogène = lié aux gestes invasifs ou ventilation.$$, 16, '2025-11-02T17:52:06.157Z');
