INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0dedu017kk498izupk98i', 'cmhi0decq016ik498gaxtomse', $$Prévention des récidives$$, $$[{"text":"Éviter les vols aériens après guérison.","isCorrect":true},{"text":"Interdiction de la plongée sous-marine.","isCorrect":true},{"text":"Sevrage tabagique impératif.","isCorrect":true},{"text":"Vaccination antipneumococcique.","isCorrect":false,"justification":"Non spécifique."},{"text":"Contrôle radiologique post-traitement.","isCorrect":true}]$$::jsonb, $$Prévention = arrêt tabac, contrôle, éviter variations de pression.$$, 18, '2025-11-02T17:52:06.162Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0dedx017mk49881m4gokq', 'cmhi0decq016ik498gaxtomse', $$Synthèse$$, $$[{"text":"Pneumothorax = air pleural.","isCorrect":true},{"text":"Causes : spontanée, secondaire, traumatique.","isCorrect":true},{"text":"Compressif = urgence vitale.","isCorrect":true},{"text":"Diagnostic : Rx + tympanisme + abolition MV.","isCorrect":true},{"text":"Traitement = drainage systématique.","isCorrect":false,"justification":"Selon gravité."}]$$::jsonb, $$Pneumothorax = diagnostic clinique + Rx, urgence si compressif.$$, 19, '2025-11-02T17:52:06.165Z');
