INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0dee2017qk498fv1u04b3', 'cmhi0dee0017ok498e0czuwjs', $$Définition$$, $$[{"text":"Inflammation de la plèvre avec épanchement liquidien.","isCorrect":true},{"text":"Toujours purulente.","isCorrect":false,"justification":"Peut être citrine, hématique, chyleuse."},{"text":"Toujours bilatérale.","isCorrect":false,"justification":"Généralement unilatérale."},{"text":"Peut être sèche sans liquide.","isCorrect":true},{"text":"Provoque une douleur pleurale.","isCorrect":true}]$$::jsonb, $$Pleurésie = inflammation pleurale ± liquide ± douleur.$$, 0, '2025-11-02T17:52:06.170Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0dee3017sk498h6hq9ey5', 'cmhi0dee0017ok498e0czuwjs', $$Pleurésie sèche$$, $$[{"text":"Correspond à un épanchement massif.","isCorrect":false},{"text":"Ne provoque jamais de douleur.","isCorrect":false},{"text":"S’accompagne d’un frottement pleural audible.","isCorrect":false,"justification":"Faux ici, frottement rare."},{"text":"Nécessite toujours un drainage.","isCorrect":false},{"text":"Est une urgence vitale.","isCorrect":false}]$$::jsonb, $$Ici, aucune proposition n’est vraie → piège de reconnaissance clinique.$$, 1, '2025-11-02T17:52:06.172Z');
