INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0dee8017wk4989bw09bmi', 'cmhi0dee0017ok498e0czuwjs', $$Signes physiques typiques$$, $$[{"text":"Matité basithoracique.","isCorrect":true},{"text":"Diminution FVT et MV.","isCorrect":true},{"text":"Frottement pleural audible.","isCorrect":true},{"text":"Hypersonorité.","isCorrect":false,"justification":"Pneumothorax."},{"text":"Souffle pleurétique sus-jacent.","isCorrect":true}]$$::jsonb, $$Physique = matité, FVT↓, souffle sus-jacent.$$, 3, '2025-11-02T17:52:06.176Z');
