INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0deeb0180k498fycz3tso', 'cmhi0dee0017ok498e0czuwjs', $$Pleurésie hématique$$, $$[{"text":"Liquide rouge non coagulable.","isCorrect":true},{"text":"Peut survenir après traumatisme.","isCorrect":true},{"text":"Se voit dans les néoplasies.","isCorrect":true},{"text":"Est bénigne.","isCorrect":false,"justification":"Potentiellement grave."},{"text":"Nécessite exploration étiologique.","isCorrect":true}]$$::jsonb, $$Sang pleural = traumatique ou tumoral → rechercher cause.$$, 5, '2025-11-02T17:52:06.180Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0deed0182k498elz4561j', 'cmhi0dee0017ok498e0czuwjs', $$Pleurésie chyleuse$$, $$[{"text":"Liquide lactescent, riche en triglycérides.","isCorrect":true},{"text":"Due à rupture du canal thoracique.","isCorrect":true},{"text":"Peut suivre une chirurgie thoracique.","isCorrect":true},{"text":"Nécessite ponction diagnostique.","isCorrect":true},{"text":"Toujours infectieuse.","isCorrect":false,"justification":"Mécanique."}]$$::jsonb, $$Chyleuse = aspect lactescent, TG↑, cause mécanique.$$, 6, '2025-11-02T17:52:06.181Z');
