INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0deem018ek498ocvmchj4', 'cmhi0dee0017ok498e0czuwjs', $$Radiographie thoracique$$, $$[{"text":"Opacité déclive homogène.","isCorrect":true},{"text":"Ligne concave de Damoiseau.","isCorrect":true},{"text":"Disparition du cul-de-sac costo-diaphragmatique.","isCorrect":true},{"text":"Hyperclarté localisée.","isCorrect":false,"justification":"Pneumothorax."},{"text":"Refoulement médiastinal vers le côté sain.","isCorrect":true}]$$::jsonb, $$Rx = opacité déclive + refoulement médiastinal.$$, 12, '2025-11-02T17:52:06.191Z');
