INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0deev018ok498jaijkk0r', 'cmhi0dee0017ok498e0czuwjs', $$Pleurésie maligne$$, $$[{"text":"Due à cancer bronchique ou mammaire.","isCorrect":true},{"text":"Liquide récidivant.","isCorrect":true},{"text":"Cytologie positive.","isCorrect":true},{"text":"Drainage souvent inefficace.","isCorrect":true},{"text":"Pleurodèse utile pour prévention.","isCorrect":true}]$$::jsonb, $$Pleurésie maligne = récidive → pleurodèse thérapeutique.$$, 17, '2025-11-02T17:52:06.200Z');
