INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0c74a00044dg6xjfpsvb6', 'cmhi0c74500024dg60wt7mya4', $$Définition$$, $$[{"text":"Infection intestinale due à Entamoeba histolytica.","isCorrect":true},{"text":"Se transmet par piqûre d’insecte.","isCorrect":false,"justification":"Transmission féco-orale, pas vectorielle."},{"text":"Peut rester asymptomatique.","isCorrect":true},{"text":"Toujours associée à un abcès hépatique.","isCorrect":false,"justification":"La forme intestinale est la plus fréquente."},{"text":"Causée par un ver intestinal.","isCorrect":false,"justification":"Protozoaire, non helminthique."}]$$::jsonb, $$L’amibiase est une protozoose intestinale féco-orale, souvent asymptomatique.$$, 0, '2025-11-02T17:51:10.091Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0c74h00064dg65qslbabt', 'cmhi0c74500024dg60wt7mya4', $$Agent pathogène$$, $$[{"text":"Entamoeba histolytica est pathogène pour l’homme.","isCorrect":true},{"text":"Entamoeba dispar est pathogène.","isCorrect":false,"justification":"Espèce non pathogène."},{"text":"Se multiplie par division binaire.","isCorrect":true},{"text":"Possède un cycle sexué.","isCorrect":false,"justification":"Multiplication asexuée uniquement."},{"text":"Vit dans l’intestin grêle.","isCorrect":false,"justification":"Dans le côlon."}]$$::jsonb, $$Seule E. histolytica est pathogène, cycle asexué dans le côlon.$$, 1, '2025-11-02T17:51:10.097Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0c74k00084dg6ce5arb55', 'cmhi0c74500024dg60wt7mya4', $$Formes parasitaires$$, $$[{"text":"Kyste : forme de résistance et d’infestation.","isCorrect":true},{"text":"Trophozoïte : forme mobile et pathogène.","isCorrect":true},{"text":"Le kyste ne contient qu’un noyau.","isCorrect":false,"justification":"4 noyaux dans le kyste mûr."},{"text":"Le trophozoïte est résistant à la dessiccation.","isCorrect":false,"justification":"Très fragile."},{"text":"Le kyste survit dans l’eau plusieurs jours.","isCorrect":true}]$$::jsonb, $$kyste = forme infestante et résistante ; trophozoïte = forme mobile pathogène.$$, 2, '2025-11-02T17:51:10.100Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0c74n000a4dg65dby5lm3', 'cmhi0c74500024dg60wt7mya4', $$Cycle parasitaire$$, $$[{"text":"L’homme est l’unique hôte.","isCorrect":true},{"text":"Le kyste est la forme infestante.","isCorrect":true},{"text":"Contamination par voie féco-orale.","isCorrect":true},{"text":"Multiplication sexuée dans le foie.","isCorrect":false,"justification":"Multiplication asexuée dans le côlon."},{"text":"Libération des trophozoïtes dans l’estomac.","isCorrect":false,"justification":"Excystation dans le grêle."}]$$::jsonb, $$cycle monoxène humain, contamination féco-orale, kyste infestant.$$, 3, '2025-11-02T17:51:10.103Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0c74q000c4dg66ztajs63', 'cmhi0c74500024dg60wt7mya4', $$Épidémiologie$$, $$[{"text":"Fréquence élevée en zones tropicales.","isCorrect":true},{"text":"Transmission interhumaine directe par trophozoïtes.","isCorrect":false,"justification":"Par kystes dans selles."},{"text":"Facteurs favorisants : promiscuité et mauvaise hygiène.","isCorrect":true},{"text":"L’animal est le principal réservoir.","isCorrect":false,"justification":"Réservoir humain."},{"text":"L’eau non traitée est un vecteur majeur.","isCorrect":true}]$$::jsonb, $$réservoir humain, transmission féco-orale, favorisée par l’hygiène précaire.$$, 4, '2025-11-02T17:51:10.106Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0c74t000e4dg6rtl5nynm', 'cmhi0c74500024dg60wt7mya4', $$Pathogénie$$, $$[{"text":"Libération de toxines nécrosantes.","isCorrect":true},{"text":"Invasion de la muqueuse colique.","isCorrect":true},{"text":"Lésions superficielles non hémorragiques.","isCorrect":false,"justification":"Ulcères profonds, hémorragiques."},{"text":"Extension possible au foie par voie porte.","isCorrect":true},{"text":"Réponse inflammatoire absente.","isCorrect":false,"justification":"Présente et importante."}]$$::jsonb, $$destruction tissulaire colique, extension hépatique possible.$$, 5, '2025-11-02T17:51:10.109Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0c74w000g4dg63ilhg54c', 'cmhi0c74500024dg60wt7mya4', $$Forme intestinale$$, $$[{"text":"Diarrhée glairo-sanglante typique.","isCorrect":true},{"text":"Douleurs abdominales diffuses.","isCorrect":true},{"text":"Fièvre constante et élevée.","isCorrect":false,"justification":"Fièvre souvent absente ou modérée."},{"text":"Peut se compliquer d’un mégacôlon toxique.","isCorrect":true},{"text":"Touche préférentiellement l’iléon.","isCorrect":false,"justification":"Côlon, surtout sigmoïde et rectum."}]$$::jsonb, $$forme colique = dysenterie amibienne, ulcères profonds du côlon.$$, 6, '2025-11-02T17:51:10.112Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0c74z000i4dg6fd6zmugh', 'cmhi0c74500024dg60wt7mya4', $$Forme hépatique (abcès amibien)$$, $$[{"text":"Résulte d’une dissémination hématogène.","isCorrect":true},{"text":"Douleur de l’hypocondre droit et fièvre.","isCorrect":true},{"text":"Localisation souvent unique au foie droit.","isCorrect":true},{"text":"Pus épais “chocolat”.","isCorrect":true},{"text":"Présence fréquente d’amibes dans le pus.","isCorrect":false,"justification":"Rare, souvent stérile."}]$$::jsonb, $$abcès hépatique unique, fiévreux, pus chocolat stérile.$$, 7, '2025-11-02T17:51:10.115Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0c751000k4dg6yd64j909', 'cmhi0c74500024dg60wt7mya4', $$Formes extra-hépatiques$$, $$[{"text":"Pulmonaire : par rupture diaphragmatique.","isCorrect":true},{"text":"Cérébrale : rare mais grave.","isCorrect":true},{"text":"Cutanée : autour de l’anus.","isCorrect":true},{"text":"Osseuse : forme fréquente.","isCorrect":false,"justification":"Exceptionnelle."},{"text":"Oculaire : par extension directe.","isCorrect":false,"justification":"Pas décrite."}]$$::jsonb, $$formes extra-intestinales rares, par extension hépatique secondaire.$$, 8, '2025-11-02T17:51:10.118Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0c754000m4dg6llmbv4bw', 'cmhi0c74500024dg60wt7mya4', $$Diagnostic biologique direct$$, $$[{"text":"EPS : recherche de trophozoïtes et kystes.","isCorrect":true},{"text":"Trophozoïte observable à température ambiante après 24 h.","isCorrect":false,"justification":"Doit être examiné immédiatement."},{"text":"Coloration au lugol ou MIF possible.","isCorrect":true},{"text":"PCR permet distinction E. histolytica / dispar.","isCorrect":true},{"text":"Culture obligatoire.","isCorrect":false,"justification":"Rarement réalisée."}]$$::jsonb, $$diagnostic direct = EPS + examen rapide + PCR spécifique.$$, 9, '2025-11-02T17:51:10.120Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0c756000o4dg6x6unazdd', 'cmhi0c74500024dg60wt7mya4', $$Diagnostic indirect$$, $$[{"text":"Sérologie utile dans l’amibiase hépatique.","isCorrect":true},{"text":"ELISA détecte les anticorps anti-amibiens.","isCorrect":true},{"text":"Sérologie positive signe toujours une infection active.","isCorrect":false,"justification":"Persiste après guérison."},{"text":"Pas d’intérêt dans la forme intestinale.","isCorrect":true},{"text":"Utilisée pour le suivi post-thérapeutique.","isCorrect":false,"justification":"Pas spécifique."}]$$::jsonb, $$sérologie utile pour forme hépatique, mais pas pour la colique.$$, 10, '2025-11-02T17:51:10.123Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0c758000q4dg60ih23xkm', 'cmhi0c74500024dg60wt7mya4', $$Diagnostic différentiel$$, $$[{"text":"Dysenterie bacillaire (Shigella).","isCorrect":true},{"text":"Colite à Clostridium difficile.","isCorrect":true},{"text":"Cancer colique.","isCorrect":true},{"text":"Giardiase.","isCorrect":false,"justification":"Diarrhée non sanglante."},{"text":"Salmonellose typhique.","isCorrect":true}]$$::jsonb, $$diagnostic différentiel multiple ; l’EPS oriente le diagnostic.$$, 11, '2025-11-02T17:51:10.125Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0c75a000s4dg6w2xyc9yg', 'cmhi0c74500024dg60wt7mya4', $$Traitement curatif$$, $$[{"text":"Métronidazole 7–10 jours.","isCorrect":true},{"text":"Tinidazole équivalent.","isCorrect":true},{"text":"Albendazole indiqué.","isCorrect":false,"justification":"Inefficace sur protozoaires."},{"text":"Diloxanide furoate après métronidazole.","isCorrect":true},{"text":"Corticoïdes indiqués en phase aiguë.","isCorrect":false,"justification":"Contre-indiqués."}]$$::jsonb, $$traitement = métronidazole + amoebicide de contact (diloxanide/paromomycine).$$, 12, '2025-11-02T17:51:10.127Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0c75c000u4dg6i7g21lia', 'cmhi0c74500024dg60wt7mya4', $$Traitement de l’abcès hépatique$$, $$[{"text":"Métronidazole seul guérit la plupart des cas.","isCorrect":true},{"text":"Ponction si abcès volumineux ou menaçant.","isCorrect":true},{"text":"Chirurgie toujours nécessaire.","isCorrect":false,"justification":"Rare, cas particuliers."},{"text":"Drainage échoguidé possible.","isCorrect":true},{"text":"Traitement identique à celui des abcès bactériens.","isCorrect":false,"justification":"Diffère par étiologie et antibiothérapie."}]$$::jsonb, $$traitement médical en première intention, drainage si échec ou volumineux.$$, 13, '2025-11-02T17:51:10.129Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0c75f000w4dg66mezv2uf', 'cmhi0c74500024dg60wt7mya4', $$Prévention$$, $$[{"text":"Hygiène des mains.","isCorrect":true},{"text":"Boire de l’eau bouillie.","isCorrect":true},{"text":"Lutte antivectorielle.","isCorrect":false,"justification":"Pas de vecteur."},{"text":"Traitement des porteurs sains.","isCorrect":true},{"text":"Vaccin efficace disponible.","isCorrect":false,"justification":"Aucun vaccin."}]$$::jsonb, $$hygiène + traitement du portage = meilleure prévention.$$, 14, '2025-11-02T17:51:10.131Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0c75h000y4dg64q7qw2qs', 'cmhi0c74500024dg60wt7mya4', $$Population à risque$$, $$[{"text":"Habitants de zones tropicales.","isCorrect":true},{"text":"Enfants en bas âge.","isCorrect":true},{"text":"Voyageurs.","isCorrect":true},{"text":"Animaux domestiques.","isCorrect":false,"justification":"Non concernés."},{"text":"Personnel de santé.","isCorrect":true}]$$::jsonb, $$risque élevé dans les zones endémiques et en voyage.$$, 15, '2025-11-02T17:51:10.134Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0c75k00104dg6xex9hsi6', 'cmhi0c74500024dg60wt7mya4', $$Complications$$, $$[{"text":"Mégacôlon toxique.","isCorrect":true},{"text":"Péritonite par perforation colique.","isCorrect":true},{"text":"Hémorragie digestive.","isCorrect":true},{"text":"Insuffisance rénale aiguë.","isCorrect":false,"justification":"Pas liée."},{"text":"Abcès cérébral.","isCorrect":true}]$$::jsonb, $$complications graves = perforation, hémorragie, abcès secondaires.$$, 16, '2025-11-02T17:51:10.137Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0c75n00124dg6vxvt88b0', 'cmhi0c74500024dg60wt7mya4', $$Particularités histologiques$$, $$[{"text":"Ulcères en “flacon”.","isCorrect":true},{"text":"Infiltrat inflammatoire riche en polynucléaires.","isCorrect":true},{"text":"Nécrose tissulaire par enzymes amibiennes.","isCorrect":true},{"text":"Trophozoïtes uniquement dans la lumière colique.","isCorrect":false,"justification":"Aussi dans la paroi."},{"text":"Atteinte superficielle limitée.","isCorrect":false,"justification":"Ulcères profonds."}]$$::jsonb, $$ulcères typiques en flacon, destruction tissulaire profonde.$$, 17, '2025-11-02T17:51:10.139Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0c75p00144dg67qjpzlse', 'cmhi0c74500024dg60wt7mya4', $$Diagnostic radiologique$$, $$[{"text":"Échographie utile pour l’abcès hépatique.","isCorrect":true},{"text":"Scanner abdomino-pelvien localise les abcès multiples.","isCorrect":true},{"text":"Radiographie thoracique pour rupture diaphragmatique.","isCorrect":true},{"text":"IRM inutilisable.","isCorrect":false,"justification":"Utile si doute lésionnel."},{"text":"Radiologie colique montre ulcères superficiels.","isCorrect":false,"justification":"Ulcères profonds."}]$$::jsonb, $$imagerie utile pour localiser les abcès et complications extra-intestinales.$$, 18, '2025-11-02T17:51:10.141Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0c75r00164dg6ytfrefsi', 'cmhi0c74500024dg60wt7mya4', $$Points clés à retenir$$, $$[{"text":"E. histolytica = protozoaire pathogène du côlon.","isCorrect":true},{"text":"Transmission féco-orale par kystes.","isCorrect":true},{"text":"Diagnostic par EPS et sérologie.","isCorrect":true},{"text":"Traitement : métronidazole + amoebicide de contact.","isCorrect":true},{"text":"Prévention : vaccin obligatoire.","isCorrect":false,"justification":"Aucun vaccin."}]$$::jsonb, $$amibiase = parasitose intestinale fréquente, potentiellement grave mais évitable.$$, 19, '2025-11-02T17:51:10.144Z');
INSERT INTO "Chapter" (id, "subjectId", title, description, "orderIndex", "pdfUrl", "createdAt") VALUES ('cmhi0c75v00184dg6m8c2iy5h', 'cmhi0c73200004dg67jqmn6q5', $$Chapitre 10 — Douves et Distomatoses (Fasciolose, Clonorchiase, Dicrocœliose)$$, NULL, 2, NULL, '2025-11-02T17:51:10.147Z');
