INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0c763001e4dg68ml3mobo', 'cmhi0c75v00184dg6m8c2iy5h', $$Cycle de Fasciola hepatica$$, $$[{"text":"Hôte intermédiaire : escargot d’eau douce.","isCorrect":true},{"text":"Contamination par ingestion de végétaux crus.","isCorrect":true},{"text":"Migration : intestin → foie → voies biliaires.","isCorrect":true},{"text":"Reproduction sexuée dans le mollusque.","isCorrect":false,"justification":"Multiplication asexuée."},{"text":"Œufs émis par voie urinaire.","isCorrect":false,"justification":"Par voie fécale."}]$$::jsonb, $$cycle complexe aquatique, contamination alimentaire.$$, 2, '2025-11-02T17:51:10.156Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0c766001g4dg6kcaca95u', 'cmhi0c75v00184dg6m8c2iy5h', $$Fasciolose : phase hépatique$$, $$[{"text":"Fièvre et douleurs de l’hypocondre droit.","isCorrect":true},{"text":"Hépatomégalie.","isCorrect":true},{"text":"Éosinophilie majeure.","isCorrect":true},{"text":"Ictère constant.","isCorrect":false,"justification":"Inconstant."},{"text":"Œufs visibles dans les selles dès le 3ᵉ jour.","isCorrect":false,"justification":"Après maturation dans voies biliaires."}]$$::jsonb, $$phase hépatique migratoire fébrile et éosinophile.$$, 3, '2025-11-02T17:51:10.158Z');
