INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0c76a001k4dg6o3m8wfln', 'cmhi0c75v00184dg6m8c2iy5h', $$Diagnostic de la fasciolose$$, $$[{"text":"Mise en évidence d’œufs dans les selles.","isCorrect":true},{"text":"Sérologie utile en phase prépatente.","isCorrect":true},{"text":"Échographie : images de tunnels hépatiques.","isCorrect":true},{"text":"PCR spécifique possible.","isCorrect":true},{"text":"Culture sur gélose sang.","isCorrect":false}]$$::jsonb, $$diagnostic biologique direct ou sérologique selon le stade.$$, 5, '2025-11-02T17:51:10.162Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0c76c001m4dg6lh2ecj44', 'cmhi0c75v00184dg6m8c2iy5h', $$Traitement de la fasciolose$$, $$[{"text":"Triclabendazole = traitement de choix.","isCorrect":true},{"text":"Praziquantel efficace.","isCorrect":false,"justification":"Inefficace sur Fasciola."},{"text":"","isCorrect":false},{"text":"Réhydratation utile.","isCorrect":true},{"text":"Corticoïdes systématiques.","isCorrect":false,"justification":"Non nécessaires."}]$$::jsonb, $$traitement spécifique = triclabendazole, surveillance hépatique.$$, 6, '2025-11-02T17:51:10.165Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0c76f001o4dg6l15an0v9', 'cmhi0c75v00184dg6m8c2iy5h', $$Clonorchiase (Clonorchis sinensis)$$, $$[{"text":"Endémique en Asie (Chine, Corée, Vietnam).","isCorrect":true},{"text":"Transmission par poissons crus.","isCorrect":true},{"text":"Parasite du duodénum.","isCorrect":false,"justification":"Voies biliaires."},{"text":"Risque de cholangiocarcinome.","isCorrect":true},{"text":"Traitement : praziquantel.","isCorrect":true}]$$::jsonb, $$clonorchiase = trématodose biliaire liée à consommation de poisson cru.$$, 7, '2025-11-02T17:51:10.168Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0c76h001q4dg6jmrr8896', 'cmhi0c75v00184dg6m8c2iy5h', $$Dicrocœliose$$, $$[{"text":"Transmission par ingestion de fourmis infectées.","isCorrect":true},{"text":"Cycle terrestre avec deux hôtes intermédiaires.","isCorrect":true},{"text":"Atteinte biliaire discrète.","isCorrect":true},{"text":"Diagnostic : œufs dans les urines.","isCorrect":false,"justification":"Dans les selles."},{"text":"Traitement : praziquantel.","isCorrect":true}]$$::jsonb, $$dicrocœliose = trématodose bénigne des voies biliaires, cycle terrestre original.$$, 8, '2025-11-02T17:51:10.170Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0c76j001s4dg6nzx3tdyq', 'cmhi0c75v00184dg6m8c2iy5h', $$Points clés$$, $$[{"text":"Trématodes biliaires à cycle complexe.","isCorrect":true},{"text":"Transmission alimentaire (végétaux ou poissons crus).","isCorrect":true},{"text":"Atteinte hépatobiliaire chronique.","isCorrect":true},{"text":"Traitement : triclabendazole ou praziquantel selon l’espèce.","isCorrect":true},{"text":"Vaccin efficace disponible.","isCorrect":false}]$$::jsonb, $$distomatoses = trématodoses biliaires, diagnostic par œufs, prévention alimentaire.$$, 9, '2025-11-02T17:51:10.171Z');
INSERT INTO "Chapter" (id, "subjectId", title, description, "orderIndex", "pdfUrl", "createdAt") VALUES ('cmhi0c76m001u4dg6zutgm3d4', 'cmhi0c73200004dg67jqmn6q5', $$Chapitre 11 — Téniasis et Cysticercose (Taenia spp.)$$, NULL, 3, NULL, '2025-11-02T17:51:10.175Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0c76o001w4dg6hvmm0slx', 'cmhi0c76m001u4dg6zutgm3d4', $$Généralités$$, $$[{"text":"Taenia saginata et Taenia solium sont des protozoaires.","isCorrect":false,"justification":"Ce sont des cestodes."},{"text":"Ce sont des vers plats segmentés (strobiles).","isCorrect":true},{"text":"L’homme héberge la forme adulte dans l’intestin.","isCorrect":true},{"text":"Les larves se développent dans les muscles des animaux.","isCorrect":true},{"text":"L’infection humaine est toujours mortelle.","isCorrect":false,"justification":"Généralement bénigne."}]$$::jsonb, $$téniasis = infestation intestinale par cestodes du bœuf ou du porc.$$, 0, '2025-11-02T17:51:10.177Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0c76q001y4dg6chyq3ea7', 'cmhi0c76m001u4dg6zutgm3d4', $$Agents et répartition$$, $$[{"text":"T. solium : hôte intermédiaire = bœuf.","isCorrect":false,"justification":"Porc."},{"text":"T. saginata : hôte intermédiaire = bœuf.","isCorrect":true},{"text":"T. solium : risque de cysticercose humaine.","isCorrect":true},{"text":"T. asiatica : Asie du Sud-Est.","isCorrect":true},{"text":"T. saginata provoque des larves chez l’homme.","isCorrect":false,"justification":"Non, seulement adulte."}]$$::jsonb, $$solium = porc → risque larvaire ; saginata = bœuf → téniasis simple.$$, 1, '2025-11-02T17:51:10.179Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0c76s00204dg6s3asc3dd', 'cmhi0c76m001u4dg6zutgm3d4', $$Cycle de Taenia$$, $$[{"text":"Transmission par piqûre d’insecte.","isCorrect":false,"justification":"Ingestion de viande crue."},{"text":"Œufs libérés dans les selles humaines.","isCorrect":true},{"text":"Larves (cysticerques) dans les muscles animaux.","isCorrect":true},{"text":"Contamination humaine par viande crue ou peu cuite.","isCorrect":true},{"text":"Hôte intermédiaire : escargot.","isCorrect":false}]$$::jsonb, $$cycle indirect homme ↔ animal herbivore via ingestion de viande contaminée.$$, 2, '2025-11-02T17:51:10.181Z');
