INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0c7iv00da4dg6xh7xaf7m', 'cmhi0c7hq00c84dg64n7e4iv0', $$Épidémiologie$$, $$[{"text":"Endémie dans les eaux douces tropicales.","isCorrect":true},{"text":"Transmission en eau de mer.","isCorrect":false,"justification":"Les furcocercaires n’y vivent pas."},{"text":"Groupes à risque : pêcheurs, agriculteurs, enfants.","isCorrect":true},{"text":"Réinfestation fréquente.","isCorrect":true},{"text":"Réservoir animal principal.","isCorrect":false,"justification":"Humain."}]$$::jsonb, $$maladie liée aux eaux douces polluées, réinfestations fréquentes en zone endémique.$$, 18, '2025-11-02T17:51:10.615Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0c7iy00dc4dg6z21scykb', 'cmhi0c7hq00c84dg64n7e4iv0', $$Points clés$$, $$[{"text":"Trématodes sanguins à cycle aquatique.","isCorrect":true},{"text":"Contamination cutanée par furcocercaires.","isCorrect":true},{"text":"Atteinte urinaire et digestive.","isCorrect":true},{"text":"Traitement par praziquantel.","isCorrect":true},{"text":"Prévention par vaccin.","isCorrect":false}]$$::jsonb, $$schistosomiases = grandes bilharzioses tropicales, traitement simple mais prévention hydrique essentielle.$$, 19, '2025-11-02T17:51:10.618Z');
INSERT INTO "Subject" (id, title, description, semester, tags, "totalQCM", "createdAt", views) VALUES ('cmhi0dd2g0000k498vt34o2z3', $$Sémiologie Cardiovasculaire$$, $$Cours de Sémiologie Cardiovasculaire$$, 'DCEM1', ARRAY[$$DCEM1$$, $$Sémiologie$$, $$Sémiologie Cardiovasculaire$$]::text[], 125, '2025-11-02T17:52:04.457Z', 0);
INSERT INTO "Chapter" (id, "subjectId", title, description, "orderIndex", "pdfUrl", "createdAt") VALUES ('cmhi0dd2x0002k498x0sde7j0', 'cmhi0dd2g0000k498vt34o2z3', $$Chapitre 1 – Particularités de l’observation médicale en cardiologie$$, NULL, 1, NULL, '2025-11-02T17:52:04.473Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0dd330004k498dha2j3y9', 'cmhi0dd2x0002k498x0sde7j0', $$Identification et motif de consultation$$, $$[{"text":"L’âge, la profession et l’origine géographique du patient doivent être précisés lors de l’identification.","isCorrect":true},{"text":"Le motif de consultation correspond toujours au diagnostic présumé.","isCorrect":false,"justification":"Il s’agit du symptôme principal rapporté par le patient, non d’un diagnostic."},{"text":"La fiabilité des informations dépend de la mémoire et de l’état intellectuel du patient.","isCorrect":true},{"text":"L’entourage n’est jamais une source fiable d’information médicale.","isCorrect":false,"justification":"Il peut être une source précieuse, notamment chez les patients confus ou âgés."},{"text":"Le motif de consultation est noté après l’examen clinique.","isCorrect":false,"justification":"Il est recueilli avant tout examen, dès l’interrogatoire initial."}]$$::jsonb, $$L’identification et le motif de consultation sont la première étape de toute observation médicale, conditionnant la qualité du diagnostic.$$, 0, '2025-11-02T17:52:04.479Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0dd390006k498zictyjte', 'cmhi0dd2x0002k498x0sde7j0', $$Histoire de la maladie$$, $$[{"text":"L’histoire de la maladie développe la plainte principale.","isCorrect":true},{"text":"Les sept attributs d’un symptôme incluent le siège, la qualité et l’intensité.","isCorrect":true},{"text":"Les circonstances déclenchantes ne font pas partie de l’interrogatoire.","isCorrect":false,"justification":"Elles orientent pourtant l’étiologie (ex. douleur à l’effort)."},{"text":"Les facteurs de rémission ou d’aggravation sont utiles pour le diagnostic différentiel.","isCorrect":true},{"text":"Les manifestations associées n’ont pas d’intérêt diagnostique.","isCorrect":false,"justification":"Elles aident à identifier les syndromes (ex. dyspnée + œdèmes → insuffisance cardiaque)."}]$$::jsonb, $$L’analyse systématique des attributs d’un symptôme permet d’en définir la cause probable et la gravité clinique.$$, 1, '2025-11-02T17:52:04.485Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0dd3b0008k498qzfcv3yr', 'cmhi0dd2x0002k498x0sde7j0', $$Antécédents personnels et familiaux$$, $$[{"text":"Les antécédents de rhumatisme articulaire aigu orientent vers une cardiopathie valvulaire.","isCorrect":true},{"text":"Les antécédents d’infarctus du myocarde doivent être considérés comme non pertinents pour une nouvelle douleur thoracique.","isCorrect":false,"justification":"Ils indiquent un risque élevé de récidive coronaire."},{"text":"L’hypertension artérielle est un facteur de risque cardiovasculaire modifiable.","isCorrect":true},{"text":"Les antécédents familiaux de coronaropathie avant 55 ans chez un parent de 1er degré sont significatifs.","isCorrect":true},{"text":"Les antécédents respiratoires ne doivent jamais être pris en compte dans un bilan cardiologique.","isCorrect":false,"justification":"Ils peuvent influencer la symptomatologie (ex. dyspnée)."}]$$::jsonb, $$L’étude complète des antécédents révèle le terrain du patient et oriente la recherche des pathologies cardiovasculaires héréditaires ou acquises.$$, 2, '2025-11-02T17:52:04.488Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0dd3e000ak498ig4qtcec', 'cmhi0dd2x0002k498x0sde7j0', $$Facteurs de risque cardiovasculaire$$, $$[{"text":"L’âge et le sexe sont des facteurs de risque non modifiables.","isCorrect":true},{"text":"Le diabète et l’hypercholestérolémie sont des facteurs de risque modifiables.","isCorrect":true},{"text":"Le tabagisme actif augmente le risque de coronaropathie.","isCorrect":true},{"text":"La sédentarité n’a aucun impact sur la santé cardiovasculaire.","isCorrect":false,"justification":"Elle favorise l’obésité, l’HTA et l’athérosclérose."},{"text":"Les antécédents familiaux ne sont pas considérés comme un facteur de risque.","isCorrect":false,"justification":"Ils sont au contraire déterminants dans l’évaluation du risque global."}]$$::jsonb, $$L’identification des facteurs de risque est essentielle pour la prévention et la prise en charge des maladies cardiovasculaires.$$, 3, '2025-11-02T17:52:04.491Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0dd3i000ck4986ij7efdx', 'cmhi0dd2x0002k498x0sde7j0', $$Examen clinique général$$, $$[{"text":"L’examen clinique doit toujours commencer par l’inspection.","isCorrect":true},{"text":"La mesure du pouls, de la tension artérielle et de la saturation fait partie des constantes vitales.","isCorrect":true},{"text":"L’auscultation cardiaque se réalise avant la palpation.","isCorrect":false,"justification":"L’ordre logique est inspection → palpation → percussion → auscultation."},{"text":"L’examen clinique doit s’effectuer dans un environnement calme et bien éclairé.","isCorrect":true},{"text":"L’examen des autres appareils n’est pas nécessaire dans un bilan cardiovasculaire.","isCorrect":false,"justification":"Les atteintes systémiques (ex. rénale, pulmonaire) sont souvent associées."}]$$::jsonb, $$Un examen clinique rigoureux et méthodique reste l’outil central du diagnostic cardiovasculaire.$$, 4, '2025-11-02T17:52:04.494Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0dd3m000ek498j34e91c5', 'cmhi0dd2x0002k498x0sde7j0', $$Inspection thoracique$$, $$[{"text":"Le thorax normal est symétrique et sans déformation.","isCorrect":true},{"text":"Un thorax en entonnoir est typique des insuffisants cardiaques.","isCorrect":false,"justification":"Il s’agit d’une déformation osseuse sans lien direct avec le cœur."},{"text":"La respiration doit être observée à l’œil nu avant toute auscultation.","isCorrect":true},{"text":"La turgescence jugulaire témoigne d’une élévation de la pression veineuse centrale.","isCorrect":true},{"text":"Le reflux hépato-jugulaire indique une insuffisance cardiaque gauche.","isCorrect":false,"justification":"Il est spécifique de l’insuffisance cardiaque droite."}]$$::jsonb, $$L’inspection du thorax et du cou fournit des indices immédiats sur l’état hémodynamique du patient.$$, 5, '2025-11-02T17:52:04.498Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0dd3p000gk4988tq79gsa', 'cmhi0dd2x0002k498x0sde7j0', $$Palpation cardiaque$$, $$[{"text":"Le choc de pointe normal se situe au 4e EIC gauche, près du mamelon.","isCorrect":true},{"text":"Un choc de pointe déplacé vers le bas et en dehors traduit une dilatation du ventricule gauche.","isCorrect":true},{"text":"Le frémissement cataire correspond à un souffle diastolique du rétrécissement mitral.","isCorrect":true},{"text":"Un choc aboli évoque souvent une cardiomégalie.","isCorrect":false,"justification":"Il est surtout observé en cas de péricardite ou d’emphysème pulmonaire."},{"text":"La palpation ne doit jamais être réalisée en décubitus.","isCorrect":false,"justification":"Au contraire, cette position facilite la détection du choc de pointe."}]$$::jsonb, $$La palpation complète l’inspection et permet de localiser les anomalies de contraction du cœur.$$, 6, '2025-11-02T17:52:04.502Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0dd3t000ik498iurgd6hl', 'cmhi0dd2x0002k498x0sde7j0', $$Percussion cardiaque$$, $$[{"text":"La percussion du thorax permet d’évaluer la matité cardiaque.","isCorrect":true},{"text":"C’est un examen de grande précision pour la taille du cœur.","isCorrect":false,"justification":"Sa fiabilité est limitée, surtout chez les patients obèses."},{"text":"Elle permet de détecter un épanchement péricardique important.","isCorrect":true},{"text":"Elle doit toujours être réalisée avant la palpation.","isCorrect":false,"justification":"Elle vient après la palpation et avant l’auscultation."},{"text":"Elle est inutile en sémiologie moderne.","isCorrect":false,"justification":"Elle reste formatrice pour l’apprentissage clinique."}]$$::jsonb, $$La percussion, bien que moins utilisée aujourd’hui, garde une valeur d’enseignement pour la reconnaissance des anomalies précordiales.$$, 7, '2025-11-02T17:52:04.505Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0dd3v000kk498zusi3awu', 'cmhi0dd2x0002k498x0sde7j0', $$Auscultation cardiaque$$, $$[{"text":"Le premier bruit (B1) correspond à la fermeture des valves mitrale et tricuspide.","isCorrect":true},{"text":"Le deuxième bruit (B2) correspond à la fermeture des valves aortique et pulmonaire.","isCorrect":true},{"text":"Un souffle diastolique au foyer mitral évoque une insuffisance aortique.","isCorrect":false,"justification":"Il évoque plutôt un rétrécissement mitral."},{"text":"Un souffle holosystolique à l’apex évoque une insuffisance mitrale.","isCorrect":true},{"text":"L’auscultation doit se faire uniquement en position assise.","isCorrect":false,"justification":"Certaines anomalies ne s’entendent qu’en décubitus ou penché en avant."}]$$::jsonb, $$L’auscultation cardiaque reste un art clinique majeur permettant de distinguer les souffles valvulaires et leurs origines.$$, 8, '2025-11-02T17:52:04.507Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0dd3y000mk4988io36hxs', 'cmhi0dd2x0002k498x0sde7j0', $$Synthèse et raisonnement clinique$$, $$[{"text":"Le résumé syndromique permet de regrouper les signes pour formuler une hypothèse.","isCorrect":true},{"text":"Le clinicien ne doit évoquer qu’un seul diagnostic à la fois.","isCorrect":false,"justification":"Il doit formuler plusieurs hypothèses classées par probabilité."},{"text":"Les examens complémentaires servent à confirmer ou infirmer les hypothèses.","isCorrect":true},{"text":"L’électrocardiogramme (ECG) se fait toujours avant l’examen clinique.","isCorrect":false,"justification":"Il est réalisé après l’examen physique."},{"text":"Le traitement est défini avant d’établir le diagnostic.","isCorrect":false,"justification":"Il découle du diagnostic final confirmé par les examens."}]$$::jsonb, $$L’observation médicale est un processus raisonné qui relie l’interrogatoire, l’examen clinique et les explorations complémentaires pour aboutir à un diagnostic fiable.$$, 9, '2025-11-02T17:52:04.510Z');
INSERT INTO "Chapter" (id, "subjectId", title, description, "orderIndex", "pdfUrl", "createdAt") VALUES ('cmhi0dd41000ok498kl300tmu', 'cmhi0dd2g0000k498vt34o2z3', $$Chapitre 10 – Le Rétrécissement Aortique (RAo)$$, NULL, 2, NULL, '2025-11-02T17:52:04.514Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0dd4m0016k498irk5yomx', 'cmhi0dd41000ok498kl300tmu', $$Complications$$, $$[{"text":"L’insuffisance cardiaque gauche est une complication terminale.","isCorrect":true},{"text":"Les troubles du rythme ventriculaire sont fréquents.","isCorrect":true},{"text":"L’endocardite infectieuse est une complication possible.","isCorrect":true},{"text":"Les embolies systémiques sont très fréquentes.","isCorrect":false,"justification":"Elles sont rares sauf en cas de FA associée."},{"text":"L’angor est toujours lié à une sténose coronaire.","isCorrect":false,"justification":"Il peut survenir sans lésion coronaire par ischémie relative."}]$$::jsonb, $$Le RAo évolue vers la défaillance cardiaque, les troubles du rythme et parfois l’endocardite.$$, 8, '2025-11-02T17:52:04.534Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0dd44000qk498igd01l3l', 'cmhi0dd41000ok498kl300tmu', $$Définition et généralités$$, $$[{"text":"Le RAo est un obstacle à l’éjection du sang du ventricule gauche vers l’aorte.","isCorrect":true},{"text":"Il entraîne une hypertrophie excentrique du ventricule gauche.","isCorrect":false,"justification":"Elle est concentrique, car la surcharge est de pression."},{"text":"La surface normale de l’orifice aortique est d’environ 3 cm².","isCorrect":true},{"text":"Une surface < 1 cm² définit un RAo serré.","isCorrect":true},{"text":"Il provoque une surcharge de volume du ventricule gauche.","isCorrect":false,"justification":"La surcharge est de pression, pas de volume."}]$$::jsonb, $$Le RAo est une obstruction à l’éjection ventriculaire gauche, provoquant une hypertrophie concentrique et un débit limité.$$, 0, '2025-11-02T17:52:04.516Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0dd46000sk498b5v3cbyj', 'cmhi0dd41000ok498kl300tmu', $$Étiologies$$, $$[{"text":"Le RAo congénital bicuspide est fréquent chez le jeune adulte.","isCorrect":true},{"text":"Le RAo dégénératif touche surtout le sujet âgé.","isCorrect":true},{"text":"Le RAo rhumatismal est encore courant dans les pays en développement.","isCorrect":true},{"text":"Le prolapsus valvulaire mitral est une cause majeure de RAo.","isCorrect":false,"justification":"Il provoque une insuffisance mitrale, pas aortique."},{"text":"La maladie de Paget peut exceptionnellement donner un RAo.","isCorrect":true}]$$::jsonb, $$Le RAo a des causes variées : congénitales, rhumatismales, dégénératives ou métaboliques rares.$$, 1, '2025-11-02T17:52:04.519Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0dd48000uk49837mxmq36', 'cmhi0dd41000ok498kl300tmu', $$Physiopathologie$$, $$[{"text":"La postcharge du ventricule gauche est augmentée.","isCorrect":true},{"text":"L’hypertrophie concentrique compense initialement la surcharge.","isCorrect":true},{"text":"Le débit cardiaque augmente en cas de RAo serré.","isCorrect":false,"justification":"Il reste fixe malgré l’effort."},{"text":"La perfusion coronaire est compromise.","isCorrect":true},{"text":"Le RAo provoque une dilatation du ventricule gauche.","isCorrect":false,"justification":"Il reste non dilaté jusqu’à la décompensation."}]$$::jsonb, $$Le RAo impose un effort constant au ventricule gauche, menant à l’hypertrophie et à l’ischémie secondaire.$$, 2, '2025-11-02T17:52:04.521Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0dd4a000wk498yid4wnrl', 'cmhi0dd41000ok498kl300tmu', $$Circonstances de découverte$$, $$[{"text":"Le RAo peut être découvert à l’auscultation systématique.","isCorrect":true},{"text":"Les syncopes d’effort sont typiques.","isCorrect":true},{"text":"L’angor d’effort peut survenir sans coronaropathie.","isCorrect":true},{"text":"L’œdème pulmonaire aigu est un signe de décompensation tardif.","isCorrect":true},{"text":"Les palpitations sont le signe inaugural le plus fréquent.","isCorrect":false,"justification":"Elles sont rares et non spécifiques."}]$$::jsonb, $$Le RAo se révèle souvent par la triade classique : dyspnée d’effort, angor et syncope.$$, 3, '2025-11-02T17:52:04.523Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0dd4d000yk498vkm4ijif', 'cmhi0dd41000ok498kl300tmu', $$Examen clinique$$, $$[{"text":"Le choc de pointe est dur et localisé.","isCorrect":true},{"text":"Le souffle systolique est râpeux, éjectionnel, au foyer aortique.","isCorrect":true},{"text":"Il irradie vers les carotides.","isCorrect":true},{"text":"Le B2 est souvent diminué ou aboli.","isCorrect":true},{"text":"Le souffle diastolique aspiratif est typique du RAo.","isCorrect":false,"justification":"Il correspond à une insuffisance aortique."}]$$::jsonb, $$Le RAo présente un souffle systolique éjectionnel au foyer aortique, irradiant vers le cou, avec diminution du B2.$$, 4, '2025-11-02T17:52:04.525Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0dd4f0010k4985ysujrgq', 'cmhi0dd41000ok498kl300tmu', $$Signes périphériques$$, $$[{"text":"Le pouls carotidien est lent et faible (pulsus parvus et tardus).","isCorrect":true},{"text":"La pression différentielle est élargie.","isCorrect":false,"justification":"Elle est réduite dans le RAo serré."},{"text":"La pâleur du visage d’effort est classique.","isCorrect":true},{"text":"La cyanose est un signe précoce.","isCorrect":false,"justification":"Elle apparaît à un stade terminal."},{"text":"Les bruits de galop sont rares.","isCorrect":true}]$$::jsonb, $$Le RAo serré se manifeste par un pouls carotidien faible, une pression différentielle réduite et une pâleur d’effort.$$, 5, '2025-11-02T17:52:04.528Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0dd4i0012k498uyr4i91b', 'cmhi0dd41000ok498kl300tmu', $$Examens complémentaires$$, $$[{"text":"L’ECG montre une hypertrophie ventriculaire gauche.","isCorrect":true},{"text":"La radiographie du thorax est toujours normale.","isCorrect":false,"justification":"Elle peut montrer cardiomégalie ou calcification valvulaire."},{"text":"L’échocardiographie mesure la surface valvulaire et le gradient transvalvulaire.","isCorrect":true},{"text":"Le cathétérisme cardiaque est l’examen de première intention.","isCorrect":false,"justification":"Il est réservé aux bilans chirurgicaux."},{"text":"L’échographie Doppler permet d’évaluer la sévérité du RAo.","isCorrect":true}]$$::jsonb, $$L’échographie-Doppler est l’examen de référence pour le diagnostic, la sévérité et le suivi du RAo.$$, 6, '2025-11-02T17:52:04.530Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0dd4k0014k498lyzjtuy1', 'cmhi0dd41000ok498kl300tmu', $$Formes cliniques$$, $$[{"text":"Le RAo congénital bicuspide évolue lentement.","isCorrect":true},{"text":"Le RAo dégénératif est la forme la plus fréquente après 70 ans.","isCorrect":true},{"text":"Le RAo post-rhumatismal s’accompagne souvent d’une insuffisance aortique.","isCorrect":true},{"text":"Le RAo du jeune adulte est toujours symptomatique.","isCorrect":false,"justification":"Il peut rester asymptomatique longtemps."},{"text":"Le RAo calcifié s’associe souvent à une sténose mitrale.","isCorrect":false,"justification":"Cette association est rare."}]$$::jsonb, $$Le RAo peut être congénital, rhumatismal ou dégénératif, avec une évolution lente vers la décompensation.$$, 7, '2025-11-02T17:52:04.532Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0dd4o0018k498wyrzdb5s', 'cmhi0dd41000ok498kl300tmu', $$Traitement$$, $$[{"text":"Le traitement médical soulage les symptômes mais ne corrige pas la sténose.","isCorrect":true},{"text":"La chirurgie est indiquée dès l’apparition des symptômes.","isCorrect":true},{"text":"Le remplacement valvulaire est le traitement de référence.","isCorrect":true},{"text":"La dilatation percutanée est réservée aux sujets âgés inopérables.","isCorrect":true},{"text":"L’antibioprophylaxie est inutile.","isCorrect":false,"justification":"Elle est indiquée avant les gestes invasifs chez les patients à risque."}]$$::jsonb, $$Le traitement du RAo repose sur le remplacement valvulaire précoce dès les premiers signes de décompensation$$, 9, '2025-11-02T17:52:04.536Z');
INSERT INTO "Chapter" (id, "subjectId", title, description, "orderIndex", "pdfUrl", "createdAt") VALUES ('cmhi0dd4r001ak4981ifkgkrb', 'cmhi0dd2g0000k498vt34o2z3', $$Chapitre 11 – L’Insuffisance Coronaire$$, NULL, 3, NULL, '2025-11-02T17:52:04.539Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0dd4s001ck498bx0lxtez', 'cmhi0dd4r001ak4981ifkgkrb', $$Définition et généralités$$, $$[{"text":"L’insuffisance coronaire est une inadéquation entre besoins et apports en oxygène du myocarde.","isCorrect":true},{"text":"Elle résulte toujours d’une embolie coronarienne.","isCorrect":false,"justification":"La cause principale est l’athérosclérose."},{"text":"Elle se manifeste uniquement par l’infarctus.","isCorrect":false,"justification":"Elle comprend angor stable et syndromes coronariens aigus."},{"text":"Elle entraîne une ischémie myocardique.","isCorrect":true},{"text":"Elle peut être silencieuse.","isCorrect":true}]$$::jsonb, $$L’insuffisance coronaire regroupe toutes les situations d’ischémie myocardique, symptomatique ou non, le plus souvent d’origine athéroscléreuse.$$, 0, '2025-11-02T17:52:04.541Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0dd4v001ek498eheqriw8', 'cmhi0dd4r001ak4981ifkgkrb', $$Étiologies et facteurs de risque$$, $$[{"text":"L’athérosclérose est la cause majeure.","isCorrect":true},{"text":"Le tabagisme favorise la formation de plaques.","isCorrect":true},{"text":"Le diabète protège contre les maladies coronaires.","isCorrect":false,"justification":"Il multiplie le risque par 2 à 4."},{"text":"L’hypertension artérielle est un facteur aggravant.","isCorrect":true},{"text":"L’âge jeune élimine le risque coronaire.","isCorrect":false,"justification":"L’angor peut survenir avant 40 ans, surtout chez l’homme."}]$$::jsonb, $$Les principaux facteurs de risque sont l’athérosclérose, le tabac, le diabète, l’hypertension et l’âge.$$, 1, '2025-11-02T17:52:04.543Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0dd4x001gk498tvccr9c7', 'cmhi0dd4r001ak4981ifkgkrb', $$Angor d’effort$$, $$[{"text":"La douleur est constrictive et rétrosternale.","isCorrect":true},{"text":"Elle irradie vers le bras gauche ou la mâchoire.","isCorrect":true},{"text":"Elle est déclenchée par le stress ou l’effort.","isCorrect":true},{"text":"Elle cède au repos ou à la trinitrine.","isCorrect":true},{"text":"Elle dure plus de 30 minutes.","isCorrect":false,"justification":"Au-delà de 30 min, il faut évoquer un infarctus."}]$$::jsonb, $$L’angor d’effort est une ischémie transitoire réversible, calmée par le repos et la nitroglycérine.$$, 2, '2025-11-02T17:52:04.545Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0dd4z001ik498hb4swxop', 'cmhi0dd4r001ak4981ifkgkrb', $$Angor instable$$, $$[{"text":"Il correspond à une rupture de plaque sans nécrose myocardique.","isCorrect":true},{"text":"Il est stable dans le temps.","isCorrect":false,"justification":"Il est progressif, imprévisible."},{"text":"Il cède toujours à la trinitrine.","isCorrect":false,"justification":"Il peut résister au traitement."},{"text":"Il ne nécessite pas d’hospitalisation.","isCorrect":false,"justification":"Il s’agit d’une urgence coronarienne."},{"text":"Il ne précède jamais l’infarctus.","isCorrect":false,"justification":"Il peut le précéder ou le déclencher."}]$$::jsonb, $$L’angor instable traduit une instabilité de plaque athéromateuse, annonçant souvent un infarctus du myocarde.$$, 3, '2025-11-02T17:52:04.547Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0dd50001kk4984d28lg5k', 'cmhi0dd4r001ak4981ifkgkrb', $$Infarctus du myocarde$$, $$[{"text":"Il résulte d’une thrombose sur plaque athéromateuse rompue.","isCorrect":true},{"text":"Il entraîne une nécrose myocardique irréversible.","isCorrect":true},{"text":"Il s’accompagne de douleurs prolongées > 30 min.","isCorrect":true},{"text":"Il peut se compliquer de choc cardiogénique.","isCorrect":true},{"text":"Il provoque une élévation des troponines cardiaques.","isCorrect":true}]$$::jsonb, $$L’infarctus aigu du myocarde est une nécrose ischémique du muscle cardiaque secondaire à une occlusion artérielle prolongée.$$, 4, '2025-11-02T17:52:04.549Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0dd52001mk49848g2nmrz', 'cmhi0dd4r001ak4981ifkgkrb', $$Douleur angineuse typique$$, $$[{"text":"Elle est piquante et bien localisée.","isCorrect":false,"justification":"Elle est constrictive et diffuse."},{"text":"Elle augmente à la palpation du thorax.","isCorrect":false,"justification":"Cela évoque une douleur musculosquelettique."},{"text":"Elle s’aggrave à l’inspiration profonde.","isCorrect":false,"justification":"Cela oriente vers une pleurésie."},{"text":"Elle est calmée par le changement de position.","isCorrect":false,"justification":"Elle ne varie pas avec la posture."},{"text":"Elle dure quelques secondes.","isCorrect":false,"justification":"Elle dure plusieurs minutes en cas d’angor."}]$$::jsonb, $$La douleur angineuse est typiquement rétrosternale, constrictive, irradiée et non influencée par la position ni la respiration.$$, 5, '2025-11-02T17:52:04.550Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0dd54001ok4983jk1z7vz', 'cmhi0dd4r001ak4981ifkgkrb', $$Signes cliniques associés$$, $$[{"text":"La sueur froide accompagne souvent la crise d’angor.","isCorrect":true},{"text":"Les nausées sont possibles.","isCorrect":true},{"text":"L’angoisse de mort est fréquente.","isCorrect":true},{"text":"La toux est un signe spécifique.","isCorrect":false,"justification":"Elle n’a aucune spécificité coronarienne."},{"text":"La douleur est parfois absente chez le diabétique.","isCorrect":true}]$$::jsonb, $$La crise angineuse s’accompagne souvent de manifestations neurovégétatives et peut être indolore chez le diabétique.$$, 6, '2025-11-02T17:52:04.553Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0dd56001qk498n1xlk6ks', 'cmhi0dd4r001ak4981ifkgkrb', $$Examens complémentaires$$, $$[{"text":"L’ECG peut montrer un sus-décalage du ST dans l’infarctus STEMI.","isCorrect":true},{"text":"Les troponines sont les marqueurs les plus spécifiques.","isCorrect":true},{"text":"La radiographie thoracique suffit pour confirmer le diagnostic.","isCorrect":false,"justification":"Elle ne montre que les complications."},{"text":"La coronarographie identifie l’artère occluse.","isCorrect":true},{"text":"L’échocardiographie ne sert à rien en phase aiguë.","isCorrect":false,"justification":"Elle évalue la cinétique et la fonction ventriculaire."}]$$::jsonb, $$L’ECG, les troponines et la coronarographie sont les piliers du diagnostic de l’infarctus du myocarde.$$, 7, '2025-11-02T17:52:04.555Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0dd58001sk498lq8as8r8', 'cmhi0dd4r001ak4981ifkgkrb', $$Complications$$, $$[{"text":"Les troubles du rythme ventriculaire sont une cause majeure de mort subite.","isCorrect":true},{"text":"L’insuffisance cardiaque gauche est fréquente.","isCorrect":true},{"text":"Les ruptures cardiaques surviennent dans les formes transmural.","isCorrect":true},{"text":"L’embolie pulmonaire est typique du syndrome coronarien aigu.","isCorrect":false,"justification":"Elle est veineuse, non coronaire."},{"text":"L’anévrysme ventriculaire est une complication tardive.","isCorrect":true}]$$::jsonb, $$Les complications du syndrome coronarien sont rythmique, hémodynamique, mécanique et thrombo-embolique.$$, 8, '2025-11-02T17:52:04.557Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0dd5b001uk498bi5jo5rj', 'cmhi0dd4r001ak4981ifkgkrb', $$Traitement$$, $$[{"text":"L’aspirine doit être administrée dès la phase aiguë.","isCorrect":true},{"text":"La morphine est utilisée pour calmer la douleur.","isCorrect":true},{"text":"La thrombolyse ou l’angioplastie rétablissent le flux coronaire.","isCorrect":true},{"text":"Les bêta-bloquants sont contre-indiqués dans l’infarctus.","isCorrect":false,"justification":"Ils diminuent la mortalité s’il n’y a pas de choc."},{"text":"Le repos strict au lit n’est plus indiqué après reperfusion.","isCorrect":true}]$$::jsonb, $$Le traitement associe reperfusion rapide, antiagrégants, bêta-bloquants et prévention secondaire pour améliorer le pronostic vital.$$, 9, '2025-11-02T17:52:04.559Z');
INSERT INTO "Chapter" (id, "subjectId", title, description, "orderIndex", "pdfUrl", "createdAt") VALUES ('cmhi0dd5f001wk498iuhem3c8', 'cmhi0dd2g0000k498vt34o2z3', $$Chapitre 12 – Les Péricardites et la Tamponnade$$, NULL, 4, NULL, '2025-11-02T17:52:04.563Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0dd5h001yk498yd28zkht', 'cmhi0dd5f001wk498iuhem3c8', $$Définition et généralités$$, $$[{"text":"La péricardite est une inflammation des feuillets du péricarde.","isCorrect":true},{"text":"Elle est toujours d’origine bactérienne.","isCorrect":false,"justification":"Elle est le plus souvent virale ou idiopathique."},{"text":"Elle peut s’accompagner d’un épanchement péricardique.","isCorrect":true},{"text":"Elle touche exclusivement les valvules cardiaques.","isCorrect":false,"justification":"Le péricarde est l’enveloppe du cœur, non les valves."},{"text":"La tamponnade est une complication grave de péricardite.","isCorrect":true}]$$::jsonb, $$La péricardite est une inflammation du péricarde, parfois compliquée d’un épanchement compressif menaçant la vie du patient.$$, 0, '2025-11-02T17:52:04.565Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0dd5j0020k498x1oslo7p', 'cmhi0dd5f001wk498iuhem3c8', $$Étiologies$$, $$[{"text":"Les virus sont la cause la plus fréquente.","isCorrect":true},{"text":"Les péricardites bactériennes sont rares mais graves.","isCorrect":true},{"text":"Les causes néoplasiques doivent être recherchées chez les patients âgés.","isCorrect":true},{"text":"La tuberculose est une cause fréquente dans les pays en développement.","isCorrect":true},{"text":"La cause auto-immune est impossible.","isCorrect":false,"justification":"Les lupus et connectivites peuvent provoquer des péricardites."}]$$::jsonb, $$Les péricardites sont surtout virales ou idiopathiques, mais des causes bactériennes, tuberculeuses, tumorales ou auto-immunes existent.$$, 1, '2025-11-02T17:52:04.568Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0dd5m0022k498d061vqwk', 'cmhi0dd5f001wk498iuhem3c8', $$Douleur péricardique$$, $$[{"text":"Elle est rétrosternale, piquante et augmentée par l’inspiration.","isCorrect":true},{"text":"Elle s’aggrave en position assise.","isCorrect":false,"justification":"Elle diminue en position assise penchée en avant."},{"text":"Elle peut irradier vers le trapèze gauche.","isCorrect":true},{"text":"Elle est calmée par la trinitrine.","isCorrect":false,"justification":"Elle ne répond pas aux dérivés nitrés."},{"text":"Elle est indépendante de l’effort.","isCorrect":true}]$$::jsonb, $$La douleur péricardique est typiquement pleurale, accentuée à l’inspiration et soulagée en position penchée en avant.$$, 2, '2025-11-02T17:52:04.570Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0dd5o0024k498zfzacfib', 'cmhi0dd5f001wk498iuhem3c8', $$Signes cliniques de péricardite aiguë$$, $$[{"text":"Le frottement péricardique est pathognomonique.","isCorrect":true},{"text":"Il est systolique uniquement.","isCorrect":false,"justification":"Il est triple : systolique, diastolique et télédiastolique."},{"text":"La fièvre est possible.","isCorrect":true},{"text":"La turgescence jugulaire est un signe constant.","isCorrect":false,"justification":"Elle apparaît seulement en cas de tamponnade."},{"text":"La dyspnée est fréquente.","isCorrect":true}]$$::jsonb, $$Le frottement péricardique, la fièvre et la douleur typique définissent le tableau clinique de la péricardite aiguë.$$, 3, '2025-11-02T17:52:04.572Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0dd5p0026k4984gaq9dw3', 'cmhi0dd5f001wk498iuhem3c8', $$Électrocardiogramme$$, $$[{"text":"Il montre un sus-décalage diffus du segment ST.","isCorrect":true},{"text":"L’onde T est inversée précocement.","isCorrect":false,"justification":"Elle s’inverse tardivement au stade de récupération."},{"text":"Le ST est concave vers le haut.","isCorrect":true},{"text":"Il existe souvent une microvoltage diffus.","isCorrect":true},{"text":"L’ECG est toujours normal.","isCorrect":false,"justification":"Les anomalies électriques sont fréquentes et caractéristiques."}]$$::jsonb, $$L’ECG montre un sus-décalage ST concave diffus, une microvoltage et parfois une inversion secondaire de T.$$, 4, '2025-11-02T17:52:04.574Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0dd5s0028k498kyw7tajb', 'cmhi0dd5f001wk498iuhem3c8', $$Examens complémentaires$$, $$[{"text":"L’échocardiographie permet de détecter un épanchement péricardique.","isCorrect":true},{"text":"Le dosage de troponine est inutile.","isCorrect":false,"justification":"Il aide à distinguer une péricardite d’un infarctus (élévation modérée possible)."},{"text":"La radiographie thoracique peut montrer une cardiomégalie par épanchement.","isCorrect":true},{"text":"Le bilan inflammatoire est souvent élevé.","isCorrect":true},{"text":"Le scanner thoracique n’a aucune indication.","isCorrect":false,"justification":"Il peut être utile pour les formes chroniques ou néoplasiques."}]$$::jsonb, $$L’échographie confirme la présence d’un épanchement, tandis que la biologie et l’imagerie complètent l’évaluation étiologique.$$, 5, '2025-11-02T17:52:04.576Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0dd5v002ak498urzhobw6', 'cmhi0dd5f001wk498iuhem3c8', $$Tamponnade péricardique$$, $$[{"text":"Elle correspond à une compression du cœur par l’épanchement péricardique.","isCorrect":true},{"text":"Elle entraîne une baisse du remplissage ventriculaire.","isCorrect":true},{"text":"Elle provoque une chute du débit cardiaque.","isCorrect":true},{"text":"Elle se manifeste par hypotension, turgescence jugulaire et BDC assourdis.","isCorrect":true},{"text":"Elle constitue une urgence vitale.","isCorrect":true}]$$::jsonb, $$La tamponnade est une urgence absolue caractérisée par le tableau de Beck : hypotension, turgescence jugulaire, bruits assourdis.$$, 6, '2025-11-02T17:52:04.579Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0dd5x002ck498g52w9qac', 'cmhi0dd5f001wk498iuhem3c8', $$Éléments du diagnostic de tamponnade$$, $$[{"text":"Le pouls paradoxal correspond à une chute inspiratoire > 10 mmHg de la PA systolique.","isCorrect":true},{"text":"L’ECG montre un sus-décalage du ST diffus.","isCorrect":false,"justification":"C’est le signe d’une péricardite, pas d’une tamponnade."},{"text":"L’échographie montre une dilatation du VG.","isCorrect":false,"justification":"Elle montre une compression diastolique du VD."},{"text":"La tension artérielle est élevée.","isCorrect":false,"justification":"Elle est abaissée."},{"text":"Les bruits du cœur sont augmentés.","isCorrect":false,"justification":"Ils sont assourdis."}]$$::jsonb, $$Le diagnostic de tamponnade repose sur les signes cliniques de choc et la confirmation échographique.$$, 7, '2025-11-02T17:52:04.582Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0dd60002ek4985qf23voj', 'cmhi0dd5f001wk498iuhem3c8', $$Complications et évolutions$$, $$[{"text":"La péricardite peut évoluer vers une péricardite chronique constrictive.","isCorrect":true},{"text":"L’épanchement peut se surinfecter.","isCorrect":true},{"text":"Une récidive est possible.","isCorrect":true},{"text":"La calcification péricardique est toujours bénigne.","isCorrect":false,"justification":"Elle peut entraîner une constriction invalidante."},{"text":"Une endocardite survient fréquemment.","isCorrect":false,"justification":"Ce sont deux atteintes distinctes."}]$$::jsonb, $$Les péricardites peuvent se compliquer de récidive, de constriction ou d’infection secondaire.$$, 8, '2025-11-02T17:52:04.584Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0dd62002gk498wxp8hd3w', 'cmhi0dd5f001wk498iuhem3c8', $$Traitement$$, $$[{"text":"Les anti-inflammatoires non stéroïdiens sont le traitement de première intention.","isCorrect":true},{"text":"La colchicine réduit le risque de récidive.","isCorrect":true},{"text":"Les corticoïdes sont indiqués d’emblée dans toutes les formes.","isCorrect":false,"justification":"Ils sont réservés aux causes auto-immunes."},{"text":"La ponction péricardique est indiquée en cas de tamponnade.","isCorrect":true},{"text":"Le repos est inutile dans la phase aiguë.","isCorrect":false,"justification":"Il est indispensable pour limiter la récidive."}]$$::jsonb, $$Le traitement de la péricardite repose sur les AINS, la colchicine et le repos ; la tamponnade impose une ponction urgente.$$, 9, '2025-11-02T17:52:04.586Z');
INSERT INTO "Chapter" (id, "subjectId", title, description, "orderIndex", "pdfUrl", "createdAt") VALUES ('cmhi0dd65002ik498gwaobhft', 'cmhi0dd2g0000k498vt34o2z3', $$Chapitre 2 : Examen cardiovasculaire$$, NULL, 5, NULL, '2025-11-02T17:52:04.589Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0dd67002kk498jvv9ge6x', 'cmhi0dd65002ik498gwaobhft', $$Conditions générales de l’examen cardiovasculaire$$, $$[{"text":"L’examen cardiaque doit toujours se faire dans une pièce calme et chaude.","isCorrect":true},{"text":"Le patient doit être torse nu et examiné en plusieurs positions.","isCorrect":true},{"text":"Le stéthoscope à simple pavillon suffit pour tous les bruits cardiaques.","isCorrect":false,"justification":"Il faut un double pavillon : membrane (sons aigus) et cloche (sons graves)."},{"text":"L’auscultation se fait uniquement en décubitus dorsal.","isCorrect":false,"justification":"Elle se réalise aussi assise, penchée en avant, ou en décubitus latéral gauche."},{"text":"L’examen ne nécessite pas la prise du pouls simultanément.","isCorrect":false,"justification":"La synchronisation avec le pouls radial est indispensable pour repérer les souffles systoliques."}]$$::jsonb, $$L’examen cardiovasculaire demande calme, rigueur et bonne technique d’auscultation pour détecter les anomalies fines.$$, 0, '2025-11-02T17:52:04.591Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0dd69002mk4980jdyd9bc', 'cmhi0dd65002ik498gwaobhft', $$Inspection précordiale et cervicale$$, $$[{"text":"Un soulèvement localisé au niveau de l’apex correspond au choc de pointe.","isCorrect":true},{"text":"La turgescence jugulaire traduit une augmentation de la pression veineuse centrale.","isCorrect":true},{"text":"Le reflux hépato-jugulaire est normal chez le sujet sain.","isCorrect":false,"justification":"Il indique une insuffisance cardiaque droite."},{"text":"L’asymétrie thoracique est un signe spécifique de cardiomégalie.","isCorrect":false,"justification":"Elle est souvent d’origine osseuse ou respiratoire."},{"text":"Le thorax en tonneau est un signe classique d’insuffisance ventriculaire gauche.","isCorrect":false,"justification":"Il correspond à un emphysème pulmonaire, non cardiaque."}]$$::jsonb, $$L’inspection thoracique et cervicale révèle souvent les signes visibles d’insuffisance cardiaque ou d’anomalie du volume cardiaque.$$, 1, '2025-11-02T17:52:04.593Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0dd6c002ok498ju1o5r3h', 'cmhi0dd65002ik498gwaobhft', $$Palpation précordiale$$, $$[{"text":"Le choc de pointe normal est localisé au 5e espace intercostal gauche sur la ligne médio-claviculaire.","isCorrect":true},{"text":"Un choc de pointe diffus et étalé évoque une hypertrophie ventriculaire gauche.","isCorrect":true},{"text":"Un frémissement systolique au foyer aortique indique une sténose aortique.","isCorrect":true},{"text":"Le signe de Harzer correspond à la perception du choc du ventricule droit dans l’épigastre.","isCorrect":true},{"text":"La palpation du choc de pointe n’a aucun intérêt diagnostique.","isCorrect":false,"justification":"Elle permet d’évaluer la position, la force et l’ampleur de la contraction cardiaque."}]$$::jsonb, $$La palpation oriente sur la dilatation ou l’hypertrophie d’une cavité cardiaque et détecte les frémissements pathologiques.$$, 2, '2025-11-02T17:52:04.596Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0dd6e002qk4989a1rn04z', 'cmhi0dd65002ik498gwaobhft', $$Percussion précordiale$$, $$[{"text":"La percussion permet d’évaluer la matité cardiaque.","isCorrect":true},{"text":"Elle distingue la cardiomégalie du simple emphysème thoracique.","isCorrect":true},{"text":"Elle est toujours plus fiable que l’échocardiographie.","isCorrect":false,"justification":"L’échographie est beaucoup plus précise pour mesurer les volumes cardiaques."},{"text":"Elle se pratique avant la palpation.","isCorrect":false,"justification":"L’ordre correct est inspection → palpation → percussion → auscultation."},{"text":"Elle reste utile pour localiser le bord droit du cœur.","isCorrect":true}]$$::jsonb, $$Bien que délaissée, la percussion reste utile pour une évaluation clinique initiale du volume cardiaque.$$, 3, '2025-11-02T17:52:04.598Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0dd6g002sk4981z8a78ll', 'cmhi0dd65002ik498gwaobhft', $$Auscultation : principes généraux$$, $$[{"text":"Le premier bruit (B1) correspond à la fermeture des valves auriculo-ventriculaires.","isCorrect":true},{"text":"Le deuxième bruit (B2) correspond à la fermeture des valves sigmoïdes.","isCorrect":true},{"text":"La cloche du stéthoscope sert à écouter les sons graves (B3, B4, roulements).","isCorrect":true},{"text":"Les foyers d’auscultation cardiaque sont au nombre de quatre principaux.","isCorrect":true},{"text":"Le souffle systolique est toujours pathologique.","isCorrect":false,"justification":"Il existe des souffles fonctionnels ou bénins, surtout chez l’enfant ou l’adulte jeune."}]$$::jsonb, $$L’auscultation cardiaque, bien conduite, permet de localiser les anomalies valvulaires et d’en apprécier le caractère.$$, 4, '2025-11-02T17:52:04.600Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0dd6i002uk498eyrsmhq0', 'cmhi0dd65002ik498gwaobhft', $$Foyers d’auscultation$$, $$[{"text":"Le foyer mitral se situe au 5e espace intercostal gauche, ligne médio-claviculaire.","isCorrect":true},{"text":"Le foyer aortique est au 2e espace intercostal droit, près du sternum.","isCorrect":true},{"text":"Le foyer pulmonaire est au 2e espace intercostal gauche.","isCorrect":true},{"text":"Le foyer tricuspide est au niveau de l’appendice xiphoïde.","isCorrect":true},{"text":"Les foyers accessoires n’ont aucune utilité.","isCorrect":false,"justification":"Ils permettent de mieux identifier les irradiations des souffles (ex. axillaire pour IM, base du cou pour IAo)."}]$$::jsonb, $$Les foyers d’auscultation orientent la localisation de la valvulopathie et sa propagation sonore.$$, 5, '2025-11-02T17:52:04.602Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0dd6l002wk498uecrrwz9', 'cmhi0dd65002ik498gwaobhft', $$Les bruits cardiaques normaux et accessoires$$, $$[{"text":"Le B3 est toujours pathologique.","isCorrect":false,"justification":"Il peut être physiologique chez l’enfant et l’adulte jeune."},{"text":"Le B4 est un bruit de remplissage ventriculaire rapide.","isCorrect":false,"justification":"C’est un bruit de contraction auriculaire pathologique."},{"text":"L’intensité du B1 est diminuée dans l’insuffisance mitrale.","isCorrect":true},{"text":"Le dédoublement physiologique de B2 s’entend mieux à l’inspiration.","isCorrect":true},{"text":"Le B1 correspond à la fermeture des valves sigmoïdes.","isCorrect":false,"justification":"Il correspond à la fermeture des valves mitrale et tricuspide."}]$$::jsonb, $$L’écoute attentive des bruits normaux et accessoires est essentielle pour différencier les valvulopathies et troubles de remplissage.$$, 6, '2025-11-02T17:52:04.605Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0dd6n002yk498zrbrvk7p', 'cmhi0dd65002ik498gwaobhft', $$Les souffles cardiaques$$, $$[{"text":"Un souffle holosystolique à l’apex évoque une insuffisance mitrale.","isCorrect":true},{"text":"Un souffle diastolique au foyer aortique évoque une insuffisance aortique.","isCorrect":true},{"text":"Un souffle systolique éjectionnel au foyer aortique correspond à un rétrécissement aortique.","isCorrect":true},{"text":"Un souffle diastolique au foyer mitral correspond à une sténose mitrale.","isCorrect":true},{"text":"Tous les souffles diastoliques sont bénins.","isCorrect":false,"justification":"Ils sont toujours pathologiques et nécessitent une exploration."}]$$::jsonb, $$L’analyse du temps, du siège et du timbre du souffle permet de reconnaître la valvulopathie en cause.$$, 7, '2025-11-02T17:52:04.607Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0dd6p0030k498wq20wqw6', 'cmhi0dd65002ik498gwaobhft', $$Examen artériel et veineux$$, $$[{"text":"Le pouls radial se palpe avec l’index et le majeur sur la face antéro-externe du poignet.","isCorrect":true},{"text":"La mesure de la pression artérielle doit se faire aux deux bras lors de la première consultation.","isCorrect":true},{"text":"Le pouls pédieux se palpe à la face plantaire du pied.","isCorrect":false,"justification":"Il se palpe sur la face dorsale du pied au 2e espace inter-métatarsien."},{"text":"Le pouls carotidien se prend sous l’angle de la mandibule.","isCorrect":true},{"text":"Un pouls asymétrique est toujours sans importance.","isCorrect":false,"justification":"Il peut révéler une dissection aortique ou une artériopathie."}]$$::jsonb, $$L’examen artériel et veineux évalue la perfusion périphérique et complète l’étude du système cardiovasculaire.$$, 8, '2025-11-02T17:52:04.609Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0dd6r0032k498h9g8j9zi', 'cmhi0dd65002ik498gwaobhft', $$Examens complémentaires de base$$, $$[{"text":"L’électrocardiogramme (ECG) fait partie de tout bilan cardiovasculaire.","isCorrect":true},{"text":"La radiographie thoracique permet d’évaluer la silhouette cardiaque.","isCorrect":true},{"text":"L’échocardiographie évalue la fonction et la morphologie cardiaque.","isCorrect":true},{"text":"Les enzymes cardiaques ne sont utiles qu’en cas d’infarctus suspecté.","isCorrect":true},{"text":"Le bilan biologique n’apporte aucune aide en cardiologie.","isCorrect":false,"justification":"Il détecte diabète, dyslipidémie, troubles ioniques etc."}]$$::jsonb, $$Les examens complémentaires confirment et complètent les données cliniques, orientant vers la cause et la sévérité de la pathologie.$$, 9, '2025-11-02T17:52:04.612Z');
INSERT INTO "Chapter" (id, "subjectId", title, description, "orderIndex", "pdfUrl", "createdAt") VALUES ('cmhi0dd6u0034k498qlt0eb1j', 'cmhi0dd2g0000k498vt34o2z3', $$Chapitre 3 – Les symptômes cardiovasculaires$$, NULL, 6, NULL, '2025-11-02T17:52:04.614Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0dd6v0036k498kd4iykrk', 'cmhi0dd6u0034k498qlt0eb1j', $$Dyspnée d’effort$$, $$[{"text":"La dyspnée d’effort est un signe d’insuffisance cardiaque gauche.","isCorrect":true},{"text":"Elle est toujours liée à une atteinte pulmonaire.","isCorrect":false,"justification":"Elle peut aussi provenir d’un défaut du débit cardiaque."},{"text":"Elle augmente à l’effort et s’améliore au repos.","isCorrect":true},{"text":"Elle traduit une hyperactivité du centre respiratoire.","isCorrect":false,"justification":"La cause est hémodynamique, non centrale."},{"text":"Elle s’aggrave souvent en position allongée (orthopnée).","isCorrect":true}]$$::jsonb, $$La dyspnée d’effort traduit une élévation de la pression veineuse pulmonaire par dysfonction ventriculaire gauche.$$, 0, '2025-11-02T17:52:04.616Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0dd6x0038k498cefo6hau', 'cmhi0dd6u0034k498qlt0eb1j', $$Dyspnée paroxystique nocturne$$, $$[{"text":"Elle réveille le patient la nuit avec une sensation d’étouffement.","isCorrect":true},{"text":"Elle disparaît après quelques minutes en position assise.","isCorrect":true},{"text":"Elle est typique d’une atteinte du ventricule droit.","isCorrect":false,"justification":"Elle signe une insuffisance du ventricule gauche."},{"text":"Elle s’accompagne souvent de toux nocturne.","isCorrect":true},{"text":"Elle est soulagée par le décubitus dorsal.","isCorrect":false,"justification":"Elle est aggravée en décubitus à cause du retour veineux."}]$$::jsonb, $$La dyspnée nocturne est un signe d’insuffisance cardiaque gauche décompensée, fréquemment associée à une congestion pulmonaire.$$, 1, '2025-11-02T17:52:04.617Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0dd6y003ak498k8pi1eyy', 'cmhi0dd6u0034k498qlt0eb1j', $$Dyspnée d’origine cardiaque vs respiratoire$$, $$[{"text":"La dyspnée cardiaque est souvent rapide, superficielle et régulière.","isCorrect":true},{"text":"La dyspnée pulmonaire est lente et irrégulière.","isCorrect":true},{"text":"Une cyanose centrale élimine une cause cardiaque.","isCorrect":false,"justification":"Elle peut se voir dans les cardiopathies cyanogènes."},{"text":"Les râles crépitants bilatéraux orientent vers une cause cardiaque.","isCorrect":true},{"text":"L’orthopnée est typiquement respiratoire.","isCorrect":false,"justification":"Elle est cardiaque, liée au retour veineux accru."}]$$::jsonb, $$La distinction cardio-pulmonaire repose sur le rythme, la position et les bruits auscultatoires.$$, 2, '2025-11-02T17:52:04.619Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0dd70003ck498u1rt35q2', 'cmhi0dd6u0034k498qlt0eb1j', $$Œdème aigu du poumon (OAP)$$, $$[{"text":"Il s’installe brutalement, souvent la nuit.","isCorrect":true},{"text":"Il est accompagné d’une toux avec expectoration rosée.","isCorrect":true},{"text":"Il correspond à une embolie pulmonaire massive.","isCorrect":false,"justification":"L’OAP est dû à une défaillance ventriculaire gauche aiguë, pas à une embolie."},{"text":"Il impose une position allongée stricte.","isCorrect":false,"justification":"Il faut au contraire asseoir le patient pour réduire le retour veineux."},{"text":"Il peut s’accompagner d’une angoisse majeure.","isCorrect":true}]$$::jsonb, $$L’OAP est une urgence vitale d’origine cardiaque, se manifestant par une détresse respiratoire aiguë nocturne.$$, 3, '2025-11-02T17:52:04.620Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0dd71003ek49838kf753n', 'cmhi0dd6u0034k498qlt0eb1j', $$Douleur angineuse (angor d’effort)$$, $$[{"text":"Elle est constrictive, rétrosternale et irradie vers le bras gauche.","isCorrect":true},{"text":"Elle est reproduite par la palpation du thorax.","isCorrect":false,"justification":"Cela oriente vers une cause pariétale ou musculaire."},{"text":"Elle disparaît à l’arrêt de l’effort ou après trinitrine.","isCorrect":true},{"text":"Elle dure généralement plus de 30 minutes.","isCorrect":false,"justification":"Une durée > 30 min évoque un infarctus, pas un angor stable."},{"text":"Elle s’aggrave avec le froid ou le stress.","isCorrect":true}]$$::jsonb, $$L’angor d’effort est le symptôme typique de l’ischémie myocardique transitoire réversible à l’effort.$$, 4, '2025-11-02T17:52:04.622Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0dd73003gk4981sji9slp', 'cmhi0dd6u0034k498qlt0eb1j', $$Douleurs thoraciques non cardiaques (0 juste)$$, $$[{"text":"La douleur d’origine digestive est toujours associée à un souffle cardiaque.","isCorrect":false,"justification":"Les douleurs digestives ne produisent aucun souffle."},{"text":"Les douleurs pariétales s’aggravent à la marche rapide.","isCorrect":false,"justification":"Elles augmentent à la palpation, non à l’effort."},{"text":"Le syndrome de Tietze donne une douleur constrictive rétrosternale.","isCorrect":false,"justification":"C’est une douleur chondro-costale localisée."},{"text":"Les douleurs pleurales diminuent en inspiration profonde.","isCorrect":false,"justification":"Elles s’intensifient à l’inspiration."},{"text":"L’hépatalgie d’effort traduit une sténose coronarienne.","isCorrect":false,"justification":"Elle est liée à l’insuffisance cardiaque droite."}]$$::jsonb, $$De nombreuses douleurs thoraciques non cardiaques imitent l’angor mais leur nature pariétale, digestive ou pleurale permet de les distinguer.$$, 5, '2025-11-02T17:52:04.623Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0dd74003ik498ibosp4jc', 'cmhi0dd6u0034k498qlt0eb1j', $$Palpitations (5 justes)$$, $$[{"text":"Les palpitations correspondent à la perception anormale des battements du cœur.","isCorrect":true},{"text":"Elles peuvent être dues à une extrasystole isolée.","isCorrect":true},{"text":"Elles surviennent parfois sans lésion cardiaque.","isCorrect":true},{"text":"L’anxiété ou la caféine peuvent les déclencher.","isCorrect":true},{"text":"Elles peuvent révéler une fibrillation auriculaire.","isCorrect":true}]$$::jsonb, $$Les palpitations sont un symptôme fréquent, pouvant être bénin ou révélateur d’un trouble du rythme majeur.$$, 6, '2025-11-02T17:52:04.625Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0dd76003kk498r99pxdpn', 'cmhi0dd6u0034k498qlt0eb1j', $$Syncope et lipothymie$$, $$[{"text":"La syncope est une perte de connaissance brève avec chute du tonus.","isCorrect":true},{"text":"Elle résulte d’une baisse transitoire de perfusion cérébrale.","isCorrect":true},{"text":"La lipothymie est une syncope complète.","isCorrect":false,"justification":"C’est une perte partielle de conscience."},{"text":"Les syncopes cardiaques surviennent souvent sans prodrome.","isCorrect":true},{"text":"Les syncopes d’origine vagale ne concernent jamais les jeunes.","isCorrect":false,"justification":"Elles sont fréquentes chez l’adolescent et le jeune adulte."}]$$::jsonb, $$La syncope est un signe d’alerte pouvant traduire une cause bénigne ou une bradyarythmie sévère.$$, 7, '2025-11-02T17:52:04.627Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0dd78003mk498nvmor8zt', 'cmhi0dd6u0034k498qlt0eb1j', $$Hémoptysie et toux cardiaque$$, $$[{"text":"L’hémoptysie franche est typique du rétrécissement mitral.","isCorrect":true},{"text":"La toux cardiaque est sèche, nocturne et irritative.","isCorrect":true},{"text":"L’hémoptysie abondante signe toujours un cancer bronchique.","isCorrect":false,"justification":"Elle peut aussi provenir d’un OAP ou d’une péricardite hémorragique."},{"text":"La toux de l’insuffisance cardiaque gauche s’accompagne de râles humides.","isCorrect":true},{"text":"Une toux matinale isolée évoque une angine de poitrine.","isCorrect":false,"justification":"Elle évoque une cause respiratoire."}]$$::jsonb, $$L’hémoptysie et la toux doivent être interprétées selon le contexte ; en cardiologie, elles traduisent souvent la congestion pulmonaire.$$, 8, '2025-11-02T17:52:04.629Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0dd7a003ok498lkofecq4', 'cmhi0dd6u0034k498qlt0eb1j', $$Œdèmes des membres inférieurs$$, $$[{"text":"Les œdèmes cardiaques débutent souvent au niveau des chevilles.","isCorrect":true},{"text":"Ils sont plus marqués le matin au réveil.","isCorrect":false,"justification":"Ils augmentent le soir, par stase veineuse prolongée."},{"text":"Ils sont symétriques et prennent le godet.","isCorrect":true},{"text":"Ils peuvent être unilatéraux dans l’insuffisance cardiaque droite.","isCorrect":false,"justification":"Unilatéral → suspecter une thrombose veineuse."},{"text":"Ils s’associent souvent à une prise de poids.","isCorrect":true}]$$::jsonb, $$Les œdèmes bilatéraux et prenant le godet sont typiques de l’insuffisance cardiaque droite et doivent faire rechercher une stase veineuse systémique.$$, 9, '2025-11-02T17:52:04.630Z');
INSERT INTO "Chapter" (id, "subjectId", title, description, "orderIndex", "pdfUrl", "createdAt") VALUES ('cmhi0dd7c003qk498woxtjy9p', 'cmhi0dd2g0000k498vt34o2z3', $$Chapitre 4 – Sémiologie vasculaire$$, NULL, 7, NULL, '2025-11-02T17:52:04.633Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0dd7e003sk4986o82cf1a', 'cmhi0dd7c003qk498woxtjy9p', $$Examen artériel : principes généraux$$, $$[{"text":"L’examen artériel se fait toujours en position debout.","isCorrect":false,"justification":"Il se fait en décubitus ou assis pour bien palper les artères périphériques."},{"text":"Le pouls radial doit être palpé bilatéralement et comparé.","isCorrect":true},{"text":"La régularité du pouls renseigne sur le rythme cardiaque.","isCorrect":true},{"text":"Le temps de recoloration cutanée normal est supérieur à 10 secondes.","isCorrect":false,"justification":"Il est inférieur à 6 secondes."},{"text":"Un pouls asymétrique doit toujours faire suspecter une atteinte artérielle.","isCorrect":true}]$$::jsonb, $$L’examen artériel est bilatéral et comparatif, essentiel pour détecter les troubles de perfusion et les asymétries pathologiques.$$, 0, '2025-11-02T17:52:04.634Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0dd7f003uk498fb5hpltf', 'cmhi0dd7c003qk498woxtjy9p', $$Artériopathie oblitérante des membres inférieurs$$, $$[{"text":"La claudication intermittente est un signe précoce d’artériopathie.","isCorrect":true},{"text":"Le refroidissement des extrémités est fréquent.","isCorrect":true},{"text":"L’index de pression systolique (IPS) est supérieur à 1,3 dans les formes sévères.","isCorrect":false,"justification":"Il est inférieur à 0,9, parfois < 0,5 dans les formes graves."},{"text":"Les douleurs de décubitus sont caractéristiques des stades avancés.","isCorrect":true},{"text":"Les pouls périphériques sont amplifiés.","isCorrect":false,"justification":"Ils sont diminués ou abolis."}]$$::jsonb, $$L’artériopathie oblitérante se traduit par une ischémie chronique, avec claudication, pouls diminués et IPS bas.$$, 1, '2025-11-02T17:52:04.636Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0dd7i003wk4980rs7v5e4', 'cmhi0dd7c003qk498woxtjy9p', $$Ulcères artériels et veineux$$, $$[{"text":"Les ulcères veineux sont toujours situés aux orteils.","isCorrect":false,"justification":"Ils sont préférentiellement malléolaires internes."},{"text":"Les ulcères artériels sont indolores.","isCorrect":false,"justification":"Ils sont très douloureux, surtout la nuit."},{"text":"Les ulcères veineux ont des bords nets et creusants.","isCorrect":false,"justification":"Ils ont des bords irréguliers et peu profonds."},{"text":"Les ulcères artériels sont accompagnés d’un œdème important.","isCorrect":false,"justification":"L’œdème est minime, car la perfusion est réduite."},{"text":"Les ulcères veineux cicatrisent rapidement sans traitement.","isCorrect":false,"justification":"Ils évoluent lentement et nécessitent compression et soins locaux."}]$$::jsonb, $$Les ulcères veineux et artériels se différencient par leur siège, douleur et bordure ; leur distinction oriente la prise en charge.$$, 2, '2025-11-02T17:52:04.638Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0dd7j003yk498xcn7tg7r', 'cmhi0dd7c003qk498woxtjy9p', $$Thrombose veineuse profonde (TVP)$$, $$[{"text":"La douleur unilatérale du mollet est un signe évocateur.","isCorrect":true},{"text":"Le signe de Homans correspond à une douleur du mollet à la dorsiflexion du pied.","isCorrect":true},{"text":"L’œdème est souvent bilatéral.","isCorrect":false,"justification":"Il est unilatéral, du côté atteint."},{"text":"La fièvre est constante.","isCorrect":false,"justification":"Elle est inconstante, présente dans les formes inflammatoires."},{"text":"Une chirurgie récente est un facteur de risque majeur.","isCorrect":true}]$$::jsonb, $$La TVP est une urgence diagnostique, marquée par douleur, œdème unilatéral et contexte thrombo-embolique.$$, 3, '2025-11-02T17:52:04.640Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0dd7l0040k498u5oxs2gm', 'cmhi0dd7c003qk498woxtjy9p', $$Insuffisance veineuse chronique$$, $$[{"text":"Elle se manifeste par une sensation de lourdeur des jambes.","isCorrect":true},{"text":"Les varices sont souvent visibles à l’examen.","isCorrect":true},{"text":"Les douleurs sont majorées par la position debout prolongée.","isCorrect":true},{"text":"Le port de bas de contention aggrave la stase veineuse.","isCorrect":false,"justification":"Il améliore le retour veineux et soulage les symptômes."},{"text":"La marche quotidienne est contre-indiquée.","isCorrect":false,"justification":"Elle est fortement recommandée pour activer la pompe musculaire."}]$$::jsonb, $$L’insuffisance veineuse chronique résulte d’une stase prolongée et bénéficie d’une compression adaptée et d’une activité physique régulière.$$, 4, '2025-11-02T17:52:04.642Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0dd7n0042k49832fvvbf4', 'cmhi0dd7c003qk498woxtjy9p', $$Anévrysme de l’aorte abdominale$$, $$[{"text":"Il s’agit d’une dilatation localisée et permanente de l’aorte abdominale.","isCorrect":true},{"text":"Il touche surtout les sujets jeunes.","isCorrect":false,"justification":"Il concerne les hommes âgés (> 60 ans)."},{"text":"Il peut être asymptomatique.","isCorrect":true},{"text":"Le principal risque est la thrombose distale.","isCorrect":false,"justification":"Le risque majeur est la rupture, souvent fatale."},{"text":"Le dépistage se fait par échographie abdominale.","isCorrect":true}]$$::jsonb, $$L’anévrysme de l’aorte abdominale est une découverte fréquente chez l’homme âgé et nécessite un suivi échographique régulier.$$, 5, '2025-11-02T17:52:04.644Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0dd7p0044k498eyzrvpn6', 'cmhi0dd7c003qk498woxtjy9p', $$Phénomène de Raynaud$$, $$[{"text":"Il s’agit d’un trouble paroxystique de la circulation digitale.","isCorrect":true},{"text":"Il est déclenché par le froid ou le stress.","isCorrect":true},{"text":"Il évolue en trois phases : blanche, bleue, rouge.","isCorrect":true},{"text":"Il ne survient jamais chez la femme.","isCorrect":false,"justification":"Il est plus fréquent chez la femme jeune."},{"text":"Il est toujours secondaire à une maladie auto-immune.","isCorrect":false,"justification":"Il peut être primaire (idiopathique) ou secondaire."}]$$::jsonb, $$Le phénomène de Raynaud est un trouble vasospastique fréquent, souvent bénin, mais pouvant révéler une maladie systémique.$$, 6, '2025-11-02T17:52:04.645Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0dd7r0046k498mrmfj4ts', 'cmhi0dd7c003qk498woxtjy9p', $$Ischémie aiguë de membre$$, $$[{"text":"Elle est toujours bilatérale.","isCorrect":false,"justification":"Elle est le plus souvent unilatérale, liée à une embolie ou thrombose."},{"text":"Elle associe douleur, froideur et pâleur du membre.","isCorrect":true},{"text":"Les pouls sont renforcés.","isCorrect":false,"justification":"Ils sont absents."},{"text":"L’installation est lente et progressive.","isCorrect":false,"justification":"Elle est brutale."},{"text":"Le pronostic vital n’est jamais engagé.","isCorrect":false,"justification":"Le risque est l’amputation ou la nécrose."}]$$::jsonb, $$L’ischémie aiguë du membre est une urgence vasculaire absolue nécessitant un diagnostic et une revascularisation immédiate.$$, 7, '2025-11-02T17:52:04.647Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0dd7s0048k498o0st3fdy', 'cmhi0dd7c003qk498woxtjy9p', $$Thrombophlébite superficielle$$, $$[{"text":"Elle se manifeste par une douleur sur un trajet veineux superficiel.","isCorrect":true},{"text":"On observe un cordon induré, rouge et sensible.","isCorrect":true},{"text":"Elle expose à un risque élevé d’embolie pulmonaire.","isCorrect":false,"justification":"Ce risque est faible, contrairement à la TVP."},{"text":"Le traitement repose sur les anti-inflammatoires et la marche.","isCorrect":true},{"text":"Elle est souvent favorisée par une varice.","isCorrect":true}]$$::jsonb, $$La thrombophlébite superficielle est bénigne, mais peut s’étendre vers le réseau profond, justifiant une surveillance clinique.$$, 8, '2025-11-02T17:52:04.649Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0dd7u004ak4984evfbjok', 'cmhi0dd7c003qk498woxtjy9p', $$Examen veineux des membres$$, $$[{"text":"Il doit être bilatéral et comparatif.","isCorrect":true},{"text":"L’inspection recherche des varices apparentes.","isCorrect":true},{"text":"La position debout permet d’évaluer le remplissage veineux.","isCorrect":true},{"text":"La palpation du mollet est contre-indiquée.","isCorrect":false,"justification":"Elle peut être faite doucement sauf suspicion de TVP aiguë."},{"text":"Le reflux veineux normal est supérieur à 3 secondes.","isCorrect":false,"justification":"Un reflux > 0,5 s est déjà pathologique."}]$$::jsonb, $$L’examen veineux repose sur l’observation, la palpation et la recherche de reflux ; il permet de dépister varices et insuffisance veineuse.$$, 9, '2025-11-02T17:52:04.650Z');
INSERT INTO "Chapter" (id, "subjectId", title, description, "orderIndex", "pdfUrl", "createdAt") VALUES ('cmhi0dd7w004ck4983jf4tdiv', 'cmhi0dd2g0000k498vt34o2z3', $$Chapitre 5 – Endocardites infectieuses (E.I.)$$, NULL, 8, NULL, '2025-11-02T17:52:04.653Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0dd7y004ek4985702pnqi', 'cmhi0dd7w004ck4983jf4tdiv', $$Définition et nature de la maladie$$, $$[{"text":"L’endocardite infectieuse est une inflammation aseptique du myocarde.","isCorrect":false,"justification":"Elle est infectieuse, non aseptique, et touche l’endocarde."},{"text":"C’est une infection de l’endocarde valvulaire ou pariétal.","isCorrect":true},{"text":"Elle se complique rarement d’embolie.","isCorrect":false,"justification":"Les emboles septiques sont fréquents (cerveau, reins, rate)."},{"text":"Elle est due à la greffe d’un micro-organisme sur une valve cardiaque.","isCorrect":true},{"text":"C’est une affection bénigne à évolution rapide.","isCorrect":false,"justification":"Elle est grave, souvent mortelle sans traitement rapide."}]$$::jsonb, $$L’E.I. est une infection grave du revêtement endocardique, souvent valvulaire, associée à une septicémie et un risque embolique.$$, 0, '2025-11-02T17:52:04.654Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0dd80004gk49831pyeplv', 'cmhi0dd7w004ck4983jf4tdiv', $$Germes responsables$$, $$[{"text":"Les streptocoques sont une cause fréquente d’endocardite.","isCorrect":true},{"text":"Les staphylocoques sont souvent responsables des formes aiguës.","isCorrect":true},{"text":"Les bacilles Gram négatifs peuvent exceptionnellement être en cause.","isCorrect":true},{"text":"Les levures sont impossibles à impliquer.","isCorrect":false,"justification":"Les Candida et Aspergillus peuvent infecter les valves prothétiques."},{"text":"Les hémocultures négatives excluent le diagnostic d’endocardite.","isCorrect":false,"justification":"On parle alors d’E.I. à hémocultures négatives (souvent Coxiella, Bartonella)."}]$$::jsonb, $$Les streptocoques et staphylocoques dominent, mais il faut rechercher des germes rares dans les formes atypiques ou sur prothèse.$$, 1, '2025-11-02T17:52:04.656Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0dd82004ik498xnxx5ml2', 'cmhi0dd7w004ck4983jf4tdiv', $$Facteurs de risque$$, $$[{"text":"Les valvulopathies rhumatismales sont des terrains à risque.","isCorrect":true},{"text":"La présence d’un cathéter urinaire n’a aucun rapport.","isCorrect":false,"justification":"C’est une porte d’entrée fréquente pour les bactéries."},{"text":"Les prothèses valvulaires sont protectrices.","isCorrect":false,"justification":"Elles augmentent considérablement le risque d’infection."},{"text":"L’usage intraveineux de drogues n’a aucun lien.","isCorrect":false,"justification":"Il favorise les endocardites du cœur droit."},{"text":"Les cardiopathies congénitales corrigées ne nécessitent pas de surveillance.","isCorrect":false,"justification":"Elles restent à haut risque infectieux."}]$$::jsonb, $$Les valvulopathies, les prothèses et les gestes invasifs sont les principaux terrains favorisants l’endocardite.$$, 2, '2025-11-02T17:52:04.658Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0dd83004kk4980q2t5noe', 'cmhi0dd7w004ck4983jf4tdiv', $$Portes d’entrée infectieuses$$, $$[{"text":"Les foyers dentaires sont une cause classique d’endocardite.","isCorrect":true},{"text":"Les infections ORL peuvent servir de point de départ.","isCorrect":true},{"text":"Les infections urinaires peuvent en être responsables.","isCorrect":true},{"text":"Les plaies cutanées ou les cathéters peuvent être des portes d’entrée.","isCorrect":true},{"text":"Les gestes gynécologiques peuvent aussi provoquer une bactériémie.","isCorrect":true}]$$::jsonb, $$Toute brèche infectieuse, notamment bucco-dentaire, cutanée ou urinaire, peut être à l’origine d’une endocardite infectieuse.$$, 3, '2025-11-02T17:52:04.660Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0dd85004mk498uuik21sw', 'cmhi0dd7w004ck4983jf4tdiv', $$Signes cardiaques$$, $$[{"text":"Le souffle cardiaque est présent dans la majorité des cas.","isCorrect":true},{"text":"L’apparition d’un nouveau souffle est sans importance.","isCorrect":false,"justification":"Elle est hautement évocatrice d’une E.I. active."},{"text":"Le souffle d’insuffisance aortique est fréquent.","isCorrect":true},{"text":"L’auscultation cardiaque est inutile pour le diagnostic.","isCorrect":false,"justification":"Elle reste indispensable."},{"text":"L’auscultation pulmonaire suffit pour éliminer le diagnostic.","isCorrect":false,"justification":"Les signes cardiaques sont centraux dans l’E.I."}]$$::jsonb, $$La modification ou l’apparition d’un souffle cardiaque est un signe d’alerte majeur dans le diagnostic d’endocardite.$$, 4, '2025-11-02T17:52:04.662Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0dd87004ok498sp94fyuk', 'cmhi0dd7w004ck4983jf4tdiv', $$Signes périphériques$$, $$[{"text":"Les pétéchies cutanées et conjonctivales sont fréquentes.","isCorrect":true},{"text":"Les tâches de Janeway sont douloureuses et surélevées.","isCorrect":false,"justification":"Elles sont indolores et planes, d’origine embolique."},{"text":"Les nodosités d’Osler sont douloureuses.","isCorrect":true},{"text":"La splénomégalie est rare dans l’endocardite.","isCorrect":false,"justification":"Elle est fréquente (30–40 %)."},{"text":"L’hippocratisme digital peut se voir dans les formes chroniques.","isCorrect":true}]$$::jsonb, $$Les signes périphériques reflètent la dissémination septique et la vascularite : pétéchies, nodosités, splénomégalie.$$, 5, '2025-11-02T17:52:04.664Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0dd89004qk49861hl4syb', 'cmhi0dd7w004ck4983jf4tdiv', $$Examens complémentaires$$, $$[{"text":"L’hémoculture est inutile si la fièvre est intermittente.","isCorrect":false,"justification":"Elle doit être réalisée pendant les pics fébriles."},{"text":"L’échocardiographie Doppler est fondamentale pour confirmer le diagnostic.","isCorrect":true},{"text":"L’échographie ne permet jamais de visualiser les végétations.","isCorrect":false,"justification":"Elle les détecte directement, surtout en mode transœsophagien."},{"text":"L’électrocardiogramme suffit pour poser le diagnostic.","isCorrect":false,"justification":"Il n’est que complémentaire."},{"text":"La radiographie thoracique est toujours normale.","isCorrect":false,"justification":"Elle peut montrer cardiomégalie ou œdème pulmonaire."}]$$::jsonb, $$L’échocardiographie et les hémocultures sont les piliers diagnostiques de l’endocardite infectieuse.$$, 6, '2025-11-02T17:52:04.665Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0dd8b004sk4982n6dylux', 'cmhi0dd7w004ck4983jf4tdiv', $$Diagnostic positif$$, $$[{"text":"La fièvre est absente dans la majorité des cas.","isCorrect":false,"justification":"Elle est quasi constante dans les E.I. actives."},{"text":"Le diagnostic repose uniquement sur la clinique.","isCorrect":false,"justification":"Il repose sur les critères de Duke : clinique + hémocultures + imagerie."},{"text":"L’endocardite peut être confirmée sans hémoculture ni échographie.","isCorrect":false,"justification":"Ces examens sont indispensables."},{"text":"La présence d’un souffle ancien suffit au diagnostic.","isCorrect":false,"justification":"Il faut prouver une infection active."},{"text":"La sérologie est inutile dans les formes à hémocultures négatives.","isCorrect":false,"justification":"Elle permet d’identifier Coxiella ou Bartonella."}]$$::jsonb, $$Le diagnostic d’endocardite est toujours microbiologique et échographique, jamais purement clinique.$$, 7, '2025-11-02T17:52:04.667Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0dd8c004uk4981c274bsi', 'cmhi0dd7w004ck4983jf4tdiv', $$Complications$$, $$[{"text":"Les embolies systémiques sont une complication fréquente.","isCorrect":true},{"text":"Les abcès myocardiques sont rares mais graves.","isCorrect":true},{"text":"L’insuffisance cardiaque est une complication tardive.","isCorrect":true},{"text":"L’anévrysme mycotique est une dilatation artérielle infectée.","isCorrect":true},{"text":"Les troubles du rythme sont exclus dans l’E.I.","isCorrect":false,"justification":"Les bloc auriculo-ventriculaires peuvent survenir dans les formes septales."}]$$::jsonb, $$L’E.I. entraîne des complications infectieuses, emboliques et cardiaques graves, justifiant une prise en charge urgente.$$, 8, '2025-11-02T17:52:04.669Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0dd8e004wk498drjaho4z', 'cmhi0dd7w004ck4983jf4tdiv', $$Traitement et prévention$$, $$[{"text":"Le traitement antibiotique doit être précoce et prolongé.","isCorrect":true},{"text":"Il repose sur des associations bactéricides selon l’antibiogramme.","isCorrect":true},{"text":"Les formes graves nécessitent une chirurgie valvulaire.","isCorrect":true},{"text":"La prévention bucco-dentaire est essentielle chez les patients à risque.","isCorrect":true},{"text":"L’antibioprophylaxie est indiquée avant certains gestes invasifs chez les porteurs de prothèses valvulaires.","isCorrect":true}]$$::jsonb, $$Le traitement de l’endocardite est long, combiné (médical et chirurgical) et la prévention bucco-dentaire reste la meilleure arme contre la récidive.$$, 9, '2025-11-02T17:52:04.671Z');
INSERT INTO "Chapter" (id, "subjectId", title, description, "orderIndex", "pdfUrl", "createdAt") VALUES ('cmhi0dd8h004yk49858j34ark', 'cmhi0dd2g0000k498vt34o2z3', $$Chapitre 6 – L’Électrocardiogramme (ECG)$$, NULL, 9, NULL, '2025-11-02T17:52:04.673Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0dd8i0050k498qc0ph1t6', 'cmhi0dd8h004yk49858j34ark', $$Définition et principes$$, $$[{"text":"L’ECG enregistre l’activité électrique du cœur à la surface du corps.","isCorrect":true},{"text":"Il ne renseigne pas sur la conduction intracardiaque.","isCorrect":false,"justification":"Il permet au contraire d’évaluer la conduction auriculo-ventriculaire et intraventriculaire."},{"text":"Il est réalisé uniquement en position debout.","isCorrect":false,"justification":"L’enregistrement standard se fait en décubitus dorsal."},{"text":"Il détecte les troubles du rythme, les ischémies et hypertrophies.","isCorrect":true},{"text":"Il ne nécessite pas de calibration spécifique.","isCorrect":false,"justification":"Il faut étalonner : 1 mV = 10 mm."}]$$::jsonb, $$L’ECG est un examen non invasif essentiel pour l’analyse de la conduction, du rythme et de la repolarisation cardiaque.$$, 0, '2025-11-02T17:52:04.675Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0dd8k0052k4982cftqo0h', 'cmhi0dd8h004yk49858j34ark', $$Critères de validité$$, $$[{"text":"L’identité du patient doit figurer sur le tracé.","isCorrect":true},{"text":"La vitesse du papier est fixée à 25 mm/s.","isCorrect":true},{"text":"Une vitesse différente fausse l’interprétation.","isCorrect":true},{"text":"La calibration est inutile sur les appareils modernes.","isCorrect":false,"justification":"Elle demeure obligatoire pour vérifier les amplitudes."},{"text":"Le tracé doit comporter 6 dérivations standards uniquement.","isCorrect":false,"justification":"L’ECG standard comporte 12 dérivations."}]$$::jsonb, $$Un ECG valide doit être correctement identifié, calibré et enregistré selon le standard de 12 dérivations.$$, 1, '2025-11-02T17:52:04.677Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0dd8m0054k498fvdzjjiu', 'cmhi0dd8h004yk49858j34ark', $$Dérivations$$, $$[{"text":"Les dérivations standards des membres sont D1, D2 et D3.","isCorrect":true},{"text":"Les dérivations unipolaires sont aVR, aVL et aVF.","isCorrect":true},{"text":"Les dérivations précordiales vont de V1 à V6.","isCorrect":true},{"text":"Les dérivations V7, V8 et V9 explorent la face postérieure du cœur.","isCorrect":true},{"text":"Les dérivations droites (V3R, V4R) sont inutiles.","isCorrect":false,"justification":"Elles sont indispensables pour explorer le ventricule droit."}]$$::jsonb, $$Les 12 dérivations standard explorent toutes les faces du cœur, et des dérivations supplémentaires affinent le diagnostic topographique.$$, 2, '2025-11-02T17:52:04.679Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0dd8o0056k4988y0s5r62', 'cmhi0dd8h004yk49858j34ark', $$Onde P$$, $$[{"text":"Elle correspond à la dépolarisation auriculaire.","isCorrect":true},{"text":"Elle est toujours négative en D2.","isCorrect":false,"justification":"Elle est positive en D2, D3 et aVF."},{"text":"Sa durée normale est inférieure à 0,12 s.","isCorrect":true},{"text":"Une onde P bifide traduit une hypertrophie auriculaire gauche.","isCorrect":true},{"text":"L’absence d’onde P évoque une tachycardie sinusale.","isCorrect":false,"justification":"Elle évoque plutôt une fibrillation auriculaire."}]$$::jsonb, $$L’onde P renseigne sur l’activité auriculaire ; son absence ou sa déformation permet d’identifier des troubles de conduction ou d’hypertrophie.$$, 3, '2025-11-02T17:52:04.680Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0dd8p0058k4983v6qico8', 'cmhi0dd8h004yk49858j34ark', $$Intervalle PR$$, $$[{"text":"Il s’étend du début de P au début de QRS.","isCorrect":true},{"text":"Sa valeur normale est comprise entre 0,12 et 0,20 s.","isCorrect":true},{"text":"Un allongement traduit un bloc auriculo-ventriculaire du 1er degré.","isCorrect":true},{"text":"Un PR court (< 0,12 s) évoque un syndrome de pré-excitation.","isCorrect":true},{"text":"Le PR n’a aucune valeur clinique.","isCorrect":false,"justification":"Il est fondamental pour évaluer la conduction A-V."}]$$::jsonb, $$L’intervalle PR reflète la conduction entre oreillettes et ventricules, permettant d’identifier les blocs ou les voies accessoires.$$, 4, '2025-11-02T17:52:04.682Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0dd8r005ak498idyfpe5f', 'cmhi0dd8h004yk49858j34ark', $$Complexe QRS$$, $$[{"text":"Il correspond à la dépolarisation ventriculaire.","isCorrect":true},{"text":"Sa durée normale est ≤ 0,12 s.","isCorrect":true},{"text":"Un QRS large évoque un trouble de conduction intraventriculaire.","isCorrect":true},{"text":"Sa morphologie permet de calculer l’axe électrique du cœur.","isCorrect":true},{"text":"L’amplitude du QRS est identique chez tous les patients.","isCorrect":false,"justification":"Elle varie selon la morphologie, la corpulence et l’hypertrophie."}]$$::jsonb, $$Le QRS analyse la dépolarisation ventriculaire ; sa largeur et son orientation guident le diagnostic des blocs et hypertrophies.$$, 5, '2025-11-02T17:52:04.683Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0dd8t005ck4982jcgen10', 'cmhi0dd8h004yk49858j34ark', $$Segment ST$$, $$[{"text":"Il s’étend de la fin du QRS au début de T.","isCorrect":true},{"text":"Il est normalement isoélectrique.","isCorrect":true},{"text":"Un sus-décalage traduit une ischémie sous-endocardique.","isCorrect":false,"justification":"Il indique une lésion sous-épicardique (ST+)."},{"text":"Un sous-décalage traduit une ischémie sous-endocardique.","isCorrect":true},{"text":"Le ST n’a pas d’intérêt en cardiologie d’urgence.","isCorrect":false,"justification":"Son analyse est capitale pour le diagnostic d’infarctus aigu."}]$$::jsonb, $$Les variations du segment ST sont au cœur du diagnostic des syndromes coronariens aigus.$$, 6, '2025-11-02T17:52:04.685Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0dd8u005ek498werejy9m', 'cmhi0dd8h004yk49858j34ark', $$Onde T$$, $$[{"text":"Elle représente la repolarisation ventriculaire.","isCorrect":true},{"text":"Elle est toujours négative en dérivation D2.","isCorrect":false,"justification":"Elle est positive en D2, D3, aVF, V4–V6."},{"text":"Une onde T inversée peut traduire une ischémie.","isCorrect":true},{"text":"Une onde T ample et pointue évoque une hyperkaliémie.","isCorrect":true},{"text":"L’onde T est ininterprétable chez l’adulte.","isCorrect":false,"justification":"Elle est très utile pour détecter les anomalies de repolarisation."}]$$::jsonb, $$L’onde T est un indicateur sensible des troubles métaboliques, ischémiques ou ioniques.$$, 7, '2025-11-02T17:52:04.687Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0dd8w005gk498edyfv2sx', 'cmhi0dd8h004yk49858j34ark', $$Intervalle QT$$, $$[{"text":"Il correspond à la systole électrique (dépolarisation + repolarisation).","isCorrect":true},{"text":"Il est indépendant de la fréquence cardiaque.","isCorrect":false,"justification":"Il varie avec la fréquence, d’où le QT corrigé (QTc)."},{"text":"Le QTc normal est compris entre 350 et 440 ms.","isCorrect":true},{"text":"Un QT allongé prédispose aux torsades de pointes.","isCorrect":true},{"text":"Un QT court n’a aucune conséquence.","isCorrect":false,"justification":"Il peut provoquer des arythmies malignes."}]$$::jsonb, $$Le QT corrigé est un paramètre pronostique majeur ; son allongement expose aux tachyarythmies ventriculaires.$$, 8, '2025-11-02T17:52:04.688Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0dd8y005ik4983tnkerlv', 'cmhi0dd8h004yk49858j34ark', $$Hypertrophie auriculaire et ventriculaire$$, $$[{"text":"Une onde P pointue et haute traduit une hypertrophie auriculaire droite.","isCorrect":true},{"text":"Une onde P bifide évoque une hypertrophie auriculaire gauche.","isCorrect":true},{"text":"Le critère de Sokolov-Lyon sert au diagnostic d’hypertrophie ventriculaire gauche.","isCorrect":true},{"text":"Une onde R > 35 mm en V5-V6 confirme une HVG.","isCorrect":true},{"text":"L’axe dévié à gauche est sans rapport avec une HVG.","isCorrect":false,"justification":"Il en est un signe indirect fréquent."}]$$::jsonb, $$Les hypertrophies modifient l’amplitude et l’axe des QRS, constituant des signes indirects de surcharge cavitaire.$$, 9, '2025-11-02T17:52:04.690Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0dd8z005kk498c5wgvlbu', 'cmhi0dd8h004yk49858j34ark', $$Ischémie, lésion et nécrose myocardique$$, $$[{"text":"L’ischémie se manifeste par des anomalies de l’onde T.","isCorrect":true},{"text":"La lésion par un décalage du ST.","isCorrect":true},{"text":"La nécrose par l’apparition d’une onde Q pathologique.","isCorrect":true},{"text":"Le ST- sous-décalé traduit une ischémie sous-épicardique.","isCorrect":false,"justification":"C’est une ischémie sous-endocardique."},{"text":"Une onde Q de nécrose disparaît toujours après guérison.","isCorrect":false,"justification":"Elle peut persister à vie."}]$$::jsonb, $$L’évolution ECG d’un infarctus passe par trois stades : ischémie (T), lésion (ST) et nécrose (Q).$$, 10, '2025-11-02T17:52:04.691Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0dd91005mk49814ur1d8k', 'cmhi0dd8h004yk49858j34ark', $$Troubles de conduction intraventriculaire$$, $$[{"text":"Un QRS > 0,12 s évoque un bloc de branche.","isCorrect":true},{"text":"Le bloc de branche gauche montre un QRS en M en V1-V2.","isCorrect":false,"justification":"C’est le bloc droit qui donne cet aspect."},{"text":"Le bloc gauche donne un QRS élargi avec R ample en V5-V6.","isCorrect":true},{"text":"Le bloc droit montre une onde S large en DI et V6.","isCorrect":true},{"text":"Le bloc gauche est toujours bénin.","isCorrect":false,"justification":"Il peut masquer un infarctus aigu."}]$$::jsonb, $$Les blocs de branche modifient la morphologie des QRS et orientent sur le siège de l’anomalie de conduction.$$, 11, '2025-11-02T17:52:04.693Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0dd93005ok498zsiy7o1g', 'cmhi0dd8h004yk49858j34ark', $$Troubles de conduction auriculo-ventriculaire$$, $$[{"text":"Le bloc du 1er degré correspond à un PR allongé.","isCorrect":true},{"text":"Le bloc du 2e degré type I montre un allongement progressif du PR.","isCorrect":true},{"text":"Le bloc du 2e degré type II se traduit par un blocage soudain de P.","isCorrect":true},{"text":"Le bloc complet présente une dissociation entre P et QRS.","isCorrect":true},{"text":"Les blocs A-V n’ont jamais de retentissement clinique.","isCorrect":false,"justification":"Les formes avancées peuvent provoquer des syncopes."}]$$::jsonb, $$Les blocs A-V témoignent d’un ralentissement de la conduction ; leur gravité dépend du degré et du retentissement hémodynamique.$$, 12, '2025-11-02T17:52:04.695Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0dd94005qk498ofghvmx0', 'cmhi0dd8h004yk49858j34ark', $$Troubles du rythme$$, $$[{"text":"La tachycardie sinusale se définit par une FC > 100 bpm.","isCorrect":true},{"text":"La bradycardie sinusale < 60 bpm est toujours pathologique.","isCorrect":false,"justification":"Elle est physiologique chez le sportif."},{"text":"Le flutter auriculaire montre des ondes F en dents de scie.","isCorrect":true},{"text":"La fibrillation auriculaire donne un tracé totalement irrégulier.","isCorrect":true},{"text":"La fibrillation ventriculaire conserve une efficacité circulatoire.","isCorrect":false,"justification":"Elle provoque un arrêt cardiaque immédiat."}]$$::jsonb, $$Les arythmies doivent être rapidement identifiées à l’ECG ; certaines sont bénignes, d’autres immédiatement vitales.$$, 13, '2025-11-02T17:52:04.697Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0dd96005sk498vbgh4w7q', 'cmhi0dd8h004yk49858j34ark', $$Intérêt clinique de l’ECG$$, $$[{"text":"L’ECG est l’examen de base en cardiologie.","isCorrect":true},{"text":"Il permet le suivi des traitements anti-arythmiques.","isCorrect":true},{"text":"Il est inutile en médecine d’urgence.","isCorrect":false,"justification":"Il est indispensable pour diagnostiquer un infarctus ou une arythmie."},{"text":"Il oriente le diagnostic de péricardite aiguë.","isCorrect":true},{"text":"Il ne détecte jamais les troubles métaboliques.","isCorrect":false,"justification":"Il révèle hyperkaliémie, hypokaliémie, hypocalcémie."}]$$::jsonb, $$L’ECG reste un outil incontournable : diagnostic, pronostic et suivi thérapeutique en cardiologie clinique.$$, 14, '2025-11-02T17:52:04.698Z');
INSERT INTO "Chapter" (id, "subjectId", title, description, "orderIndex", "pdfUrl", "createdAt") VALUES ('cmhi0dd98005uk49879jso6f6', 'cmhi0dd2g0000k498vt34o2z3', $$Chapitre 7 – Le Rétrécissement Mitral (RM)$$, NULL, 10, NULL, '2025-11-02T17:52:04.701Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0dd9a005wk498zd7g7fmu', 'cmhi0dd98005uk49879jso6f6', $$Définition et généralités$$, $$[{"text":"Le RM correspond à un obstacle diastolique au remplissage ventriculaire gauche.","isCorrect":true},{"text":"Il est plus fréquent chez la femme.","isCorrect":true},{"text":"Il résulte le plus souvent d’une cause rhumatismale.","isCorrect":true},{"text":"Il s’agit d’un rétrécissement de l’orifice aortique.","isCorrect":false,"justification":"L’obstacle siège au niveau de la valve mitrale."},{"text":"La surface mitrale normale est de 4 à 6 cm².","isCorrect":true}]$$::jsonb, $$Le RM est une valvulopathie post-rhumatismale entraînant une gêne au remplissage du ventricule gauche.$$, 0, '2025-11-02T17:52:04.702Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0dd9b005yk498ot8ajn34', 'cmhi0dd98005uk49879jso6f6', $$Étiologies$$, $$[{"text":"Le rhumatisme articulaire aigu est la cause la plus fréquente.","isCorrect":true},{"text":"La calcification dégénérative du sujet âgé peut être responsable.","isCorrect":true},{"text":"Le RM congénital est la forme la plus commune.","isCorrect":false,"justification":"Il est exceptionnel."},{"text":"Le prolapsus mitral provoque habituellement un RM.","isCorrect":false,"justification":"Il entraîne plutôt une insuffisance mitrale."},{"text":"Une endocardite infectieuse peut compliquer un RM préexistant.","isCorrect":true}]$$::jsonb, $$Le RM est majoritairement rhumatismal, mais d’autres causes dégénératives ou infectieuses sont possibles.$$, 1, '2025-11-02T17:52:04.704Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0dd9d0060k498dgo9uhz9', 'cmhi0dd98005uk49879jso6f6', $$Circonstances de découverte$$, $$[{"text":"Il peut être découvert à l’occasion d’un souffle à l’auscultation.","isCorrect":true},{"text":"Il est parfois révélé par un épisode de dyspnée ou d’œdème aigu du poumon.","isCorrect":true},{"text":"La grossesse peut révéler un RM latent.","isCorrect":true},{"text":"Il n’est jamais découvert de façon fortuite.","isCorrect":false,"justification":"Le diagnostic peut être accidentel à l’examen systématique."},{"text":"Il n’est jamais associé à une fibrillation auriculaire.","isCorrect":false,"justification":"Elle est fréquente, surtout dans les formes évoluées."}]$$::jsonb, $$Le RM se révèle souvent par la dyspnée d’effort, un souffle caractéristique ou la survenue d’une arythmie auriculaire.$$, 2, '2025-11-02T17:52:04.705Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0dd9f0062k498ocz5hcl8', 'cmhi0dd98005uk49879jso6f6', $$Signes fonctionnels$$, $$[{"text":"La dyspnée d’effort est le principal symptôme.","isCorrect":true},{"text":"Les palpitations peuvent traduire une fibrillation auriculaire.","isCorrect":true},{"text":"L’hémoptysie d’effort est possible.","isCorrect":true},{"text":"Les douleurs thoraciques sont constantes.","isCorrect":false,"justification":"Elles sont rares et non spécifiques."},{"text":"Les syncopes sont fréquentes au début.","isCorrect":false,"justification":"Elles surviennent tardivement ou exceptionnellement."}]$$::jsonb, $$Le RM se manifeste par une gêne respiratoire d’effort, une orthopnée et des palpitations liées à la surcharge auriculaire.$$, 3, '2025-11-02T17:52:04.707Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0dd9g0064k498c6ovftfq', 'cmhi0dd98005uk49879jso6f6', $$Examen clinique : inspection et palpation$$, $$[{"text":"Le choc de pointe est souvent abaissé et dévié à gauche.","isCorrect":true},{"text":"Un frémissement diastolique apexien peut être perçu.","isCorrect":true},{"text":"L’éclat de B1 au foyer mitral est typique.","isCorrect":true},{"text":"Le signe de Harzer est constant.","isCorrect":false,"justification":"Il traduit une dilatation du ventricule droit, non spécifique du RM."},{"text":"Un frémissement systolique au foyer aortique est caractéristique du RM.","isCorrect":false,"justification":"Il correspond plutôt à une sténose aortique."}]$$::jsonb, $$Le RM produit des signes palpatoires spécifiques à l’apex : frémissement cataire et éclat de B1.$$, 4, '2025-11-02T17:52:04.708Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0dd9i0066k498t04t126v', 'cmhi0dd98005uk49879jso6f6', $$Auscultation cardiaque$$, $$[{"text":"Le souffle diastolique du RM débute après un claquement d’ouverture mitrale.","isCorrect":true},{"text":"Il est holosystolique.","isCorrect":false,"justification":"Il est diastolique et roulant."},{"text":"Il est entendu à la pointe, en décubitus latéral gauche.","isCorrect":true},{"text":"Il irradie vers l’aisselle.","isCorrect":true},{"text":"Le renforcement présystolique est absent en rythme sinusal.","isCorrect":false,"justification":"Il est présent si le rythme est sinusal et disparaît en FA."}]$$::jsonb, $$Le souffle diastolique du RM, précédé d’un claquement d’ouverture, constitue un signe auscultatoire pathognomonique.$$, 5, '2025-11-02T17:52:04.711Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0dd9k0068k4980ekxae2w', 'cmhi0dd98005uk49879jso6f6', $$Examens complémentaires$$, $$[{"text":"L’échocardiographie permet de mesurer la surface valvulaire.","isCorrect":true},{"text":"Elle visualise l’épaississement et la calcification des valves.","isCorrect":true},{"text":"Le Doppler évalue le gradient de pression OG–VG.","isCorrect":true},{"text":"L’ECG montre souvent une hypertrophie ventriculaire gauche.","isCorrect":false,"justification":"On observe surtout une dilatation auriculaire gauche."},{"text":"La radiographie peut montrer une silhouette mitrale typique.","isCorrect":true}]$$::jsonb, $$L’échocardiographie est l’examen clé du diagnostic et du suivi du RM, complété par ECG et radiographie thoracique.$$, 6, '2025-11-02T17:52:04.712Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0dd9l006ak498rkoryzuo', 'cmhi0dd98005uk49879jso6f6', $$Complications$$, $$[{"text":"La fibrillation auriculaire est la complication la plus fréquente.","isCorrect":true},{"text":"L’œdème pulmonaire est rare dans le RM.","isCorrect":false,"justification":"Il est fréquent lors de décompensation."},{"text":"Les embolies systémiques peuvent compliquer une FA.","isCorrect":true},{"text":"L’endocardite infectieuse ne complique jamais le RM.","isCorrect":false,"justification":"Elle est possible sur valve remaniée."},{"text":"L’hypertension artérielle pulmonaire est une évolution tardive.","isCorrect":true}]$$::jsonb, $$Le RM peut évoluer vers une FA, une HTAP et une embolie artérielle systémique.$$, 7, '2025-11-02T17:52:04.714Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0dd9n006ck498te2lsg39', 'cmhi0dd98005uk49879jso6f6', $$Diagnostic différentiel$$, $$[{"text":"L’insuffisance mitrale donne un souffle diastolique semblable.","isCorrect":false,"justification":"Son souffle est systolique, non diastolique."},{"text":"Le rétrécissement tricuspide peut simuler un RM.","isCorrect":true},{"text":"L’insuffisance aortique peut donner un souffle diastolique apical.","isCorrect":true},{"text":"Le souffle de Graham-Steell accompagne l’HTAP du RM.","isCorrect":true},{"text":"La péricardite constrictive reproduit parfois un pseudo-claquement d’ouverture.","isCorrect":true}]$$::jsonb, $$Le diagnostic différentiel du RM repose sur l’analyse du foyer, du rythme et de la chronologie des souffles.$$, 8, '2025-11-02T17:52:04.715Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0dd9p006ek498oxn5kp85', 'cmhi0dd98005uk49879jso6f6', $$Traitement$$, $$[{"text":"Le traitement diurétique soulage les symptômes congestifs.","isCorrect":true},{"text":"L’anticoagulation est indiquée en cas de fibrillation auriculaire.","isCorrect":true},{"text":"La dilatation percutanée par ballon est une option thérapeutique.","isCorrect":true},{"text":"La chirurgie valvulaire n’a aucun intérêt.","isCorrect":false,"justification":"Elle est indiquée dans les formes sévères ou calcifiées."},{"text":"Le traitement prophylactique du RAA n’a pas d’importance.","isCorrect":false,"justification":"Il est essentiel pour prévenir les récidives rhumatismales."}]$$::jsonb, $$Le traitement du RM combine mesures médicales, prévention rhumatismale et, selon les cas, commissurotomie ou chirurgie valvulaire.$$, 9, '2025-11-02T17:52:04.717Z');
INSERT INTO "Chapter" (id, "subjectId", title, description, "orderIndex", "pdfUrl", "createdAt") VALUES ('cmhi0dd9r006gk4983acf51u5', 'cmhi0dd2g0000k498vt34o2z3', $$Chapitre 8 – L’Insuffisance Mitrale (IM)$$, NULL, 11, NULL, '2025-11-02T17:52:04.719Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0dd9s006ik498uwxa1e6m', 'cmhi0dd9r006gk4983acf51u5', $$Définition et généralités$$, $$[{"text":"L’IM correspond à une fuite systolique du ventricule gauche vers l’oreillette gauche.","isCorrect":true},{"text":"Elle est due à une perte d’étanchéité de la valve mitrale.","isCorrect":true},{"text":"Elle provoque une surcharge volumique du ventricule gauche.","isCorrect":true},{"text":"Elle survient uniquement dans les cardiopathies congénitales.","isCorrect":false,"justification":"Elle est souvent acquise (rhumatisme, endocardite, dégénérescence)."},{"text":"Elle touche uniquement les hommes jeunes.","isCorrect":false,"justification":"Elle peut survenir à tout âge et chez les deux sexes."}]$$::jsonb, $$L’IM est une valvulopathie de fuite entraînant une surcharge de volume et une dilatation progressive du ventricule gauche.$$, 0, '2025-11-02T17:52:04.721Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0dd9u006kk498eszlu08e', 'cmhi0dd9r006gk4983acf51u5', $$Étiologies$$, $$[{"text":"Le rhumatisme articulaire aigu reste la principale cause dans les pays en développement.","isCorrect":true},{"text":"L’endocardite infectieuse peut entraîner une IM aiguë.","isCorrect":true},{"text":"Le prolapsus de la valve mitrale est une cause fréquente dans les pays occidentaux.","isCorrect":true},{"text":"Les causes traumatiques sont impossibles.","isCorrect":false,"justification":"Elles peuvent survenir après rupture de cordage ou traumatisme thoracique."},{"text":"La cardiomyopathie dilatée peut provoquer une IM fonctionnelle.","isCorrect":true}]$$::jsonb, $$L’IM peut être organique (valve altérée) ou fonctionnelle (dilatation annulaire), selon la cause sous-jacente.$$, 1, '2025-11-02T17:52:04.722Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0dd9w006mk4988ibasgl1', 'cmhi0dd9r006gk4983acf51u5', $$Mécanisme physiopathologique$$, $$[{"text":"La fuite mitrale provoque une régurgitation systolique vers l’oreillette gauche.","isCorrect":true},{"text":"Elle augmente la pression dans l’oreillette gauche.","isCorrect":true},{"text":"Elle diminue le volume d’éjection efficace.","isCorrect":true},{"text":"Elle provoque une hypertension pulmonaire secondaire.","isCorrect":true},{"text":"Elle réduit la postcharge du ventricule gauche.","isCorrect":true}]$$::jsonb, $$L’IM chronique induit une surcharge volumique, une dilatation du VG et une hyperpression auriculaire gauche avec HTAP progressive.$$, 2, '2025-11-02T17:52:04.724Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0dd9x006ok498709qz88y', 'cmhi0dd9r006gk4983acf51u5', $$Circonstances de découverte$$, $$[{"text":"Elle est parfois découverte fortuitement par un souffle systolique.","isCorrect":true},{"text":"Elle peut se manifester par une dyspnée d’effort.","isCorrect":true},{"text":"Les palpitations sont fréquentes, souvent liées à la fibrillation auriculaire.","isCorrect":true},{"text":"L’œdème aigu du poumon peut révéler une IM aiguë.","isCorrect":true},{"text":"Elle s’accompagne toujours de syncope brutale.","isCorrect":false,"justification":"Les syncopes sont rares dans l’IM."}]$$::jsonb, $$L’IM peut être silencieuse longtemps avant de se révéler par un souffle, une dyspnée ou un épisode de décompensation aiguë.$$, 3, '2025-11-02T17:52:04.726Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0dda0006qk498x2gx8815', 'cmhi0dd9r006gk4983acf51u5', $$Examen clinique$$, $$[{"text":"Le choc de pointe est souvent dévié vers le bas et la gauche.","isCorrect":true},{"text":"Un frémissement systolique peut être perçu à la pointe.","isCorrect":true},{"text":"Le souffle systolique est holosystolique et en jet de vapeur.","isCorrect":true},{"text":"Il est maximal au foyer mitral et irradie vers l’aisselle.","isCorrect":true},{"text":"Il disparaît toujours en décubitus latéral gauche.","isCorrect":false,"justification":"Il est mieux entendu dans cette position."}]$$::jsonb, $$Le souffle systolique apical irradiant vers l’aisselle est le signe auscultatoire fondamental de l’IM.$$, 4, '2025-11-02T17:52:04.728Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0dda1006sk498zk1rpmv6', 'cmhi0dd9r006gk4983acf51u5', $$Examens complémentaires$$, $$[{"text":"L’ECG montre une hypertrophie auriculaire gauche.","isCorrect":true},{"text":"L’échocardiographie confirme le diagnostic et quantifie la fuite.","isCorrect":true},{"text":"Le Doppler mesure le jet régurgitant.","isCorrect":true},{"text":"La radiographie du thorax montre souvent une cardiomégalie.","isCorrect":true},{"text":"L’IRM cardiaque est inutile dans le suivi.","isCorrect":false,"justification":"Elle est utile pour évaluer la fonction et le volume ventriculaire gauche."}]$$::jsonb, $$L’échographie-Doppler est l’examen de référence, complétée par ECG et imagerie morphologique.$$, 5, '2025-11-02T17:52:04.730Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0dda3006uk498m2ckvcwt', 'cmhi0dd9r006gk4983acf51u5', $$Complications$$, $$[{"text":"La fibrillation auriculaire est une complication fréquente.","isCorrect":true},{"text":"L’insuffisance cardiaque gauche est une évolution possible.","isCorrect":true},{"text":"L’endocardite infectieuse est rare mais possible.","isCorrect":true},{"text":"Les embolies systémiques sont impossibles.","isCorrect":false,"justification":"Elles peuvent survenir en cas de FA associée."},{"text":"La rupture de cordage peut aggraver brutalement la fuite.","isCorrect":true}]$$::jsonb, $$Les principales complications sont la FA, l’insuffisance cardiaque et l’aggravation aiguë de la régurgitation.$$, 6, '2025-11-02T17:52:04.732Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0dda5006wk498i9lnbrz4', 'cmhi0dd9r006gk4983acf51u5', $$Diagnostic différentiel$$, $$[{"text":"L’insuffisance tricuspide donne un souffle systolique apical identique.","isCorrect":false,"justification":"Son souffle est xiphoïdien et augmente à l’inspiration."},{"text":"La sténose aortique provoque un souffle d’éjection au foyer aortique.","isCorrect":true},{"text":"L’IM fonctionnelle se distingue par l’absence de lésion valvulaire.","isCorrect":true},{"text":"Le souffle de l’IM irradie vers la base du cou.","isCorrect":false,"justification":"Il irradie vers l’aisselle gauche."},{"text":"Le prolapsus mitral peut produire un clic systolique et un souffle tardif.","isCorrect":true}]$$::jsonb, $$Le diagnostic différentiel de l’IM repose sur la topographie du souffle et l’analyse échographique.$$, 7, '2025-11-02T17:52:04.733Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0dda7006yk4985abcpswk', 'cmhi0dd9r006gk4983acf51u5', $$Évolution naturelle$$, $$[{"text":"L’IM aiguë entraîne rapidement un œdème pulmonaire.","isCorrect":true},{"text":"L’IM chronique bien tolérée ne nécessite pas de suivi.","isCorrect":false,"justification":"Elle impose un suivi échographique régulier."},{"text":"Elle peut rester longtemps asymptomatique.","isCorrect":true},{"text":"Elle évolue vers une dilatation ventriculaire gauche.","isCorrect":true},{"text":"Elle guérit spontanément avec le temps.","isCorrect":false,"justification":"Elle s’aggrave progressivement sans correction."}]$$::jsonb, $$L’IM chronique évolue lentement vers la dilatation et la défaillance du ventricule gauche, d’où l’importance du suivi.$$, 8, '2025-11-02T17:52:04.735Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0dda90070k498nqii1e1b', 'cmhi0dd9r006gk4983acf51u5', $$Traitement$$, $$[{"text":"Le traitement médical vise à contrôler la congestion et la FA.","isCorrect":true},{"text":"Les diurétiques soulagent la dyspnée.","isCorrect":true},{"text":"Les vasodilatateurs réduisent la régurgitation.","isCorrect":true},{"text":"La plastie mitrale est préférée au remplacement valvulaire quand c’est possible.","isCorrect":true},{"text":"Le traitement chirurgical n’a aucun effet sur le pronostic.","isCorrect":false,"justification":"Il améliore la survie et la fonction ventriculaire."}]$$::jsonb, $$Le traitement associe prise en charge médicale et correction chirurgicale précoce avant l’altération irréversible du ventricule gauche.$$, 9, '2025-11-02T17:52:04.737Z');
INSERT INTO "Chapter" (id, "subjectId", title, description, "orderIndex", "pdfUrl", "createdAt") VALUES ('cmhi0ddab0072k498l8ev0q0v', 'cmhi0dd2g0000k498vt34o2z3', $$Chapitre 9 – L’Insuffisance Aortique (IAo)$$, NULL, 12, NULL, '2025-11-02T17:52:04.739Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddad0074k498gm080okp', 'cmhi0ddab0072k498l8ev0q0v', $$Définition et généralités$$, $$[{"text":"L’IAo est une fuite diastolique de l’aorte vers le ventricule gauche.","isCorrect":true},{"text":"Elle est due à la non-étanchéité des valves sigmoïdes aortiques.","isCorrect":true},{"text":"Elle entraîne une surcharge volumique du ventricule gauche.","isCorrect":true},{"text":"Elle survient toujours brutalement.","isCorrect":false,"justification":"Elle peut être chronique ou aiguë selon la cause."},{"text":"Elle provoque une diminution de la pression différentielle.","isCorrect":false,"justification":"Elle l’augmente (systolique haute, diastolique basse)."}]$$::jsonb, $$L’IAo est une valvulopathie de régurgitation responsable d’une surcharge diastolique du ventricule gauche.$$, 0, '2025-11-02T17:52:04.741Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddaf0076k498k154d8oo', 'cmhi0ddab0072k498l8ev0q0v', $$Étiologies$$, $$[{"text":"Le rhumatisme articulaire aigu est une cause classique.","isCorrect":true},{"text":"L’endocardite infectieuse peut entraîner une IAo aiguë.","isCorrect":true},{"text":"La dissection aortique est une cause d’IAo brutale.","isCorrect":true},{"text":"La spondylarthrite ankylosante peut provoquer une atteinte valvulaire.","isCorrect":true},{"text":"La bicuspidie aortique congénitale ne peut jamais donner d’IAo.","isCorrect":false,"justification":"Elle peut entraîner sténose ou insuffisance aortique selon l’évolution."}]$$::jsonb, $$Les causes d’IAo incluent les atteintes rhumatismales, dégénératives, infectieuses et les maladies du tissu conjonctif.$$, 1, '2025-11-02T17:52:04.743Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddah0078k4980i6oqp8z', 'cmhi0ddab0072k498l8ev0q0v', $$Mécanisme physiopathologique$$, $$[{"text":"Le reflux aortique augmente le volume diastolique du ventricule gauche.","isCorrect":true},{"text":"La postcharge est réduite dans l’IAo.","isCorrect":false,"justification":"Elle est augmentée par la régurgitation continue."},{"text":"La dilatation ventriculaire gauche permet une compensation initiale.","isCorrect":true},{"text":"La surcharge de pression entraîne une hypertrophie concentrique.","isCorrect":true},{"text":"La baisse du débit diastolique aortique réduit la perfusion coronaire.","isCorrect":true}]$$::jsonb, $$L’IAo combine surcharge volumique et pressionnelle, conduisant à une dilatation compensatrice puis à une insuffisance ventriculaire.$$, 2, '2025-11-02T17:52:04.745Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddai007ak498uheddhaw', 'cmhi0ddab0072k498l8ev0q0v', $$Circonstances de découverte$$, $$[{"text":"Elle peut être asymptomatique pendant des années.","isCorrect":true},{"text":"Elle se manifeste par une dyspnée d’effort.","isCorrect":true},{"text":"Les palpitations sont fréquentes.","isCorrect":true},{"text":"L’angor d’effort ou nocturne peut survenir sans coronaropathie.","isCorrect":true},{"text":"Les syncopes d’effort sont très évocatrices.","isCorrect":false,"justification":"Elles évoquent plutôt un rétrécissement aortique."}]$$::jsonb, $$L’IAo peut être silencieuse longtemps, puis se révéler par une dyspnée, une palpitation ou une angine fonctionnelle.$$, 3, '2025-11-02T17:52:04.747Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddak007ck498qki6sx3e', 'cmhi0ddab0072k498l8ev0q0v', $$Examen clinique$$, $$[{"text":"Le choc de pointe est abaissé et en dôme.","isCorrect":true},{"text":"Le souffle diastolique aspiratif est typique.","isCorrect":true},{"text":"Il est entendu au 2ᵉ espace intercostal droit.","isCorrect":true},{"text":"Il irradie vers la pointe du cœur et le bord gauche du sternum.","isCorrect":true},{"text":"Le souffle systolique d’accompagnement est toujours un signe de gravité.","isCorrect":false,"justification":"Il peut être fonctionnel par hyperdébit."}]$$::jsonb, $$Le souffle diastolique doux et aspiratif au foyer aortique est le signe auscultatoire essentiel de l’IAo.$$, 4, '2025-11-02T17:52:04.748Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddal007ek498x5j4oc8b', 'cmhi0ddab0072k498l8ev0q0v', $$Signes périphériques caractéristiques$$, $$[{"text":"Le pouls de Corrigan est ample et bondissant.","isCorrect":true},{"text":"Le signe de Musset correspond au battement de la tête synchronisé au pouls.","isCorrect":true},{"text":"Le double souffle de Durozier s’entend sur les fémorales.","isCorrect":true},{"text":"L’hyperpulsatilité capillaire est typique.","isCorrect":true},{"text":"La pression artérielle différentielle est diminuée.","isCorrect":false,"justification":"Elle est élargie (ex. 160/50 mmHg)."}]$$::jsonb, $$Les signes périphériques de l’IAo sont liés à la grande amplitude du volume d’éjection et à la chute diastolique de la PA.$$, 5, '2025-11-02T17:52:04.750Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddan007gk498irw9tm44', 'cmhi0ddab0072k498l8ev0q0v', $$Examens complémentaires$$, $$[{"text":"L’ECG montre une hypertrophie ventriculaire gauche.","isCorrect":true},{"text":"La radiographie thoracique peut montrer une cardiomégalie.","isCorrect":true},{"text":"L’échocardiographie-Doppler confirme la régurgitation et sa sévérité.","isCorrect":true},{"text":"L’IRM cardiaque n’a aucun intérêt.","isCorrect":false,"justification":"Elle permet une quantification précise du reflux."},{"text":"Le cathétérisme cardiaque est réservé aux cas chirurgicaux.","isCorrect":true}]$$::jsonb, $$L’échocardiographie reste l’examen clé pour le diagnostic et la surveillance de l’IAo, l’IRM étant utile pour l’évaluation volumétrique.$$, 6, '2025-11-02T17:52:04.751Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddap007ik498gfbfw7uk', 'cmhi0ddab0072k498l8ev0q0v', $$Formes cliniques$$, $$[{"text":"L’IAo aiguë entraîne une décompensation cardiaque brutale.","isCorrect":true},{"text":"Elle est souvent secondaire à une endocardite ou une dissection.","isCorrect":true},{"text":"Le ventricule gauche n’est pas dilaté dans la forme aiguë.","isCorrect":true},{"text":"L’IAo chronique est mieux tolérée au début.","isCorrect":true},{"text":"La forme aiguë se manifeste par une pression différentielle élargie.","isCorrect":false,"justification":"Elle est réduite, car la diastolique chute très vite."}]$$::jsonb, $$L’IAo aiguë est une urgence hémodynamique, tandis que la forme chronique évolue lentement vers l’insuffisance cardiaque.$$, 7, '2025-11-02T17:52:04.753Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddaq007kk498o7s7ji65', 'cmhi0ddab0072k498l8ev0q0v', $$Évolution et complications$$, $$[{"text":"L’hypertrophie ventriculaire gauche permet une compensation transitoire.","isCorrect":true},{"text":"L’insuffisance cardiaque gauche est une complication terminale.","isCorrect":true},{"text":"L’endocardite infectieuse peut compliquer une IAo ancienne.","isCorrect":true},{"text":"L’embolie systémique est fréquente.","isCorrect":false,"justification":"Elle est rare en l’absence de FA associée."},{"text":"La dissection aortique est une complication tardive typique.","isCorrect":true}]$$::jsonb, $$L’IAo chronique évolue vers l’insuffisance cardiaque et peut se compliquer d’endocardite ou de dissection.$$, 8, '2025-11-02T17:52:04.755Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddas007mk498cpigvveu', 'cmhi0ddab0072k498l8ev0q0v', $$Traitement$$, $$[{"text":"Le traitement médical repose sur les vasodilatateurs et les diurétiques.","isCorrect":true},{"text":"Le remplacement valvulaire est indiqué en cas de forme sévère symptomatique.","isCorrect":true},{"text":"La chirurgie doit être envisagée avant l’altération du ventricule gauche.","isCorrect":true},{"text":"L’antibioprophylaxie est inutile pour les gestes dentaires.","isCorrect":false,"justification":"Elle est recommandée chez les patients à risque."},{"text":"L’activité physique intense est encouragée.","isCorrect":false,"justification":"Elle doit être limitée dans les formes modérées à sévères."}]$$::jsonb, $$Le traitement de l’IAo associe prise en charge médicale et remplacement valvulaire précoce pour préserver la fonction ventriculaire gauche.$$, 9, '2025-11-02T17:52:04.757Z');
