INSERT INTO "Chapter" (id, "subjectId", title, description, "orderIndex", "pdfUrl", "createdAt") VALUES ('cmhi0ddb4007pk498qv3s4o9x', 'cmhi0ddb0007nk498bc877ogt', $$Chapitre 1 – Examen clinique en hépato-gastro-entérologie$$, NULL, 1, NULL, '2025-11-02T17:52:04.769Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddbi0085k498gap222rf', 'cmhi0ddb4007pk498qv3s4o9x', $$Rectorragie$$, $$[{"text":"C’est une émission de sang rouge par l’anus.","isCorrect":true},{"text":"Elle indique toujours une hémorragie haute.","isCorrect":false,"justification":"Elle est basse, colique ou rectale."},{"text":"Elle peut accompagner les hémorroïdes.","isCorrect":true},{"text":"Elle justifie toujours une coloscopie.","isCorrect":true},{"text":"Elle s’accompagne d’un méléna.","isCorrect":false,"justification":"Ce sont deux entités distinctes."}]$$::jsonb, $$La rectorragie est un signe d’hémorragie basse nécessitant une exploration endoscopique complète.$$, 7, '2025-11-02T17:52:04.782Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddb6007rk4981fdqxrhk', 'cmhi0ddb4007pk498qv3s4o9x', $$Objectif de l’interrogatoire digestif$$, $$[{"text":"Identifier le siège et la nature des troubles digestifs.","isCorrect":true},{"text":"Rechercher les antécédents hépatiques, biliaires et digestifs.","isCorrect":true},{"text":"L’interrogatoire est inutile si le patient présente une douleur abdominale.","isCorrect":false,"justification":"C’est l’étape fondamentale du diagnostic digestif."},{"text":"L’interrogatoire permet d’évaluer les habitudes alimentaires.","isCorrect":true},{"text":"Le but est seulement d’obtenir un diagnostic définitif.","isCorrect":false,"justification":"Il sert à orienter le diagnostic, non à le conclure."}]$$::jsonb, $$L’interrogatoire digestif est une étape clé pour préciser la chronologie, les facteurs déclenchants et les antécédents.$$, 0, '2025-11-02T17:52:04.770Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddb8007tk498kng5vh5c', 'cmhi0ddb4007pk498qv3s4o9x', $$Antécédents à rechercher$$, $$[{"text":"Antécédents médicaux digestifs (ulcère, hépatite, lithiase).","isCorrect":true},{"text":"Antécédents chirurgicaux (appendicectomie, cholécystectomie).","isCorrect":true},{"text":"Antécédents hématologiques sont sans intérêt digestif.","isCorrect":false,"justification":"Une coagulopathie influence les hémorragies digestives."},{"text":"Prise médicamenteuse prolongée (AINS, corticoïdes).","isCorrect":true},{"text":"Antécédents familiaux de cancer colorectal.","isCorrect":true}]$$::jsonb, $$Les antécédents personnels et familiaux orientent vers les causes hépatiques, biliaires, ulcéreuses ou tumorales.$$, 1, '2025-11-02T17:52:04.772Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddb9007vk498ezcozlg1', 'cmhi0ddb4007pk498qv3s4o9x', $$Signes généraux digestifs$$, $$[{"text":"Amaigrissement, fièvre et asthénie sont des signes généraux.","isCorrect":true},{"text":"Leur association signe toujours une tumeur digestive.","isCorrect":false,"justification":"Elle peut aussi refléter infection, inflammation ou malabsorption."},{"text":"L’anorexie peut accompagner une affection hépatique.","isCorrect":true},{"text":"L’amaigrissement peut être observé dans la malabsorption intestinale.","isCorrect":true},{"text":"L’asthénie est sans intérêt clinique.","isCorrect":false,"justification":"C’est un symptôme fréquent et révélateur."}]$$::jsonb, $$Les signes généraux orientent vers une affection chronique, tumorale ou infectieuse du tube digestif.$$, 2, '2025-11-02T17:52:04.773Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddbb007xk498bhtki0e6', 'cmhi0ddb4007pk498qv3s4o9x', $$Dysphagie et odynophagie$$, $$[{"text":"La dysphagie est une douleur à la déglutition.","isCorrect":false,"justification":"C’est une gêne au passage des aliments."},{"text":"L’odynophagie est une douleur provoquée par la déglutition.","isCorrect":true},{"text":"La dysphagie mécanique s’aggrave d’abord pour les solides.","isCorrect":true},{"text":"La dysphagie motrice concerne uniquement les liquides.","isCorrect":false,"justification":"Elle touche solides et liquides simultanément."},{"text":"L’achalasie est une cause de dysphagie motrice.","isCorrect":true}]$$::jsonb, $$La distinction entre dysphagie mécanique et motrice est essentielle pour localiser la lésion.$$, 3, '2025-11-02T17:52:04.775Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddbd007zk498dnb8mtuo', 'cmhi0ddb4007pk498qv3s4o9x', $$Pyrosis et régurgitations$$, $$[{"text":"Le pyrosis est une brûlure rétro-sternale ascendante.","isCorrect":true},{"text":"Il traduit souvent un reflux gastro-œsophagien.","isCorrect":true},{"text":"Les régurgitations sont des vomissements précédés de nausées.","isCorrect":false,"justification":"Ce sont des remontées sans effort."},{"text":"Le pyrosis est calmé par les repas.","isCorrect":true},{"text":"Il est toujours d’origine cardiaque.","isCorrect":false,"justification":"Il est digestif, lié à l’acide gastrique."}]$$::jsonb, $$Pyrosis et régurgitations sont les symptômes typiques du reflux gastro-œsophagien.$$, 4, '2025-11-02T17:52:04.778Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddbf0081k498owiz4tn6', 'cmhi0ddb4007pk498qv3s4o9x', $$Vomissements$$, $$[{"text":"Le vomissement est un rejet actif du contenu gastrique.","isCorrect":true},{"text":"Il est toujours précédé de nausées.","isCorrect":false,"justification":"Il peut être brutal sans nausée, comme dans l’hypertension intracrânienne."},{"text":"Le vomissement bilieux traduit une obstruction intestinale haute.","isCorrect":true},{"text":"Le vomissement en jet est typique d’une cause centrale.","isCorrect":true},{"text":"Le vomissement alimentaire tardif évoque un ulcère pylorique.","isCorrect":true}]$$::jsonb, $$L’analyse du vomissement (aspect, timing, contenu) oriente vers la cause : gastrique, duodénale ou neurologique.$$, 5, '2025-11-02T17:52:04.779Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddbh0083k498uyae0yj8', 'cmhi0ddb4007pk498qv3s4o9x', $$Hématémèse et méléna$$, $$[{"text":"L’hématémèse est un vomissement de sang rouge ou noir.","isCorrect":true},{"text":"Le méléna correspond à des selles noires goudronneuses.","isCorrect":true},{"text":"Ces signes témoignent d’une hémorragie digestive basse.","isCorrect":false,"justification":"Ils indiquent une hémorragie digestive haute."},{"text":"Le méléna peut persister plusieurs jours après le saignement.","isCorrect":true},{"text":"L’hématémèse traduit toujours une rupture variqueuse.","isCorrect":false,"justification":"Elle peut provenir d’un ulcère ou d’une gastrite."}]$$::jsonb, $$Hématémèse et méléna orientent vers une hémorragie digestive haute, souvent ulcéreuse ou variqueuse.$$, 6, '2025-11-02T17:52:04.781Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddbk0087k498d7g45erw', 'cmhi0ddb4007pk498qv3s4o9x', $$Diarrhée$$, $$[{"text":"Elle correspond à une émission de selles liquides fréquentes.","isCorrect":true},{"text":"On parle de diarrhée au-delà de 3 selles par jour.","isCorrect":true},{"text":"Elle peut être aiguë ou chronique.","isCorrect":true},{"text":"La diarrhée infectieuse dure toujours plus d’un mois.","isCorrect":false,"justification":"Elle dure moins de 2 semaines."},{"text":"La diarrhée graisseuse évoque une malabsorption.","isCorrect":true}]$$::jsonb, $$L’analyse de la durée et de l’aspect des selles permet de distinguer les causes infectieuses, fonctionnelles ou de malabsorption.$$, 8, '2025-11-02T17:52:04.784Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddbl0089k49896hpubdw', 'cmhi0ddb4007pk498qv3s4o9x', $$Constipation$$, $$[{"text":"Elle correspond à une fréquence de selles < 3 par semaine.","isCorrect":true},{"text":"Elle peut être fonctionnelle ou organique.","isCorrect":true},{"text":"Les médicaments (opiacés, fer) peuvent l’aggraver.","isCorrect":true},{"text":"L’hypothyroïdie peut la provoquer.","isCorrect":true},{"text":"Elle est toujours bénigne.","isCorrect":false,"justification":"Elle peut révéler un cancer colique."}]$$::jsonb, $$La constipation est fréquente mais peut être le premier signe d’une obstruction organique colique.$$, 9, '2025-11-02T17:52:04.786Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddbn008bk498dhqlrach', 'cmhi0ddb4007pk498qv3s4o9x', $$Douleurs abdominales$$, $$[{"text":"Les douleurs viscérales sont diffuses et mal localisées.","isCorrect":true},{"text":"Les douleurs pariétales sont nettes et bien localisées.","isCorrect":true},{"text":"La douleur colique est calmée par l’émission de gaz ou de selles.","isCorrect":true},{"text":"Une douleur épigastrique évoque une origine pancréatique.","isCorrect":true},{"text":"Une douleur FID évoque toujours une lithiase biliaire.","isCorrect":false,"justification":"Elle suggère plutôt une appendicite."}]$$::jsonb, $$La topographie et le mode de survenue de la douleur orientent la recherche étiologique.$$, 10, '2025-11-02T17:52:04.787Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddbp008dk498o3hntncg', 'cmhi0ddb4007pk498qv3s4o9x', $$Examen de l’abdomen : inspection$$, $$[{"text":"L’inspection se fait patient en décubitus, abdomen découvert.","isCorrect":true},{"text":"On recherche une distension, une cicatrice, une circulation collatérale.","isCorrect":true},{"text":"L’asymétrie abdominale oriente vers une tumeur ou un épanchement.","isCorrect":true},{"text":"La respiration abdominale est absente en cas de péritonite.","isCorrect":true},{"text":"La palpation précède toujours l’inspection.","isCorrect":false,"justification":"L’inspection précède toute manipulation."}]$$::jsonb, $$L’inspection abdominale permet d’identifier les signes visibles d’épanchement, de masse ou de cicatrice chirurgicale.$$, 11, '2025-11-02T17:52:04.789Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddbq008fk498gypjucfh', 'cmhi0ddb4007pk498qv3s4o9x', $$Palpation abdominale$$, $$[{"text":"Elle recherche les zones de défense ou de contracture.","isCorrect":true},{"text":"Elle permet d’apprécier la taille du foie et de la rate.","isCorrect":true},{"text":"La palpation profonde explore les masses profondes.","isCorrect":true},{"text":"Une douleur provoquée au point de McBurney évoque une appendicite.","isCorrect":true},{"text":"Elle doit précéder l’auscultation.","isCorrect":false,"justification":"Elle suit inspection et auscultation."}]$$::jsonb, $$La palpation localise la douleur, explore le foie et la rate et détecte une défense abdominale.$$, 12, '2025-11-02T17:52:04.791Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddbs008hk4987shel7cw', 'cmhi0ddb4007pk498qv3s4o9x', $$Percussion et auscultation$$, $$[{"text":"La percussion aide à reconnaître une ascite.","isCorrect":true},{"text":"Une matité déclive évoque un épanchement liquidien.","isCorrect":true},{"text":"Les bruits hydroaériques sont absents dans l’occlusion.","isCorrect":true},{"text":"Le silence abdominal traduit une urgence chirurgicale.","isCorrect":true},{"text":"L’auscultation est inutile avant la palpation.","isCorrect":false,"justification":"Elle se fait avant pour ne pas modifier l’activité intestinale."}]$$::jsonb, $$La percussion et l’auscultation sont essentielles pour détecter ascite, occlusion ou hyperpéristaltisme.$$, 13, '2025-11-02T17:52:04.792Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddbv008jk498wgvtdifp', 'cmhi0ddb4007pk498qv3s4o9x', $$Examen général$$, $$[{"text":"L’ictère oriente vers une atteinte hépatobiliaire.","isCorrect":true},{"text":"La fièvre évoque une infection digestive.","isCorrect":true},{"text":"L’altération de l’état général accompagne les cancers digestifs.","isCorrect":true},{"text":"La déshydratation est rare en diarrhée aiguë.","isCorrect":false,"justification":"Elle est fréquente, surtout chez l’enfant et le vieillard."},{"text":"Les adénopathies sont sans rapport avec les affections digestives.","isCorrect":false,"justification":"Elles peuvent révéler une métastase ou une infection."}]$$::jsonb, $$L’examen général complète l’étude abdominale et révèle les signes systémiques associés à la pathologie digestive.$$, 14, '2025-11-02T17:52:04.795Z');
INSERT INTO "Chapter" (id, "subjectId", title, description, "orderIndex", "pdfUrl", "createdAt") VALUES ('cmhi0ddby008lk498q2kqibdc', 'cmhi0ddb0007nk498bc877ogt', $$Chapitre 2 – Sémiologie hépato-bilio-pancréatique$$, NULL, 2, NULL, '2025-11-02T17:52:04.798Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddc0008nk498ntjqgmg5', 'cmhi0ddby008lk498q2kqibdc', $$Ictère (jaunisse)$$, $$[{"text":"L’ictère correspond à une coloration jaune des téguments et conjonctives.","isCorrect":true},{"text":"Il traduit une hyperbilirubinémie.","isCorrect":true},{"text":"Il peut être d’origine hémolytique, hépatocellulaire ou cholestatique.","isCorrect":true},{"text":"Il s’accompagne toujours de prurit.","isCorrect":false,"justification":"Le prurit n’existe que dans les ictères cholestatiques."},{"text":"La bilirubine libre est augmentée dans la cholestase.","isCorrect":false,"justification":"C’est la conjuguée qui augmente."}]$$::jsonb, $$L’ictère est un signe d’hyperbilirubinémie dont la nature (libre ou conjuguée) oriente le diagnostic étiologique.$$, 0, '2025-11-02T17:52:04.800Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddc2008pk49830jplnxf', 'cmhi0ddby008lk498q2kqibdc', $$Hépatomégalie$$, $$[{"text":"Elle se définit par un foie palpable sous le rebord costal.","isCorrect":true},{"text":"Elle est toujours pathologique.","isCorrect":false,"justification":"Elle peut être normale chez les sujets minces ou en inspiration profonde."},{"text":"La consistance du foie oriente le diagnostic.","isCorrect":true},{"text":"Un foie dur, irrégulier, nodulaire évoque une cirrhose.","isCorrect":true},{"text":"Un foie souple et douloureux oriente vers une tumeur.","isCorrect":false,"justification":"Il évoque plutôt une congestion aiguë ou une hépatite."}]$$::jsonb, $$L’étude de la taille, de la consistance et de la sensibilité hépatique guide le diagnostic entre causes congestives, infectieuses ou tumorales.$$, 1, '2025-11-02T17:52:04.803Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddc4008rk4985eto9brc', 'cmhi0ddby008lk498q2kqibdc', $$Douleurs de l’hypochondre droit$$, $$[{"text":"Elles évoquent souvent une pathologie hépatique ou biliaire.","isCorrect":true},{"text":"Elles sont calmées par les repas.","isCorrect":false,"justification":"Elles ne sont pas alimentaires, contrairement à l’ulcère."},{"text":"Elles peuvent s’accompagner d’ictère.","isCorrect":true},{"text":"Une irradiation scapulaire droite évoque une cause biliaire.","isCorrect":true},{"text":"Elles s’accompagnent toujours de fièvre.","isCorrect":false,"justification":"La fièvre n’apparaît que dans les formes infectieuses."}]$$::jsonb, $$La douleur de l’hypochondre droit, surtout irradiée vers l’épaule, oriente vers une lithiase biliaire ou une atteinte hépatique.$$, 2, '2025-11-02T17:52:04.805Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddc6008tk498i51tjaed', 'cmhi0ddby008lk498q2kqibdc', $$Cirrhose hépatique$$, $$[{"text":"Elle correspond à une nécrose aiguë du foie.","isCorrect":false,"justification":"C’est une maladie chronique de fibrose et de nodules."},{"text":"Elle se traduit par une inversion du rapport albumine/globuline.","isCorrect":true},{"text":"Elle s’accompagne toujours d’un ictère aigu.","isCorrect":false,"justification":"L’ictère peut manquer dans la cirrhose compensée."},{"text":"Elle est irréversible.","isCorrect":false,"justification":"Certaines formes précoces peuvent se stabiliser."},{"text":"Elle ne s’accompagne jamais d’hypertension portale.","isCorrect":false,"justification":"C’est une complication majeure."}]$$::jsonb, $$La cirrhose est une fibrose hépatique diffuse entraînant des troubles métaboliques et une hypertension portale.$$, 3, '2025-11-02T17:52:04.807Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddc9008vk498catjodn1', 'cmhi0ddby008lk498q2kqibdc', $$Signes physiques de la cirrhose$$, $$[{"text":"L’angiome stellaire est fréquent sur la face et le thorax.","isCorrect":true},{"text":"L’érythème palmaire est un signe caractéristique.","isCorrect":true},{"text":"La gynécomastie traduit une hyperœstrogénie.","isCorrect":true},{"text":"La rate est souvent augmentée de volume.","isCorrect":true},{"text":"L’ascite est rare dans la cirrhose décompensée.","isCorrect":false,"justification":"Elle est au contraire fréquente."}]$$::jsonb, $$Les signes cutanés, hormonaux et spléniques traduisent la décompensation d’une cirrhose chronique.$$, 4, '2025-11-02T17:52:04.809Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddcb008xk498re4ltcsl', 'cmhi0ddby008lk498q2kqibdc', $$Ascite$$, $$[{"text":"Elle traduit un épanchement liquidien intra-abdominal.","isCorrect":true},{"text":"Elle est toujours d’origine cardiaque.","isCorrect":false,"justification":"La cause principale est la cirrhose hépatique."},{"text":"Elle peut être riche ou pauvre en protéines.","isCorrect":true},{"text":"Le signe du flot liquidien est pathognomonique.","isCorrect":true},{"text":"La ponction d’ascite est contre-indiquée.","isCorrect":false,"justification":"Elle est diagnostique et thérapeutique."}]$$::jsonb, $$L’ascite est un signe fréquent de cirrhose décompensée ; sa ponction permet d’en déterminer la cause.$$, 5, '2025-11-02T17:52:04.811Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddcd008zk498t7l18f3w', 'cmhi0ddby008lk498q2kqibdc', $$Encéphalopathie hépatique$$, $$[{"text":"Elle est due à une hypoglycémie cérébrale.","isCorrect":false,"justification":"Elle est causée par l’accumulation d’ammoniaque."},{"text":"Elle survient uniquement dans l’hépatite aiguë.","isCorrect":false,"justification":"Elle se voit aussi dans la cirrhose terminale."},{"text":"Elle se manifeste par agitation et mydriase fixe.","isCorrect":false,"justification":"Elle débute par troubles du comportement et astérixis."},{"text":"Le traitement repose uniquement sur les corticoïdes.","isCorrect":false,"justification":"Il repose sur le lactulose et les antibiotiques."},{"text":"Elle est toujours irréversible.","isCorrect":false,"justification":"Elle peut régresser avec traitement et régime."}]$$::jsonb, $$L’encéphalopathie hépatique est une complication réversible liée à l’accumulation de toxines ammoniacales.$$, 6, '2025-11-02T17:52:04.813Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddcf0091k498rxrtpr04', 'cmhi0ddby008lk498q2kqibdc', $$Colique hépatique$$, $$[{"text":"Elle est due à une obstruction du canal cystique.","isCorrect":true},{"text":"La douleur siège à l’épigastre et à l’hypochondre droit.","isCorrect":true},{"text":"Elle irradie souvent vers l’épaule droite.","isCorrect":true},{"text":"Elle s’accompagne toujours d’ictère.","isCorrect":false,"justification":"L’ictère n’apparaît que si le cholédoque est obstrué."},{"text":"Elle dure quelques secondes.","isCorrect":false,"justification":"Elle dure plusieurs heures, souvent après un repas gras."}]$$::jsonb, $$La colique hépatique est une douleur intense, prolongée, sans fièvre ni ictère, traduisant une lithiase vésiculaire.$$, 7, '2025-11-02T17:52:04.816Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddch0093k498zkc45zdw', 'cmhi0ddby008lk498q2kqibdc', $$Cholangite$$, $$[{"text":"Elle associe fièvre, douleur et ictère (triade de Charcot).","isCorrect":true},{"text":"Elle est secondaire à une obstruction biliaire infectée.","isCorrect":true},{"text":"Elle peut évoluer vers un choc septique.","isCorrect":true},{"text":"Elle nécessite un drainage biliaire urgent.","isCorrect":true},{"text":"Elle met en jeu le pronostic vital.","isCorrect":true}]$$::jsonb, $$La cholangite est une infection grave des voies biliaires nécessitant une antibiothérapie et un drainage rapide.$$, 8, '2025-11-02T17:52:04.818Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddck0095k498s8m3anqm', 'cmhi0ddby008lk498q2kqibdc', $$Pancréatite aiguë$$, $$[{"text":"Elle est souvent liée à l’alcool ou à la lithiase biliaire.","isCorrect":true},{"text":"Elle provoque une douleur épigastrique transfixiante.","isCorrect":true},{"text":"La lipase sanguine est normale.","isCorrect":false,"justification":"Elle est très augmentée."},{"text":"Elle s’accompagne fréquemment de vomissements.","isCorrect":true},{"text":"L’échographie est inutile.","isCorrect":false,"justification":"Elle recherche une lithiase biliaire."}]$$::jsonb, $$La pancréatite aiguë est une urgence digestive caractérisée par une douleur transfixiante et une hyperlipasémie majeure.$$, 9, '2025-11-02T17:52:04.820Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddcm0097k498dvuwfquq', 'cmhi0ddby008lk498q2kqibdc', $$Pancréatite chronique$$, $$[{"text":"Elle se traduit par des douleurs récidivantes post-prandiales.","isCorrect":true},{"text":"Elle est due à une infection virale.","isCorrect":false,"justification":"Elle est surtout alcoolique ou idiopathique."},{"text":"Elle se complique rarement d’insuffisance exocrine.","isCorrect":false,"justification":"Elle provoque une stéatorrhée et diabète secondaire."},{"text":"Elle est toujours réversible.","isCorrect":false,"justification":"C’est une lésion irréversible."},{"text":"Elle touche exclusivement le pancréas gauche.","isCorrect":false,"justification":"Elle concerne tout le pancréas."}]$$::jsonb, $$La pancréatite chronique est une inflammation prolongée responsable d’une insuffisance exocrine et endocrine.$$, 10, '2025-11-02T17:52:04.822Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddcq0099k498yrlt9p8b', 'cmhi0ddby008lk498q2kqibdc', $$Cancer du pancréas$$, $$[{"text":"Il se manifeste souvent par un ictère cholestatique indolore.","isCorrect":true},{"text":"Il touche préférentiellement le corps du pancréas.","isCorrect":false,"justification":"Le siège le plus fréquent est la tête."},{"text":"Il peut entraîner un amaigrissement rapide.","isCorrect":true},{"text":"Il survient exclusivement avant 40 ans.","isCorrect":false,"justification":"Il est rare avant 50 ans."},{"text":"Il est toujours curable par chirurgie.","isCorrect":false,"justification":"Le pronostic est souvent sombre."}]$$::jsonb, $$Le cancer pancréatique, souvent découvert tard, se manifeste par un ictère indolore et un amaigrissement progressif.$$, 11, '2025-11-02T17:52:04.827Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddct009bk498tweh1wma', 'cmhi0ddby008lk498q2kqibdc', $$Lithiase vésiculaire$$, $$[{"text":"Elle est plus fréquente chez l’homme.","isCorrect":false,"justification":"Elle touche davantage les femmes."},{"text":"Elle n’entraîne jamais de symptômes.","isCorrect":false,"justification":"Elle peut provoquer coliques ou cholécystites."},{"text":"Elle survient surtout chez le jeune.","isCorrect":false,"justification":"Elle est plus fréquente après 40 ans."},{"text":"L’obésité et les grossesses répétées sont protectrices.","isCorrect":false,"justification":"Ce sont des facteurs favorisants."},{"text":"La lithiase pigmentaire est due à un excès de cholestérol.","isCorrect":false,"justification":"Elle provient de la bilirubine calcique."}]$$::jsonb, $$La lithiase biliaire est fréquente, surtout chez la femme mûre et obèse, pouvant rester silencieuse ou compliquée.$$, 12, '2025-11-02T17:52:04.829Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddcv009dk498shfvo59d', 'cmhi0ddby008lk498q2kqibdc', $$Angiocholite aiguë$$, $$[{"text":"Elle est secondaire à une obstruction biliaire infectée.","isCorrect":true},{"text":"Elle se manifeste par fièvre, douleur et ictère.","isCorrect":true},{"text":"Elle guérit spontanément.","isCorrect":false,"justification":"Elle nécessite drainage et antibiotiques."},{"text":"Elle est bénigne si la fièvre est modérée.","isCorrect":false,"justification":"Elle peut évoluer vers le choc septique."},{"text":"Elle ne concerne jamais les patients lithiasiques.","isCorrect":false,"justification":"C’est leur complication principale."}]$$::jsonb, $$L’angiocholite est une urgence biliaire infectieuse nécessitant traitement et drainage rapide.$$, 13, '2025-11-02T17:52:04.831Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddcx009fk498a7p8xcky', 'cmhi0ddby008lk498q2kqibdc', $$Explorations hépato-biliaires$$, $$[{"text":"L’échographie est l’examen de première intention.","isCorrect":true},{"text":"Le scanner évalue la morphologie du foie et du pancréas.","isCorrect":true},{"text":"L’IRM biliaire (cholangio-IRM) visualise les voies biliaires.","isCorrect":true},{"text":"La biopsie hépatique est sans risque.","isCorrect":false,"justification":"Elle comporte un risque hémorragique."},{"text":"La scintigraphie hépatique est l’examen principal de dépistage.","isCorrect":false,"justification":"Elle est peu utilisée aujourd’hui."}]$$::jsonb, $$L’échographie et le scanner constituent la base du diagnostic morphologique hépato-biliaire, complétés selon les besoins par la cholangio-IRM.$$, 14, '2025-11-02T17:52:04.833Z');
INSERT INTO "Chapter" (id, "subjectId", title, description, "orderIndex", "pdfUrl", "createdAt") VALUES ('cmhi0ddd1009hk498wripsvh0', 'cmhi0ddb0007nk498bc877ogt', $$Chapitre 3 – Hémorragies digestives$$, NULL, 3, NULL, '2025-11-02T17:52:04.837Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0dddl009xk49819xmt78k', 'cmhi0ddd1009hk498wripsvh0', $$Facteurs favorisants$$, $$[{"text":"Prise d’AINS ou d’aspirine.","isCorrect":true},{"text":"Anticoagulants oraux.","isCorrect":true},{"text":"Stress chirurgical ou septique.","isCorrect":true},{"text":"Infection par Helicobacter pylori.","isCorrect":true},{"text":"Consommation d’eau froide.","isCorrect":false,"justification":"Aucun lien physiopathologique."}]$$::jsonb, $$Médicaments, infections et pathologies ulcéreuses sont des facteurs déclenchants fréquents d’hémorragie digestive.$$, 7, '2025-11-02T17:52:04.858Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0dddh009tk498la4ia536', 'cmhi0ddd1009hk498wripsvh0', $$Principales causes d’hémorragie digestive haute$$, $$[{"text":"Ulcère gastroduodénal.","isCorrect":true},{"text":"Rupture de varices œsophagiennes.","isCorrect":true},{"text":"Gastrite érosive.","isCorrect":true},{"text":"Syndrome de Mallory-Weiss.","isCorrect":true},{"text":"Cancer colorectal.","isCorrect":false,"justification":"Il provoque une hémorragie basse."}]$$::jsonb, $$Les causes principales d’hémorragie haute sont ulcéreuses, variqueuses ou iatrogènes.$$, 5, '2025-11-02T17:52:04.853Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0dddj009vk4985ftdkyih', 'cmhi0ddd1009hk498wripsvh0', $$Causes d’hémorragie digestive basse$$, $$[{"text":"Cancer colique.","isCorrect":true},{"text":"Diverticulose colique.","isCorrect":true},{"text":"Maladie hémorroïdaire.","isCorrect":true},{"text":"Maladie de Crohn.","isCorrect":true},{"text":"Ulcère duodénal.","isCorrect":false,"justification":"C’est une cause d’hémorragie haute."}]$$::jsonb, $$Les hémorragies basses sont dominées par les lésions coliques (tumeurs, diverticules, inflammations, hémorroïdes).$$, 6, '2025-11-02T17:52:04.856Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddd3009jk498hsq1ud2j', 'cmhi0ddd1009hk498wripsvh0', $$Définition$$, $$[{"text":"L’hémorragie digestive est une perte de sang dans la lumière du tube digestif.","isCorrect":true},{"text":"Elle peut être haute ou basse selon le siège.","isCorrect":true},{"text":"Elle n’a aucune répercussion sur l’état général.","isCorrect":false,"justification":"Elle peut entraîner un choc hypovolémique."},{"text":"Elle passe toujours inaperçue.","isCorrect":false,"justification":"Elle peut être visible ou occulte."},{"text":"Elle correspond uniquement à l’hématémèse.","isCorrect":false,"justification":"L’hématémèse est un mode de révélation, non la définition."}]$$::jsonb, $$L’hémorragie digestive désigne toute perte de sang dans le tube digestif, quelle qu’en soit la cause ou la présentation.$$, 0, '2025-11-02T17:52:04.839Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddd5009lk498swb6gx7d', 'cmhi0ddd1009hk498wripsvh0', $$Classification$$, $$[{"text":"Une hémorragie haute siège en amont de l’angle de Treitz.","isCorrect":true},{"text":"Une hémorragie basse concerne le côlon ou le rectum.","isCorrect":true},{"text":"Les hémorragies hautes sont les plus fréquentes.","isCorrect":true},{"text":"Les hémorragies occultes sont toujours bénignes.","isCorrect":false,"justification":"Elles peuvent révéler un cancer digestif."},{"text":"Le méléna traduit toujours une hémorragie basse.","isCorrect":false,"justification":"Il traduit une hémorragie haute."}]$$::jsonb, $$La distinction entre hémorragie haute et basse repose sur le siège anatomique et l’aspect du sang émis.$$, 1, '2025-11-02T17:52:04.841Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddda009nk4980nyedn63', 'cmhi0ddd1009hk498wripsvh0', $$Hématémèse$$, $$[{"text":"C’est un vomissement de sang rouge ou noirâtre.","isCorrect":true},{"text":"Elle témoigne d’une hémorragie digestive haute.","isCorrect":true},{"text":"Elle peut provenir d’un ulcère gastrique.","isCorrect":true},{"text":"Elle s’accompagne parfois de vertiges ou de syncope.","isCorrect":true},{"text":"Elle peut mettre en jeu le pronostic vital.","isCorrect":true}]$$::jsonb, $$L’hématémèse est un signe d’hémorragie haute grave nécessitant une évaluation et une stabilisation rapides.$$, 2, '2025-11-02T17:52:04.846Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0dddc009pk4986g9042cx', 'cmhi0ddd1009hk498wripsvh0', $$Méléna$$, $$[{"text":"Les selles sont noires, brillantes et goudronneuses.","isCorrect":true},{"text":"Il témoigne d’une hémorragie haute digérée.","isCorrect":true},{"text":"Il est souvent associé à une hématémèse.","isCorrect":true},{"text":"Il disparaît immédiatement après l’arrêt du saignement.","isCorrect":false,"justification":"Il persiste plusieurs jours."},{"text":"Il peut survenir sans anémie.","isCorrect":false,"justification":"Il s’accompagne souvent d’une anémie ferriprive."}]$$::jsonb, $$Le méléna traduit un saignement digestif ancien ou lent, souvent d’origine gastrique ou duodénale.$$, 3, '2025-11-02T17:52:04.849Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0dddf009rk4982z35pugb', 'cmhi0ddd1009hk498wripsvh0', $$Rectorragie$$, $$[{"text":"Elle correspond à une émission de sang noir digéré.","isCorrect":false,"justification":"C’est du sang rouge non digéré."},{"text":"Elle témoigne d’une hémorragie haute.","isCorrect":false,"justification":"Elle indique une hémorragie basse."},{"text":"Elle provient souvent du rectum ou du côlon.","isCorrect":true},{"text":"Elle s’accompagne d’hématémèse.","isCorrect":false,"justification":"Ces deux signes ne coexistent pas."},{"text":"Elle n’a aucun intérêt diagnostique.","isCorrect":false,"justification":"Elle oriente le siège du saignement."}]$$::jsonb, $$La rectorragie est un signe d’hémorragie basse, typiquement colique ou rectale, parfois associée à un trouble de la coagulation.$$, 4, '2025-11-02T17:52:04.851Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0dddo009zk4985x6ajx8q', 'cmhi0ddd1009hk498wripsvh0', $$Signes de gravité$$, $$[{"text":"Hypotension artérielle.","isCorrect":true},{"text":"Pouls lent et régulier.","isCorrect":false,"justification":"Le pouls est rapide en cas de choc."},{"text":"Pâleur, sueurs, extrémités froides.","isCorrect":true},{"text":"Méléna isolé.","isCorrect":false,"justification":"Il n’est pas un signe de gravité à lui seul."},{"text":"Hématémèse unique.","isCorrect":false,"justification":"Le risque dépend de la quantité perdue."}]$$::jsonb, $$Les signes de gravité sont dominés par les manifestations de choc hémorragique : hypotension et tachycardie.$$, 8, '2025-11-02T17:52:04.860Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0dddq00a1k4981n4utm0d', 'cmhi0ddd1009hk498wripsvh0', $$Évaluation clinique initiale$$, $$[{"text":"Recherche de signes de choc.","isCorrect":true},{"text":"Mesure de la tension et du pouls.","isCorrect":true},{"text":"Palpation abdominale à la recherche d’une défense.","isCorrect":true},{"text":"Interrogatoire pour médicaments et antécédents.","isCorrect":true},{"text":"Coloscopie immédiate sans stabilisation.","isCorrect":false,"justification":"Elle n’est faite qu’après stabilisation."}]$$::jsonb, $$L’évaluation initiale vise à stabiliser le patient avant toute exploration endoscopique.$$, 9, '2025-11-02T17:52:04.862Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0dddt00a3k498hyhye53n', 'cmhi0ddd1009hk498wripsvh0', $$Examens biologiques$$, $$[{"text":"NFS pour évaluer l’anémie.","isCorrect":true},{"text":"Bilan de coagulation indispensable.","isCorrect":true},{"text":"Dosage des enzymes hépatiques.","isCorrect":true},{"text":"Ionogramme sanguin inutile.","isCorrect":false,"justification":"Il évalue la gravité et les pertes."},{"text":"Groupe sanguin et RAI en urgence.","isCorrect":true}]$$::jsonb, $$Le bilan biologique vise à quantifier l’anémie, détecter un trouble de la coagulation et préparer la transfusion.$$, 10, '2025-11-02T17:52:04.865Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0dddv00a5k498cxtnjfz3', 'cmhi0ddd1009hk498wripsvh0', $$Endoscopie digestive haute$$, $$[{"text":"C’est l’examen de choix pour localiser une hémorragie haute.","isCorrect":true},{"text":"Elle peut être à visée diagnostique et thérapeutique.","isCorrect":true},{"text":"Elle doit être faite avant toute stabilisation hémodynamique.","isCorrect":false,"justification":"Elle nécessite une stabilisation préalable."},{"text":"Elle permet la ligature de varices œsophagiennes.","isCorrect":true},{"text":"Elle est inutile si le méléna est présent.","isCorrect":false,"justification":"Elle est indiquée dans tout méléna."}]$$::jsonb, $$L’endoscopie digestive est l’examen clé permettant diagnostic et hémostase dans les hémorragies hautes.$$, 11, '2025-11-02T17:52:04.867Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0dddx00a7k498lwgoci2u', 'cmhi0ddd1009hk498wripsvh0', $$Endoscopie basse$$, $$[{"text":"Elle est indiquée pour les rectorragies.","isCorrect":true},{"text":"Elle localise les lésions coliques ou rectales.","isCorrect":true},{"text":"Elle peut être réalisée en urgence après lavage colique rapide.","isCorrect":true},{"text":"Elle n’a aucun intérêt thérapeutique.","isCorrect":false,"justification":"Elle permet hémostase et biopsie."},{"text":"Elle est systématique dans l’hématémèse.","isCorrect":false,"justification":"Elle ne concerne que les hémorragies basses."}]$$::jsonb, $$La coloscopie est l’examen de référence pour explorer et traiter les hémorragies digestives basses.$$, 12, '2025-11-02T17:52:04.869Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0dddz00a9k498p37mxdne', 'cmhi0ddd1009hk498wripsvh0', $$Imagerie complémentaire$$, $$[{"text":"Le scanner abdomino-pelvien est utile si l’endoscopie est négative.","isCorrect":true},{"text":"L’angio-scanner peut localiser un saignement actif.","isCorrect":true},{"text":"L’IRM abdominale est l’examen de première intention.","isCorrect":false,"justification":"Elle n’est pas adaptée à l’urgence."},{"text":"La scintigraphie au globule marqué détecte les saignements lents.","isCorrect":true},{"text":"L’échographie abdominale suffit pour diagnostiquer un ulcère.","isCorrect":false,"justification":"L’endoscopie est le seul examen direct."}]$$::jsonb, $$Le scanner et la scintigraphie complètent l’endoscopie pour les hémorragies d’origine indéterminée.$$, 13, '2025-11-02T17:52:04.871Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0dde100abk498a333pndd', 'cmhi0ddd1009hk498wripsvh0', $$Hémorragie ulcéreuse$$, $$[{"text":"Elle est due à la rupture d’un vaisseau au fond de l’ulcère.","isCorrect":true},{"text":"Elle survient souvent après la prise d’AINS.","isCorrect":true},{"text":"Elle se manifeste par une hématémèse ou un méléna.","isCorrect":true},{"text":"Elle ne récidive jamais.","isCorrect":false,"justification":"Le risque de récidive est élevé sans traitement."},{"text":"Le traitement associe IPP et endoscopie.","isCorrect":true}]$$::jsonb, $$L’hémorragie ulcéreuse est la cause la plus fréquente et nécessite un traitement endoscopique et médical combiné.$$, 14, '2025-11-02T17:52:04.873Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0dde300adk4987vlhv59n', 'cmhi0ddd1009hk498wripsvh0', $$Rupture de varices œsophagiennes$$, $$[{"text":"Elle survient sur hypertension portale.","isCorrect":true},{"text":"Elle provoque une hématémèse massive.","isCorrect":true},{"text":"Elle est fréquente chez le cirrhotique.","isCorrect":true},{"text":"La mortalité est faible.","isCorrect":false,"justification":"Elle est élevée sans traitement rapide."},{"text":"Le traitement associe ligature et vasoconstricteurs.","isCorrect":true}]$$::jsonb, $$L’hémorragie variqueuse est une urgence vitale du cirrhotique nécessitant une prise en charge combinée et intensive.$$, 15, '2025-11-02T17:52:04.876Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0dde600afk4985pwe0fl4', 'cmhi0ddd1009hk498wripsvh0', $$Ulcère de stress$$, $$[{"text":"Il survient chez le sujet sain.","isCorrect":false,"justification":"Il touche les patients hospitalisés ou graves."},{"text":"Il apparaît après un repas copieux.","isCorrect":false,"justification":"Il est lié au stress physiologique intense."},{"text":"Il ne saigne jamais.","isCorrect":false,"justification":"Il peut provoquer une hémorragie sévère."},{"text":"Il est prévenu par des AINS.","isCorrect":false,"justification":"Les AINS le favorisent."},{"text":"Il ne concerne jamais les soins intensifs.","isCorrect":false,"justification":"Il est typique des réanimations."}]$$::jsonb, $$L’ulcère de stress est une complication des maladies graves ou des soins intensifs, pouvant entraîner une hémorragie aiguë.$$, 16, '2025-11-02T17:52:04.879Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0dde900ahk4988qxxshl2', 'cmhi0ddd1009hk498wripsvh0', $$Hémorragies iatrogènes$$, $$[{"text":"Elles peuvent survenir après endoscopie.","isCorrect":true},{"text":"Elles sont favorisées par les anticoagulants.","isCorrect":true},{"text":"Elles ne concernent jamais les biopsies.","isCorrect":false,"justification":"Les biopsies digestives peuvent saigner."},{"text":"Le risque est nul après polypectomie.","isCorrect":false,"justification":"Il existe un risque hémorragique post-polypectomie."},{"text":"Elles sont bénignes par définition.","isCorrect":false,"justification":"Certaines peuvent être graves."}]$$::jsonb, $$Les hémorragies iatrogènes nécessitent prévention, correction des coagulopathies et surveillance post-endoscopie.$$, 17, '2025-11-02T17:52:04.882Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddeb00ajk4987obu03pq', 'cmhi0ddd1009hk498wripsvh0', $$Prise en charge d’urgence$$, $$[{"text":"Pose d’une voie veineuse et remplissage rapide.","isCorrect":true},{"text":"Oxygénothérapie et surveillance cardiorespiratoire.","isCorrect":true},{"text":"Administration d’IPP en IV.","isCorrect":true},{"text":"Endoscopie immédiate avant réanimation.","isCorrect":false,"justification":"Elle vient après stabilisation."},{"text":"Recherche de la cause seulement après traitement.","isCorrect":false,"justification":"Le diagnostic étiologique est simultané à la stabilisation."}]$$::jsonb, $$La priorité en urgence est la stabilisation hémodynamique avant la recherche et le traitement de la cause.$$, 18, '2025-11-02T17:52:04.884Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0dded00alk498ln8in8da', 'cmhi0ddd1009hk498wripsvh0', $$Pronostic$$, $$[{"text":"Dépend de l’étiologie et de la rapidité de prise en charge.","isCorrect":true},{"text":"Est toujours favorable.","isCorrect":false,"justification":"Les formes massives restent graves."},{"text":"Les récidives sont possibles.","isCorrect":true},{"text":"La prévention repose sur le traitement de la cause.","isCorrect":true},{"text":"La mortalité est nulle.","isCorrect":false,"justification":"Elle peut atteindre 10 à 15 % selon le terrain."}]$$::jsonb, $$Le pronostic dépend du terrain, de la gravité initiale et de la rapidité du diagnostic et du traitement$$, 19, '2025-11-02T17:52:04.886Z');
INSERT INTO "Chapter" (id, "subjectId", title, description, "orderIndex", "pdfUrl", "createdAt") VALUES ('cmhi0ddeh00ank498bm1425sn', 'cmhi0ddb0007nk498bc877ogt', $$Chapitre 4 – Ascite$$, NULL, 4, NULL, '2025-11-02T17:52:04.889Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddej00apk4986fge0ua8', 'cmhi0ddeh00ank498bm1425sn', $$Définition$$, $$[{"text":"L’ascite est une accumulation de liquide dans la cavité pleurale.","isCorrect":false,"justification":"Elle concerne le péritoine, pas la plèvre."},{"text":"C’est un épanchement péritonéal liquidien.","isCorrect":true},{"text":"Elle traduit toujours une infection.","isCorrect":false,"justification":"Elle peut être cirrhotique, cardiaque, pancréatique, etc."},{"text":"Elle peut être localisée ou diffuse.","isCorrect":true},{"text":"Elle ne modifie jamais le volume abdominal.","isCorrect":false,"justification":"Elle provoque une distension abdominale visible."}]$$::jsonb, $$L’ascite est un épanchement liquidien intra-péritonéal d’origine variée, souvent révélateur d’une maladie chronique.$$, 0, '2025-11-02T17:52:04.891Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddel00ark4988fttaar1', 'cmhi0ddeh00ank498bm1425sn', $$Principales causes$$, $$[{"text":"Cirrhose hépatique.","isCorrect":true},{"text":"Insuffisance cardiaque droite.","isCorrect":true},{"text":"Syndrome néphrotique.","isCorrect":true},{"text":"Péritonite tuberculeuse.","isCorrect":true},{"text":"Appendicite aiguë.","isCorrect":false,"justification":"Elle ne provoque pas d’ascite vraie."}]$$::jsonb, $$Les causes dominantes sont la cirrhose, l’insuffisance cardiaque, les syndromes néphrotiques et les infections péritonéales.$$, 1, '2025-11-02T17:52:04.894Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddeo00atk498bydzpoqk', 'cmhi0ddeh00ank498bm1425sn', $$Physiopathologie$$, $$[{"text":"L’ascite cirrhotique est liée à l’hypertension portale.","isCorrect":true},{"text":"L’hypoalbuminémie contribue à la formation d’ascite.","isCorrect":true},{"text":"L’augmentation de la pression lymphatique péritonéale est un facteur.","isCorrect":true},{"text":"L’hyperprotéinémie favorise l’ascite.","isCorrect":false,"justification":"C’est l’hypoprotéinémie qui y contribue."},{"text":"L’hypertension pulmonaire est la cause principale.","isCorrect":false,"justification":"L’hypertension portale est la cause majeure."}]$$::jsonb, $$L’ascite résulte d’un déséquilibre entre pressions hydrostatiques et oncotique, surtout en cas d’hypertension portale.$$, 2, '2025-11-02T17:52:04.896Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddeq00avk49828yar0a6', 'cmhi0ddeh00ank498bm1425sn', $$Signes fonctionnels$$, $$[{"text":"Distension abdominale progressive.","isCorrect":true},{"text":"Dyspnée par compression diaphragmatique.","isCorrect":true},{"text":"Nausées et pesanteur abdominale.","isCorrect":true},{"text":"Douleur abdominale intense brutale.","isCorrect":false,"justification":"Elle suggère une péritonite, non une ascite."},{"text":"Ralentissement du transit intestinal.","isCorrect":true}]$$::jsonb, $$L’ascite se manifeste par distension, gêne abdominale, dyspnée et parfois troubles digestifs.$$, 3, '2025-11-02T17:52:04.898Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddes00axk4983ms0qh2s', 'cmhi0ddeh00ank498bm1425sn', $$Inspection abdominale$$, $$[{"text":"Abdomen distendu et bombé.","isCorrect":true},{"text":"Ombilic saillant ou éversé.","isCorrect":true},{"text":"Disparition des reliefs musculaires.","isCorrect":true},{"text":"Circulation collatérale abdominale visible.","isCorrect":true},{"text":"Hernie ombilicale rare.","isCorrect":false,"justification":"Elle est fréquente dans l’ascite chronique."}]$$::jsonb, $$L’inspection révèle distension, circulation collatérale et ombilic saillant, signes typiques de l’ascite chronique.$$, 4, '2025-11-02T17:52:04.900Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddeu00azk49839qza9nz', 'cmhi0ddeh00ank498bm1425sn', $$Palpation$$, $$[{"text":"La palpation détecte la matité liquidienne.","isCorrect":true},{"text":"Le signe du flot est pathognomonique.","isCorrect":true},{"text":"La palpation du foie et de la rate reste possible.","isCorrect":false,"justification":"Elle est souvent gênée par le liquide."},{"text":"L’abdomen est toujours douloureux.","isCorrect":false,"justification":"Il est souvent indolore sauf infection."},{"text":"Le signe de glaçon est spécifique d’un foie flottant.","isCorrect":true}]$$::jsonb, $$La palpation recherche les signes liquidiens (flot, glaçon) et les organomégalies associées.$$, 5, '2025-11-02T17:52:04.903Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddew00b1k498f5z12paa', 'cmhi0ddeh00ank498bm1425sn', $$Percussion$$, $$[{"text":"La matité déclive change de place selon la position.","isCorrect":true},{"text":"C’est un signe majeur d’ascite.","isCorrect":true},{"text":"La sonorité est centrale en décubitus.","isCorrect":true},{"text":"La percussion est inutile pour l’ascite.","isCorrect":false,"justification":"Elle est essentielle pour détecter le liquide."},{"text":"Le signe du flot peut être retrouvé à la percussion seule.","isCorrect":false,"justification":"Il nécessite la palpation."}]$$::jsonb, $$La percussion est un moyen simple et fiable pour détecter la présence de liquide libre abdominal.$$, 6, '2025-11-02T17:52:04.905Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddez00b3k498kh4bl7un', 'cmhi0ddeh00ank498bm1425sn', $$Auscultation abdominale$$, $$[{"text":"Les bruits hydroaériques sont augmentés.","isCorrect":false,"justification":"Ils sont souvent diminués."},{"text":"L’absence de bruits traduit une occlusion.","isCorrect":false,"justification":"Elle évoque un iléus paralytique."},{"text":"Le souffle systolique est un signe d’ascite.","isCorrect":false,"justification":"C’est un signe vasculaire."},{"text":"L’auscultation est inutile dans l’ascite.","isCorrect":false,"justification":"Elle fait partie de l’examen complet."},{"text":"Une diminution globale des bruits intestinaux est fréquente.","isCorrect":true}]$$::jsonb, $$L’ascite entraîne souvent une diminution des bruits intestinaux par compression des anses.$$, 7, '2025-11-02T17:52:04.907Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddf100b5k498yi1g7lwk', 'cmhi0ddeh00ank498bm1425sn', $$Ascite cirrhotique$$, $$[{"text":"Elle s’accompagne souvent d’ictère et d’angiomes stellaires.","isCorrect":true},{"text":"Elle traduit une hypertension portale chronique.","isCorrect":true},{"text":"Elle s’accompagne d’un liquide riche en protéines.","isCorrect":false,"justification":"Il est pauvre en protéines."},{"text":"Le liquide est souvent clair et citrin.","isCorrect":true},{"text":"Elle s’accompagne rarement de splénomégalie.","isCorrect":false,"justification":"La splénomégalie est fréquente."}]$$::jsonb, $$L’ascite cirrhotique est la forme la plus courante, associant liquide citrin pauvre en protéines et signes d’hypertension portale.$$, 8, '2025-11-02T17:52:04.909Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddf400b7k498cm6drlzg', 'cmhi0ddeh00ank498bm1425sn', $$Ascite d’origine cardiaque$$, $$[{"text":"Elle s’accompagne d’une turgescence jugulaire.","isCorrect":true},{"text":"Elle est riche en protéines.","isCorrect":true},{"text":"Elle s’accompagne souvent d’un œdème des membres inférieurs.","isCorrect":true},{"text":"L’épanchement est citrin clair.","isCorrect":false,"justification":"Il est jaune ambré ou légèrement trouble."},{"text":"Elle est indolore.","isCorrect":true}]$$::jsonb, $$L’ascite cardiaque est riche en protéines et associée à une insuffisance cardiaque droite.$$, 9, '2025-11-02T17:52:04.912Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddf600b9k498iyh7rwkl', 'cmhi0ddeh00ank498bm1425sn', $$Ascite pancréatique$$, $$[{"text":"Elle est pauvre en amylase.","isCorrect":false,"justification":"Elle est riche en amylase."},{"text":"Elle est pauvre en protéines.","isCorrect":false,"justification":"Elle est riche en protéines."},{"text":"Elle est toujours d’origine infectieuse.","isCorrect":false,"justification":"Elle est enzymatique."},{"text":"Elle est indolore.","isCorrect":false,"justification":"Elle est souvent douloureuse."},{"text":"Elle se résorbe spontanément.","isCorrect":false,"justification":"Elle nécessite drainage et traitement du pancréas."}]$$::jsonb, $$L’ascite pancréatique est riche en enzymes et protéines, due à une rupture canalaire.$$, 10, '2025-11-02T17:52:04.914Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddf800bbk498wtcw14h3', 'cmhi0ddeh00ank498bm1425sn', $$Ascite cancéreuse$$, $$[{"text":"Le liquide est hématique ou trouble.","isCorrect":true},{"text":"Il contient des cellules tumorales à la cytologie.","isCorrect":true},{"text":"Elle s’accompagne rarement d’amaigrissement.","isCorrect":false,"justification":"L’amaigrissement est fréquent."},{"text":"Elle guérit sous diurétiques.","isCorrect":false,"justification":"Elle résiste aux traitements habituels."},{"text":"Elle est pauvre en protéines.","isCorrect":false,"justification":"Elle est riche en protéines."}]$$::jsonb, $$L’ascite néoplasique se caractérise par un liquide riche, souvent hématique et la présence de cellules malignes.$$, 11, '2025-11-02T17:52:04.916Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddfa00bdk49804meyles', 'cmhi0ddeh00ank498bm1425sn', $$Ascite tuberculeuse$$, $$[{"text":"Elle est due à une infection du péritoine par le bacille de Koch.","isCorrect":true},{"text":"Le liquide est riche en protéines.","isCorrect":true},{"text":"La ponction retrouve des lymphocytes prédominants.","isCorrect":true},{"text":"Elle s’accompagne souvent de fièvre et de douleurs diffuses.","isCorrect":true},{"text":"Elle est stérile à la culture.","isCorrect":true}]$$::jsonb, $$L’ascite tuberculeuse est lymphocytaire, riche en protéines et associée à un contexte fébrile.$$, 12, '2025-11-02T17:52:04.919Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddfd00bfk498jsjer2ub', 'cmhi0ddeh00ank498bm1425sn', $$Ascite chyleuse$$, $$[{"text":"Le liquide est laiteux et riche en triglycérides.","isCorrect":true},{"text":"Elle résulte d’une rupture des canaux lymphatiques.","isCorrect":true},{"text":"Elle est pauvre en protéines.","isCorrect":false,"justification":"Elle est riche en protéines et lipides."},{"text":"Elle est spécifique de la cirrhose.","isCorrect":false,"justification":"Elle peut être néoplasique, post-opératoire ou traumatique."},{"text":"Elle est souvent associée à une hyperlipémie.","isCorrect":true}]$$::jsonb, $$L’ascite chyleuse se distingue par son aspect laiteux, riche en graisses et en lymphocytes.$$, 13, '2025-11-02T17:52:04.921Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddff00bhk49825gloe1i', 'cmhi0ddeh00ank498bm1425sn', $$Ascite infectée spontanée$$, $$[{"text":"Elle survient surtout chez le cirrhotique.","isCorrect":true},{"text":"Elle est due à la translocation bactérienne.","isCorrect":true},{"text":"Le liquide contient > 250 PNN/mm³.","isCorrect":true},{"text":"Elle nécessite une antibiothérapie immédiate.","isCorrect":true},{"text":"Elle est bénigne sans traitement.","isCorrect":false,"justification":"Elle peut être fatale."}]$$::jsonb, $$L’infection spontanée du liquide d’ascite est une urgence fréquente du cirrhotique nécessitant traitement immédiat.$$, 14, '2025-11-02T17:52:04.924Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddfi00bjk498ytualfjh', 'cmhi0ddeh00ank498bm1425sn', $$Signes de gravité$$, $$[{"text":"Fièvre et douleur abdominale.","isCorrect":true},{"text":"Altération de la conscience.","isCorrect":true},{"text":"Hypotension et tachycardie.","isCorrect":true},{"text":"Ascite indolore chronique.","isCorrect":false,"justification":"C’est une forme stable."},{"text":"Pétéchies cutanées.","isCorrect":true}]$$::jsonb, $$L’apparition de fièvre, hypotension ou troubles neurologiques traduit une complication infectieuse ou hémorragique.$$, 15, '2025-11-02T17:52:04.926Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddfl00blk498t2dxz835', 'cmhi0ddeh00ank498bm1425sn', $$Ponction d’ascite$$, $$[{"text":"Elle est réalisée en décubitus dorsal.","isCorrect":true},{"text":"Elle permet de préciser la nature du liquide.","isCorrect":true},{"text":"Elle est contre-indiquée en cas d’ascite infectée.","isCorrect":false,"justification":"Elle est indiquée justement pour le diagnostic."},{"text":"Elle peut être thérapeutique si l’ascite est volumineuse.","isCorrect":true},{"text":"Elle expose à un risque d’hémorragie.","isCorrect":true}]$$::jsonb, $$La ponction d’ascite est un geste diagnostique et thérapeutique essentiel, mais non dénué de risques.$$, 16, '2025-11-02T17:52:04.929Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddfn00bnk498cl6hw9ht', 'cmhi0ddeh00ank498bm1425sn', $$Aspect macroscopique du liquide$$, $$[{"text":"Citrin clair dans la cirrhose.","isCorrect":true},{"text":"Laiteux dans l’ascite chyleuse.","isCorrect":true},{"text":"Hématique dans la carcinose.","isCorrect":true},{"text":"Purulent dans la péritonite.","isCorrect":true},{"text":"Vert bilieux dans l’ascite cardiaque.","isCorrect":false,"justification":"Ce aspect bilieux évoque une perforation biliaire."}]$$::jsonb, $$L’aspect du liquide d’ascite donne immédiatement une orientation étiologique.$$, 17, '2025-11-02T17:52:04.931Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddfp00bpk498yik4d1wh', 'cmhi0ddeh00ank498bm1425sn', $$Biologie du liquide d’ascite$$, $$[{"text":"Dosage des protéines totales.","isCorrect":true},{"text":"Numération cellulaire.","isCorrect":true},{"text":"Recherche de bactéries et culture.","isCorrect":true},{"text":"Dosage de la lipase pour l’étiologie pancréatique.","isCorrect":true},{"text":"Dosage des triglycérides inutile.","isCorrect":false,"justification":"Il oriente vers une ascite chyleuse."}]$$::jsonb, $$L’étude biochimique et cytologique du liquide est indispensable à la recherche de la cause.$$, 18, '2025-11-02T17:52:04.934Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddfs00brk498n3ba3799', 'cmhi0ddeh00ank498bm1425sn', $$Évolution$$, $$[{"text":"L’ascite peut régresser sous régime désodé et diurétiques.","isCorrect":true},{"text":"Elle récidive souvent dans la cirrhose.","isCorrect":true},{"text":"Elle peut se compliquer d’infection.","isCorrect":true},{"text":"Elle n’a jamais de conséquences respiratoires.","isCorrect":false,"justification":"Une dyspnée de compression est possible."},{"text":"Elle peut évoluer vers la défaillance hépatique.","isCorrect":true}]$$::jsonb, $$L’évolution dépend de la cause et du traitement ; la récidive et l’infection sont fréquentes dans la cirrhose.$$, 19, '2025-11-02T17:52:04.936Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddfu00btk498g83mf95n', 'cmhi0ddeh00ank498bm1425sn', $$Traitement diurétique$$, $$[{"text":"Spironolactone est le diurétique de choix.","isCorrect":true},{"text":"Les doses doivent être progressives.","isCorrect":true},{"text":"L’association avec furosémide est fréquente.","isCorrect":true},{"text":"L’arrêt du sel est inutile.","isCorrect":false,"justification":"Le régime hyposodé est indispensable."},{"text":"Une surveillance biologique est obligatoire.","isCorrect":true}]$$::jsonb, $$Le traitement diurétique est efficace mais nécessite un suivi clinique et biologique attentif.$$, 20, '2025-11-02T17:52:04.939Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddfx00bvk498ydfi81p4', 'cmhi0ddeh00ank498bm1425sn', $$Traitement de la cause$$, $$[{"text":"La cirrhose justifie un traitement étiologique spécifique.","isCorrect":true},{"text":"L’insuffisance cardiaque doit être corrigée.","isCorrect":true},{"text":"L’infection péritonéale impose une antibiothérapie.","isCorrect":true},{"text":"L’ascite cancéreuse guérit après diurétiques.","isCorrect":false,"justification":"Elle résiste aux diurétiques."},{"text":"Le drainage est systématique dans toutes les ascites.","isCorrect":false,"justification":"Il est réservé aux formes tendues ou résistantes."}]$$::jsonb, $$Le traitement dépend de la cause sous-jacente et associe mesures générales, diurétiques et traitement étiologique.$$, 21, '2025-11-02T17:52:04.941Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddg000bxk498m21hsjq0', 'cmhi0ddeh00ank498bm1425sn', $$Complications post-ponction$$, $$[{"text":"Infection secondaire.","isCorrect":true},{"text":"Hémorragie pariétale.","isCorrect":true},{"text":"Perforation d’un organe.","isCorrect":true},{"text":"Hypotension artérielle.","isCorrect":true},{"text":"Hypertension portale aggravée.","isCorrect":false,"justification":"Elle n’est pas directement liée à la ponction."}]$$::jsonb, $$Les complications post-ponction sont rares mais justifient des précautions d’asepsie et une surveillance.$$, 22, '2025-11-02T17:52:04.944Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddg300bzk498an8avr30', 'cmhi0ddeh00ank498bm1425sn', $$Prévention des récidives$$, $$[{"text":"Régime désodé strict.","isCorrect":true},{"text":"Traitement diurétique d’entretien.","isCorrect":true},{"text":"Surveillance échographique régulière.","isCorrect":true},{"text":"Correction des troubles nutritionnels.","isCorrect":true},{"text":"Arrêt du traitement une fois l’ascite disparue.","isCorrect":false,"justification":"Il faut poursuivre la surveillance."}]$$::jsonb, $$La prévention repose sur un suivi rigoureux, un régime hyposodé et une prise en charge hépatique continue.$$, 23, '2025-11-02T17:52:04.947Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddg500c1k498eg9e9wdg', 'cmhi0ddeh00ank498bm1425sn', $$Pronostic$$, $$[{"text":"Dépend de la cause sous-jacente.","isCorrect":true},{"text":"Est toujours favorable.","isCorrect":false,"justification":"L’ascite signale souvent une maladie avancée."},{"text":"Dans la cirrhose, elle marque un tournant évolutif.","isCorrect":true},{"text":"La transplantation hépatique améliore la survie.","isCorrect":true},{"text":"L’ascite maligne est de bon pronostic.","isCorrect":false,"justification":"Elle témoigne d’un stade avancé."}]$$::jsonb, $$Le pronostic de l’ascite dépend étroitement de son étiologie ; la transplantation reste la seule option curative dans la cirrhose terminale.$$, 24, '2025-11-02T17:52:04.949Z');
