INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddgw00cgk4986zfe3mzn', 'cmhi0ddgj00c4k4980nvofm16', $$Signes neuropsychiques$$, $$[{"text":"Asthénie et dépression.","isCorrect":true},{"text":"Confusion mentale.","isCorrect":true},{"text":"Somnolence, coma.","isCorrect":true},{"text":"Hyperactivité motrice.","isCorrect":false,"justification":"Lenteur, non agitation."},{"text":"Troubles anxieux prédominants.","isCorrect":false,"justification":"Moindre fréquence."}]$$::jsonb, $$Hypercalcémie = syndrome neuro-psychique léthargique ou confuso-coma.$$, 5, '2025-11-02T17:52:04.976Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddgy00cik4988nemstx5', 'cmhi0ddgj00c4k4980nvofm16', $$Hyperparathyroïdie secondaire$$, $$[{"text":"Liée à l’insuffisance rénale chronique.","isCorrect":true},{"text":"PTH basse.","isCorrect":false,"justification":"Réaction PTH ↑."},{"text":"Calcémie normale ou basse.","isCorrect":true},{"text":"Phosphatémie normale.","isCorrect":false,"justification":"Souvent ↑."},{"text":"Vitamine D effondrée.","isCorrect":true}]$$::jsonb, $$Secondaire = réaction à l’hypocalcémie d’origine rénale ou vit. D.$$, 6, '2025-11-02T17:52:04.979Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddgl00c6k498205xex9s', 'cmhi0ddgj00c4k4980nvofm16', $$Rôle de la parathormone (PTH)$$, $$[{"text":"Elle augmente la réabsorption rénale du calcium.","isCorrect":true},{"text":"Elle augmente la réabsorption du phosphate.","isCorrect":false,"justification":"La PTH la diminue."},{"text":"Elle stimule la 1-α-hydroxylase rénale.","isCorrect":true},{"text":"Elle inhibe la résorption osseuse.","isCorrect":false,"justification":"Elle l’augmente."},{"text":"Elle abaisse la calcémie.","isCorrect":false,"justification":"Elle l’élève."}]$$::jsonb, $$La PTH augmente la calcémie par action sur os, reins et vitamine D.$$, 0, '2025-11-02T17:52:04.965Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddgn00c8k498h2dyd6li', 'cmhi0ddgj00c4k4980nvofm16', $$Hyperparathyroïdie primaire$$, $$[{"text":"Souvent due à un adénome parathyroïdien.","isCorrect":true},{"text":"S’accompagne d’hypocalcémie.","isCorrect":false,"justification":"Hypercalcémie."},{"text":"Provoque polyurie et lithiases rénales.","isCorrect":true},{"text":"S’accompagne d’hypophosphorémie.","isCorrect":true},{"text":"PTH abaissée.","isCorrect":false,"justification":"PTH élevée."}]$$::jsonb, $$Hyperparathyroïdie = adénome → PTH↑ → Ca²⁺↑ + PO₄³⁻↓ + lithiases.$$, 1, '2025-11-02T17:52:04.967Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddgp00cak4981i6l6kae', 'cmhi0ddgj00c4k4980nvofm16', $$Signes osseux de l’hyperparathyroïdie$$, $$[{"text":"Déminéralisation généralisée.","isCorrect":true},{"text":"Ostéite fibrokystique de Recklinghausen.","isCorrect":true},{"text":"Fractures spontanées.","isCorrect":true},{"text":"Hyperostose cranienne.","isCorrect":false,"justification":"Résorption, non sclérose."},{"text":"Ostéosclérose lombaire.","isCorrect":false,"justification":"Lésions lytique, pas sclérose."}]$$::jsonb, $$L’atteinte osseuse est lythique : ostéite fibrokystique et fractures.$$, 2, '2025-11-02T17:52:04.970Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddgs00cck4989bebf811', 'cmhi0ddgj00c4k4980nvofm16', $$Manifestations rénales$$, $$[{"text":"Lithiases calciques récidivantes.","isCorrect":true},{"text":"Polyurie-polydipsie.","isCorrect":true},{"text":"Insuffisance rénale chronique fréquente.","isCorrect":true},{"text":"Néphrocalcinose rare.","isCorrect":false,"justification":"Souvent présente."},{"text":"Protéinurie glomérulaire majeure.","isCorrect":false,"justification":"Tubulaire faible."}]$$::jsonb, $$Le rein est touché → lithiases, néphrocalcinose, IRC.$$, 3, '2025-11-02T17:52:04.972Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddgu00cek498y42gq13p', 'cmhi0ddgj00c4k4980nvofm16', $$Troubles digestifs liés à l’hypercalcémie$$, $$[{"text":"Constipation.","isCorrect":true},{"text":"Nausées et vomissements.","isCorrect":true},{"text":"Ulcère gastroduodénal.","isCorrect":true},{"text":"Diarrhée profuse.","isCorrect":false,"justification":"Transit ralenti."},{"text":"Pancréatite aiguë possible.","isCorrect":true}]$$::jsonb, $$L’hypercalcémie ralentit le tube digestif et peut induire une pancréatite.$$, 4, '2025-11-02T17:52:04.974Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddh000ckk4986j6rky7r', 'cmhi0ddgj00c4k4980nvofm16', $$Hyperparathyroïdie tertiaire$$, $$[{"text":"Conséquence d’une secondaire ancienne.","isCorrect":true},{"text":"Autonomisation d’une sécrétion PTH.","isCorrect":true},{"text":"Survient après greffe rénale.","isCorrect":true},{"text":"Calcémie basse.","isCorrect":false,"justification":"Re-élevée."},{"text":"PTH normale.","isCorrect":false,"justification":"Toujours ↑."}]$$::jsonb, $$Tertiaire = autonomie parathyroïdienne post-IRC avec hypercalcémie.$$, 7, '2025-11-02T17:52:04.980Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddh200cmk498xos5fkoh', 'cmhi0ddgj00c4k4980nvofm16', $$Hypoparathyroïdie$$, $$[{"text":"PTH basse → hypocalcémie.","isCorrect":true},{"text":"Crise de tétanie possible.","isCorrect":true},{"text":"Hyperphosphorémie.","isCorrect":true},{"text":"QT raccourci.","isCorrect":false,"justification":"QT allongé."},{"text":"Paresthésies péribuccales.","isCorrect":true}]$$::jsonb, $$Hypoparathyroïdie = PTH↓ → Ca²⁺↓, PO₄³⁻↑, tétanie.$$, 8, '2025-11-02T17:52:04.982Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddh300cok498yft5ot89', 'cmhi0ddgj00c4k4980nvofm16', $$Signes cliniques de l’hypocalcémie$$, $$[{"text":"Tétanie musculaire.","isCorrect":true},{"text":"Signe de Chvostek positif.","isCorrect":true},{"text":"Trousseau : spasm carpien après garrot.","isCorrect":true},{"text":"QT court.","isCorrect":false,"justification":"Long."},{"text":"Tachycardie.","isCorrect":false,"justification":"Bradycardie."}]$$::jsonb, $$Hypocalcémie = hyperexcitabilité neuromusculaire ± ECG QT↑.$$, 9, '2025-11-02T17:52:04.983Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddh500cqk49852frmeou', 'cmhi0ddgj00c4k4980nvofm16', $$Causes d’hypoparathyroïdie$$, $$[{"text":"Post-chirurgicale (thyroïdectomie).","isCorrect":true},{"text":"Auto-immune.","isCorrect":true},{"text":"Congénitale (syndrome Di George).","isCorrect":true},{"text":"Toxique (métaux lourds).","isCorrect":true},{"text":"Hypercalcitoninémie.","isCorrect":false,"justification":"Non liée."}]$$::jsonb, $$Hypoparathyroïdie = post-chirurgie, auto-immune ou congénitale.$$, 10, '2025-11-02T17:52:04.985Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddh600csk498sca2ed54', 'cmhi0ddgj00c4k4980nvofm16', $$Biologie de l’hypoparathyroïdie$$, $$[{"text":"PTH basse.","isCorrect":true},{"text":"Calcémie basse.","isCorrect":true},{"text":"Phosphorémie élevée.","isCorrect":true},{"text":"Magnésémie normale.","isCorrect":false,"justification":"Souvent basse."},{"text":"QT normal.","isCorrect":false,"justification":"Allongé."}]$$::jsonb, $$Triade bio = PTH↓ + Ca²⁺↓ + PO₄³⁻↑.$$, 11, '2025-11-02T17:52:04.987Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddh800cuk4988qnymer4', 'cmhi0ddgj00c4k4980nvofm16', $$Crise de tétanie$$, $$[{"text":"Spasme carpien.","isCorrect":true},{"text":"Laryngospasme.","isCorrect":true},{"text":"Troubles respiratoires graves.","isCorrect":true},{"text":"Crise épileptiforme sans fièvre.","isCorrect":true},{"text":"Hyperréflexie tendineuse.","isCorrect":true}]$$::jsonb, $$Crise de tétanie = urgence hypocalcémique vitale.$$, 12, '2025-11-02T17:52:04.988Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddh900cwk498gk9fbmlm', 'cmhi0ddgj00c4k4980nvofm16', $$ECG de l’hypocalcémie$$, $$[{"text":"QT allongé.","isCorrect":true},{"text":"Ondes T aplaties.","isCorrect":true},{"text":"Troubles du rythme.","isCorrect":true},{"text":"Bloc AV haut grade.","isCorrect":false,"justification":"Rare."},{"text":"ST sous-dénivelé.","isCorrect":false,"justification":"Pas typique."}]$$::jsonb, $$Hypocalcémie = QT prolongé, troubles du rythme possibles.$$, 13, '2025-11-02T17:52:04.990Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddhb00cyk4989ang9uwj', 'cmhi0ddgj00c4k4980nvofm16', $$Causes d’hypercalcémie$$, $$[{"text":"Hyperparathyroïdie.","isCorrect":true},{"text":"Cancer osseux ou métastatique.","isCorrect":true},{"text":"Hypervitaminose D.","isCorrect":true},{"text":"Diurétiques thiazidiques.","isCorrect":true},{"text":"Hypoparathyroïdie.","isCorrect":false,"justification":"Hypocalcémie."}]$$::jsonb, $$Hypercalcémie = PTH↑ ou résorption osseuse ↑ ou vit D ↑.$$, 14, '2025-11-02T17:52:04.991Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddhd00d0k4987amenkoc', 'cmhi0ddgj00c4k4980nvofm16', $$Signes ECG de l’hypercalcémie$$, $$[{"text":"QT raccourci.","isCorrect":true},{"text":"Troubles du rythme ventriculaire.","isCorrect":true},{"text":"Bradycardie sinusale.","isCorrect":true},{"text":"QT long.","isCorrect":false,"justification":"Inverse."},{"text":"T négative.","isCorrect":false,"justification":"Non spécifique."}]$$::jsonb, $$Hypercalcémie → QT court, bradycardie, arythmies.$$, 15, '2025-11-02T17:52:04.994Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddhg00d2k498sab9x1nb', 'cmhi0ddgj00c4k4980nvofm16', $$Traitement hypercalcémie$$, $$[{"text":"Réhydratation NaCl 0,9 %.","isCorrect":true},{"text":"Diurétiques de l’anse (Furosémide).","isCorrect":true},{"text":"Biphosphonates.","isCorrect":true},{"text":"Vitamine D haute dose.","isCorrect":false,"justification":"Contre-indiquée."},{"text":"Corticothérapie si sarcoïdose.","isCorrect":true}]$$::jsonb, $$Traitement = réhydratation + furosémide + biphosphonates ± corticoïdes.$$, 16, '2025-11-02T17:52:04.996Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddhh00d4k498wphjs9sz', 'cmhi0ddgj00c4k4980nvofm16', $$Traitement de l’hypocalcémie$$, $$[{"text":"Gluconate de calcium IV.","isCorrect":true},{"text":"Correction du Mg²⁺.","isCorrect":true},{"text":"Apport vitamine D.","isCorrect":true},{"text":"Diurétiques thiazidiques.","isCorrect":false,"justification":"Aggravent."},{"text":"Furosémide IV.","isCorrect":false,"justification":"Favorise perte Ca²⁺."}]$$::jsonb, $$Hypocalcémie = Ca²⁺ IV + Vit. D + corriger Mg²⁺.$$, 17, '2025-11-02T17:52:04.998Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddhj00d6k4980otfmthz', 'cmhi0ddgj00c4k4980nvofm16', $$Dosages biologiques à demander$$, $$[{"text":"Calcium total et ionisé.","isCorrect":true},{"text":"Phosphore.","isCorrect":true},{"text":"Magnésium.","isCorrect":true},{"text":"PTH.","isCorrect":true},{"text":"TSH.","isCorrect":false,"justification":"Pas en première intention."}]$$::jsonb, $$Le bilan biologique de calcium associe Ca, PO₄, Mg, PTH.$$, 18, '2025-11-02T17:52:04.999Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddhk00d8k4985tgl1p2c', 'cmhi0ddgj00c4k4980nvofm16', $$Complications chroniques$$, $$[{"text":"Calcifications vasculaires.","isCorrect":true},{"text":"Néphrocalcinose.","isCorrect":true},{"text":"Troubles cardiaques.","isCorrect":true},{"text":"Ostéite fibrokystique.","isCorrect":true},{"text":"Hypokaliémie sévère.","isCorrect":false,"justification":"Non liée."}]$$::jsonb, $$Les complications chroniques dérivent de l’hypercalcémie prolongée.$$, 19, '2025-11-02T17:52:05.001Z');
INSERT INTO "Chapter" (id, "subjectId", title, description, "orderIndex", "pdfUrl", "createdAt") VALUES ('cmhi0ddhn00dak498f4cyevzn', 'cmhi0ddgc00c2k498wjm0hlhk', $$Chapitre 2 – Pathologies cortico-surrénaliennes$$, NULL, 2, NULL, '2025-11-02T17:52:05.003Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddhq00dek498mn88bh1t', 'cmhi0ddhn00dak498f4cyevzn', $$ICS primaire (Addison)$$, $$[{"text":"Hypotension orthostatique.","isCorrect":true},{"text":"Mélanodermie.","isCorrect":true},{"text":"Hypernatrémie.","isCorrect":false,"justification":"Hyponatrémie."},{"text":"Hyperkaliémie.","isCorrect":true},{"text":"Pâleur cutanée.","isCorrect":false,"justification":"Coloration mélanique."}]$$::jsonb, $$Addison = mélanodermie + hypoTA + Na↓ + K↑.$$, 1, '2025-11-02T17:52:05.006Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddht00dgk4987xrugbbf', 'cmhi0ddhn00dak498f4cyevzn', $$ICS secondaire (hypophysaire)$$, $$[{"text":"Absence de mélanodermie.","isCorrect":true},{"text":"ACTH basse.","isCorrect":true},{"text":"Aldostérone conservée.","isCorrect":true},{"text":"Cortisol élevé.","isCorrect":false,"justification":"Bas."},{"text":"Hyperpigmentation marquée.","isCorrect":false,"justification":"Absente."}]$$::jsonb, $$ICS secondaire = ACTH basse, pas de mélanodermie, aldostérone normale.$$, 2, '2025-11-02T17:52:05.009Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddho00dck4981bkpvb2k', 'cmhi0ddhn00dak498f4cyevzn', $$Insuffisance corticosurrénalienne (ICS) : définition$$, $$[{"text":"Déficit de sécrétion du cortisol et/ou d’aldostérone.","isCorrect":true},{"text":"Excès d’adrénaline médullosurrénalienne.","isCorrect":false,"justification":"C’est l’inverse."},{"text":"D’origine toujours hypophysaire.","isCorrect":false,"justification":"Peut être primitive (Addison) ou secondaire."},{"text":"Touche préférentiellement les femmes.","isCorrect":true}]$$::jsonb, $$L’ICS = défaut de cortisol ± aldostérone, souvent auto-immune et féminine.$$, 0, '2025-11-02T17:52:05.005Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddhv00dik498jc26vq8b', 'cmhi0ddhn00dak498f4cyevzn', $$Forme aiguë d’ICS$$, $$[{"text":"Choc collapsus.","isCorrect":true},{"text":"Hypoglycémie sévère.","isCorrect":true},{"text":"Hyperthermie intense.","isCorrect":false,"justification":"Normo- ou hypothermie."},{"text":"Vomissements, diarrhée, déshydratation.","isCorrect":true},{"text":"Bradycardie réflexe.","isCorrect":false,"justification":"Tachycardie compensatrice."}]$$::jsonb, $$ICS aiguë = choc + troubles digestifs + hypoglycémie.$$, 3, '2025-11-02T17:52:05.012Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddhx00dkk498pw6x8zd0', 'cmhi0ddhn00dak498f4cyevzn', $$Biologie de l’ICS$$, $$[{"text":"Na ↓ K ↑ glycémie ↓.","isCorrect":true},{"text":"Cortisol urinaire ↑.","isCorrect":false,"justification":"Baisse."},{"text":"ACTH ↑ dans Addison.","isCorrect":true},{"text":"Réponse normale au Synacthène.","isCorrect":false,"justification":"Nulle ou faible."},{"text":"Alcalose métabolique.","isCorrect":false,"justification":"Acidose."}]$$::jsonb, $$ICS = Na↓ K↑ gly↓ ACTH↑ cortisol ↓.$$, 4, '2025-11-02T17:52:05.013Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddhz00dmk4982owh4r9k', 'cmhi0ddhn00dak498f4cyevzn', $$Traitement de l’ICS$$, $$[{"text":"Hydrocortisone per os ou IV.","isCorrect":true},{"text":"Fludrocortisone pour le remplacement minéralocorticoïde.","isCorrect":true},{"text":"Administration unique matinale.","isCorrect":true},{"text":"Arrêt brutal sans danger.","isCorrect":false,"justification":"Risque de crise addisonienne."},{"text":"Régime pauvre en sel.","isCorrect":false,"justification":"Riche en sel."}]$$::jsonb, $$Substitution = hydrocortisone + fludrocortisone, jamais arrêt brutal.$$, 5, '2025-11-02T17:52:05.015Z');
