INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddk200fyk498iulxxatr', 'cmhi0ddix00eqk498jfvq9v25', $$Bilan thyroïdien$$, $$[{"text":"Dosage TSH en 1ʳᵉ intention.","isCorrect":true},{"text":"Si anormale → T4 libre.","isCorrect":true},{"text":"Anticorps anti-TPO pour auto-immunité.","isCorrect":true},{"text":"Biopsie toujours nécessaire.","isCorrect":false,"justification":"Seulement si nodule suspect."},{"text":"Scintigraphie en 1ʳᵉ ligne.","isCorrect":false,"justification":"Après biologie."}]$$::jsonb, $$Bilan = TSH → T4L → Ac anti-TPO.$$, 21, '2025-11-02T17:52:05.090Z');
