INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddkd00g8k498zxx3c56n', 'cmhi0ddkb00g6k498xz2bo886', $$Rôle de l’hypophyse$$, $$[{"text":"Produit directement l’insuline.","isCorrect":false,"justification":"Sécrétée par le pancréas."},{"text":"Régule les glandes endocrines périphériques.","isCorrect":true},{"text":"Sécrète la GH, la TSH, l’ACTH, la FSH, la LH et la prolactine.","isCorrect":true},{"text":"N’a aucun lien avec l’hypothalamus.","isCorrect":false,"justification":"L’hypothalamus la contrôle via les RH/IH."},{"text":"Ne contient qu’un seul type cellulaire.","isCorrect":false,"justification":"Plusieurs types endocrines distincts."}]$$::jsonb, $$L’hypophyse est la glande chef d’orchestre du système endocrinien.$$, 0, '2025-11-02T17:52:05.101Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddkf00gak4980c4t9zxy', 'cmhi0ddkb00g6k498xz2bo886', $$Insuffisance antéhypophysaire globale$$, $$[{"text":"Provoque une hyperpigmentation cutanée.","isCorrect":false,"justification":"C’est l’Addison (ACTH↑)."},{"text":"Entraîne fatigue, amaigrissement et hypotension.","isCorrect":true},{"text":"Se manifeste par une aménorrhée chez la femme.","isCorrect":true},{"text":"S’accompagne d’hyperglycémie.","isCorrect":false,"justification":"Hypoglycémie par cortisol bas."},{"text":"Cause une hyperthyroïdie secondaire.","isCorrect":false,"justification":"Hypothyroïdie secondaire."}]$$::jsonb, $$L’hypopituitarisme global = pâleur, hypotension, aménorrhée, hypoglycémie.$$, 1, '2025-11-02T17:52:05.103Z');
