INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddki00gek4988eb900bc', 'cmhi0ddkb00g6k498xz2bo886', $$Hypopituitarisme partiel$$, $$[{"text":"Touche toujours toutes les hormones.","isCorrect":false,"justification":"Peut être sélectif."},{"text":"Le déficit gonadotrope provoque une impuissance.","isCorrect":true},{"text":"Le déficit thyréotrope cause une TSH basse.","isCorrect":true},{"text":"Le déficit corticotrope élève le cortisol.","isCorrect":false,"justification":"Cortisol bas."},{"text":"Le déficit somatotrope provoque une obésité androïde.","isCorrect":true}]$$::jsonb, $$L’hypopituitarisme peut être partiel, selon l’axe hormonal atteint.$$, 3, '2025-11-02T17:52:05.106Z');
