INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddlj00hck498m64cfoll', 'cmhi0ddkb00g6k498xz2bo886', $$Traitement chirurgical$$, $$[{"text":"Adénomectomie trans-sphénoïdale.","isCorrect":true},{"text":"Radiothérapie complémentaire.","isCorrect":true},{"text":"Substitution hormonale postopératoire.","isCorrect":true},{"text":"Cure médicamenteuse définitive.","isCorrect":false,"justification":"Chirurgie souvent nécessaire."},{"text":"Accès fronto-orbitaire privilégié.","isCorrect":false,"justification":"Par voie trans-sphénoïdale."}]$$::jsonb, $$Chirurgie = voie trans-sphénoïdale + substitution hormonale.$$, 20, '2025-11-02T17:52:05.143Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddll00hek498hyarupbe', 'cmhi0ddkb00g6k498xz2bo886', $$Médicaments de l’hyperprolactinémie$$, $$[{"text":"Bromocriptine.","isCorrect":true},{"text":"Cabergoline.","isCorrect":true},{"text":"Quinagolide.","isCorrect":true},{"text":"Leuprolide.","isCorrect":false,"justification":"Agoniste GnRH."},{"text":"Thiamazole.","isCorrect":false,"justification":"Thyroïde."}]$$::jsonb, $$Traitement = agonistes dopaminergiques (bromocriptine, cabergoline).$$, 21, '2025-11-02T17:52:05.145Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddln00hgk4984z2w60mc', 'cmhi0ddkb00g6k498xz2bo886', $$Adénome à GH : traitement$$, $$[{"text":"Chirurgie trans-sphénoïdale.","isCorrect":true},{"text":"Analogues de la somatostatine.","isCorrect":true},{"text":"Antagonistes de GH (pegvisomant).","isCorrect":true},{"text":"L-thyroxine.","isCorrect":false,"justification":"Non liée."},{"text":"Antithyroïdiens de synthèse.","isCorrect":false,"justification":"Non."}]$$::jsonb, $$Acromégalie → chirurgie + analogues somatostatine + pegvisomant.$$, 22, '2025-11-02T17:52:05.147Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddlo00hik498nx0jse9o', 'cmhi0ddkb00g6k498xz2bo886', $$Hypopituitarisme post-traumatique$$, $$[{"text":"Peut suivre un traumatisme crânien.","isCorrect":true},{"text":"Peut provoquer une ICS secondaire.","isCorrect":true},{"text":"Peut causer diabète insipide.","isCorrect":true},{"text":"TSH↑.","isCorrect":false,"justification":"Basse."},{"text":"Hyperglycémie.","isCorrect":false,"justification":"Hypoglycémie."}]$$::jsonb, $$Post-traumatique = hypopituitarisme partiel + DI possible.$$, 23, '2025-11-02T17:52:05.149Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddlq00hkk498qbkmnjtu', 'cmhi0ddkb00g6k498xz2bo886', $$Récapitulatif$$, $$[{"text":"ICS secondaire → ACTH↓, pas de mélanodermie.","isCorrect":true},{"text":"Hypothyroïdie secondaire → TSH↓.","isCorrect":true},{"text":"GH↑ → acromégalie/gigantisme.","isCorrect":true},{"text":"Prolactine↑ → aménorrhée/galactorrhée.","isCorrect":true},{"text":"ADH↓ → diabète insipide.","isCorrect":true}]$$::jsonb, $$L’hypophyse = chef d’orchestre, tout déficit/hypersecrétion a signature clinique propre$$, 24, '2025-11-02T17:52:05.150Z');
INSERT INTO "Chapter" (id, "subjectId", title, description, "orderIndex", "pdfUrl", "createdAt") VALUES ('cmhi0ddls00hmk498r39ffx6u', 'cmhi0ddgc00c2k498wjm0hlhk', $$Chapitre 5 – Troubles du métabolisme glucidique & lipidique$$, NULL, 5, NULL, '2025-11-02T17:52:05.152Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddlu00hok498rw84mkn1', 'cmhi0ddls00hmk498r39ffx6u', $$Définition du diabète sucré$$, $$[{"text":"Maladie caractérisée par une hypoglycémie chronique.","isCorrect":false,"justification":"C’est une hyperglycémie chronique."},{"text":"Résulte d’un déficit ou d’une résistance à l’insuline.","isCorrect":true},{"text":"Toujours due à une destruction auto-immune des cellules β.","isCorrect":false,"justification":"Type 2 = résistance périphérique."},{"text":"Peut entraîner des complications micro et macrovasculaires.","isCorrect":true},{"text":"Ne concerne que l’adulte.","isCorrect":false,"justification":"Tous les âges."}]$$::jsonb, $$Le diabète sucré = hyperglycémie chronique, par défaut d’insuline ou résistance à son action.$$, 0, '2025-11-02T17:52:05.154Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddlv00hqk498v0uwlk16', 'cmhi0ddls00hmk498r39ffx6u', $$Principaux signes cliniques du diabète$$, $$[{"text":"Prise de poids rapide.","isCorrect":false,"justification":"Amaigrissement malgré appétit ↑."},{"text":"Polyurie et polydipsie.","isCorrect":true},{"text":"Polypnée de Kussmaul.","isCorrect":true},{"text":"Hypotension orthostatique rare.","isCorrect":false,"justification":"Fréquente par déshydratation."},{"text":"Sueurs froides et tremblements.","isCorrect":false,"justification":"Hypoglycémie, pas hyper."}]$$::jsonb, $$Le diabète se manifeste par polyurie, polydipsie, amaigrissement, fatigue.$$, 1, '2025-11-02T17:52:05.155Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddlx00hsk49899q9djkd', 'cmhi0ddls00hmk498r39ffx6u', $$Diagnostic biologique$$, $$[{"text":"Glycémie à jeun ≥ 1,26 g/L (×2) = diabète.","isCorrect":true},{"text":"HbA1c ≥ 6,5 % = critère diagnostic.","isCorrect":true},{"text":"Test d’hyperglycémie provoquée positif si 2h ≥ 2,0 g/L.","isCorrect":true},{"text":"Diagnostic si glycémie postprandiale < 1,0 g/L.","isCorrect":false,"justification":"Trop basse."},{"text":"Un seul test suffit toujours.","isCorrect":false,"justification":"Confirmation nécessaire."}]$$::jsonb, $$Diagnostic si GAJ ≥ 1,26, HGPO ≥ 2,0, ou HbA1c ≥ 6,5 %.$$, 2, '2025-11-02T17:52:05.157Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddlz00huk498k6qnvwxw', 'cmhi0ddls00hmk498r39ffx6u', $$Complications aiguës du diabète$$, $$[{"text":"Hypoglycémie.","isCorrect":true},{"text":"Coma acidocétosique.","isCorrect":true},{"text":"Coma hyperosmolaire.","isCorrect":true},{"text":"Hyponatrémie de dilution.","isCorrect":false,"justification":"Hypernatrémie fréquente."},{"text":"Hypercalcémie associée.","isCorrect":false,"justification":"Non spécifique."}]$$::jsonb, $$Les urgences diabétiques = hypoglycémie, acidocétose, HHS.$$, 3, '2025-11-02T17:52:05.159Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddm100hwk498iwsv1vf1', 'cmhi0ddls00hmk498r39ffx6u', $$Hypoglycémie (triade de Whipple)$$, $$[{"text":"Hypoglycémie < 1,5 g/L.","isCorrect":false,"justification":"< 0,5 g/L (3 mmol/L)."},{"text":"Symptômes neurovégétatifs et neuroglycopéniques.","isCorrect":true},{"text":"Régression après correction glycémique.","isCorrect":true},{"text":"Toujours asymptomatique.","isCorrect":false,"justification":"Symptomatique typique."},{"text":"Survient uniquement chez DT1.","isCorrect":false,"justification":"Aussi DT2 sous insuline/sulfamides."}]$$::jsonb, $$Triade de Whipple = symptômes + gly basse + correction efficace.$$, 4, '2025-11-02T17:52:05.162Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddm300hyk498w9fi2w34', 'cmhi0ddls00hmk498r39ffx6u', $$Coma acidocétosique diabétique$$, $$[{"text":"Survient surtout chez le diabétique de type 2.","isCorrect":false,"justification":"Typique du type 1."},{"text":"Due à un déficit profond en insuline.","isCorrect":true},{"text":"Se traduit par des vomissements et une polypnée de Kussmaul.","isCorrect":true},{"text":"Alcalose métabolique.","isCorrect":false,"justification":"C’est une acidose."},{"text":"Odeur d’amande amère à l’haleine.","isCorrect":false,"justification":"Fruitée (acétonique)."}]$$::jsonb, $$Le coma acidocétosique = déficit en insuline → cétose + acidose métabolique.$$, 5, '2025-11-02T17:52:05.163Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddm500i0k498jrbhi954', 'cmhi0ddls00hmk498r39ffx6u', $$Coma hyperosmolaire$$, $$[{"text":"Acidose métabolique profonde.","isCorrect":false,"justification":"pH souvent normal."},{"text":"Sujet âgé, diabète de type 2.","isCorrect":true},{"text":"Hyperglycémie majeure > 6 g/L.","isCorrect":true},{"text":"Déshydratation souvent modérée.","isCorrect":false,"justification":"Massive."},{"text":"Cétose importante.","isCorrect":false,"justification":"Minime."}]$$::jsonb, $$Le coma hyperosmolaire = gly très ↑, déshydratation ++, sans cétose.$$, 6, '2025-11-02T17:52:05.165Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddm600i2k498kixcfp9f', 'cmhi0ddls00hmk498r39ffx6u', $$Hypoglycémie sévère$$, $$[{"text":"Peut être déclenchée par un excès d’insuline.","isCorrect":true},{"text":"Toujours précédée de céphalées intenses.","isCorrect":false,"justification":"Non systématique."},{"text":"Se manifeste par sueurs, tremblements, palpitations.","isCorrect":true},{"text":"Doit être corrigée par sucre lent.","isCorrect":false,"justification":"Sucre rapide nécessaire."},{"text":"La conscience revient lentement après traitement.","isCorrect":true}]$$::jsonb, $$Hypoglycémie = sueurs + tremblements + correction immédiate par sucre rapide.$$, 7, '2025-11-02T17:52:05.167Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddm800i4k498efinycc4', 'cmhi0ddls00hmk498r39ffx6u', $$Complications chroniques du diabète$$, $$[{"text":"Rétinopathie, néphropathie, neuropathie.","isCorrect":true},{"text":"Arthrite diabétique spécifique.","isCorrect":false,"justification":"Pas de lien direct."},{"text":"Cardiopathie ischémique, AVC, artériopathie.","isCorrect":true},{"text":"Myosite infectieuse rare.","isCorrect":false,"justification":"Non caractéristique."},{"text":"Néphrite auto-immune.","isCorrect":false,"justification":"Néphropathie glomérulaire diabétique."}]$$::jsonb, $$Complications micro + macrovasculaires : rétino, néphro, neuro, cardiaque, AOMI, AVC.$$, 8, '2025-11-02T17:52:05.168Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddm900i6k498c848xkzy', 'cmhi0ddls00hmk498r39ffx6u', $$Syndrome du pied diabétique$$, $$[{"text":"Atteinte isolée du gros orteil.","isCorrect":false,"justification":"Ulcérations plantaires distales fréquentes."},{"text":"Résulte d’une neuropathie et d’une artériopathie.","isCorrect":true},{"text":"Risque d’amputation élevé.","isCorrect":true},{"text":"Hyperalgésie constante.","isCorrect":false,"justification":"Hypoalgésie (perte de sensibilité)."},{"text":"Cicatrisation rapide.","isCorrect":false,"justification":"Lente et difficile."}]$$::jsonb, $$Pied diabétique = neuropathie + ischémie + infection → amputation possible$$, 9, '2025-11-02T17:52:05.170Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddmb00i8k498x3ryntvu', 'cmhi0ddls00hmk498r39ffx6u', $$Diabète et appareil cardiovasculaire$$, $$[{"text":"Le diabète n’a aucun effet sur les artères coronaires.","isCorrect":false,"justification":"Risque coronarien multiplié par 3 à 4."},{"text":"L’athérosclérose est accélérée.","isCorrect":true},{"text":"L’HTA est fréquente chez les diabétiques.","isCorrect":true},{"text":"L’hypotension est un signe classique.","isCorrect":false,"justification":"HTA prédomine."},{"text":"Les statines sont contre-indiquées.","isCorrect":false,"justification":"Indiquées en prévention CV."}]$$::jsonb, $$Diabète = athérogène → risque ↑ de coronaropathie, AVC, AOMI.$$, 10, '2025-11-02T17:52:05.171Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddmc00iak498a5ftzvyd', 'cmhi0ddls00hmk498r39ffx6u', $$Néphropathie diabétique$$, $$[{"text":"Se manifeste d’abord par une macroalbuminurie.","isCorrect":false,"justification":"Microalbuminurie initiale."},{"text":"Est une atteinte glomérulaire chronique.","isCorrect":true},{"text":"Peut évoluer vers l’insuffisance rénale terminale.","isCorrect":true},{"text":"Est toujours réversible.","isCorrect":false,"justification":"Irréversible si tardive."},{"text":"Est aggravée par une HTA mal contrôlée.","isCorrect":true}]$$::jsonb, $$Néphropathie = microalbuminurie → IRC, aggravée par HTA.$$, 11, '2025-11-02T17:52:05.173Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddme00ick49880mrx0l7', 'cmhi0ddls00hmk498r39ffx6u', $$Rétinopathie diabétique$$, $$[{"text":"Résulte d’une atteinte vasculaire rétinienne chronique.","isCorrect":true},{"text":"Est asymptomatique longtemps.","isCorrect":true},{"text":"Se manifeste d’abord par une baisse brutale de la vision.","isCorrect":false,"justification":"Tardive."},{"text":"Peut nécessiter photocoagulation au laser.","isCorrect":true},{"text":"Est plus fréquente dans le diabète de type 2 uniquement.","isCorrect":false,"justification":"Présente dans les deux types."}]$$::jsonb, $$Rétinopathie = atteinte microvasculaire progressive → dépistage ophtalmo régulier.$$, 12, '2025-11-02T17:52:05.174Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddmh00iek498m4yt666v', 'cmhi0ddls00hmk498r39ffx6u', $$Neuropathie diabétique$$, $$[{"text":"Toujours douloureuse.","isCorrect":false,"justification":"Parfois indolore (perte de sensibilité)."},{"text":"Atteinte sensitive, motrice ou végétative.","isCorrect":true},{"text":"Se manifeste par des paresthésies en « gants et chaussettes ».","isCorrect":true},{"text":"Les réflexes sont exagérés.","isCorrect":false,"justification":"Abolis."},{"text":"Elle est d’origine auto-immune.","isCorrect":false,"justification":"Métabolique et vasculaire."}]$$::jsonb, $$Neuropathie = atteinte distale sensitive/motrice, cause du pied diabétique.$$, 13, '2025-11-02T17:52:05.177Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddmj00igk498a0ot6ibu', 'cmhi0ddls00hmk498r39ffx6u', $$Hypoglycémie iatrogène$$, $$[{"text":"Survient uniquement avec l’insuline.","isCorrect":false,"justification":"Aussi sulfamides hypoglycémiants."},{"text":"Peut être favorisée par un repas sauté.","isCorrect":true},{"text":"Est toujours grave.","isCorrect":false,"justification":"Gravité variable selon symptômes."},{"text":"Peut entraîner un coma.","isCorrect":true},{"text":"Ne provoque jamais de troubles de conscience.","isCorrect":false,"justification":"Possible coma hypoglycémique."}]$$::jsonb, $$Hypoglycémie iatrogène = médicaments + jeûne + excès d’insuline → coma possible.$$, 14, '2025-11-02T17:52:05.179Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddml00iik498zy7egtan', 'cmhi0ddls00hmk498r39ffx6u', $$Coma hyperosmolaire (rappel sémiologique)$$, $$[{"text":"Touche surtout les jeunes diabétiques.","isCorrect":false,"justification":"Sujet âgé avec diabète de type 2."},{"text":"L’acidose métabolique est constante.","isCorrect":false,"justification":"Absente ou légère."},{"text":"L’hyperglycémie est majeure (> 6 g/L).","isCorrect":true},{"text":"L’hyperosmolarité plasmatique est marquée.","isCorrect":true},{"text":"Les corps cétoniques sont abondants.","isCorrect":false,"justification":"Faibles."}]$$::jsonb, $$Coma hyperosmolaire = glycémie très élevée, déshydratation, pas de cétose.$$, 15, '2025-11-02T17:52:05.181Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddmn00ikk498jvplzhxx', 'cmhi0ddls00hmk498r39ffx6u', $$Coma acidocétosique (rappel)$$, $$[{"text":"Résulte d’un excès d’insuline.","isCorrect":false,"justification":"Déficit profond d’insuline."},{"text":"Cétonémie élevée.","isCorrect":true},{"text":"pH < 7,30.","isCorrect":true},{"text":"Hyperosmolarité absente.","isCorrect":false,"justification":"Modérée."},{"text":"Kaliémie toujours basse au début.","isCorrect":false,"justification":"Souvent normale ou ↑, puis ↓ après traitement."}]$$::jsonb, $$Acidocétose = déficit insulinique + cétose + acidose métabolique.$$, 16, '2025-11-02T17:52:05.183Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddmp00imk498lswssaku', 'cmhi0ddls00hmk498r39ffx6u', $$Troubles lipidiques : généralités$$, $$[{"text":"Les dyslipidémies sont toujours d’origine génétique.","isCorrect":false,"justification":"Souvent secondaires (diabète, obésité)."},{"text":"L’excès de LDL favorise l’athérosclérose.","isCorrect":true},{"text":"L’excès de HDL est athérogène.","isCorrect":false,"justification":"Protecteur."},{"text":"L’hypertriglycéridémie expose à la pancréatite aiguë.","isCorrect":true},{"text":"L’hypocholestérolémie est toujours pathologique.","isCorrect":false,"justification":"Pas toujours."}]$$::jsonb, $$Les dyslipidémies = LDL ↑ → risque CV, TG ↑ → pancréatite.$$, 17, '2025-11-02T17:52:05.185Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddmr00iok4982wftmm4t', 'cmhi0ddls00hmk498r39ffx6u', $$Sémiologie clinique des hyperlipidémies$$, $$[{"text":"Xanthomes tendineux dans l’hypercholestérolémie.","isCorrect":true},{"text":"Xanthélasmas palpébraux.","isCorrect":true},{"text":"Arc cornéen précoce chez le jeune.","isCorrect":true},{"text":"Pétéchies cutanées diffuses.","isCorrect":false,"justification":"Non spécifiques."},{"text":"Taches café-au-lait.","isCorrect":false,"justification":"Aucune relation."}]$$::jsonb, $$Hyperlipidémie = xanthomes, xanthélasmas, arc cornéen.$$, 18, '2025-11-02T17:52:05.187Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddms00iqk498260n8pi6', 'cmhi0ddls00hmk498r39ffx6u', $$Hypercholestérolémie familiale$$, $$[{"text":"Transmission autosomique dominante.","isCorrect":true},{"text":"Mutation du gène du récepteur LDL.","isCorrect":true},{"text":"LDL normaux.","isCorrect":false,"justification":"Très élevés."},{"text":"HDL très bas.","isCorrect":false,"justification":"Normaux."},{"text":"Risque coronarien précoce.","isCorrect":true}]$$::jsonb, $$Hypercholestérolémie familiale = LDL↑↑, xanthomes, coronaropathie précoce.$$, 19, '2025-11-02T17:52:05.189Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddmu00isk498tgihy5uq', 'cmhi0ddls00hmk498r39ffx6u', $$Pancréatite aiguë d’origine métabolique$$, $$[{"text":"Liée à une hypercholestérolémie pure.","isCorrect":false,"justification":"C’est l’hypertriglycéridémie sévère (>10 g/L)."},{"text":"Peut compliquer un diabète déséquilibré.","isCorrect":true},{"text":"Se traduit par des douleurs épigastriques transfixiantes.","isCorrect":true},{"text":"Est favorisée par les statines.","isCorrect":false,"justification":"Non, plutôt protectrices cardiovasculaires."},{"text":"Nécessite une insulinothérapie et un jeûne.","isCorrect":true}]$$::jsonb, $$Pancréatite métabolique = TG très ↑, douleurs abdominales, traitement par jeûne + insuline ± fibrates.$$, 20, '2025-11-02T17:52:05.190Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddmw00iuk498pbwpqbz5', 'cmhi0ddls00hmk498r39ffx6u', $$Syndrome métabolique$$, $$[{"text":"Regroupe obésité, HTA, hyperglycémie et dyslipidémie.","isCorrect":true},{"text":"Diminue le risque coronarien.","isCorrect":false,"justification":"L’augmente fortement."},{"text":"Fait partie des causes de diabète de type 1.","isCorrect":false,"justification":"Type 2."},{"text":"S’accompagne souvent d’une résistance à l’insuline.","isCorrect":true},{"text":"Est toujours d’origine génétique.","isCorrect":false,"justification":"Souvent environnementale."}]$$::jsonb, $$Syndrome métabolique = résistance insulinique + obésité + HTA + dyslipidémie.$$, 21, '2025-11-02T17:52:05.193Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddmz00iwk498ikmv8fpq', 'cmhi0ddls00hmk498r39ffx6u', $$Traitement du diabète$$, $$[{"text":"L’insuline est réservée au diabète de type 2.","isCorrect":false,"justification":"Type 1 obligatoire, parfois type 2."},{"text":"La metformine diminue la production hépatique de glucose.","isCorrect":true},{"text":"Les sulfamides hypoglycémiants stimulent la sécrétion d’insuline.","isCorrect":true},{"text":"Les analogues du GLP-1 entraînent une prise de poids.","isCorrect":false,"justification":"Perte pondérale."},{"text":"Les inhibiteurs de SGLT2 favorisent la rétention hydrique.","isCorrect":false,"justification":"Effet diurétique."}]$$::jsonb, $$Traitement = insuline (DT1) ou oraux (DT2) : metformine, sulfamides, GLP-1, SGLT2.$$, 22, '2025-11-02T17:52:05.195Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddn100iyk498m9i9umbd', 'cmhi0ddls00hmk498r39ffx6u', $$Diabète et grossesse$$, $$[{"text":"Le diabète gestationnel est rare.","isCorrect":false,"justification":"Fréquent (≈ 10 % des grossesses)."},{"text":"L’insuline est le traitement de référence.","isCorrect":true},{"text":"La metformine est sans risque au 1ᵉʳ trimestre.","isCorrect":false,"justification":"À éviter, insuline préférée."},{"text":"Un contrôle glycémique strict est essentiel.","isCorrect":true},{"text":"Le dépistage se fait par hyperglycémie provoquée orale.","isCorrect":true}]$$::jsonb, $$Diabète gestationnel = HGPO au 2ᵉ trimestre, ttt par insuline, contrôle strict.$$, 23, '2025-11-02T17:52:05.197Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddn300j0k4983ay3xhsc', 'cmhi0ddls00hmk498r39ffx6u', $$Synthèse finale : points clés sémiologiques$$, $$[{"text":"Diabète → hyperglycémie chronique.","isCorrect":true},{"text":"Hypoglycémie → déficit en glucagon.","isCorrect":false,"justification":"Excès d’insuline."},{"text":"Acidocétose → pH bas et cétonurie positive.","isCorrect":true},{"text":"Hyperlipidémie → xanthomes et pancréatite.","isCorrect":true},{"text":"Syndrome métabolique → baisse du risque cardiovasculaire.","isCorrect":false,"justification":"Risque ↑."}]$$::jsonb, $$L’équilibre glycémique et lipidique est essentiel pour prévenir comas et complications vasculaires.$$, 24, '2025-11-02T17:52:05.199Z');
