INSERT INTO "Chapter" (id, "subjectId", title, description, "orderIndex", "pdfUrl", "createdAt") VALUES ('cmhi0ddnd00j3k498tr21p4v6', 'cmhi0ddn900j1k4988obeo56w', $$Chapitre 1 – Syndrome neurogène périphérique$$, NULL, 1, NULL, '2025-11-02T17:52:05.209Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddnk00jbk498towiyjyo', 'cmhi0ddnd00j3k498tr21p4v6', $$Amyotrophie$$, $$[{"text":"Elle correspond à une diminution du volume musculaire.","isCorrect":true},{"text":"Elle apparaît en quelques jours à semaines.","isCorrect":true},{"text":"Elle est toujours symétrique.","isCorrect":false,"justification":"Elle peut être focale ou asymétrique."},{"text":"Elle traduit une dénervation.","isCorrect":true},{"text":"Elle est douloureuse.","isCorrect":false,"justification":"Elle est indolore sauf myalgies associées."}]$$::jsonb, $$L’amyotrophie est précoce, localisée et indolore, reflet d’une dénervation musculaire.$$, 3, '2025-11-02T17:52:05.217Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddnf00j5k4984mlq21su', 'cmhi0ddnd00j3k498tr21p4v6', $$Définition$$, $$[{"text":"Le syndrome neurogène périphérique traduit une atteinte du motoneurone périphérique.","isCorrect":true},{"text":"Il correspond à une lésion du faisceau pyramidal.","isCorrect":false,"justification":"Cela définit le syndrome pyramidal."},{"text":"Il s’accompagne d’un déficit moteur flasque.","isCorrect":true},{"text":"Il s’accompagne toujours d’une spasticité.","isCorrect":false,"justification":"La spasticité est centrale."},{"text":"Il est caractérisé par une amyotrophie et une aréflexie.","isCorrect":true}]$$::jsonb, $$Le syndrome neurogène périphérique associe déficit moteur flasque, aréflexie et amyotrophie.$$, 0, '2025-11-02T17:52:05.211Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddnh00j7k4987r2kxenh', 'cmhi0ddnd00j3k498tr21p4v6', $$Topographie possible$$, $$[{"text":"Motoneurone périphérique (corne antérieure).","isCorrect":true},{"text":"Racine antérieure.","isCorrect":true},{"text":"Nerf périphérique.","isCorrect":true},{"text":"Plaque motrice.","isCorrect":true},{"text":"Cortex moteur.","isCorrect":false,"justification":"Il appartient au territoire central."}]$$::jsonb, $$Le SNP peut résulter d’une atteinte du motoneurone périphérique, radiculaire, tronculaire ou jonctionnelle.$$, 1, '2025-11-02T17:52:05.213Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddnj00j9k498g1gugunf', 'cmhi0ddnd00j3k498tr21p4v6', $$Signes moteurs$$, $$[{"text":"Déficit moteur flasque.","isCorrect":true},{"text":"Hypotonie.","isCorrect":true},{"text":"ROT abolis.","isCorrect":true},{"text":"Réflexe cutané plantaire en extension.","isCorrect":false,"justification":"Ce Babinski positif signe une atteinte centrale."},{"text":"Amyotrophie tardive.","isCorrect":false,"justification":"Elle apparaît précocement dans le SNP."}]$$::jsonb, $$Le syndrome moteur périphérique associe hypotonie, aréflexie et amyotrophie rapide.$$, 2, '2025-11-02T17:52:05.215Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddnm00jdk498t6cimmi4', 'cmhi0ddnd00j3k498tr21p4v6', $$Tonus musculaire$$, $$[{"text":"Augmenté dans tous les cas.","isCorrect":false,"justification":"Il est diminué (flasque)."},{"text":"Normal au repos.","isCorrect":false,"justification":"Il est abaissé."},{"text":"Aboli ou flasque.","isCorrect":true},{"text":"Spastique.","isCorrect":false,"justification":"Typique du syndrome pyramidal."},{"text":"Rigidité plastique.","isCorrect":false,"justification":"C’est extrapyramidal."}]$$::jsonb, $$Le SNP se distingue par une hypotonie ou flaccidité musculaire.$$, 4, '2025-11-02T17:52:05.218Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddno00jfk498fynrysy2', 'cmhi0ddnd00j3k498tr21p4v6', $$Reflexes ostéo-tendineux$$, $$[{"text":"Toujours abolis.","isCorrect":true},{"text":"Toujours exagérés.","isCorrect":false,"justification":"Exagération = lésion centrale."},{"text":"Normaux.","isCorrect":false,"justification":"Ils sont diminués ou absents."},{"text":"Peuvent réapparaître après un délai.","isCorrect":false,"justification":"Ils restent abolis tant que la lésion persiste."},{"text":"Le Babinski est présent.","isCorrect":false,"justification":"Il est négatif."}]$$::jsonb, $$L’aréflexie est un signe constant de l’atteinte périphérique motrice.$$, 5, '2025-11-02T17:52:05.220Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddnp00jhk498vlwlw4he', 'cmhi0ddnd00j3k498tr21p4v6', $$Signes sensitifs associés$$, $$[{"text":"Paresthésies et hypoesthésies.","isCorrect":true},{"text":"Douleurs radiculaires ou en chaussettes.","isCorrect":true},{"text":"Anesthésie complète.","isCorrect":true},{"text":"Atteinte proprioceptive isolée.","isCorrect":false,"justification":"Elle suggère une atteinte postérieure centrale."},{"text":"Signes sensitifs absents dans les neuropathies.","isCorrect":false,"justification":"Ils sont souvent présents."}]$$::jsonb, $$Les signes sensitifs varient selon la topographie : radiculaire, tronculaire ou plurinévritique.$$, 6, '2025-11-02T17:52:05.222Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddnr00jjk498p0t8ls40', 'cmhi0ddnd00j3k498tr21p4v6', $$Signes trophiques$$, $$[{"text":"Peau chaude et humide.","isCorrect":false,"justification":"Elle devient froide et sèche."},{"text":"Ongles épaissis et cassants.","isCorrect":true},{"text":"Poils clairsemés ou absents.","isCorrect":true},{"text":"Œdème inflammatoire.","isCorrect":false,"justification":"Œdème vasomoteur possible, non inflammatoire."},{"text":"Hyperpilosité localisée.","isCorrect":false,"justification":"C’est l’inverse."}]$$::jsonb, $$Les troubles trophiques témoignent d’une atteinte neurovégétative périphérique.$$, 7, '2025-11-02T17:52:05.223Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddnt00jlk4986vudb8nx', 'cmhi0ddnd00j3k498tr21p4v6', $$Crampes et fasciculations$$, $$[{"text":"Crampes musculaires fréquentes.","isCorrect":true},{"text":"Fasciculations visibles sous la peau.","isCorrect":true},{"text":"Crampes dues à une hyperexcitabilité musculaire.","isCorrect":true},{"text":"Absence de fasciculations dans le SNP.","isCorrect":false,"justification":"Elles sont typiques."},{"text":"Crampes toujours douloureuses.","isCorrect":true}]$$::jsonb, $$Les crampes et fasciculations résultent de la dénervation et de l’hyperexcitabilité neuromusculaire.$$, 8, '2025-11-02T17:52:05.225Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddnv00jnk498wdjy45it', 'cmhi0ddnd00j3k498tr21p4v6', $$Syndrome radiculaire$$, $$[{"text":"Douleur en trajet radiculaire.","isCorrect":true},{"text":"Hyporéflexie segmentaire.","isCorrect":true},{"text":"Troubles moteurs limités à un territoire myotique.","isCorrect":true},{"text":"Troubles sensitifs topographiés en dermatome.","isCorrect":true},{"text":"ROT diffusément abolis.","isCorrect":false,"justification":"L’abolition est segmentaire."}]$$::jsonb, $$Le syndrome radiculaire associe douleur, déficit moteur et trouble sensitif selon un territoire métamérique précis.$$, 9, '2025-11-02T17:52:05.227Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddnx00jpk498m40r9jib', 'cmhi0ddnd00j3k498tr21p4v6', $$Syndrome tronculaire$$, $$[{"text":"Atteinte d’un seul nerf périphérique.","isCorrect":true},{"text":"Douleur et paresthésies dans un territoire nerveux précis.","isCorrect":true},{"text":"ROT abolis localement.","isCorrect":true},{"text":"Troubles sensitifs diffus bilatéraux.","isCorrect":false,"justification":"Lésions localisées unilatérales."},{"text":"Étiologie la plus fréquente : sclérose en plaques.","isCorrect":false,"justification":"C’est une atteinte centrale."}]$$::jsonb, $$Le syndrome tronculaire correspond à une lésion d’un nerf unique avec déficit moteur et sensitif topographié.$$, 10, '2025-11-02T17:52:05.229Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddnz00jrk498l1yajitc', 'cmhi0ddnd00j3k498tr21p4v6', $$Polyneuropathie périphérique$$, $$[{"text":"Atteinte bilatérale et symétrique.","isCorrect":true},{"text":"Distribution en « gants et chaussettes ».","isCorrect":true},{"text":"Aréflexie diffuse.","isCorrect":true},{"text":"Troubles végétatifs possibles.","isCorrect":true},{"text":"Troubles corticaux associés.","isCorrect":false,"justification":"Ils sont absents."}]$$::jsonb, $$La polyneuropathie est une atteinte bilatérale, symétrique, distale et sensitivo-motrice.$$, 11, '2025-11-02T17:52:05.231Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddo000jtk4981jvu1yer', 'cmhi0ddnd00j3k498tr21p4v6', $$Principales causes$$, $$[{"text":"Diabète sucré.","isCorrect":true},{"text":"Carence en vitamine B₁₂.","isCorrect":true},{"text":"Intoxication alcoolique.","isCorrect":true},{"text":"Guillain-Barré.","isCorrect":true},{"text":"Sclérose latérale amyotrophique.","isCorrect":false,"justification":"Elle touche motoneurones centraux et périphériques."}]$$::jsonb, $$Les neuropathies périphériques ont des causes métaboliques, carentielles, toxiques ou immunologiques.$$, 12, '2025-11-02T17:52:05.233Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddo200jvk498joki9rmg', 'cmhi0ddnd00j3k498tr21p4v6', $$Syndrome de Guillain-Barré$$, $$[{"text":"Polyneuropathie aiguë inflammatoire démyélinisante.","isCorrect":true},{"text":"Évolution ascendante.","isCorrect":true},{"text":"ROT abolis.","isCorrect":true},{"text":"Atteinte respiratoire possible.","isCorrect":true},{"text":"Cause : hypokaliémie aiguë.","isCorrect":false,"justification":"C’est une réaction auto-immune post-infectieuse."}]$$::jsonb, $$Le Guillain-Barré est une urgence neurologique avec paralysie ascendante et risque d’insuffisance respiratoire.$$, 13, '2025-11-02T17:52:05.234Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddo400jxk498ntlcn3vn', 'cmhi0ddnd00j3k498tr21p4v6', $$Syndrome de Charcot-Marie-Tooth$$, $$[{"text":"Neuropathie héréditaire démyélinisante.","isCorrect":true},{"text":"Début brutal et douloureux.","isCorrect":false,"justification":"Il est progressif et indolore."},{"text":"Transmission toujours récessive.","isCorrect":false,"justification":"Elle peut être dominante ou récessive."},{"text":"Aréflexie absente.","isCorrect":false,"justification":"Elle est présente."},{"text":"Pas d’amyotrophie.","isCorrect":false,"justification":"Il existe amyotrophie distale."}]$$::jsonb, $$La maladie de Charcot-Marie-Tooth est une neuropathie héréditaire démyélinisante à évolution lente et distale.$$, 14, '2025-11-02T17:52:05.236Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddo500jzk4989bea775n', 'cmhi0ddnd00j3k498tr21p4v6', $$Exploration paraclinique$$, $$[{"text":"EMG et étude de conduction nerveuse.","isCorrect":true},{"text":"Ponction lombaire.","isCorrect":true},{"text":"Biopsie musculaire.","isCorrect":true},{"text":"Scanner cérébral systématique.","isCorrect":false,"justification":"Il n’explore pas le SNP."},{"text":"Dosage des CPK toujours augmenté.","isCorrect":false,"justification":"Il est normal dans les neuropathies."}]$$::jsonb, $$Les examens de référence sont l’EMG et l’étude de conduction nerveuse.$$, 15, '2025-11-02T17:52:05.238Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddo700k1k498vnu29xaq', 'cmhi0ddnd00j3k498tr21p4v6', $$Électromyogramme$$, $$[{"text":"Montre une vitesse de conduction ralentie.","isCorrect":true},{"text":"Montre des potentiels de dénervation.","isCorrect":true},{"text":"Toujours normal dans le SNP.","isCorrect":false,"justification":"Il est pathologique."},{"text":"Permet de localiser la lésion.","isCorrect":true},{"text":"Permet d’identifier la nature axonale ou démyélinisante.","isCorrect":true}]$$::jsonb, $$L’EMG est indispensable pour caractériser et localiser la lésion nerveuse périphérique.$$, 16, '2025-11-02T17:52:05.239Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddo900k3k498ura6u09n', 'cmhi0ddnd00j3k498tr21p4v6', $$Liquide céphalo-rachidien (LCR)$$, $$[{"text":"Peut montrer une dissociation albumino-cytologique.","isCorrect":true},{"text":"Normal dans la plupart des polyneuropathies chroniques.","isCorrect":true},{"text":"Toujours riche en cellules.","isCorrect":false,"justification":"Le nombre de cellules est normal."},{"text":"Utile au diagnostic de Guillain-Barré.","isCorrect":true},{"text":"Indique la nature du déficit musculaire.","isCorrect":false,"justification":"Il n’évalue pas la motricité."}]$$::jsonb, $$L’analyse du LCR aide à diagnostiquer les neuropathies inflammatoires (dissociation albumino-cytologique).$$, 17, '2025-11-02T17:52:05.241Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddoc00k5k498gwgn359w', 'cmhi0ddnd00j3k498tr21p4v6', $$Diagnostic différentiel$$, $$[{"text":"Syndrome pyramidal.","isCorrect":true},{"text":"Syndrome myogène.","isCorrect":true},{"text":"Syndrome extrapyramidal.","isCorrect":true},{"text":"Syndrome cérébelleux.","isCorrect":true},{"text":"Insuffisance cardiaque.","isCorrect":false,"justification":"Ce n’est pas neurologique."}]$$::jsonb, $$Le diagnostic différentiel repose sur l’analyse du tonus, des réflexes et de la topographie du déficit.$$, 18, '2025-11-02T17:52:05.244Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddoe00k7k498h69ye42y', 'cmhi0ddnd00j3k498tr21p4v6', $$Évolution et pronostic$$, $$[{"text":"Toujours réversible.","isCorrect":false,"justification":"Certaines neuropathies sont irréversibles."},{"text":"Peut être chronique ou aiguë.","isCorrect":true},{"text":"Les formes carentielles régressent sous traitement.","isCorrect":true},{"text":"Les formes héréditaires évoluent lentement.","isCorrect":true},{"text":"Ne justifie jamais de rééducation.","isCorrect":false,"justification":"La rééducation est essentielle."}]$$::jsonb, $$Le pronostic varie selon l’étiologie ; certaines neuropathies sont curables, d’autres nécessitent un suivi fonctionnel prolongé.$$, 19, '2025-11-02T17:52:05.246Z');
INSERT INTO "Chapter" (id, "subjectId", title, description, "orderIndex", "pdfUrl", "createdAt") VALUES ('cmhi0ddoh00k9k498klyrex4v', 'cmhi0ddn900j1k4988obeo56w', $$Chapitre 2 – Syndrome myogène$$, NULL, 2, NULL, '2025-11-02T17:52:05.249Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddoj00kbk498t4diqszb', 'cmhi0ddoh00k9k498klyrex4v', $$Définition et cadre général$$, $$[{"text":"Le syndrome myogène traduit une atteinte primaire du muscle.","isCorrect":true},{"text":"Il s’accompagne classiquement d’un déficit à prédominance proximale.","isCorrect":true},{"text":"Il s’associe à des troubles sensitifs nets.","isCorrect":false,"justification":"Pas de signes sensitifs intrinsèques."},{"text":"Il correspond à une atteinte du motoneurone périphérique.","isCorrect":false,"justification":"Neurogène, pas myogène."},{"text":"Les ROT sont toujours abolis d’emblée.","isCorrect":false,"justification":"Souvent conservés, chutent tardivement."}]$$::jsonb, $$Le myogène est une atteinte musculaire primitive avec déficit proximal sans trouble sensitif.$$, 0, '2025-11-02T17:52:05.251Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddok00kdk4982motlnpf', 'cmhi0ddoh00k9k498klyrex4v', $$Phénotype moteur typique$$, $$[{"text":"Difficulté à se lever d’une chaise (quadriceps).","isCorrect":true},{"text":"Difficulté à coiffer/porter (deltoïdes).","isCorrect":true},{"text":"Chute du tonus en extension des orteils isolée.","isCorrect":false,"justification":"Plutôt neuropathique distal."},{"text":"Fatigabilité proximale aux activités au-dessus de la tête.","isCorrect":true},{"text":"Paralysie faciale périphérique isolée.","isCorrect":false,"justification":"Nerf, pas muscle."}]$$::jsonb, $$Les muscles proximaux et axiaux sont préférentiellement touchés.$$, 1, '2025-11-02T17:52:05.253Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddom00kfk498xhlhoqpq', 'cmhi0ddoh00k9k498klyrex4v', $$Myalgies et douleurs$$, $$[{"text":"Les myalgies orientent vers une myosite.","isCorrect":true},{"text":"Les douleurs mécaniques aggravées à l’effort signent toujours une myopathie.","isCorrect":false,"justification":"Non spécifiques."},{"text":"L’absence de douleur élimine une cause musculaire.","isCorrect":false,"justification":"Nombreuses myopathies indolores."},{"text":"Douleur + CK élevée évoquent une nécrose musculaire.","isCorrect":true},{"text":"Crampes nocturnes isolées = myopathie.","isCorrect":false,"justification":"Non spécifiques."}]$$::jsonb, $$La douleur n’est pas obligatoire ; le couple myalgie + CK↑ évoque myolyse.$$, 2, '2025-11-02T17:52:05.254Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddoo00khk498921igtdx', 'cmhi0ddoh00k9k498klyrex4v', $$ROT et réflexes$$, $$[{"text":"Les ROT sont souvent conservés au début.","isCorrect":true},{"text":"Ils sont systématiquement vifs.","isCorrect":false,"justification":"Plutôt normaux ou diminués tardifs."},{"text":"Le réflexe idiomusculaire est aboli.","isCorrect":true},{"text":"Un Babinski oriente vers une myopathie pure.","isCorrect":false,"justification":"Atteinte centrale."},{"text":"L’aréflexie diffuse signe une polyradiculonévrite.","isCorrect":false,"justification":"Neurogène périphérique."}]$$::jsonb, $$ROT conservés (ou ↓ tard), idiomusculaire aboli, Babinski absent.$$, 3, '2025-11-02T17:52:05.256Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddop00kjk498kftr4wxc', 'cmhi0ddoh00k9k498klyrex4v', $$Tonus et trophicité$$, $$[{"text":"Hypotonie possible aux stades avancés.","isCorrect":true},{"text":"Spasticité fréquente.","isCorrect":false,"justification":"Pyramidal, pas myogène."},{"text":"Amyotrophie souvent tardive (sauf dystrophies).","isCorrect":true},{"text":"Fasciculations visibles courantes.","isCorrect":false,"justification":"Neurogène."},{"text":"Pseudo-hypertrophie des mollets dans certaines dystrophies.","isCorrect":true}]$$::jsonb, $$Tonicité non spastique, amyotrophie tardive, parfois pseudo-hypertrophie.$$, 4, '2025-11-02T17:52:05.257Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddor00klk498il9f036d', 'cmhi0ddoh00k9k498klyrex4v', $$Répartition du déficit$$, $$[{"text":"Atteinte proximale > distale.","isCorrect":true},{"text":"Axial (paravertébraux, fléchisseurs cervicaux) possible.","isCorrect":true},{"text":"Distal strict exclusif typique.","isCorrect":false,"justification":"Plutôt neuropathies, sauf myopathies distales rares."},{"text":"Ceinture scapulaire/ pelvienne souvent atteintes.","isCorrect":true},{"text":"Asymétrie franche constante.","isCorrect":false,"justification":"Plutôt symétrique, sauf lésions focales."}]$$::jsonb, $$Profil proximal, axial, symétrique dominant.$$, 5, '2025-11-02T17:52:05.259Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddou00knk498i7gfd04e', 'cmhi0ddoh00k9k498klyrex4v', $$Signes « négatifs » utiles$$, $$[{"text":"Pas de troubles sensitifs objectifs.","isCorrect":true},{"text":"Pas de Babinski.","isCorrect":true},{"text":"Pas de fasciculations.","isCorrect":true},{"text":"Pas de douleur jamais.","isCorrect":false,"justification":"Certaines myosites douloureuses."},{"text":"Pas d’atteinte oculomotrice possible.","isCorrect":false,"justification":"Mitochondriales : ptosis/ophthalmoplégie."}]$$::jsonb, $$Absence de sensitif/Pyramidal/fasciculations oriente vers le myogène.$$, 6, '2025-11-02T17:52:05.262Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddov00kpk498ew5vqizf', 'cmhi0ddoh00k9k498klyrex4v', $$Myotonie (maladie de Steinert & co)$$, $$[{"text":"Délai de décontraction après pression ou percussion.","isCorrect":true},{"text":"Soulagée par le « warm-up » (répétitions).","isCorrect":true},{"text":"Toujours signe d’atteinte neurogène.","isCorrect":false,"justification":"Myopathies myotoniques."},{"text":"CPK toujours normales.","isCorrect":false,"justification":"Variables."},{"text":"EMG avec décharges myotoniques typiques.","isCorrect":true}]$$::jsonb, $$La myotonie est myogène (décharges myotoniques EMG), améliorée par réchauffement.$$, 7, '2025-11-02T17:52:05.264Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddox00krk498x14qst31', 'cmhi0ddoh00k9k498klyrex4v', $$Atteinte respiratoire/bulbaire$$, $$[{"text":"Dysphagie dans les myosites sévères.","isCorrect":true},{"text":"Insuffisance respiratoire restrictive possible (diaphragme).","isCorrect":true},{"text":"Dysarthrie nasale possible.","isCorrect":true},{"text":"Déficit de la déglutition contre-indique une prise en charge.","isCorrect":false,"justification":"PEC urgente (kiné, nutrition)."},{"text":"Toux inefficace par faiblesse des muscles expiratoires.","isCorrect":true}]$$::jsonb, $$L’atteinte bulbo-respiratoire conditionne la gravité.$$, 8, '2025-11-02T17:52:05.265Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddoz00ktk4984j6j6xus', 'cmhi0ddoh00k9k498klyrex4v', $$Biologie simple$$, $$[{"text":"CPK élevées dans les myolyses/myosites.","isCorrect":true},{"text":"Aldolase peut être augmentée.","isCorrect":true},{"text":"ASAT/ALAT peuvent s’élever d’origine musculaire.","isCorrect":true},{"text":"CPK normales excluent une myopathie.","isCorrect":false,"justification":"Certaines myopathies CK N (stéroïdes…)."},{"text":"Myoglobinurie possible en rhabdomyolyse.","isCorrect":true}]$$::jsonb, $$CK/aldolase guident ; CK N ≠ exclusion.$$, 9, '2025-11-02T17:52:05.267Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddp000kvk498hyvt6e43', 'cmhi0ddoh00k9k498klyrex4v', $$EMG myogène$$, $$[{"text":"Potentiels unitaires de faible amplitude et courte durée.","isCorrect":true},{"text":"Recrutement précoce.","isCorrect":true},{"text":"Vitesses de conduction motrice très ralenties.","isCorrect":false,"justification":"Conduction N (atteinte non neurogène)."},{"text":"Décharges myotoniques possibles.","isCorrect":true},{"text":"Tracés de dénervation typiques.","isCorrect":false,"justification":"Neurogène."}]$$::jsonb, $$EMG : petits MUAP, recrutement précoce, conduction N.$$, 10, '2025-11-02T17:52:05.268Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddp200kxk498vtx4h6rq', 'cmhi0ddoh00k9k498klyrex4v', $$Biopsie musculaire$$, $$[{"text":"Confirme inflammation, nécrose, régénération.","isCorrect":true},{"text":"Utile dans dystrophies (dystrophine, sarcoglycanes).","isCorrect":true},{"text":"Toujours normale si CK élevées.","isCorrect":false,"justification":"Peut montrer nécrose."},{"text":"Immunohistochimie oriente le type de myopathie.","isCorrect":true},{"text":"Jamais nécessaire si EMG pathologique.","isCorrect":false,"justification":"Complémentaire pour l’étiologie."}]$$::jsonb, $$La biopsie documente la lésion et typage (inflammatoire/dystrophique/métabolique).$$, 11, '2025-11-02T17:52:05.270Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddp300kzk498gcluskrc', 'cmhi0ddoh00k9k498klyrex4v', $$Différencier myogène vs neurogène$$, $$[{"text":"Fasciculations → neurogène.","isCorrect":true},{"text":"Sensitif anormal → neurogène.","isCorrect":true},{"text":"Déficit distal prédominant → plutôt neurogène.","isCorrect":true},{"text":"ROT conservés → plutôt myogène.","isCorrect":true},{"text":"Babinski présent → myogène.","isCorrect":false,"justification":"Central."}]$$::jsonb, $$Fasciculations/sensitif/Babinski = non myogène.$$, 12, '2025-11-02T17:52:05.271Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddp500l1k498pe0o155t', 'cmhi0ddoh00k9k498klyrex4v', $$Myopathie induite par corticoïdes$$, $$[{"text":"Déficit proximal indolore.","isCorrect":true},{"text":"CPK très élevées d’emblée.","isCorrect":false,"justification":"Souvent normales."},{"text":"Atteinte distale prédominante.","isCorrect":false,"justification":"Proximale."},{"text":"Amélioration après réduction/arrêt des stéroïdes.","isCorrect":true},{"text":"Myalgies intenses caractéristiques.","isCorrect":false,"justification":"Peu douloureuse."}]$$::jsonb, $$Myopathie proximale, indolore, CK N, liée aux corticoïdes.$$, 13, '2025-11-02T17:52:05.273Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddp700l3k4982zq1wvyq', 'cmhi0ddoh00k9k498klyrex4v', $$Myopathies endocriniennes$$, $$[{"text":"Hypothyroïdie : myalgies, crampes, CK↑ possible.","isCorrect":true},{"text":"Hyperthyroïdie : faiblesse proximale avec amyotrophie.","isCorrect":true},{"text":"Cushing : spasticité.","isCorrect":false,"justification":"Myopathie proximale, pas spasticité."},{"text":"Hyperparathyroïdie : calcifications musculaires diffuses constantes.","isCorrect":false,"justification":"Non spécifiques/rares."},{"text":"Diabète : myopathie endocrinienne constante.","isCorrect":false,"justification":"Neuropathie plus typique."}]$$::jsonb, $$Thyroïdiennes et Cushing donnent des myopathies proximales.$$, 14, '2025-11-02T17:52:05.275Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddp900l5k498b4mugq0t', 'cmhi0ddoh00k9k498klyrex4v', $$Rhabdomyolyse$$, $$[{"text":"Myalgies aiguës + faiblesse + urines brunes.","isCorrect":true},{"text":"CK > 5× N, souvent > 5000 UI/L.","isCorrect":true},{"text":"Risque d’insuffisance rénale aiguë par myoglobinurie.","isCorrect":true},{"text":"Traitement : hydratation et alcalinisation si besoin.","isCorrect":true},{"text":"Statines jamais en cause.","isCorrect":false,"justification":"Peuvent induire myolyse."}]$$::jsonb, $$Urgence : CK très élevées, IRA possible → remplissage rapide.$$, 15, '2025-11-02T17:52:05.278Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddpb00l7k49816unm9k8', 'cmhi0ddoh00k9k498klyrex4v', $$Dystrophies de Duchenne/Becker$$, $$[{"text":"Garçon, début pédiatrique (Duchenne précoce, Becker plus tardif).","isCorrect":true},{"text":"Signe de Gowers (lever « en s’aidant des cuisses »).","isCorrect":true},{"text":"Pseudo-hypertrophie des mollets.","isCorrect":true},{"text":"CK normales.","isCorrect":false,"justification":"Très élevées."},{"text":"Cardiomyopathie possible.","isCorrect":true}]$$::jsonb, $$Dystrophinopathies : déficit proximal, CK↑↑, Gowers, cardiopathie.$$, 16, '2025-11-02T17:52:05.279Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddpd00l9k498hfh3fi7k', 'cmhi0ddoh00k9k498klyrex4v', $$Myopathies inflammatoires (PM/DM)$$, $$[{"text":"Déficit proximal progressif, parfois douleurs.","isCorrect":true},{"text":"Éruption cutanée en héliotrope et papules de Gottron (DM).","isCorrect":true},{"text":"CPK toujours normales.","isCorrect":false,"justification":"Souvent ↑."},{"text":"Réponse possible aux corticoïdes/immunosuppresseurs.","isCorrect":true},{"text":"Pas de risque néoplasique.","isCorrect":false,"justification":"DM de l’adulte : dépistage cancer."}]$$::jsonb, $$Polymyosite/Dermatomyosite : CK↑, déficit proximal, DM cutanée, immunothérapie.$$, 17, '2025-11-02T17:52:05.281Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddpe00lbk498e7hmgilp', 'cmhi0ddoh00k9k498klyrex4v', $$Myopathies mitochondriales$$, $$[{"text":"Intolérance à l’effort, fatigabilité.","isCorrect":true},{"text":"Acidose lactique possible.","isCorrect":true},{"text":"Ptosis et ophtalmoplégie externe chronique.","isCorrect":true},{"text":"Transmission typiquement maternelle.","isCorrect":true},{"text":"Biopsie avec fibres « tornade » (ragged-red fibers).","isCorrect":true}]$$::jsonb, $$Phénotype muscle + oculaire, lactates, hérédité maternelle, fibres rouges déchiquetées.$$, 18, '2025-11-02T17:52:05.283Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddpg00ldk498k5ix4rol', 'cmhi0ddoh00k9k498klyrex4v', $$Principes de prise en charge$$, $$[{"text":"Traiter l’étiologie (inflammatoire, endocrinienne, toxique).","isCorrect":true},{"text":"Rééducation motrice et respiratoire adaptées.","isCorrect":true},{"text":"Supplémentations nutritionnelles si sarcopénie/myolyse.","isCorrect":true},{"text":"Arrêt des traitements toxiques (statines, corticoïdes si iatrogène).","isCorrect":true},{"text":"Éviter tout exercice physique.","isCorrect":false,"justification":"Encadrer l’activité (aérobie modérée)."}]$$::jsonb, $$PEC étiologique + fonctionnelle (kiné, nutrition, sevrage toxiques), surveillance respi si atteinte axiale/bulbaire.$$, 19, '2025-11-02T17:52:05.284Z');
INSERT INTO "Chapter" (id, "subjectId", title, description, "orderIndex", "pdfUrl", "createdAt") VALUES ('cmhi0ddpj00lfk498sx82umkz', 'cmhi0ddn900j1k4988obeo56w', $$Chapitre 3 – Syndrome extrapyramidal$$, NULL, 3, NULL, '2025-11-02T17:52:05.287Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddpl00lhk498iggef8ci', 'cmhi0ddpj00lfk498sx82umkz', $$Définition$$, $$[{"text":"Ensemble de troubles du mouvement liés aux noyaux gris centraux.","isCorrect":true},{"text":"Il concerne les voies corticospinales.","isCorrect":false,"justification":"Ce sont les voies pyramidales."},{"text":"Il entraîne hypertonie et lenteur des mouvements.","isCorrect":true},{"text":"Il est toujours d’origine médullaire.","isCorrect":false,"justification":"Il est sous-cortical, pas médullaire."},{"text":"Il peut associer tremblement, akinésie, rigidité.","isCorrect":true}]$$::jsonb, $$Le syndrome extrapyramidal associe akinésie, hypertonie plastique et tremblement d’origine sous-corticale.$$, 0, '2025-11-02T17:52:05.289Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddpn00ljk498uiqtg7sd', 'cmhi0ddpj00lfk498sx82umkz', $$Structures concernées$$, $$[{"text":"Noyaux gris centraux (striatum, pallidum, thalamus).","isCorrect":true},{"text":"Substantia nigra.","isCorrect":true},{"text":"Cervelet.","isCorrect":false,"justification":"Donne syndrome cérébelleux."},{"text":"Tronc cérébral (voie nigro-striée).","isCorrect":true},{"text":"Moelle épinière.","isCorrect":false,"justification":"Non concernée."}]$$::jsonb, $$Le système extrapyramidal comprend les noyaux gris centraux et leurs connexions nigro-striées.$$, 1, '2025-11-02T17:52:05.291Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddpq00llk4987dkh5guk', 'cmhi0ddpj00lfk498sx82umkz', $$Triade parkinsonienne$$, $$[{"text":"Tremblement de repos.","isCorrect":true},{"text":"Hypertonie plastique.","isCorrect":true},{"text":"Akinésie.","isCorrect":true},{"text":"Marche à petits pas.","isCorrect":true},{"text":"Diminution du balancement des bras.","isCorrect":true}]$$::jsonb, $$La triade de Parkinson = tremblement de repos, akinésie, hypertonie plastique avec marche lente et bras figés.$$, 2, '2025-11-02T17:52:05.294Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddpr00lnk498s0gw68nr', 'cmhi0ddpj00lfk498sx82umkz', $$Tremblement parkinsonien$$, $$[{"text":"Présent au repos.","isCorrect":true},{"text":"Disparaît au mouvement volontaire.","isCorrect":true},{"text":"Repris à l’émotion.","isCorrect":true},{"text":"De fréquence rapide (8–10 Hz).","isCorrect":false,"justification":"Il est lent (4–6 Hz)."},{"text":"Symétrique d’emblée.","isCorrect":false,"justification":"Asymétrique au début."}]$$::jsonb, $$Tremblement lent, de repos, asymétrique, s’atténuant à l’action, typique du syndrome parkinsonien.$$, 3, '2025-11-02T17:52:05.296Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddpt00lpk498p7jkxg5q', 'cmhi0ddpj00lfk498sx82umkz', $$Hypertonie plastique$$, $$[{"text":"Tonus constant tout au long du mouvement passif.","isCorrect":true},{"text":"Sensation de « tuyau de plomb ».","isCorrect":true},{"text":"Dues à des réflexes de raccourcissement exagérés.","isCorrect":false,"justification":"Typique pyramidal."},{"text":"Peut être entrecoupée d’à-coups : phénomène de « roue dentée ».","isCorrect":true},{"text":"Toujours douloureuse.","isCorrect":false,"justification":"Généralement indolore."}]$$::jsonb, $$L’hypertonie plastique est uniforme, indolore, parfois crantée dans la maladie de Parkinson.$$, 4, '2025-11-02T17:52:05.298Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddpv00lrk498a6exmpue', 'cmhi0ddpj00lfk498sx82umkz', $$Akinésie$$, $$[{"text":"Lenteur d’initiation et d’exécution du mouvement.","isCorrect":true},{"text":"Difficulté à débuter un geste.","isCorrect":true},{"text":"Hyperactivité motrice constante.","isCorrect":false,"justification":"Au contraire, pauvreté des gestes."},{"text":"Diminution de l’expression faciale (amimie).","isCorrect":true},{"text":"Augmentation du débit de la parole.","isCorrect":false,"justification":"Voix monotone, lente, hypophonique."}]$$::jsonb, $$L’akinésie associe lenteur, pauvreté gestuelle et hypomimie caractéristiques.$$, 5, '2025-11-02T17:52:05.299Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddpx00ltk4984spvgz41', 'cmhi0ddpj00lfk498sx82umkz', $$Marche parkinsonienne$$, $$[{"text":"Marche à petits pas traînants.","isCorrect":true},{"text":"Difficulté à démarrer (freezing).","isCorrect":true},{"text":"Balancement exagéré des bras.","isCorrect":false,"justification":"Il est diminué."},{"text":"Chutes fréquentes en avant.","isCorrect":true},{"text":"Rétropulsion (chute arrière) possible.","isCorrect":true}]$$::jsonb, $$La marche parkinsonienne est lente, à petits pas, avec instabilité posturale et freezing.$$, 6, '2025-11-02T17:52:05.302Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddpz00lvk498e9986o99', 'cmhi0ddpj00lfk498sx82umkz', $$Tremblements extrapyramidaux$$, $$[{"text":"Tremblement intentionnel.","isCorrect":false,"justification":"Cérébelleux."},{"text":"Tremblement de repos.","isCorrect":true},{"text":"Tremblement postural.","isCorrect":false,"justification":"Essentiel."},{"text":"Tremblement rapide de 10 Hz.","isCorrect":false,"justification":"Trop rapide pour Parkinson."},{"text":"Tremblement provoqué par stress uniquement.","isCorrect":false,"justification":"Présent au repos."}]$$::jsonb, $$Le tremblement extrapyramidal est de repos, lent, s’atténuant à l’action.$$, 7, '2025-11-02T17:52:05.303Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddq100lxk4985zc6ovjm', 'cmhi0ddpj00lfk498sx82umkz', $$Syndrome parkinsonien secondaire$$, $$[{"text":"Peut être induit par des neuroleptiques.","isCorrect":true},{"text":"Peut être post-encéphalitique.","isCorrect":true},{"text":"Peut être vasculaire.","isCorrect":true},{"text":"Peut être médicamenteux (métoclopramide, flunarizine).","isCorrect":true},{"text":"Toujours idiopathique.","isCorrect":false,"justification":"Nombreuses causes secondaires."}]$$::jsonb, $$Le syndrome parkinsonien peut être idiopathique ou secondaire (médicaments, AVC, infections).$$, 8, '2025-11-02T17:52:05.305Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddq300lzk498rfqywdsd', 'cmhi0ddpj00lfk498sx82umkz', $$Maladie de Parkinson idiopathique$$, $$[{"text":"Début asymétrique.","isCorrect":true},{"text":"Bonne réponse à la L-dopa.","isCorrect":true},{"text":"Absence de signes pyramidaux.","isCorrect":true},{"text":"Tremblement intentionnel dominant.","isCorrect":false,"justification":"De repos."},{"text":"Déclin cognitif rapide initial.","isCorrect":false,"justification":"Tardif."}]$$::jsonb, $$Parkinson idiopathique = début asymétrique, réponse dopaminergique, sans signe pyramidal initial.$$, 9, '2025-11-02T17:52:05.307Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddq600m1k498fhmhf6sx', 'cmhi0ddpj00lfk498sx82umkz', $$Maladies apparentées (parkinson plus)$$, $$[{"text":"Atrophie multisystématisée (AMS).","isCorrect":true},{"text":"Paralysie supranucléaire progressive.","isCorrect":true},{"text":"Dégénérescence cortico-basale.","isCorrect":true},{"text":"Démence à corps de Lewy.","isCorrect":true},{"text":"Maladie de Charcot.","isCorrect":false,"justification":"Atteinte motoneurone, pas extrapyramidale."}]$$::jsonb, $$Les syndromes parkinson plus associent signes extrapyramidaux à d’autres atteintes (autonomiques, oculaires, cognitives).$$, 10, '2025-11-02T17:52:05.310Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddq800m3k4981yi641pt', 'cmhi0ddpj00lfk498sx82umkz', $$Syndrome choréique$$, $$[{"text":"Mouvements involontaires brusques et irréguliers.","isCorrect":true},{"text":"Disparaissent au sommeil.","isCorrect":true},{"text":"S’aggravent à l’émotion.","isCorrect":true},{"text":"Tremblement fin régulier.","isCorrect":false,"justification":"Mouvement irrégulier, non rythmique."},{"text":"Toujours secondaire à la dopamine.","isCorrect":false,"justification":"Causes multiples."}]$$::jsonb, $$La chorée = mouvements involontaires, irréguliers, hyperkinétiques et émotifs.$$, 11, '2025-11-02T17:52:05.313Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddqa00m5k498o5qurqx4', 'cmhi0ddpj00lfk498sx82umkz', $$Chorée de Huntington$$, $$[{"text":"Transmission autosomique dominante.","isCorrect":true},{"text":"Détérioration intellectuelle progressive.","isCorrect":true},{"text":"Mouvements lents et rythmiques.","isCorrect":false,"justification":"Irréguliers."},{"text":"Début toujours tardif (> 70 ans).","isCorrect":false,"justification":"Vers 40 ans."},{"text":"Pas de troubles psychiatriques.","isCorrect":false,"justification":"Fréquents."}]$$::jsonb, $$La chorée de Huntington associe hyperkinésie + démence + troubles psychiatriques.$$, 12, '2025-11-02T17:52:05.315Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddqd00m7k498r6yb6ej6', 'cmhi0ddpj00lfk498sx82umkz', $$Athétose$$, $$[{"text":"Mouvements lents, sinueux, reptants.","isCorrect":true},{"text":"Prédomine aux extrémités.","isCorrect":true},{"text":"Exagérée au repos.","isCorrect":false,"justification":"Aggravée à l’action."},{"text":"Toujours bilatérale.","isCorrect":false,"justification":"Souvent unilatérale post-lésionnelle."},{"text":"Signe d’une atteinte du cortex moteur.","isCorrect":false,"justification":"Noyaux gris centraux."}]$$::jsonb, $$L’athétose est une hyperkinésie lente, distale, aggravée à l’effort, par atteinte striatale.$$, 13, '2025-11-02T17:52:05.317Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddqf00m9k498yowga22s', 'cmhi0ddpj00lfk498sx82umkz', $$Hémiballisme$$, $$[{"text":"Mouvements brusques, amples, violents d’un hémicorps.","isCorrect":true},{"text":"Lésion du noyau sous-thalamique.","isCorrect":true},{"text":"Toujours bilatéral.","isCorrect":false,"justification":"Unilatéral."},{"text":"Cause fréquente : AVC.","isCorrect":true},{"text":"Traitement : dopaminergiques.","isCorrect":false,"justification":"Neuroleptiques sédatifs."}]$$::jsonb, $$L’hémiballisme = hyperkinésie violente unilatérale post-AVC sous-thalamique.$$, 14, '2025-11-02T17:52:05.319Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddqh00mbk498kkpd2ywi', 'cmhi0ddpj00lfk498sx82umkz', $$Myoclonies$$, $$[{"text":"Secousses rythmiques régulières.","isCorrect":false,"justification":"Irrégulières."},{"text":"Toujours corticales.","isCorrect":false,"justification":"Peuvent être sous-corticales ou spinales."},{"text":"Toujours pathologiques.","isCorrect":false,"justification":"Certaines physiologiques (hypniques)."},{"text":"Résistantes aux benzodiazépines.","isCorrect":false,"justification":"Sensibles."},{"text":"Spécifiques de Parkinson.","isCorrect":false,"justification":"Non spécifiques."}]$$::jsonb, $$Les myoclonies sont des secousses involontaires, irrégulières, d’origine variable.$$, 15, '2025-11-02T17:52:05.321Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddqj00mdk4984br8kv8i', 'cmhi0ddpj00lfk498sx82umkz', $$Tremblement essentiel$$, $$[{"text":"Tremblement postural ou d’action.","isCorrect":true},{"text":"Disparaît au repos.","isCorrect":true},{"text":"Fréquence rapide (8–10 Hz).","isCorrect":true},{"text":"S’améliore avec alcool.","isCorrect":true},{"text":"Répond à la L-dopa.","isCorrect":false,"justification":"Traitement : propranolol, primidone."}]$$::jsonb, $$Le tremblement essentiel est d’action, symétrique, souvent familial, sensible au bêtabloquant.$$, 16, '2025-11-02T17:52:05.323Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddql00mfk498fkaaj9xc', 'cmhi0ddpj00lfk498sx82umkz', $$Hyperkinésies dystoniques$$, $$[{"text":"Contractions musculaires soutenues, involontaires.","isCorrect":true},{"text":"Provoquent des attitudes anormales.","isCorrect":true},{"text":"Disparaissent toujours au sommeil.","isCorrect":true},{"text":"Aggravées par stress.","isCorrect":true},{"text":"Réversibles avec dopamine.","isCorrect":false,"justification":"Anticholinergiques parfois efficaces."}]$$::jsonb, $$Les dystonies = contractions soutenues, postures anormales, aggravées par stress.$$, 17, '2025-11-02T17:52:05.325Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddqn00mhk498jlbli7op', 'cmhi0ddpj00lfk498sx82umkz', $$Causes générales du syndrome extrapyramidal$$, $$[{"text":"Médicaments dopaminergiques.","isCorrect":false,"justification":"Traite, ne cause pas."},{"text":"Neuroleptiques.","isCorrect":true},{"text":"Encéphalite post-virale.","isCorrect":true},{"text":"Lésions vasculaires des noyaux gris.","isCorrect":true},{"text":"Hypothyroïdie.","isCorrect":false,"justification":"Myopathie possible, pas extrapyramidal."}]$$::jsonb, $$Causes fréquentes = neuroleptiques, AVC, post-encéphalite.$$, 18, '2025-11-02T17:52:05.327Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddqp00mjk498i9522sn7', 'cmhi0ddpj00lfk498sx82umkz', $$Traitement global$$, $$[{"text":"L-Dopa et agonistes dopaminergiques pour Parkinson.","isCorrect":true},{"text":"Anticholinergiques pour rigidité.","isCorrect":true},{"text":"Neuroleptiques pour tremblements.","isCorrect":false,"justification":"Aggravent."},{"text":"Rééducation motrice et équilibre.","isCorrect":true},{"text":"Réduction progressive des traitements causaux.","isCorrect":true}]$$::jsonb, $$Traitement = dopaminergiques, kinésithérapie, sevrage médicamenteux causal.$$, 19, '2025-11-02T17:52:05.329Z');
INSERT INTO "Chapter" (id, "subjectId", title, description, "orderIndex", "pdfUrl", "createdAt") VALUES ('cmhi0ddqs00mlk498lvw24tle', 'cmhi0ddn900j1k4988obeo56w', $$Chapitre 4 – Syndrome d’hypertension intracrânienne (HIC)$$, NULL, 4, NULL, '2025-11-02T17:52:05.332Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddrl00nbk498sz8mte2q', 'cmhi0ddqs00mlk498lvw24tle', $$Examen du fond d’œil$$, $$[{"text":"Montre un flou des bords papillaires.","isCorrect":true},{"text":"Hémorragies péripapillaires possibles.","isCorrect":true},{"text":"Disparition de la cupule optique.","isCorrect":true},{"text":"Signes bilatéraux.","isCorrect":true},{"text":"Toujours normal en HIC.","isCorrect":false,"justification":"Souvent anormal."}]$$::jsonb, $$L’examen du fond d’œil est essentiel dans tout syndrome d’HIC suspecté.$$, 12, '2025-11-02T17:52:05.362Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddqt00mnk498sm0osrdg', 'cmhi0ddqs00mlk498lvw24tle', $$Définition$$, $$[{"text":"L’HIC correspond à une élévation anormale de la pression intracrânienne.","isCorrect":true},{"text":"La pression normale du LCR est de 10 à 15 cmH₂O.","isCorrect":true},{"text":"Elle devient pathologique au-delà de 20 cmH₂O.","isCorrect":true},{"text":"Elle est toujours secondaire à une tumeur cérébrale.","isCorrect":false,"justification":"Multiples causes : tumeur, hématome, méningite, hydrocéphalie…"},{"text":"Elle se traduit uniquement par un coma.","isCorrect":false,"justification":"Le coma n’est qu’un stade extrême."}]$$::jsonb, $$L’HIC est une élévation du LCR > 20 cmH₂O, quelles qu’en soient les causes.$$, 0, '2025-11-02T17:52:05.334Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddqv00mpk498bggivrqn', 'cmhi0ddqs00mlk498lvw24tle', $$Principales causes$$, $$[{"text":"Tumeur intracrânienne.","isCorrect":true},{"text":"Hématome intracrânien.","isCorrect":true},{"text":"Hydrocéphalie.","isCorrect":true},{"text":"Méningite ou encéphalite.","isCorrect":true},{"text":"Hypoglycémie sévère.","isCorrect":false,"justification":"Trouble métabolique, non HIC."}]$$::jsonb, $$Les causes sont occupantes, obstructives ou inflammatoires du crâne.$$, 1, '2025-11-02T17:52:05.336Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddqx00mrk498lpzjfqa7', 'cmhi0ddqs00mlk498lvw24tle', $$Physiopathologie$$, $$[{"text":"Crâne = boîte inextensible : sang + LCR + tissu cérébral.","isCorrect":true},{"text":"Toute augmentation d’un composant doit être compensée par la baisse d’un autre.","isCorrect":true},{"text":"Si la compensation échoue → HIC.","isCorrect":true},{"text":"Le LCR est produit par les plexus choroïdes.","isCorrect":true},{"text":"Son excès est toujours compensé par la fuite veineuse.","isCorrect":false,"justification":"Compensation limitée."}]$$::jsonb, $$Le principe de Monro-Kellie explique la régulation intracrânienne : équilibre sang–LCR–tissu cérébral.$$, 2, '2025-11-02T17:52:05.338Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddqz00mtk498sviuzo0k', 'cmhi0ddqs00mlk498lvw24tle', $$Signes fonctionnels précoces$$, $$[{"text":"Céphalée matinale.","isCorrect":true},{"text":"Vomissements en jet.","isCorrect":true},{"text":"Diplopie par atteinte du VI.","isCorrect":true},{"text":"Fièvre élevée constante.","isCorrect":false,"justification":"Présente uniquement si cause infectieuse."},{"text":"Photophobie et raideur de nuque.","isCorrect":false,"justification":"Typiques du syndrome méningé."}]$$::jsonb, $$Les signes cardinaux de l’HIC = céphalée, vomissement, diplopie.$$, 3, '2025-11-02T17:52:05.339Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddr200mvk4985sj0p3o9', 'cmhi0ddqs00mlk498lvw24tle', $$Céphalée d’HIC$$, $$[{"text":"S’aggrave le matin.","isCorrect":true},{"text":"S’aggrave à la toux, l’effort, la position déclive.","isCorrect":true},{"text":"Calmée par le sommeil.","isCorrect":false,"justification":"Réveillante nocturne."},{"text":"Souvent occipitale.","isCorrect":true},{"text":"Toujours unilatérale.","isCorrect":false,"justification":"Diffuse le plus souvent."}]$$::jsonb, $$Céphalée matinale, diffuse, réveillante, majorée à l’effort = typique d’HIC.$$, 4, '2025-11-02T17:52:05.342Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddr400mxk498anzwkiun', 'cmhi0ddqs00mlk498lvw24tle', $$Vomissements d’HIC$$, $$[{"text":"Surviennent en jet sans effort.","isCorrect":true},{"text":"Soulagent parfois la céphalée.","isCorrect":true},{"text":"Toujours précédés de nausées.","isCorrect":false,"justification":"Souvent brusques, sans nausée."},{"text":"D’origine réflexe bulbaire.","isCorrect":true},{"text":"Spécifiques de la méningite.","isCorrect":false,"justification":"Non spécifiques, communs à plusieurs causes d’HIC."}]$$::jsonb, $$Vomissement brutal, en jet, sans effort, traduisant l’irritation bulbaire.$$, 5, '2025-11-02T17:52:05.344Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddr700mzk498ao19brhw', 'cmhi0ddqs00mlk498lvw24tle', $$Troubles visuels$$, $$[{"text":"Flou visuel intermittent.","isCorrect":true},{"text":"Baisse visuelle bilatérale progressive.","isCorrect":true},{"text":"Diplopie horizontale par atteinte du VI.","isCorrect":true},{"text":"Scintillements photopsiques.","isCorrect":true},{"text":"Mydriase bilatérale constante.","isCorrect":false,"justification":"Signe tardif d’engagement."}]$$::jsonb, $$Les troubles visuels sont liés à la stase papillaire et à l’atteinte du VI.$$, 6, '2025-11-02T17:52:05.348Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddra00n1k4984iuuo0ao', 'cmhi0ddqs00mlk498lvw24tle', $$Signes physiques généraux$$, $$[{"text":"Raideur de nuque.","isCorrect":false,"justification":"Méningé, pas HIC pur."},{"text":"Bradycardie et hypertension.","isCorrect":true},{"text":"Œdème papillaire.","isCorrect":true},{"text":"Vomissements sans effort.","isCorrect":true},{"text":"Dyspnée inspiratoire.","isCorrect":false,"justification":"Signe d’engagement terminal."}]$$::jsonb, $$L’association bradycardie + HTA + œdème papillaire signe l’HIC avancée.$$, 7, '2025-11-02T17:52:05.350Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddrc00n3k498kpeixj4c', 'cmhi0ddqs00mlk498lvw24tle', $$Œdème papillaire$$, $$[{"text":"Visible au fond d’œil.","isCorrect":true},{"text":"Traduit une stase veineuse rétinienne.","isCorrect":true},{"text":"Peut s’accompagner d’hémorragies péripapillaires.","isCorrect":true},{"text":"Toujours unilatéral.","isCorrect":false,"justification":"En général bilatéral."},{"text":"Réversible si cause traitée tôt.","isCorrect":true}]$$::jsonb, $$L’œdème papillaire est le signe fondamental d’HIC objectivé à l’ophtalmoscope.$$, 8, '2025-11-02T17:52:05.352Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddre00n5k498drvcipt7', 'cmhi0ddqs00mlk498lvw24tle', $$Évolution naturelle$$, $$[{"text":"Vers altération de la vigilance.","isCorrect":true},{"text":"Vers engagement cérébral.","isCorrect":true},{"text":"Vers atrophie optique irréversible.","isCorrect":true},{"text":"Toujours spontanément régressive.","isCorrect":false,"justification":"Rarement sans prise en charge."},{"text":"Sans conséquence sur la conscience.","isCorrect":false,"justification":"Peut mener au coma."}]$$::jsonb, $$L’HIC non traitée évolue vers coma et engagement cérébral.$$, 9, '2025-11-02T17:52:05.354Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddrg00n7k498mp6r77g0', 'cmhi0ddqs00mlk498lvw24tle', $$Signes d’engagement temporal$$, $$[{"text":"Mydriase homolatérale aréactive.","isCorrect":true},{"text":"Déviation conjuguée du regard controlatérale.","isCorrect":true},{"text":"Paralysie du VI bilatérale.","isCorrect":false,"justification":"Typique d’HIC globale."},{"text":"Hyperréflexie diffuse.","isCorrect":false,"justification":"Non spécifique."},{"text":"Disparition du Babinski.","isCorrect":false,"justification":"Signe pyramidal présent."}]$$::jsonb, $$L’engagement temporal = mydriase unilatérale, troubles oculaires, coma rapide.$$, 10, '2025-11-02T17:52:05.357Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddrj00n9k4982wkwyyji', 'cmhi0ddqs00mlk498lvw24tle', $$Signes d’engagement tonsillaire$$, $$[{"text":"Dépression respiratoire.","isCorrect":true},{"text":"Mydriase unilatérale.","isCorrect":false,"justification":"Temporal."},{"text":"Céphalée occipitale calmée par la position debout.","isCorrect":false,"justification":"Aggravée."},{"text":"Fièvre.","isCorrect":false,"justification":"Non spécifique."},{"text":"Diplopie isolée.","isCorrect":false,"justification":"Haut niveau."}]$$::jsonb, $$L’engagement tonsillaire (amygdalien) = bradypnée, troubles cardio-respiratoires → urgence vitale.$$, 11, '2025-11-02T17:52:05.359Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddrn00ndk498kur6otr9', 'cmhi0ddqs00mlk498lvw24tle', $$Imagerie cérébrale$$, $$[{"text":"TDM ou IRM confirment la cause.","isCorrect":true},{"text":"Permettent de rechercher un effet de masse.","isCorrect":true},{"text":"La PL doit précéder le scanner.","isCorrect":false,"justification":"Danger : engagement."},{"text":"IRM : utile pour hydrocéphalie ou tumeur.","isCorrect":true},{"text":"Imagerie inutile si œdème papillaire présent.","isCorrect":false,"justification":"Toujours nécessaire."}]$$::jsonb, $$L’imagerie précède toute PL et oriente le siège et la cause de l’HIC.$$, 13, '2025-11-02T17:52:05.364Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddrp00nfk498ibqw9m30', 'cmhi0ddqs00mlk498lvw24tle', $$Ponction lombaire$$, $$[{"text":"Contre-indiquée en cas de signes d’engagement.","isCorrect":true},{"text":"Permet de mesurer la pression du LCR.","isCorrect":true},{"text":"Peut provoquer un engagement si faite à tort.","isCorrect":true},{"text":"Toujours nécessaire avant scanner.","isCorrect":false,"justification":"Jamais avant imagerie."},{"text":"Inutile pour le diagnostic.","isCorrect":false,"justification":"Utile, si pas de masse visible."}]$$::jsonb, $$La PL confirme l’HIC uniquement si imagerie normale et sans engagement.$$, 14, '2025-11-02T17:52:05.365Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddrr00nhk498wbpt79ag', 'cmhi0ddqs00mlk498lvw24tle', $$Syndrome d’HIC bénigne idiopathique (pseudotumeur cérébrale)$$, $$[{"text":"Plus fréquente chez la femme jeune obèse.","isCorrect":true},{"text":"Céphalée, vomissements, œdème papillaire.","isCorrect":true},{"text":"Scanner/IRM normaux.","isCorrect":true},{"text":"Pression du LCR augmentée.","isCorrect":true},{"text":"Traitement chirurgical d’emblée.","isCorrect":false,"justification":"Médical d’abord (acétazolamide, perte pondérale)."}]$$::jsonb, $$L’HIC idiopathique = femme jeune, scanner normal, LCR↑, traitement médical.$$, 15, '2025-11-02T17:52:05.368Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddrt00njk4980pkc07pa', 'cmhi0ddqs00mlk498lvw24tle', $$HIC post-traumatique$$, $$[{"text":"Secondaire à un hématome sous-dural ou épidural.","isCorrect":true},{"text":"Peut évoluer rapidement vers un coma.","isCorrect":true},{"text":"Toujours bénigne.","isCorrect":false,"justification":"Urgence vitale."},{"text":"Traitement chirurgical souvent indiqué.","isCorrect":true},{"text":"Diagnostic posé par LCR.","isCorrect":false,"justification":"Scanner en urgence."}]$$::jsonb, $$HIC post-traumatique = urgence neurochirurgicale liée à un hématome compressif.$$, 16, '2025-11-02T17:52:05.369Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddru00nlk498fdplv349', 'cmhi0ddqs00mlk498lvw24tle', $$Évolution non traitée$$, $$[{"text":"Coma.","isCorrect":true},{"text":"Arrêt respiratoire.","isCorrect":true},{"text":"Bradycardie et hypertension terminales.","isCorrect":true},{"text":"Régression spontanée.","isCorrect":false,"justification":"Exceptionnelle."},{"text":"Survie prolongée sans traitement.","isCorrect":false,"justification":"Pronostic fatal."}]$$::jsonb, $$L’HIC non traitée conduit au coma et décès par engagement.$$, 17, '2025-11-02T17:52:05.371Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddrw00nnk4987brgh72s', 'cmhi0ddqs00mlk498lvw24tle', $$Traitement d’urgence$$, $$[{"text":"Oxygénation et ventilation.","isCorrect":true},{"text":"Tête surélevée à 30°.","isCorrect":true},{"text":"Mannitol IV ou hypertonique.","isCorrect":true},{"text":"PL immédiate.","isCorrect":false,"justification":"Contre-indiquée."},{"text":"Sédation légère et surveillance.","isCorrect":true}]$$::jsonb, $$L’urgence vise à baisser la pression (manitol, posture, ventilation) avant traitement étiologique.$$, 18, '2025-11-02T17:52:05.372Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddrx00npk4982kk4eu1h', 'cmhi0ddqs00mlk498lvw24tle', $$Pronostic$$, $$[{"text":"Dépend de la cause.","isCorrect":true},{"text":"Dépend de la rapidité de prise en charge.","isCorrect":true},{"text":"Toujours favorable.","isCorrect":false,"justification":"Grave sans traitement."},{"text":"L’engagement aggrave le pronostic.","isCorrect":true},{"text":"Aucun risque visuel.","isCorrect":false,"justification":"Atrophie optique possible."}]$$::jsonb, $$Le pronostic dépend du délai thérapeutique ; l’engagement menace le pronostic vital et visuel.$$, 19, '2025-11-02T17:52:05.374Z');
INSERT INTO "Chapter" (id, "subjectId", title, description, "orderIndex", "pdfUrl", "createdAt") VALUES ('cmhi0dds000nrk498q6k3r2x5', 'cmhi0ddn900j1k4988obeo56w', $$Chapitre 5 – Syndrome méningé$$, NULL, 5, NULL, '2025-11-02T17:52:05.376Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0dds200ntk498r3uk1f5z', 'cmhi0dds000nrk498q6k3r2x5', $$Définition$$, $$[{"text":"Ensemble de signes traduisant une irritation des méninges.","isCorrect":true},{"text":"C’est un syndrome moteur pur.","isCorrect":false,"justification":"Il comporte signes douloureux et réflexes, pas moteurs."},{"text":"Peut être infectieux, inflammatoire ou hémorragique.","isCorrect":true},{"text":"N’existe qu’en cas de méningite purulente.","isCorrect":false,"justification":"Aussi dans hémorragie méningée."},{"text":"Touche exclusivement les enfants.","isCorrect":false,"justification":"Peut concerner tout âge."}]$$::jsonb, $$Le syndrome méningé traduit une inflammation ou irritation des méninges d’origine variée.$$, 0, '2025-11-02T17:52:05.378Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0dds400nvk49839scodqj', 'cmhi0dds000nrk498q6k3r2x5', $$Anatomie fonctionnelle$$, $$[{"text":"Les méninges entourent l’encéphale et la moelle épinière.","isCorrect":true},{"text":"Les trois feuillets sont : dure-mère, arachnoïde, pie-mère.","isCorrect":true},{"text":"L’espace sous-arachnoïdien contient le LCR.","isCorrect":true},{"text":"La pie-mère est la plus externe.","isCorrect":false,"justification":"C’est la plus interne."},{"text":"L’irritation méningée touche toujours le cortex.","isCorrect":false,"justification":"Méninges, non substance cérébrale."}]$$::jsonb, $$Le LCR circule entre arachnoïde et pie-mère, où s’exprime le syndrome méningé.$$, 1, '2025-11-02T17:52:05.380Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0dds500nxk498860j5huf', 'cmhi0dds000nrk498q6k3r2x5', $$Causes infectieuses$$, $$[{"text":"Méningite bactérienne (méningocoque, pneumocoque).","isCorrect":true},{"text":"Méningite virale.","isCorrect":true},{"text":"Méningite tuberculeuse.","isCorrect":true},{"text":"Méningite parasitaire (toxoplasmose, cysticercose).","isCorrect":true},{"text":"Méningite fongique (cryptococcose).","isCorrect":true}]$$::jsonb, $$Les causes infectieuses incluent bactéries, virus, bacilles, champignons et parasites.$$, 2, '2025-11-02T17:52:05.381Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0dds700nzk498gx487a25', 'cmhi0dds000nrk498q6k3r2x5', $$Causes non infectieuses$$, $$[{"text":"Hémorragie méningée.","isCorrect":true},{"text":"Tumeurs méningées (carcinose).","isCorrect":true},{"text":"Médicaments (anti-inflammatoires, immuno).","isCorrect":true},{"text":"Lésions traumatiques.","isCorrect":true},{"text":"Hypocalcémie aiguë.","isCorrect":false,"justification":"Donne tétanie, pas syndrome méningé."}]$$::jsonb, $$De nombreuses causes non infectieuses peuvent irriter les méninges (sang, tumeur, inflammation chimique).$$, 3, '2025-11-02T17:52:05.383Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0dds800o1k498asq0gsgf', 'cmhi0dds000nrk498q6k3r2x5', $$Signes fonctionnels généraux$$, $$[{"text":"Céphalée intense.","isCorrect":true},{"text":"Vomissements en jet.","isCorrect":true},{"text":"Fièvre.","isCorrect":true},{"text":"Diplopie constante.","isCorrect":false,"justification":"Inconstante, parfois par atteinte du VI."},{"text":"Raideur de nuque.","isCorrect":true}]$$::jsonb, $$La triade céphalée + vomissement + raideur de nuque définit le tableau cardinal.$$, 4, '2025-11-02T17:52:05.384Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0dds900o3k498mkqsdnik', 'cmhi0dds000nrk498q6k3r2x5', $$Céphalée méningée$$, $$[{"text":"Diffuse, continue, intense.","isCorrect":true},{"text":"Majorée par la lumière (photophobie).","isCorrect":true},{"text":"Aggravée par les bruits (phonophobie).","isCorrect":true},{"text":"Calmée par le décubitus.","isCorrect":false,"justification":"Souvent aggravée."},{"text":"Soulagée par les antalgiques simples.","isCorrect":false,"justification":"Résiste aux antalgiques usuels."}]$$::jsonb, $$Céphalée diffuse, résistante, photophobique est typique de l’irritation méningée.$$, 5, '2025-11-02T17:52:05.386Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddsb00o5k498no7210re', 'cmhi0dds000nrk498q6k3r2x5', $$Raideur de nuque$$, $$[{"text":"Résistance à la flexion passive de la tête.","isCorrect":true},{"text":"Témoin de contracture réflexe des muscles cervicaux.","isCorrect":true},{"text":"Disparaît à l’endormissement.","isCorrect":true},{"text":"Présente dans toutes les méningites.","isCorrect":true},{"text":"Spécifique des méningites bactériennes.","isCorrect":false,"justification":"Non spécifique (existe aussi dans hémorragies)."}]$$::jsonb, $$Raideur de nuque = résistance réflexe de flexion, signe d’irritation méningée diffuse.$$, 6, '2025-11-02T17:52:05.387Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddsd00o7k498std6ave9', 'cmhi0dds000nrk498q6k3r2x5', $$Signes de Kernig et Brudzinski$$, $$[{"text":"Kernig : impossibilité d’étendre le genou sur hanche fléchie à 90°.","isCorrect":true},{"text":"Brudzinski : flexion involontaire des jambes lors de la flexion de la nuque.","isCorrect":true},{"text":"Signe d’une atteinte pyramidale.","isCorrect":false,"justification":"Méningée."},{"text":"Toujours présents.","isCorrect":false,"justification":"Parfois absents chez l’enfant ou le vieillard."},{"text":"Mécanisme réflexe douloureux méningé.","isCorrect":true}]$$::jsonb, $$Les signes de Kernig et Brudzinski sont caractéristiques mais inconstants.$$, 7, '2025-11-02T17:52:05.389Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddse00o9k498grgymned', 'cmhi0dds000nrk498q6k3r2x5', $$Photophobie et phonophobie$$, $$[{"text":"Liées à une irritation du cortex moteur.","isCorrect":false,"justification":"Méningée."},{"text":"Résultent de l’hyperexcitabilité méningée.","isCorrect":true},{"text":"Présentes uniquement dans les hémorragies.","isCorrect":false,"justification":"Aussi méningites virales."},{"text":"Spécifiques de l’enfant.","isCorrect":false,"justification":"Tout âge."},{"text":"Accompagnées d’une amaurose.","isCorrect":false,"justification":"Non associée."}]$$::jsonb, $$Photophobie et phonophobie traduisent une irritation sensorielle méningée.$$, 8, '2025-11-02T17:52:05.391Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddsg00obk498xd542c6y', 'cmhi0dds000nrk498q6k3r2x5', $$Troubles de la conscience$$, $$[{"text":"Présents dans les formes graves.","isCorrect":true},{"text":"Peuvent aller jusqu’au coma.","isCorrect":true},{"text":"Fréquents dans les méningites virales bénignes.","isCorrect":false,"justification":"Rares, réservés aux formes graves."},{"text":"S’expliquent par œdème cérébral associé.","isCorrect":true},{"text":"Toujours irréversibles.","isCorrect":false,"justification":"Réversibles sous traitement."}]$$::jsonb, $$Les troubles de conscience traduisent la souffrance corticale ou encéphalique secondaire à l’HIC.$$, 9, '2025-11-02T17:52:05.392Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddsi00odk498giyvcoyz', 'cmhi0dds000nrk498q6k3r2x5', $$Signes d’accompagnement$$, $$[{"text":"Fièvre élevée.","isCorrect":true},{"text":"Éruptions pétéchiales (méningocoque).","isCorrect":true},{"text":"Crises convulsives possibles.","isCorrect":true},{"text":"Bradycardie.","isCorrect":false,"justification":"HIC, pas méningite simple."},{"text":"Hypertonie plastique.","isCorrect":false,"justification":"Extrapyramidal."}]$$::jsonb, $$Fièvre, éruption et convulsions sont des signes d’accompagnement fréquents des méningites infectieuses.$$, 10, '2025-11-02T17:52:05.395Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddsk00ofk498xnuw0qkz', 'cmhi0dds000nrk498q6k3r2x5', $$Examen du fond d’œil$$, $$[{"text":"Recherche un œdème papillaire.","isCorrect":true},{"text":"Contre-indique la ponction lombaire s’il est présent.","isCorrect":true},{"text":"Toujours normal dans la méningite.","isCorrect":false,"justification":"Peut montrer stase."},{"text":"Nécessaire avant PL.","isCorrect":true},{"text":"Permet d’évaluer la tension artérielle.","isCorrect":false,"justification":"Ce n’est pas son but principal."}]$$::jsonb, $$Le fond d’œil doit précéder la PL pour éliminer une HIC.$$, 11, '2025-11-02T17:52:05.396Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddsl00ohk4985u0fnikg', 'cmhi0dds000nrk498q6k3r2x5', $$Examens complémentaires$$, $$[{"text":"Scanner ou IRM avant PL si signes de gravité.","isCorrect":true},{"text":"Ponction lombaire indispensable au diagnostic.","isCorrect":true},{"text":"Hémocultures systématiques avant antibiotiques.","isCorrect":true},{"text":"Dosage de la CRP inutile.","isCorrect":false,"justification":"Aide à différencier bactérienne/virale."},{"text":"La PL est contre-indiquée si suspicion d’hématome.","isCorrect":true}]$$::jsonb, $$Le diagnostic repose sur PL + imagerie préalable si signes d’engagement.$$, 12, '2025-11-02T17:52:05.398Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddsn00ojk498tf7rw65j', 'cmhi0dds000nrk498q6k3r2x5', $$Aspect du LCR$$, $$[{"text":"Clair dans les méningites virales.","isCorrect":true},{"text":"Purulent dans les méningites bactériennes.","isCorrect":true},{"text":"Xanthochromique dans les hémorragies.","isCorrect":true},{"text":"Hypertenu dans la majorité des cas.","isCorrect":true},{"text":"Toujours stérile.","isCorrect":false,"justification":"Bactéries identifiées à la culture."}]$$::jsonb, $$L’aspect du LCR oriente immédiatement vers la cause infectieuse ou hémorragique.$$, 13, '2025-11-02T17:52:05.399Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddsp00olk49851nntfvz', 'cmhi0dds000nrk498q6k3r2x5', $$LCR bactérien typique$$, $$[{"text":"Hyperprotéinorachie.","isCorrect":true},{"text":"Hypoglycorachie.","isCorrect":true},{"text":"Polynucléaires neutrophiles prédominants.","isCorrect":true},{"text":"Pression d’ouverture augmentée.","isCorrect":true},{"text":"Culture toujours négative.","isCorrect":false,"justification":"Culture positive oriente l’antibiothérapie."}]$$::jsonb, $$LCR bactérien = purulent, hyperprotéiné, hypoglycorachique, PNN++.$$, 14, '2025-11-02T17:52:05.401Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddsq00onk498nsa6n2ci', 'cmhi0dds000nrk498q6k3r2x5', $$LCR viral typique$$, $$[{"text":"Clair.","isCorrect":true},{"text":"Hyperlymphocytose modérée.","isCorrect":true},{"text":"Glycorachie normale.","isCorrect":true},{"text":"Protéines légèrement augmentées.","isCorrect":true},{"text":"Pression normale ou légèrement élevée.","isCorrect":true}]$$::jsonb, $$LCR viral = clair, lymphocytaire, protéines↑ modérées, glycorachie normale.$$, 15, '2025-11-02T17:52:05.402Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddss00opk498pxjf3jcz', 'cmhi0dds000nrk498q6k3r2x5', $$LCR tuberculeux$$, $$[{"text":"Jaunâtre xanthochromique.","isCorrect":true},{"text":"Hyperlymphocytose.","isCorrect":true},{"text":"Hypoglycorachie marquée.","isCorrect":true},{"text":"Protéines très élevées.","isCorrect":true},{"text":"Bacilles visibles à l’examen direct.","isCorrect":true}]$$::jsonb, $$LCR trouble, lymphocytaire, hypoglycorachique, protéines ↑↑, typique de la tuberculose.$$, 16, '2025-11-02T17:52:05.404Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddst00ork498ecb38njg', 'cmhi0dds000nrk498q6k3r2x5', $$Signes de gravité$$, $$[{"text":"Troubles de conscience.","isCorrect":true},{"text":"Convulsions.","isCorrect":true},{"text":"Purpura extensif.","isCorrect":true},{"text":"Hypotension.","isCorrect":true},{"text":"Raideur isolée sans fièvre.","isCorrect":false,"justification":"Forme bénigne ou non infectieuse."}]$$::jsonb, $$Les signes de gravité traduisent une atteinte méningo-encéphalique ou sepsis sévère.$$, 17, '2025-11-02T17:52:05.406Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddsv00otk4980fppv226', 'cmhi0dds000nrk498q6k3r2x5', $$Traitement d’urgence$$, $$[{"text":"Antibiothérapie IV immédiate (avant PL si retard).","isCorrect":true},{"text":"Corticothérapie adjuvante (dexaméthasone).","isCorrect":true},{"text":"Isolement du patient en cas de méningocoque.","isCorrect":true},{"text":"Réhydratation et antipyretique.","isCorrect":true},{"text":"PL systématique avant traitement.","isCorrect":false,"justification":"Seulement si possible sans risque."}]$$::jsonb, $$En urgence : antibiothérapie + corticoïdes + isolement si méningocoque.$$, 18, '2025-11-02T17:52:05.407Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddsx00ovk4986orn8amv', 'cmhi0dds000nrk498q6k3r2x5', $$Prévention$$, $$[{"text":"Vaccination antiméningococcique.","isCorrect":true},{"text":"Prophylaxie antibiotique des contacts.","isCorrect":true},{"text":"Port du masque obligatoire pour le personnel.","isCorrect":true},{"text":"Isolement respiratoire inutile.","isCorrect":false,"justification":"Indiqué pour méningocoque."},{"text":"Déclaration obligatoire de cas de méningite bactérienne.","isCorrect":true}]$$::jsonb, $$La prévention repose sur vaccination, isolement, prophylaxie et déclaration sanitaire.$$, 19, '2025-11-02T17:52:05.409Z');
INSERT INTO "Chapter" (id, "subjectId", title, description, "orderIndex", "pdfUrl", "createdAt") VALUES ('cmhi0ddt000oxk498qp7y0nd5', 'cmhi0ddn900j1k4988obeo56w', $$Chapitre 6 – Syndrome cérébelleux$$, NULL, 6, NULL, '2025-11-02T17:52:05.413Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddt200ozk49828ylbpsf', 'cmhi0ddt000oxk498qp7y0nd5', $$Définition$$, $$[{"text":"Ensemble de signes dus à une atteinte du cervelet ou de ses voies.","isCorrect":true},{"text":"Toujours d’origine médullaire.","isCorrect":false,"justification":"Atteinte sous-corticale postérieure."},{"text":"Responsable de troubles de coordination et d’équilibre.","isCorrect":true},{"text":"Ne modifie jamais le tonus musculaire.","isCorrect":false,"justification":"Souvent hypotonie."},{"text":"Toujours accompagné de déficit moteur pyramidal.","isCorrect":false,"justification":"Ataxie pure, sans paralysie."}]$$::jsonb, $$Le syndrome cérébelleux = trouble de coordination, d’équilibre et du tonus, sans déficit moteur.$$, 0, '2025-11-02T17:52:05.414Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddt300p1k4989x2jhuvb', 'cmhi0ddt000oxk498qp7y0nd5', $$Rappel anatomique$$, $$[{"text":"Cervelet divisé en 3 parties : archi-, paleo-, néocervelet.","isCorrect":true},{"text":"Archicervelet = équilibre.","isCorrect":true},{"text":"Paléocervelet = tonus et posture.","isCorrect":true},{"text":"Néocervelet = coordination fine et mouvements volontaires.","isCorrect":true},{"text":"Cervelet situé dans le lobe frontal.","isCorrect":false,"justification":"Il est sous-occipital."}]$$::jsonb, $$Le cervelet a une organisation fonctionnelle tridivisée : équilibre, tonus, coordination.$$, 1, '2025-11-02T17:52:05.416Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddt500p3k498bsqg0tvp', 'cmhi0ddt000oxk498qp7y0nd5', $$Fonctions principales$$, $$[{"text":"Coordination motrice.","isCorrect":true},{"text":"Équilibre et posture.","isCorrect":true},{"text":"Apprentissage moteur.","isCorrect":true},{"text":"Régulation du tonus musculaire.","isCorrect":true},{"text":"Sensibilité superficielle.","isCorrect":false,"justification":"Fonction sensorielle, pas cérébelleuse."}]$$::jsonb, $$Le cervelet assure coordination, équilibre, tonus et adaptation du mouvement.$$, 2, '2025-11-02T17:52:05.417Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddt700p5k4984j2y43d3', 'cmhi0ddt000oxk498qp7y0nd5', $$Types de syndrome cérébelleux$$, $$[{"text":"Statique (vermis).","isCorrect":true},{"text":"Cinétique (hémisphères).","isCorrect":true},{"text":"Mixtes (lésions étendues).","isCorrect":true},{"text":"Pyramidal.","isCorrect":false,"justification":"Autre voie."},{"text":"Vestibulaire.","isCorrect":false,"justification":"Différencié, mais parfois associé."}]$$::jsonb, $$Les atteintes du vermis → équilibre, celles des hémisphères → coordination.$$, 3, '2025-11-02T17:52:05.419Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddt900p7k4981a0fmocq', 'cmhi0ddt000oxk498qp7y0nd5', $$Syndrome statique$$, $$[{"text":"Trouble de la station debout.","isCorrect":true},{"text":"Chute à la fermeture des yeux (Romberg +).","isCorrect":false,"justification":"Négatif (pas sensoriel)."},{"text":"Oscillations du tronc.","isCorrect":true},{"text":"Polygone de sustentation élargi.","isCorrect":true},{"text":"Marche ébrieuse, titubante.","isCorrect":true}]$$::jsonb, $$Le syndrome statique = instabilité posturale, oscillations troncales, marche ébrieuse.$$, 4, '2025-11-02T17:52:05.421Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddta00p9k498kbiylixz', 'cmhi0ddt000oxk498qp7y0nd5', $$Syndrome cinétique$$, $$[{"text":"Dysmétrie.","isCorrect":true},{"text":"Asynergie.","isCorrect":true},{"text":"Adiadococinésie.","isCorrect":true},{"text":"Tremblement intentionnel.","isCorrect":true},{"text":"Babinski présent.","isCorrect":false,"justification":"Pyramidal, pas cérébelleux."}]$$::jsonb, $$Le syndrome cinétique = atteinte des gestes coordonnés volontaires.$$, 5, '2025-11-02T17:52:05.423Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddtc00pbk49882vjt02r', 'cmhi0ddt000oxk498qp7y0nd5', $$Dysmétrie$$, $$[{"text":"Dépassement ou insuffisance du but.","isCorrect":true},{"text":"Signe du doigt-nez positif.","isCorrect":true},{"text":"Signe du talon-genou.","isCorrect":true},{"text":"Exagérée à la fermeture des yeux.","isCorrect":false,"justification":"Identique yeux ouverts/fermés."},{"text":"Touche toujours les deux côtés.","isCorrect":false,"justification":"Homolatérale à la lésion."}]$$::jsonb, $$La dysmétrie traduit une mauvaise régulation d’amplitude du mouvement.$$, 6, '2025-11-02T17:52:05.424Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddte00pdk498jactygxu', 'cmhi0ddt000oxk498qp7y0nd5', $$Asynergie$$, $$[{"text":"Bonne coordination des mouvements synergiques.","isCorrect":false,"justification":"C’est la perte de coordination."},{"text":"Rupture de l’enchaînement harmonieux du mouvement.","isCorrect":true},{"text":"Accentuation du tonus musculaire.","isCorrect":false,"justification":"Hypotonie."},{"text":"Disparaît yeux fermés.","isCorrect":false,"justification":"Persiste."},{"text":"Spécifique des atteintes pyramidales.","isCorrect":false,"justification":"Cérébelleuse."}]$$::jsonb, $$L’asynergie est la perte d’harmonie du mouvement global.$$, 7, '2025-11-02T17:52:05.426Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddtg00pfk49889292y1u', 'cmhi0ddt000oxk498qp7y0nd5', $$Adiadococinésie$$, $$[{"text":"Incapacité à effectuer des mouvements alternés rapides.","isCorrect":true},{"text":"Due à un déficit de la motricité automatique.","isCorrect":true},{"text":"Présente dans les atteintes cérébelleuses hémisphériques.","isCorrect":true},{"text":"Disparaît yeux fermés.","isCorrect":false,"justification":"Persiste."},{"text":"Toujours bilatérale.","isCorrect":false,"justification":"Souvent homolatérale."}]$$::jsonb, $$L’adiadococinésie traduit une désynchronisation des mouvements alternatifs.$$, 8, '2025-11-02T17:52:05.429Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddti00phk498g6a0klp9', 'cmhi0ddt000oxk498qp7y0nd5', $$Tremblement cérébelleux$$, $$[{"text":"Apparaît à l’action.","isCorrect":true},{"text":"Tremblement d’attitude.","isCorrect":false,"justification":"Intentionnel."},{"text":"Disparaît au repos.","isCorrect":true},{"text":"Irrégulier, lent et ample.","isCorrect":true},{"text":"Sensible à la L-Dopa.","isCorrect":false,"justification":"Non dopasensible."}]$$::jsonb, $$Tremblement intentionnel, lent, ample, apparaissant à l’action, typiquement cérébelleux.$$, 9, '2025-11-02T17:52:05.431Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddtk00pjk498l8qin12c', 'cmhi0ddt000oxk498qp7y0nd5', $$Hypotonie cérébelleuse$$, $$[{"text":"Baisse du tonus musculaire.","isCorrect":true},{"text":"Souvent associée à un ballant exagéré.","isCorrect":true},{"text":"Se manifeste par un retard à la contraction.","isCorrect":true},{"text":"Accompagnée de spasticité.","isCorrect":false,"justification":"Atonie, pas spasticité."},{"text":"Bilatérale d’emblée.","isCorrect":false,"justification":"Homolatérale à la lésion."}]$$::jsonb, $$L’hypotonie cérébelleuse = flaccidité, ballant accru, contraction retardée.$$, 10, '2025-11-02T17:52:05.432Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddtm00plk498svwlaw0b', 'cmhi0ddt000oxk498qp7y0nd5', $$Dysarthrie cérébelleuse$$, $$[{"text":"Voix scandée, explosive.","isCorrect":true},{"text":"Parole lente, irrégulière.","isCorrect":true},{"text":"Voix monotone et hypophonique.","isCorrect":false,"justification":"Parkinsonienne."},{"text":"Peut être accompagnée d’ataxie bucco-linguée.","isCorrect":true},{"text":"Toujours d’origine périphérique.","isCorrect":false,"justification":"Centrale."}]$$::jsonb, $$Dysarthrie scandée et explosive, typique d’une atteinte cérébelleuse.$$, 11, '2025-11-02T17:52:05.434Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddto00pnk498dnl0ubed', 'cmhi0ddt000oxk498qp7y0nd5', $$Ataxie cérébelleuse$$, $$[{"text":"Trouble de la coordination motrice sans déficit moteur.","isCorrect":true},{"text":"Aggravée yeux fermés.","isCorrect":false,"justification":"Non aggravée (≠ proprioceptive)."},{"text":"Avec marche ébrieuse.","isCorrect":true},{"text":"Ataxie optocinétique associée.","isCorrect":false,"justification":"Non visuelle."},{"text":"Troubles posturaux même yeux ouverts.","isCorrect":true}]$$::jsonb, $$L’ataxie cérébelleuse = instabilité globale, non influencée par la vision.$$, 12, '2025-11-02T17:52:05.436Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddtq00ppk498d6jvv7cm', 'cmhi0ddt000oxk498qp7y0nd5', $$Épreuves de coordination$$, $$[{"text":"Épreuve doigt-nez.","isCorrect":true},{"text":"Épreuve talon-genou.","isCorrect":true},{"text":"Épreuve main-genou.","isCorrect":true},{"text":"Épreuve talon-oreille.","isCorrect":false,"justification":"Non utilisée."},{"text":"Épreuve talon-talon utile.","isCorrect":true}]$$::jsonb, $$Les épreuves de doigt-nez et talon-genou révèlent les troubles de coordination.$$, 13, '2025-11-02T17:52:05.438Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddts00prk4980sbfb9ha', 'cmhi0ddt000oxk498qp7y0nd5', $$Signes oculaires cérébelleux$$, $$[{"text":"Nystagmus horizontal.","isCorrect":true},{"text":"Nystagmus changeant de direction.","isCorrect":true},{"text":"Nystagmus vertical pur.","isCorrect":false,"justification":"Tronc cérébral."},{"text":"Aggravé à la fixation.","isCorrect":true},{"text":"Lié à un trouble de coordination oculomotrice.","isCorrect":true}]$$::jsonb, $$Le nystagmus cérébelleux est horizontal, multidirectionnel, aggravé à la fixation.$$, 14, '2025-11-02T17:52:05.440Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddtu00ptk498afhosy7j', 'cmhi0ddt000oxk498qp7y0nd5', $$Particularités topographiques$$, $$[{"text":"Syndrome vermien → instabilité posturale.","isCorrect":true},{"text":"Syndrome hémisphérique → troubles de coordination homolatéraux.","isCorrect":true},{"text":"Lésion du flocculo-nodulaire → troubles de l’équilibre et du regard.","isCorrect":true},{"text":"Lésion du pédoncule supérieur → syndrome sensitif.","isCorrect":false,"justification":"Moteur."},{"text":"Lésion bilatérale → hypotonie diffuse.","isCorrect":true}]$$::jsonb, $$Topographie : vermis = équilibre, hémisphère = coordination, flocculo-nodulaire = oculomotricité.$$, 15, '2025-11-02T17:52:05.442Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddtw00pvk498okth33zz', 'cmhi0ddt000oxk498qp7y0nd5', $$Étiologies fréquentes$$, $$[{"text":"Vasculaires (AVC du territoire vertébro-basilaire).","isCorrect":true},{"text":"Tumorales (médulloblastome, astrocytome).","isCorrect":true},{"text":"Toxiques (alcool, antiepileptiques).","isCorrect":true},{"text":"Infectieuses (abcès, méningite).","isCorrect":true},{"text":"Traumatiques.","isCorrect":true}]$$::jsonb, $$Les causes du syndrome cérébelleux sont vasculaires, tumorales, toxiques, infectieuses ou traumatiques.$$, 16, '2025-11-02T17:52:05.444Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddty00pxk498j9vynw6b', 'cmhi0ddt000oxk498qp7y0nd5', $$Cérébelleux vs Vestibulaire$$, $$[{"text":"Cérébelleux : marche ébrieuse, yeux ouverts ≠ vestibulaire : aggravée yeux fermés.","isCorrect":true},{"text":"Cérébelleux : nystagmus changeant, vestibulaire : unidirectionnel.","isCorrect":true},{"text":"Cérébelleux : pas de vertige vrai.","isCorrect":true},{"text":"Vestibulaire : signes auditifs associés.","isCorrect":true},{"text":"Les deux présentent Babinski.","isCorrect":false,"justification":"Aucun."}]$$::jsonb, $$Comparativement, le vestibulaire = vertige, cérébelleux = ataxie sans vertige.$$, 17, '2025-11-02T17:52:05.447Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddu000pzk4986ozhqtcv', 'cmhi0ddt000oxk498qp7y0nd5', $$Examens complémentaires$$, $$[{"text":"IRM cérébrale = examen de référence.","isCorrect":true},{"text":"Scanner utile en urgence (hémorragie).","isCorrect":true},{"text":"Épreuve calorique vestibulaire utile pour différencier.","isCorrect":true},{"text":"Électromyogramme.","isCorrect":false,"justification":"Musculaire, pas cérébelleux."},{"text":"Tests génétiques pour ataxies héréditaires.","isCorrect":true}]$$::jsonb, $$IRM, scanner et tests vestibulaires ou génétiques orientent la cause du syndrome cérébelleux.$$, 18, '2025-11-02T17:52:05.449Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddu200q1k4985pgkplw7', 'cmhi0ddt000oxk498qp7y0nd5', $$Traitement et évolution$$, $$[{"text":"Dépend de la cause.","isCorrect":true},{"text":"Rééducation motrice et posturale.","isCorrect":true},{"text":"Correction des causes toxiques (alcool, médicaments).","isCorrect":true},{"text":"Chirurgie si tumeur ou hémorragie compressive.","isCorrect":true},{"text":"Guérison spontanée toujours complète.","isCorrect":false,"justification":"Souvent séquelles."}]$$::jsonb, $$La prise en charge est étiologique et fonctionnelle ; la récupération dépend de la cause.$$, 19, '2025-11-02T17:52:05.451Z');
INSERT INTO "Chapter" (id, "subjectId", title, description, "orderIndex", "pdfUrl", "createdAt") VALUES ('cmhi0ddu500q3k498b9wzjfgd', 'cmhi0ddn900j1k4988obeo56w', $$Chapitre 7 – Syndrome pyramidal$$, NULL, 7, NULL, '2025-11-02T17:52:05.454Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddu700q5k4982o3g43gl', 'cmhi0ddu500q3k498b9wzjfgd', $$Définition$$, $$[{"text":"Ensemble de signes dus à une lésion du faisceau cortico-spinal.","isCorrect":true},{"text":"Traduit une atteinte périphérique.","isCorrect":false,"justification":"C’est une atteinte centrale."},{"text":"S’accompagne toujours de troubles sensitifs.","isCorrect":false,"justification":"Purement moteur."},{"text":"N’entraîne jamais de troubles du tonus.","isCorrect":false,"justification":"Donne hypertonie spastique."},{"text":"Exclusivement cérébelleux.","isCorrect":false,"justification":"Différent."}]$$::jsonb, $$Le syndrome pyramidal résulte d’une atteinte centrale motrice (voie cortico-spinale), sans trouble sensitif.$$, 0, '2025-11-02T17:52:05.456Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddua00q7k498hdv4sw93', 'cmhi0ddu500q3k498b9wzjfgd', $$Voie concernée$$, $$[{"text":"Faisceau cortico-spinal croisé.","isCorrect":true},{"text":"Faisceau spinothalamique.","isCorrect":false,"justification":"Voie sensitive."},{"text":"Faisceau vestibulo-spinal.","isCorrect":false,"justification":"Équilibre."},{"text":"Faisceau rubrospinal.","isCorrect":false,"justification":"Extrapyramidal."},{"text":"Voie du réflexe myotatique.","isCorrect":false,"justification":"Segmentaire."}]$$::jsonb, $$La voie cortico-spinale croisée est responsable de la motricité volontaire.$$, 1, '2025-11-02T17:52:05.458Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0dduc00q9k498slsy2igv', 'cmhi0ddu500q3k498b9wzjfgd', $$Lésion du faisceau pyramidal$$, $$[{"text":"Donne une paralysie flasque d’emblée.","isCorrect":true},{"text":"Puis une hypertonie spastique secondaire.","isCorrect":true},{"text":"Conserve les ROT normaux.","isCorrect":false,"justification":"ROT exagérés."},{"text":"Babinski négatif.","isCorrect":false,"justification":"Positif."},{"text":"Amyotrophie rapide.","isCorrect":false,"justification":"Lente et modérée."}]$$::jsonb, $$Une lésion pyramidale évolue d’un flasque initial → spasticité secondaire.$$, 2, '2025-11-02T17:52:05.461Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0dduf00qbk498wehclx7u', 'cmhi0ddu500q3k498b9wzjfgd', $$Tonus musculaire$$, $$[{"text":"Hypertonie plastique.","isCorrect":false,"justification":"C’est extrapyramidal."},{"text":"Hypertonie spastique.","isCorrect":true},{"text":"Hypotonie prolongée.","isCorrect":false,"justification":"Transitoire au début."},{"text":"Toujours uniforme.","isCorrect":false,"justification":"Variable selon groupe musculaire."},{"text":"Non influencée par la vitesse.","isCorrect":false,"justification":"Dépend de la vitesse."}]$$::jsonb, $$L’hypertonie spastique, dépendante de la vitesse du mouvement, est typique du pyramidal.$$, 3, '2025-11-02T17:52:05.463Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0dduh00qdk498wiqda5o9', 'cmhi0ddu500q3k498b9wzjfgd', $$ROT (réflexes ostéo-tendineux)$$, $$[{"text":"Exagérés.","isCorrect":true},{"text":"Abolis.","isCorrect":false,"justification":"Neurogène périphérique."},{"text":"Normaux.","isCorrect":false,"justification":"Exagérés avec diffusion."},{"text":"Douloureux.","isCorrect":false,"justification":"Non douloureux."},{"text":"Absents au début.","isCorrect":true}]$$::jsonb, $$Les ROT sont abolis transitoirement puis exagérés secondairement.$$, 4, '2025-11-02T17:52:05.465Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0dduj00qfk4982h3vqrrr', 'cmhi0ddu500q3k498b9wzjfgd', $$Réflexe cutané plantaire$$, $$[{"text":"En flexion du gros orteil = normal.","isCorrect":true},{"text":"En extension = signe de Babinski.","isCorrect":true},{"text":"Disparaît dans l’HIC.","isCorrect":false,"justification":"Non spécifique."},{"text":"Toujours bilatéral.","isCorrect":false,"justification":"Peut être unilatéral."},{"text":"Spécifique du périphérique.","isCorrect":false,"justification":"Central."}]$$::jsonb, $$Babinski = extension du gros orteil, signe d’atteinte cortico-spinale.$$, 5, '2025-11-02T17:52:05.467Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddul00qhk4983f8mvu5g', 'cmhi0ddu500q3k498b9wzjfgd', $$Répartition du déficit$$, $$[{"text":"Déficit prédominant distal.","isCorrect":true},{"text":"Déficit proximal pur.","isCorrect":false,"justification":"Myogène."},{"text":"Distribution en territoire nerveux.","isCorrect":false,"justification":"Non systématisé."},{"text":"Topographie variable selon le siège lésionnel.","isCorrect":true},{"text":"Bilatérale d’emblée.","isCorrect":false,"justification":"Souvent unilatérale."}]$$::jsonb, $$Le déficit moteur pyramidal touche surtout les muscles distaux.$$, 6, '2025-11-02T17:52:05.469Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddun00qjk498gbqw9yvu', 'cmhi0ddu500q3k498b9wzjfgd', $$Paralysie faciale centrale$$, $$[{"text":"Intéresse la moitié inférieure du visage controlatérale.","isCorrect":true},{"text":"Respecte le front.","isCorrect":true},{"text":"Intéresse la moitié entière du visage.","isCorrect":false,"justification":"Périphérique."},{"text":"Toujours homolatérale.","isCorrect":false,"justification":"Controlatérale."},{"text":"Toujours associée à un trouble de la déglutition.","isCorrect":false,"justification":"Pas systématique."}]$$::jsonb, $$La paralysie faciale centrale respecte le front et touche l’hémiface inférieure opposée.$$, 7, '2025-11-02T17:52:05.471Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddup00qlk49848mdpimz', 'cmhi0ddu500q3k498b9wzjfgd', $$Signe de Babinski$$, $$[{"text":"Flexion du gros orteil.","isCorrect":false,"justification":"Normal."},{"text":"Extension lente du gros orteil.","isCorrect":true},{"text":"Réflexe cutané abdominal.","isCorrect":false,"justification":"Autre."},{"text":"Toujours bilatéral.","isCorrect":false,"justification":"Souvent unilatéral."},{"text":"Douloureux.","isCorrect":false,"justification":"Indolore."}]$$::jsonb, $$Le Babinski positif = extension lente du gros orteil à la stimulation plantaire.$$, 8, '2025-11-02T17:52:05.473Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddur00qnk4981acp68n5', 'cmhi0ddu500q3k498b9wzjfgd', $$Autres réflexes pathologiques$$, $$[{"text":"Hoffmann.","isCorrect":true},{"text":"Chaddock.","isCorrect":true},{"text":"Oppenheim.","isCorrect":true},{"text":"Romberg.","isCorrect":false,"justification":"Sensoriel."},{"text":"Mingazzini.","isCorrect":true}]$$::jsonb, $$Les réflexes pathologiques traduisent hyperexcitabilité des arcs réflexes dans l’atteinte centrale.$$, 9, '2025-11-02T17:52:05.475Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0dduu00qpk498ytbhfkik', 'cmhi0ddu500q3k498b9wzjfgd', $$Phase initiale du syndrome$$, $$[{"text":"Hypotonie et aréflexie.","isCorrect":true},{"text":"Hypertonie et ROT vifs.","isCorrect":false,"justification":"Phase tardive."},{"text":"Babinski présent d’emblée.","isCorrect":true},{"text":"Amyotrophie rapide.","isCorrect":false,"justification":"Lente."},{"text":"Réflexe cutané aboli.","isCorrect":true}]$$::jsonb, $$Phase initiale : flasque + Babinski + aréflexie → puis spasticité secondaire.$$, 10, '2025-11-02T17:52:05.478Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0dduw00qrk498tsqwdznp', 'cmhi0ddu500q3k498b9wzjfgd', $$Signes secondaires$$, $$[{"text":"Spasticité.","isCorrect":true},{"text":"Clonus.","isCorrect":true},{"text":"Réflexe pendulaire.","isCorrect":false,"justification":"Cérébelleux."},{"text":"Hyporéflexie.","isCorrect":false,"justification":"Hyperréflexie."},{"text":"Hypotonie prolongée.","isCorrect":false,"justification":"Transitoire."}]$$::jsonb, $$Signes secondaires = spasticité + clonus + hyperréflexie.$$, 11, '2025-11-02T17:52:05.480Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0dduy00qtk498zn40z2tx', 'cmhi0ddu500q3k498b9wzjfgd', $$Distribution lésionnelle$$, $$[{"text":"Cortex moteur → hémiparésie controlatérale.","isCorrect":true},{"text":"Capsule interne → hémiplégie massive.","isCorrect":true},{"text":"Tronc cérébral → alternée.","isCorrect":true},{"text":"Moelle cervicale → tétraparésie.","isCorrect":true},{"text":"Moelle lombaire → hémianesthésie.","isCorrect":false,"justification":"Motricité, pas sensibilité."}]$$::jsonb, $$La topographie dépend du niveau de la voie cortico-spinale.$$, 12, '2025-11-02T17:52:05.482Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddv000qvk498fi1juxrf', 'cmhi0ddu500q3k498b9wzjfgd', $$Hémiplégie capsulaire$$, $$[{"text":"Intense et massive.","isCorrect":true},{"text":"Atteint face, bras, jambe du même côté.","isCorrect":true},{"text":"Hypertonie spastique rapide.","isCorrect":true},{"text":"Rotation du membre inférieur en dehors.","isCorrect":true},{"text":"Babinski toujours négatif.","isCorrect":false,"justification":"Positif."}]$$::jsonb, $$L’hémiplégie capsulaire = massive, controlatérale, spastique.$$, 13, '2025-11-02T17:52:05.484Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddv200qxk4989c23j8kj', 'cmhi0ddu500q3k498b9wzjfgd', $$Syndrome médullaire pyramidal$$, $$[{"text":"Paraparésie spastique.","isCorrect":true},{"text":"ROT abolis.","isCorrect":false,"justification":"Vifs."},{"text":"Babinski bilatéral.","isCorrect":true},{"text":"Troubles sphinctériens associés.","isCorrect":true},{"text":"Sensibilité toujours conservée.","isCorrect":false,"justification":"Parfois atteinte associée."}]$$::jsonb, $$Atteinte médullaire → paraparésie spastique, ROT↑, Babinski bilatéral, sphinctériens.$$, 14, '2025-11-02T17:52:05.486Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddv400qzk4986kdybsbw', 'cmhi0ddu500q3k498b9wzjfgd', $$Clonus$$, $$[{"text":"Secousses rythmiques après étirement brusque.","isCorrect":true},{"text":"Disparaît à la contraction volontaire.","isCorrect":true},{"text":"Présent au niveau du pied et du genou.","isCorrect":true},{"text":"Témoin d’hyperexcitabilité motoneuronale.","isCorrect":true},{"text":"Spécifique du périphérique.","isCorrect":false,"justification":"Central."}]$$::jsonb, $$Le clonus est un signe d’hyperexcitabilité réflexe dans l’atteinte pyramidale.$$, 15, '2025-11-02T17:52:05.488Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddv600r1k498lvvxuprg', 'cmhi0ddu500q3k498b9wzjfgd', $$Topographie et latéralisation$$, $$[{"text":"Atteinte au-dessus de la décussation → déficit controlatéral.","isCorrect":true},{"text":"Sous la décussation → déficit homolatéral.","isCorrect":true},{"text":"Dans le tronc cérébral → signes alternés.","isCorrect":true},{"text":"Dans le cortex → déficit axial.","isCorrect":false,"justification":"Distal prédominant."},{"text":"Dans la moelle → déficit bilatéral systématique.","isCorrect":false,"justification":"Souvent latéralisé."}]$$::jsonb, $$La latéralisation dépend de la décussation pyramidale bulbaire.$$, 16, '2025-11-02T17:52:05.490Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddv800r3k49805hp9sxs', 'cmhi0ddu500q3k498b9wzjfgd', $$Différentiel$$, $$[{"text":"Pyramidal vs neurogène : ROT vifs vs abolis.","isCorrect":true},{"text":"Pyramidal vs myogène : déficit distal vs proximal.","isCorrect":true},{"text":"Pyramidal vs cérébelleux : spasticité vs ataxie.","isCorrect":true},{"text":"Pyramidal vs extrapyramidal : lenteur vs tremblement.","isCorrect":true},{"text":"Tous donnent Babinski.","isCorrect":false,"justification":"Uniquement pyramidal."}]$$::jsonb, $$La comparaison du tonus et des réflexes oriente toujours le diagnostic différentiel.$$, 17, '2025-11-02T17:52:05.493Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddva00r5k498rtnfhi7b', 'cmhi0ddu500q3k498b9wzjfgd', $$Causes fréquentes$$, $$[{"text":"AVC ischémique.","isCorrect":true},{"text":"Traumatisme crânien.","isCorrect":true},{"text":"Sclérose en plaques.","isCorrect":true},{"text":"Tumeur compressive.","isCorrect":true},{"text":"Myasthénie.","isCorrect":false,"justification":"Jonction neuromusculaire, pas pyramidal."}]$$::jsonb, $$Causes majeures : AVC, SEP, tumeur, traumatisme.$$, 18, '2025-11-02T17:52:05.495Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddvc00r7k498qqnvfptd', 'cmhi0ddu500q3k498b9wzjfgd', $$Rééducation et pronostic$$, $$[{"text":"Kinésithérapie précoce.","isCorrect":true},{"text":"Prévention de la spasticité.","isCorrect":true},{"text":"Rééducation de la marche.","isCorrect":true},{"text":"Récupération toujours complète.","isCorrect":false,"justification":"Parfois séquelles spastiques."},{"text":"Rééducation inutile.","isCorrect":false,"justification":"Indispensable."}]$$::jsonb, $$La récupération dépend de la cause et de la précocité de la rééducation.$$, 19, '2025-11-02T17:52:05.497Z');
INSERT INTO "Chapter" (id, "subjectId", title, description, "orderIndex", "pdfUrl", "createdAt") VALUES ('cmhi0ddvh00r9k4982h6wy7rq', 'cmhi0ddn900j1k4988obeo56w', $$Chapitre 8 — Syndromes déficitaires moteurs et sensitifs$$, NULL, 8, NULL, '2025-11-02T17:52:05.501Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddvj00rbk498runrf1c4', 'cmhi0ddvh00r9k4982h6wy7rq', $$Définition générale$$, $$[{"text":"Ensemble de symptômes traduisant une perte ou diminution de fonction neurologique.","isCorrect":true},{"text":"Toujours moteur.","isCorrect":false,"justification":"Peut être moteur, sensitif ou mixte."},{"text":"Résulte d’une hyperexcitabilité nerveuse.","isCorrect":false,"justification":"C’est une perte de fonction."},{"text":"S’accompagne d’une douleur constante.","isCorrect":false,"justification":"Pas obligatoire."},{"text":"Implique systématiquement le cervelet.","isCorrect":false,"justification":"Pas toujours."}]$$::jsonb, $$Le syndrome déficitaire correspond à une perte de fonction neurologique motrice et/ou sensitive.$$, 0, '2025-11-02T17:52:05.503Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddvk00rdk498ywl50mm0', 'cmhi0ddvh00r9k4982h6wy7rq', $$Syndrome moteur central$$, $$[{"text":"Hypertonie spastique.","isCorrect":true},{"text":"ROT abolis.","isCorrect":false,"justification":"Exagérés."},{"text":"Babinski présent.","isCorrect":true},{"text":"Amyotrophie rapide.","isCorrect":false,"justification":"Lente."},{"text":"Hypotonie prolongée.","isCorrect":false,"justification":"Flasque transitoire."}]$$::jsonb, $$Le syndrome moteur central = spasticité + ROT↑ + Babinski.$$, 1, '2025-11-02T17:52:05.505Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddvm00rfk498evxz75av', 'cmhi0ddvh00r9k4982h6wy7rq', $$Syndrome moteur périphérique$$, $$[{"text":"Hypotonie et aréflexie.","isCorrect":true},{"text":"Babinski présent.","isCorrect":false,"justification":"Absent."},{"text":"Amyotrophie rapide.","isCorrect":true},{"text":"ROT exagérés.","isCorrect":false,"justification":"Abolis."},{"text":"Spasticité secondaire.","isCorrect":false,"justification":"Pas de spasticité."}]$$::jsonb, $$Le moteur périphérique = hypotonie + aréflexie + amyotrophie précoce.$$, 2, '2025-11-02T17:52:05.506Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddvo00rhk498rv84iwzl', 'cmhi0ddvh00r9k4982h6wy7rq', $$Déficit moteur pur$$, $$[{"text":"Peut s’observer dans les lésions de la capsule interne.","isCorrect":true},{"text":"Associé à des troubles de la sensibilité.","isCorrect":false,"justification":"Pur moteur."},{"text":"Toujours bilatéral.","isCorrect":false,"justification":"Souvent unilatéral (hémiparésie)."},{"text":"Nécessite atteinte du cervelet.","isCorrect":false,"justification":"Voie motrice."},{"text":"Non systématisé.","isCorrect":true}]$$::jsonb, $$Le déficit moteur pur traduit une atteinte du faisceau cortico-spinal.$$, 3, '2025-11-02T17:52:05.508Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddvr00rjk498858ohiqf', 'cmhi0ddvh00r9k4982h6wy7rq', $$Déficit sensitif$$, $$[{"text":"Perte partielle ou totale de la sensibilité.","isCorrect":true},{"text":"Peut être superficielle, profonde ou mixte.","isCorrect":true},{"text":"Se traduit toujours par une hyperesthésie.","isCorrect":false,"justification":"Parfois hypo- ou anesthésie."},{"text":"Limitée aux territoires distaux.","isCorrect":false,"justification":"Variable."},{"text":"Indépendante des voies sensitives.","isCorrect":false,"justification":"Directement liée à leur lésion."}]$$::jsonb, $$Le déficit sensitif = atteinte des voies superficielles et/ou profondes.$$, 4, '2025-11-02T17:52:05.511Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddvs00rlk498jeqip0qs', 'cmhi0ddvh00r9k4982h6wy7rq', $$Types de sensibilité$$, $$[{"text":"Superficielle : douleur, température, toucher.","isCorrect":true},{"text":"Profonde : position, vibration.","isCorrect":true},{"text":"Interne : viscérale.","isCorrect":true},{"text":"Haptique : audition.","isCorrect":false,"justification":"Sensoriel auditif."},{"text":"Corticale : discrimination fine.","isCorrect":true}]$$::jsonb, $$Sensibilité = superficielle, profonde, viscérale, et intégrative (corticale).$$, 5, '2025-11-02T17:52:05.513Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddvu00rnk498xnv96y5j', 'cmhi0ddvh00r9k4982h6wy7rq', $$Syndrome sensitif périphérique$$, $$[{"text":"Distribution en « gants et chaussettes ».","isCorrect":true},{"text":"Atteinte unilatérale systématisée.","isCorrect":false,"justification":"Bilatérale symétrique."},{"text":"Aréflexie associée.","isCorrect":true},{"text":"Hypertonie spastique.","isCorrect":false,"justification":"Flasque."},{"text":"Babinski positif.","isCorrect":false,"justification":"Négatif."}]$$::jsonb, $$Sensitif périphérique = symétrique distal + aréflexie.$$, 6, '2025-11-02T17:52:05.514Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddvw00rpk4981qfkl1lt', 'cmhi0ddvh00r9k4982h6wy7rq', $$Syndrome sensitif central$$, $$[{"text":"Atteinte d’un hémicorps.","isCorrect":true},{"text":"Topographie radiculaire.","isCorrect":false,"justification":"Périphérique."},{"text":"Douleurs thalamiques possibles.","isCorrect":true},{"text":"Hypoesthésie croisée.","isCorrect":true},{"text":"ROT abolis.","isCorrect":false,"justification":"Normaux ou vifs."}]$$::jsonb, $$Sensitif central = hémihypoesthésie controlatérale, parfois douleur thalamique.$$, 7, '2025-11-02T17:52:05.516Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddvx00rrk49862y190gt', 'cmhi0ddvh00r9k4982h6wy7rq', $$Syndrome thalamique de Dejerine-Roussy$$, $$[{"text":"Hémianesthésie controlatérale.","isCorrect":true},{"text":"Douleurs intenses, brûlantes.","isCorrect":true},{"text":"ROT abolis.","isCorrect":false,"justification":"Conservés."},{"text":"Hypotonie permanente.","isCorrect":false,"justification":"Spasticité secondaire."},{"text":"Ataxie motrice.","isCorrect":false,"justification":"Non motrice."}]$$::jsonb, $$Thalamique = anesthésie + douleurs paradoxales controlatérales.$$, 8, '2025-11-02T17:52:05.518Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddvz00rtk498zfb7i6w0', 'cmhi0ddvh00r9k4982h6wy7rq', $$Syndrome de Brown-Séquard$$, $$[{"text":"Hémisection médullaire.","isCorrect":true},{"text":"Atteinte motrice homolatérale.","isCorrect":true},{"text":"Perte thermoalgique controlatérale.","isCorrect":true},{"text":"Perte proprioceptive controlatérale.","isCorrect":false,"justification":"Homolatérale."},{"text":"Babinski bilatéral.","isCorrect":false,"justification":"Unilatéral."}]$$::jsonb, $$Brown-Séquard = motricité et sensibilité dissociées croisées.$$, 9, '2025-11-02T17:52:05.519Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddw000rvk498cuxv96hb', 'cmhi0ddvh00r9k4982h6wy7rq', $$Déficit sensitif cortical$$, $$[{"text":"Atteinte pariétale controlatérale.","isCorrect":true},{"text":"Astéréognosie.","isCorrect":true},{"text":"Sensibilité élémentaire abolie.","isCorrect":false,"justification":"Conservée."},{"text":"Douleur intense permanente.","isCorrect":false,"justification":"Rare."},{"text":"Hypotonie.","isCorrect":false,"justification":"Pyramidal, pas sensitif."}]$$::jsonb, $$Cortical pariétal → déficit de reconnaissance sans perte élémentaire.$$, 10, '2025-11-02T17:52:05.521Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddw200rxk498ie09h2k1', 'cmhi0ddvh00r9k4982h6wy7rq', $$Atteinte radiculaire$$, $$[{"text":"Douleur en trajet radiculaire.","isCorrect":true},{"text":"Topographie en bande (dermatome).","isCorrect":true},{"text":"ROT abolis au niveau du segment atteint.","isCorrect":true},{"text":"Bilatérale symétrique.","isCorrect":false,"justification":"Unilatérale."},{"text":"Babinski positif.","isCorrect":false,"justification":"Absent."}]$$::jsonb, $$Radiculaire = douleur, hypoesthésie, aréflexie segmentaire.$$, 11, '2025-11-02T17:52:05.522Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddw300rzk4983n4nz09q', 'cmhi0ddvh00r9k4982h6wy7rq', $$Polyneuropathies sensitives$$, $$[{"text":"Atteinte distale bilatérale.","isCorrect":true},{"text":"Aréflexie distale.","isCorrect":true},{"text":"Douleurs en chaussettes.","isCorrect":true},{"text":"Hypotonie centrale.","isCorrect":false,"justification":"Distale périphérique."},{"text":"Atteinte toujours aiguë.","isCorrect":false,"justification":"Souvent chronique."}]$$::jsonb, $$Polyneuropathies = distales, bilatérales, symétriques, douloureuses.$$, 12, '2025-11-02T17:52:05.524Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddw500s1k49833mba22s', 'cmhi0ddvh00r9k4982h6wy7rq', $$Déficit moteur et sensitif mixte$$, $$[{"text":"Possible dans la moelle épinière.","isCorrect":true},{"text":"S’observe dans les lésions médullaires compressives.","isCorrect":true},{"text":"Toujours périphérique.","isCorrect":false,"justification":"Souvent central."},{"text":"Jamais associé à des troubles sphinctériens.","isCorrect":false,"justification":"Souvent présents."},{"text":"Limitée aux membres supérieurs.","isCorrect":false,"justification":"Variable."}]$$::jsonb, $$Mixte = atteinte médullaire souvent avec troubles sphinctériens.$$, 13, '2025-11-02T17:52:05.525Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddw800s3k498kl99znma', 'cmhi0ddvh00r9k4982h6wy7rq', $$Troubles de la sensibilité profonde$$, $$[{"text":"Perte de la proprioception.","isCorrect":true},{"text":"Romberg positif.","isCorrect":true},{"text":"Dysmétrie aggravée yeux fermés.","isCorrect":true},{"text":"Tremblement intentionnel.","isCorrect":false,"justification":"Cérébelleux."},{"text":"Sensibilité douloureuse abolie.","isCorrect":false,"justification":"Conservée."}]$$::jsonb, $$Sensibilité profonde atteinte → ataxie sensorielle avec Romberg +.$$, 14, '2025-11-02T17:52:05.528Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddw900s5k498ycfpa66w', 'cmhi0ddvh00r9k4982h6wy7rq', $$Troubles de la sensibilité superficielle$$, $$[{"text":"Hypoesthésie thermo-algique.","isCorrect":true},{"text":"Respect de la sensibilité vibratoire.","isCorrect":true},{"text":"Lésion du faisceau spinothalamique.","isCorrect":true},{"text":"Romberg positif.","isCorrect":false,"justification":"Proprioception."},{"text":"Babinski présent.","isCorrect":false,"justification":"Central moteur."}]$$::jsonb, $$Sensibilité superficielle = douleur/température, faisceau spinothalamique.$$, 15, '2025-11-02T17:52:05.530Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddwb00s7k498q4n28b0m', 'cmhi0ddvh00r9k4982h6wy7rq', $$Causes centrales de déficit mixte$$, $$[{"text":"AVC.","isCorrect":true},{"text":"Sclérose en plaques.","isCorrect":true},{"text":"Lésions médullaires traumatiques.","isCorrect":true},{"text":"Myasthénie.","isCorrect":false,"justification":"Jonction, pas centrale."},{"text":"Polyradiculonévrite.","isCorrect":false,"justification":"Périphérique."}]$$::jsonb, $$Les atteintes centrales combinent souvent moteur + sensitif.$$, 16, '2025-11-02T17:52:05.531Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddwd00s9k4982rxka8l1', 'cmhi0ddvh00r9k4982h6wy7rq', $$Causes périphériques$$, $$[{"text":"Diabète.","isCorrect":true},{"text":"Carence en vitamine B₁₂.","isCorrect":true},{"text":"Alcoolisme.","isCorrect":true},{"text":"SEP.","isCorrect":false,"justification":"Centrale."},{"text":"AVC.","isCorrect":false,"justification":"Central."}]$$::jsonb, $$Les causes périphériques = diabète, alcool, carences.$$, 17, '2025-11-02T17:52:05.533Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddwe00sbk498rwvwg3wk', 'cmhi0ddvh00r9k4982h6wy7rq', $$Examens complémentaires$$, $$[{"text":"EMG pour atteinte périphérique.","isCorrect":true},{"text":"IRM cérébrale et médullaire pour atteinte centrale.","isCorrect":true},{"text":"Ponction lombaire pour neuropathie inflammatoire.","isCorrect":true},{"text":"Scanner thoracique systématique.","isCorrect":false,"justification":"Non spécifique."},{"text":"EEG.","isCorrect":false,"justification":"Crises, pas déficits."}]$$::jsonb, $$Les explorations ciblent le niveau lésionnel (EMG ↔ périph., IRM ↔ central).$$, 18, '2025-11-02T17:52:05.534Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddwg00sdk498tcx512ci', 'cmhi0ddvh00r9k4982h6wy7rq', $$Évolution et pronostic$$, $$[{"text":"Dépendent de la cause.","isCorrect":true},{"text":"Peuvent régresser totalement.","isCorrect":true},{"text":"Sont toujours irréversibles.","isCorrect":false,"justification":"Certaines curables."},{"text":"N’impliquent jamais la rééducation.","isCorrect":false,"justification":"Essentielle."},{"text":"La plasticité neuronale favorise la récupération.","isCorrect":true}]$$::jsonb, $$Le pronostic dépend de l’étiologie et de la plasticité neuronale, d’où l’importance de la rééducation.$$, 19, '2025-11-02T17:52:05.536Z');
INSERT INTO "Chapter" (id, "subjectId", title, description, "orderIndex", "pdfUrl", "createdAt") VALUES ('cmhi0ddwi00sfk4987sgge76b', 'cmhi0ddn900j1k4988obeo56w', $$Chapitre 9 – Syndromes neuropsychiatriques$$, NULL, 9, NULL, '2025-11-02T17:52:05.539Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddwk00shk498235mlphu', 'cmhi0ddwi00sfk4987sgge76b', $$Définition$$, $$[{"text":"Ensemble de troubles psychiques d’origine cérébrale organique.","isCorrect":true},{"text":"Ne concernent que les affections psychiatriques pures.","isCorrect":false,"justification":"Ils sont neurologiques avec retentissement mental."},{"text":"Englobent confusion, démence, syndrome frontal.","isCorrect":true},{"text":"Relèvent toujours d’une cause psychogène.","isCorrect":false,"justification":"Cause organique."},{"text":"N’entraînent jamais de troubles cognitifs.","isCorrect":false,"justification":"Fréquents."}]$$::jsonb, $$Les syndromes neuropsychiatriques traduisent une atteinte cérébrale affectant le comportement et la cognition.$$, 0, '2025-11-02T17:52:05.540Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddwm00sjk498wbd7f0gf', 'cmhi0ddwi00sfk4987sgge76b', $$Syndrome confusionnel$$, $$[{"text":"Trouble aigu de la conscience et de l’attention.","isCorrect":true},{"text":"Début brutal et réversible.","isCorrect":true},{"text":"Début insidieux et chronique.","isCorrect":false,"justification":"C’est la démence."},{"text":"Hallucinations rares.","isCorrect":false,"justification":"Fréquentes, visuelles."},{"text":"Orientation spatio-temporelle conservée.","isCorrect":false,"justification":"Altérée."}]$$::jsonb, $$La confusion = état aigu, réversible, avec désorientation et troubles attentionnels.$$, 1, '2025-11-02T17:52:05.543Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddwp00slk4980qzv8ger', 'cmhi0ddwi00sfk4987sgge76b', $$Causes du syndrome confusionnel$$, $$[{"text":"Encéphalopathie métabolique.","isCorrect":true},{"text":"Infection (méningo-encéphalite, sepsis).","isCorrect":true},{"text":"Traumatisme crânien.","isCorrect":true},{"text":"Crise d’épilepsie.","isCorrect":true},{"text":"Dépression majeure.","isCorrect":false,"justification":"Psychique, pas confusionnelle."}]$$::jsonb, $$Les causes de confusion sont organiques, toxiques, métaboliques ou infectieuses.$$, 2, '2025-11-02T17:52:05.545Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddwr00snk498axy1o1fy', 'cmhi0ddwi00sfk4987sgge76b', $$Syndrome frontal$$, $$[{"text":"Troubles du comportement et de l’initiative.","isCorrect":true},{"text":"Réduction des inhibitions sociales.","isCorrect":true},{"text":"Apathie ou désinhibition.","isCorrect":true},{"text":"Atteinte du cortex pariétal.","isCorrect":false,"justification":"Frontal."},{"text":"Absence de trouble intellectuel.","isCorrect":false,"justification":"Perturbation du raisonnement."}]$$::jsonb, $$Le syndrome frontal = troubles du comportement, du jugement et de l’initiative.$$, 3, '2025-11-02T17:52:05.547Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddwt00spk498orh2k8j1', 'cmhi0ddwi00sfk4987sgge76b', $$Signes cliniques du syndrome frontal$$, $$[{"text":"Familiarité excessive.","isCorrect":true},{"text":"Vulgarité verbale.","isCorrect":true},{"text":"Comportement puéril.","isCorrect":true},{"text":"Apraxie idéomotrice.","isCorrect":false,"justification":"Pariétal."},{"text":"Hallucinations auditives.","isCorrect":false,"justification":"Psychotique."}]$$::jsonb, $$Frontal = désinhibition, conduite sociale altérée, perte du contrôle critique.$$, 4, '2025-11-02T17:52:05.549Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddwu00srk498jods5ezn', 'cmhi0ddwi00sfk4987sgge76b', $$Syndrome démentiel$$, $$[{"text":"Trouble chronique et progressif des fonctions cognitives.","isCorrect":true},{"text":"Désorientation dans le temps et l’espace.","isCorrect":true},{"text":"Hallucinations visuelles constantes.","isCorrect":false,"justification":"Accessoires."},{"text":"Mémoire préservée.","isCorrect":false,"justification":"Altérée."},{"text":"Réversible avec traitement.","isCorrect":false,"justification":"Irréversible le plus souvent."}]$$::jsonb, $$La démence = trouble global, chronique, irréversible des fonctions supérieures.$$, 5, '2025-11-02T17:52:05.551Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddww00stk498l1u5lmz5', 'cmhi0ddwi00sfk4987sgge76b', $$Distinguer démence et confusion$$, $$[{"text":"Confusion = aiguë, démence = chronique.","isCorrect":true},{"text":"Confusion = réversible, démence = irréversible.","isCorrect":true},{"text":"Démence = hallucinations visuelles.","isCorrect":false,"justification":"Possible mais non spécifique."},{"text":"Confusion = mémoire intacte.","isCorrect":false,"justification":"Altérée par inattention."},{"text":"Démence = conscience fluctuante.","isCorrect":false,"justification":"Stable."}]$$::jsonb, $$Confusion = aiguë, fluctuante, Démence = chronique, stable.$$, 6, '2025-11-02T17:52:05.552Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddwy00svk498go8bevhz', 'cmhi0ddwi00sfk4987sgge76b', $$Syndrome de Korsakoff$$, $$[{"text":"Amnésie antérograde sévère.","isCorrect":true},{"text":"Contexte d’éthylisme chronique.","isCorrect":true},{"text":"Hallucinations auditives.","isCorrect":false,"justification":"Non."},{"text":"Confabulations (fausses histoires).","isCorrect":true},{"text":"Atteinte du cervelet.","isCorrect":false,"justification":"Corps mamillaires et thalamus."}]$$::jsonb, $$Korsakoff = amnésie antérograde + confabulations, sur carence en B₁ (alcoolisme).$$, 7, '2025-11-02T17:52:05.554Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddwz00sxk498sgtspssv', 'cmhi0ddwi00sfk4987sgge76b', $$Syndrome de Wernicke$$, $$[{"text":"Triade : ophtalmoplégie, ataxie, confusion.","isCorrect":true},{"text":"Due à une carence en vitamine B₁.","isCorrect":true},{"text":"Toujours réversible.","isCorrect":false,"justification":"Peut évoluer vers Korsakoff."},{"text":"Survient après sevrage prolongé.","isCorrect":false,"justification":"Pendant alcoolisme actif."},{"text":"Touche les lobes frontaux.","isCorrect":false,"justification":"Tronc et diencéphale."}]$$::jsonb, $$Wernicke = urgence carentielle, à traiter par vitamine B₁ IV immédiate.$$, 8, '2025-11-02T17:52:05.556Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddx100szk498a66br0hx', 'cmhi0ddwi00sfk4987sgge76b', $$Syndrome dépressif pseudo-démentiel$$, $$[{"text":"Démence due à la dépression.","isCorrect":true},{"text":"Réversible sous antidépresseurs.","isCorrect":true},{"text":"Confusion aiguë.","isCorrect":false,"justification":"Subaiguë."},{"text":"Amnésie sélective.","isCorrect":true},{"text":"Hypotonie faciale et lenteur psychomotrice.","isCorrect":true}]$$::jsonb, $$Pseudo-démence = dépression masquée imitant une démence.$$, 9, '2025-11-02T17:52:05.558Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddx400t1k498dz88ys50', 'cmhi0ddwi00sfk4987sgge76b', $$Syndrome de lobe temporal$$, $$[{"text":"Hallucinations olfactives.","isCorrect":true},{"text":"Crises partielles complexes.","isCorrect":true},{"text":"Troubles de la mémoire épisodique.","isCorrect":true},{"text":"Apraxie idéatoire.","isCorrect":false,"justification":"Pariétal."},{"text":"Hémianopsie bilatérale.","isCorrect":false,"justification":"Controlatérale."}]$$::jsonb, $$Temporal = hallucinations olfactives, troubles mnésiques, crises épileptiques.$$, 10, '2025-11-02T17:52:05.560Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddx700t3k498awr807h6', 'cmhi0ddwi00sfk4987sgge76b', $$Syndrome de lobe pariétal$$, $$[{"text":"Astéréognosie.","isCorrect":true},{"text":"Héminégligence controlatérale.","isCorrect":true},{"text":"Agraphie, acalculie (syndrome de Gerstmann).","isCorrect":true},{"text":"Aphasie de Wernicke.","isCorrect":false,"justification":"Temporal."},{"text":"Apraxie idéomotrice.","isCorrect":true}]$$::jsonb, $$Pariétal = troubles de reconnaissance, négligence, apraxies.$$, 11, '2025-11-02T17:52:05.563Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddx900t5k498jtfhy9yc', 'cmhi0ddwi00sfk4987sgge76b', $$Syndrome de lobe occipital$$, $$[{"text":"Cécité corticale.","isCorrect":true},{"text":"Hallucinations visuelles.","isCorrect":true},{"text":"Hémianopsie latérale homonyme.","isCorrect":true},{"text":"Agnosie visuelle.","isCorrect":true},{"text":"Aphasie motrice.","isCorrect":false,"justification":"Frontal."}]$$::jsonb, $$Occipital = troubles visuels purs ou intégratifs.$$, 12, '2025-11-02T17:52:05.565Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddxb00t7k4984q6iq60k', 'cmhi0ddwi00sfk4987sgge76b', $$Troubles du comportement$$, $$[{"text":"Désinhibition sociale.","isCorrect":true},{"text":"Agressivité ou apathie.","isCorrect":true},{"text":"Mutisme akinétique (lésions frontales médiales).","isCorrect":true},{"text":"Troubles sensitifs profonds.","isCorrect":false,"justification":"Affectif, pas sensitif."},{"text":"Hyperréflexie.","isCorrect":false,"justification":"Pyramidal."}]$$::jsonb, $$Les lésions frontales perturbent le comportement social et la motivation.$$, 13, '2025-11-02T17:52:05.567Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddxe00t9k498q0otp2wt', 'cmhi0ddwi00sfk4987sgge76b', $$Troubles de la mémoire$$, $$[{"text":"Amnésie antérograde : oubli des faits récents.","isCorrect":true},{"text":"Amnésie rétrograde : oubli des faits anciens.","isCorrect":true},{"text":"Amnésie psychogène : réversible.","isCorrect":true},{"text":"Toujours localisée au cortex moteur.","isCorrect":false,"justification":"Temporal et hippocampe."},{"text":"Toujours irréversible.","isCorrect":false,"justification":"Souvent partielle."}]$$::jsonb, $$Types d’amnésies : antérograde, rétrograde, psychogène selon la topographie.$$, 14, '2025-11-02T17:52:05.570Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddxg00tbk49883lo6fcb', 'cmhi0ddwi00sfk4987sgge76b', $$Syndrome démentiel de type Alzheimer$$, $$[{"text":"Début insidieux et progressif.","isCorrect":true},{"text":"Troubles de mémoire antérograde.","isCorrect":true},{"text":"Troubles du langage (aphasie).","isCorrect":true},{"text":"Réversibilité complète.","isCorrect":false,"justification":"Irréversible."},{"text":"Hallucinations olfactives.","isCorrect":false,"justification":"Temporal secondaire."}]$$::jsonb, $$Alzheimer = démence progressive, amnésie antérograde, aphasie, désorientation.$$, 15, '2025-11-02T17:52:05.572Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddxi00tdk498be71gcbz', 'cmhi0ddwi00sfk4987sgge76b', $$Syndrome de Pick (frontal temporal)$$, $$[{"text":"Début précoce (50–60 ans).","isCorrect":true},{"text":"Troubles du comportement initiaux.","isCorrect":true},{"text":"Démence tardive après 80 ans.","isCorrect":false,"justification":"Précoce."},{"text":"Troubles mnésiques isolés.","isCorrect":false,"justification":"Comportementaux."},{"text":"Lésions occipitales diffuses.","isCorrect":false,"justification":"Frontales."}]$$::jsonb, $$Maladie de Pick = démence frontale précoce, désinhibition et perte d’empathie.$$, 16, '2025-11-02T17:52:05.574Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddxk00tfk4982361rq3n', 'cmhi0ddwi00sfk4987sgge76b', $$Démence vasculaire$$, $$[{"text":"Installation en paliers.","isCorrect":true},{"text":"Antécédents d’AVC multiples.","isCorrect":true},{"text":"Confusion aiguë réversible.","isCorrect":false,"justification":"Chronique."},{"text":"Troubles moteurs associés.","isCorrect":true},{"text":"Hallucinations auditives.","isCorrect":false,"justification":"Non typiques."}]$$::jsonb, $$Démence vasculaire = paliers évolutifs, AVC multiples, signes moteurs associés.$$, 17, '2025-11-02T17:52:05.577Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddxm00thk49839g0nif0', 'cmhi0ddwi00sfk4987sgge76b', $$Étiologies infectieuses$$, $$[{"text":"VIH.","isCorrect":true},{"text":"Encéphalite herpétique.","isCorrect":true},{"text":"Neurosyphilis.","isCorrect":true},{"text":"SEP.","isCorrect":false,"justification":"Inflammatoire non infectieuse."},{"text":"Méningite bactérienne.","isCorrect":true}]$$::jsonb, $$Les infections chroniques ou aiguës peuvent causer des syndromes neuropsychiatriques.$$, 18, '2025-11-02T17:52:05.579Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddxo00tjk498c39jjpq8', 'cmhi0ddwi00sfk4987sgge76b', $$Prise en charge générale$$, $$[{"text":"Traiter la cause organique.","isCorrect":true},{"text":"Soutien psychologique.","isCorrect":true},{"text":"Rééducation cognitive.","isCorrect":true},{"text":"Isolement systématique.","isCorrect":false,"justification":"Contre-indiqué."},{"text":"Multidisciplinarité essentielle.","isCorrect":true}]$$::jsonb, $$La prise en charge doit être étiologique, psychologique et rééducative, dans une approche pluridisciplinaire.$$, 19, '2025-11-02T17:52:05.580Z');
INSERT INTO "Subject" (id, title, description, semester, tags, "totalQCM", "createdAt", views) VALUES ('cmhi0ddxt00tkk498snuvbsi9', $$Sémiologie Pédiatrique$$, $$Cours de Sémiologie Pédiatrique$$, 'DCEM1', ARRAY[$$DCEM1$$, $$Sémiologie$$, $$Sémiologie Pédiatrique$$]::text[], 95, '2025-11-02T17:52:05.585Z', 0);
INSERT INTO "Chapter" (id, "subjectId", title, description, "orderIndex", "pdfUrl", "createdAt") VALUES ('cmhi0ddxx00tmk498as23gopf', 'cmhi0ddxt00tkk498snuvbsi9', $$Chapitre 1 – Principes généraux de la consultation pédiatrique$$, NULL, 1, NULL, '2025-11-02T17:52:05.589Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddxy00tok498lg5p5zls', 'cmhi0ddxx00tmk498as23gopf', $$Accueil de l’enfant$$, $$[{"text":"L’enfant doit être interrogé avant les parents.","isCorrect":false,"justification":"L’ordre dépend de l’âge et de la situation."},{"text":"La mise en confiance est essentielle pour la qualité de l’examen.","isCorrect":true},{"text":"Les pleurs doivent être interrompus par contrainte.","isCorrect":false,"justification":"Il faut attendre le calme."},{"text":"La présence du parent sécurise l’enfant.","isCorrect":true},{"text":"L’enfant doit être examiné sans prévenir.","isCorrect":false,"justification":"Préparer le geste évite la peur."}]$$::jsonb, $$Une consultation pédiatrique efficace repose sur calme, confiance et accompagnement parental.$$, 0, '2025-11-02T17:52:05.590Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddy000tqk498pwldpdrm', 'cmhi0ddxx00tmk498as23gopf', $$Anamnèse pédiatrique$$, $$[{"text":"Les antécédents familiaux sont secondaires.","isCorrect":false,"justification":"Ils sont majeurs (génétiques, infectieux)."},{"text":"Les habitudes alimentaires doivent être précisées.","isCorrect":true},{"text":"Les vaccinations ne sont pas pertinentes.","isCorrect":false,"justification":"Elles font partie de l’anamnèse."},{"text":"Le développement psychomoteur doit être exploré.","isCorrect":true},{"text":"Le contexte social n’a aucun intérêt.","isCorrect":false,"justification":"Facteur important de santé infantile."}]$$::jsonb, $$L’anamnèse pédiatrique est globale, intégrant milieu, croissance et prévention.$$, 1, '2025-11-02T17:52:05.592Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddy300tsk498ntoej6y1', 'cmhi0ddxx00tmk498as23gopf', $$Conduite de l’entretien$$, $$[{"text":"Le langage médical doit être privilégié avec les parents.","isCorrect":false,"justification":"Utiliser un langage simple."},{"text":"Le ton doit être rassurant et neutre.","isCorrect":true},{"text":"L’entretien peut se limiter à un seul parent.","isCorrect":false,"justification":"Les deux si possible."},{"text":"L’écoute active améliore la coopération.","isCorrect":true},{"text":"Interrompre souvent permet de cadrer l’entretien.","isCorrect":false,"justification":"Favoriser l’expression."}]$$::jsonb, $$Une bonne communication = écoute + clarté + empathie.$$, 2, '2025-11-02T17:52:05.595Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddy500tuk498n577z8na', 'cmhi0ddxx00tmk498as23gopf', $$Observation de l’enfant$$, $$[{"text":"L’observation précède l’examen.","isCorrect":true},{"text":"L’attitude, la démarche et les mimiques sont à noter.","isCorrect":true},{"text":"La position de l’enfant dans les bras n’apporte rien.","isCorrect":false,"justification":"Très parlante chez le nourrisson."},{"text":"Le pleur n’a aucune valeur sémiologique.","isCorrect":false,"justification":"Peut indiquer douleur ou détresse."},{"text":"Les interactions avec les parents sont à évaluer.","isCorrect":true}]$$::jsonb, $$L’observation fournit des indices diagnostiques avant tout contact.$$, 3, '2025-11-02T17:52:05.597Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddy700twk498md09p6sm', 'cmhi0ddxx00tmk498as23gopf', $$Comportement et relation$$, $$[{"text":"L’enfant doit rester passif.","isCorrect":false,"justification":"Participation souhaitable."},{"text":"Le jeu facilite l’examen.","isCorrect":true},{"text":"L’examinateur doit garder une attitude ludique.","isCorrect":true},{"text":"Les pleurs doivent interrompre l’examen.","isCorrect":false,"justification":"S’adapter, pas forcer."},{"text":"Les encouragements renforcent la coopération.","isCorrect":true}]$$::jsonb, $$Le jeu et la bienveillance rendent l’examen moins angoissant et plus fiable.$$, 4, '2025-11-02T17:52:05.599Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddy900tyk4988fnyge3j', 'cmhi0ddxx00tmk498as23gopf', $$Conditions matérielles$$, $$[{"text":"L’éclairage doit être tamisé.","isCorrect":false,"justification":"Bonne lumière nécessaire."},{"text":"La température ambiante doit être confortable.","isCorrect":true},{"text":"Le matériel doit être prêt avant l’enfant.","isCorrect":true},{"text":"Les instruments froids rassurent.","isCorrect":false,"justification":"Les réchauffer au besoin."},{"text":"L’intimité est inutile.","isCorrect":false,"justification":"Respect indispensable."}]$$::jsonb, $$Une bonne préparation de la salle favorise la confiance et la précision de l’examen.$$, 5, '2025-11-02T17:52:05.601Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddyb00u0k498dlsg2rle', 'cmhi0ddxx00tmk498as23gopf', $$Vaccinations$$, $$[{"text":"Leur vérification n’entre pas dans la consultation.","isCorrect":false,"justification":"Toujours à contrôler."},{"text":"Le carnet vaccinal doit être demandé systématiquement.","isCorrect":true},{"text":"Les vaccinations protègent aussi la collectivité.","isCorrect":true},{"text":"Les rappels ne concernent que le nourrisson.","isCorrect":false,"justification":"Jusqu’à l’adolescence."},{"text":"Les contre-indications sont rares mais à connaitre.","isCorrect":true}]$$::jsonb, $$La consultation pédiatrique intègre le contrôle vaccinal et la prévention.$$, 6, '2025-11-02T17:52:05.603Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddyc00u2k4987eg47kvu', 'cmhi0ddxx00tmk498as23gopf', $$Développement psychomoteur$$, $$[{"text":"Les acquisitions sont similaires chez tous les enfants.","isCorrect":false,"justification":"Grande variabilité."},{"text":"La tenue de tête vers 3 mois est un repère.","isCorrect":true},{"text":"La station assise autonome vers 6–8 mois.","isCorrect":true},{"text":"La marche vers 24 mois chez tous.","isCorrect":false,"justification":"10–18 mois variable."},{"text":"Le langage vers 2 ans doit être complet.","isCorrect":false,"justification":"Début progressif."}]$$::jsonb, $$Le développement psychomoteur = repères dynamiques, pas rigides.$$, 7, '2025-11-02T17:52:05.605Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddye00u4k4982jw93oz7', 'cmhi0ddxx00tmk498as23gopf', $$Croissance$$, $$[{"text":"Le poids double à 6 mois.","isCorrect":true},{"text":"Le poids triple à 1 an.","isCorrect":true},{"text":"La taille augmente de 50 cm par an au cours de la 1ʳᵉ année.","isCorrect":false,"justification":"≈ 25 cm."},{"text":"Le périmètre crânien croît de 20 cm par an.","isCorrect":false,"justification":"≈ 12 cm la 1ʳᵉ année."},{"text":"La courbe de croissance se fait en cm/mois jusqu’à 2 ans.","isCorrect":true}]$$::jsonb, $$Croissance rapide 1ʳᵉ année → suivi pondéral, taille et PC réguliers.$$, 8, '2025-11-02T17:52:05.607Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddyh00u6k498hxlgi497', 'cmhi0ddxx00tmk498as23gopf', $$Anamnèse alimentaire$$, $$[{"text":"L’alimentation de l’enfant n’a pas d’impact sur sa croissance.","isCorrect":false,"justification":"Impact majeur."},{"text":"Le type de lait doit être identifié.","isCorrect":true},{"text":"Les troubles digestifs (nausées, diarrhée) sont à rechercher.","isCorrect":true},{"text":"Les carences en fer sont rares chez le nourrisson.","isCorrect":false,"justification":"Fréquentes."},{"text":"L’introduction des solides avant 4 mois est recommandée.","isCorrect":false,"justification":"À partir de 4–6 mois."}]$$::jsonb, $$L’anamnèse alimentaire évalue qualité et rythme nutritionnels.$$, 9, '2025-11-02T17:52:05.609Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddyj00u8k498rku8t3y5', 'cmhi0ddxx00tmk498as23gopf', $$Contexte familial$$, $$[{"text":"Les conditions de vie sont sans intérêt clinique.","isCorrect":false,"justification":"Elles influencent santé et développement."},{"text":"Le rang de naissance peut avoir un impact.","isCorrect":true},{"text":"Les maladies familiales héréditaires sont à rechercher.","isCorrect":true},{"text":"Le niveau d’éducation parental n’a aucune influence.","isCorrect":false,"justification":"Impact sur la prévention."},{"text":"Les antécédents de maltraitance doivent être écartés.","isCorrect":true}]$$::jsonb, $$L’environnement familial est un facteur déterminant de la santé enfantine.$$, 10, '2025-11-02T17:52:05.612Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddym00uak498foyyh76k', 'cmhi0ddxx00tmk498as23gopf', $$Communication avec les parents$$, $$[{"text":"L’entretien doit être directif.","isCorrect":false,"justification":"Ouvert et adapté."},{"text":"Expliquer chaque geste avant de le faire.","isCorrect":true},{"text":"Les parents sont impliqués dans l’examen.","isCorrect":true},{"text":"Les remarques critiques sont souhaitables.","isCorrect":false,"justification":"Empathie nécessaire."},{"text":"Les questions parentales doivent être limitées.","isCorrect":false,"justification":"Au contraire, encouragées."}]$$::jsonb, $$La communication parent-médecin = explications + empathie + partenariat.$$, 11, '2025-11-02T17:52:05.614Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddyo00uck498dnqaw2hk', 'cmhi0ddxx00tmk498as23gopf', $$Douleur de l’enfant$$, $$[{"text":"L’enfant ne ressent pas la douleur comme l’adulte.","isCorrect":false,"justification":"Il la ressent dès la naissance."},{"text":"L’échelle de douleur doit être adaptée à l’âge.","isCorrect":true},{"text":"Le refus de s’alimenter peut être un signe de douleur.","isCorrect":true},{"text":"Les pleurs sont toujours psychogènes.","isCorrect":false,"justification":"Souvent réflexes."},{"text":"Le traitement antalgique n’est jamais urgent.","isCorrect":false,"justification":"Toujours prioritaire."}]$$::jsonb, $$L’évaluation et le soulagement de la douleur sont fondamentaux en pédiatrie.$$, 12, '2025-11-02T17:52:05.616Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddyr00uek498bzce1kpb', 'cmhi0ddxx00tmk498as23gopf', $$Particularités psychologiques$$, $$[{"text":"Le bébé a peur de l’étranger vers 9 mois.","isCorrect":true},{"text":"L’enfant d’âge scolaire comprend le motif de la consultation.","isCorrect":true},{"text":"L’adolescent refuse souvent l’autorité médicale.","isCorrect":true},{"text":"Les punitions favorisent l’observance.","isCorrect":false,"justification":"Dialogue préférable."},{"text":"L’approche doit être identique à tous les âges.","isCorrect":false,"justification":"Adaptée à la maturité."}]$$::jsonb, $$L’approche psychologique varie selon âge et niveau de compréhension.$$, 13, '2025-11-02T17:52:05.619Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddyt00ugk498tnj82v8m', 'cmhi0ddxx00tmk498as23gopf', $$Rôle du médecin pédiatre$$, $$[{"text":"Se limite au diagnostic et au traitement.","isCorrect":false,"justification":"Inclut prévention et éducation."},{"text":"Implique la surveillance de la croissance et du développement.","isCorrect":true},{"text":"Comprend le repérage de la maltraitance.","isCorrect":true},{"text":"Se focalise sur la maladie aiguë.","isCorrect":false,"justification":"Approche globale."},{"text":"Exclut la relation de confiance avec les parents.","isCorrect":false,"justification":"Centrale."}]$$::jsonb, $$Le pédiatre est un acteur de soin, prévention et développement intégral de l’enfant.$$, 14, '2025-11-02T17:52:05.622Z');
INSERT INTO "Chapter" (id, "subjectId", title, description, "orderIndex", "pdfUrl", "createdAt") VALUES ('cmhi0ddyx00uik4989pa0o3sv', 'cmhi0ddxt00tkk498snuvbsi9', $$Chapitre 2 – Examen physique de l’enfant$$, NULL, 2, NULL, '2025-11-02T17:52:05.625Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddz000ukk498g1w43wda', 'cmhi0ddyx00uik4989pa0o3sv', $$Préparation à l’examen$$, $$[{"text":"L’enfant doit être examiné à jeun.","isCorrect":false,"justification":"Pas nécessaire sauf examens digestifs."},{"text":"La salle doit être calme et chaude.","isCorrect":true},{"text":"Le matériel doit être prêt avant le contact avec l’enfant.","isCorrect":true},{"text":"Il faut commencer directement par l’auscultation.","isCorrect":false,"justification":"Observer d’abord."},{"text":"L’examen doit être réalisé sans les parents.","isCorrect":false,"justification":"Leur présence rassure."}]$$::jsonb, $$Un environnement adapté et rassurant est essentiel avant l’examen.$$, 0, '2025-11-02T17:52:05.628Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddz200umk4982fhoopjx', 'cmhi0ddyx00uik4989pa0o3sv', $$Ordre de l’examen$$, $$[{"text":"Commencer par les gestes les plus intrusifs.","isCorrect":false,"justification":"Toujours en dernier."},{"text":"Aller du simple vers le complexe.","isCorrect":true},{"text":"L’examen se termine par la mesure du poids.","isCorrect":false,"justification":"Poids au début."},{"text":"Adapter l’ordre selon la coopération de l’enfant.","isCorrect":true},{"text":"Le sommeil empêche tout examen.","isCorrect":false,"justification":"Profiter du sommeil pour observer respiration."}]$$::jsonb, $$L’examen pédiatrique = progressif, souple et individualisé.$$, 1, '2025-11-02T17:52:05.630Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddz500uok498r38yfpej', 'cmhi0ddyx00uik4989pa0o3sv', $$Inspection générale$$, $$[{"text":"Doit être réalisée après la palpation.","isCorrect":false,"justification":"Toujours avant."},{"text":"Évalue la conscience, le tonus, la coloration.","isCorrect":true},{"text":"L’attitude et les mouvements spontanés sont notés.","isCorrect":true},{"text":"Les pleurs sont inutiles à interpréter.","isCorrect":false,"justification":"Significatifs."},{"text":"Les proportions corporelles n’ont pas d’importance.","isCorrect":false,"justification":"Reflètent la croissance."}]$$::jsonb, $$L’inspection donne les premiers indices cliniques essentiels.$$, 2, '2025-11-02T17:52:05.633Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddz700uqk498o8frmkcw', 'cmhi0ddyx00uik4989pa0o3sv', $$Mesures anthropométriques$$, $$[{"text":"La taille se mesure debout dès la naissance.","isCorrect":false,"justification":"Allongé jusqu’à 2 ans."},{"text":"Le poids doit être comparé aux courbes de référence.","isCorrect":true},{"text":"Le périmètre crânien se mesure systématiquement.","isCorrect":true},{"text":"L’IMC n’a aucune utilité en pédiatrie.","isCorrect":false,"justification":"Indicateur d’obésité."},{"text":"Les courbes de croissance sont facultatives.","isCorrect":false,"justification":"Indispensables."}]$$::jsonb, $$Les mesures anthropométriques évaluent croissance et nutrition.$$, 3, '2025-11-02T17:52:05.636Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddza00usk49846bwtq3k', 'cmhi0ddyx00uik4989pa0o3sv', $$Examen de la peau$$, $$[{"text":"Les lésions cutanées sont rares chez l’enfant.","isCorrect":false,"justification":"Très fréquentes."},{"text":"La couleur de la peau reflète l’état hémodynamique.","isCorrect":true},{"text":"La déshydratation s’évalue par le pli cutané.","isCorrect":true},{"text":"Les angiomes congénitaux doivent être ignorés.","isCorrect":false,"justification":"À noter et surveiller."},{"text":"Le tégument n’a pas de valeur sémiologique.","isCorrect":false,"justification":"Très informative."}]$$::jsonb, $$La peau est un miroir de l’état général et circulatoire.$$, 4, '2025-11-02T17:52:05.638Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddzc00uuk498wooc2at2', 'cmhi0ddyx00uik4989pa0o3sv', $$Examen de la tête$$, $$[{"text":"Les fontanelles doivent être palpées.","isCorrect":true},{"text":"La fermeture précoce traduit une microcéphalie.","isCorrect":true},{"text":"La macrocrânie est sans signification.","isCorrect":false,"justification":"Peut traduire HIC."},{"text":"La symétrie faciale n’a pas d’intérêt.","isCorrect":false,"justification":"Évaluée systématiquement."},{"text":"Les bosses pariétales sont toujours pathologiques.","isCorrect":false,"justification":"Peuvent être post-traumatiques bénignes."}]$$::jsonb, $$L’examen du crâne évalue croissance et anomalies morphologiques.$$, 5, '2025-11-02T17:52:05.640Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddzf00uwk4987qek4iga', 'cmhi0ddyx00uik4989pa0o3sv', $$Yeux et vision$$, $$[{"text":"Le réflexe photomoteur doit être recherché.","isCorrect":true},{"text":"Le strabisme est toujours physiologique.","isCorrect":false,"justification":"Normal < 3 mois seulement."},{"text":"Le fond d’œil est inutile chez le nourrisson.","isCorrect":false,"justification":"Important si prématurité ou rétinopathie."},{"text":"Le test de fixation visuelle se fait dès 2 mois.","isCorrect":true},{"text":"L’œil rouge chronique est bénin.","isCorrect":false,"justification":"Peut signaler glaucome ou infection."}]$$::jsonb, $$L’examen oculaire dépiste strabisme, cataracte, infections précoces.$$, 6, '2025-11-02T17:52:05.643Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddzi00uyk4989aww4isk', 'cmhi0ddyx00uik4989pa0o3sv', $$Oreilles et audition$$, $$[{"text":"Le réflexe cochléo-palpébral est inutile.","isCorrect":false,"justification":"Recherché dès la naissance."},{"text":"Le tympan doit être translucide et mobile.","isCorrect":true},{"text":"L’otite séreuse entraîne une hypoacousie.","isCorrect":true},{"text":"Le test auditif est réservé à l’adolescence.","isCorrect":false,"justification":"Dépistage néonatal obligatoire."},{"text":"Les bouchons de cérumen n’existent pas chez l’enfant.","isCorrect":false,"justification":"Fréquents."}]$$::jsonb, $$Vérifier tympan, audition et réflexes auditifs précocement.$$, 7, '2025-11-02T17:52:05.646Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddzk00v0k498pw53a0rw', 'cmhi0ddyx00uik4989pa0o3sv', $$Nez et bouche$$, $$[{"text":"L’obstruction nasale du nourrisson peut entraîner une détresse.","isCorrect":true},{"text":"Les végétations sont toujours congénitales.","isCorrect":false,"justification":"Acquises."},{"text":"Les aphtes sont spécifiques de l’immunodépression.","isCorrect":false,"justification":"Pas forcément."},{"text":"Le frein de langue doit être observé.","isCorrect":true},{"text":"La dentition débute vers 1 an.","isCorrect":true}]$$::jsonb, $$Cavité buccale et nez → respiration, alimentation, phonation.$$, 8, '2025-11-02T17:52:05.648Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddzm00v2k4988330ow2s', 'cmhi0ddyx00uik4989pa0o3sv', $$Cou et thyroïde$$, $$[{"text":"Les adénopathies cervicales sont toujours pathologiques.","isCorrect":false,"justification":"Souvent réactionnelles."},{"text":"La thyroïde est normalement non palpable.","isCorrect":true},{"text":"La mobilité du cou doit être vérifiée.","isCorrect":true},{"text":"Les torticolis congénitaux sont rares.","isCorrect":false,"justification":"Assez fréquents (sternocléidomastoïdien)."},{"text":"L’examen veineux cervical est inutile.","isCorrect":false,"justification":"Permet dépister obstacle vasculaire."}]$$::jsonb, $$Le cou = mobilité + masses + adénopathies + thyroïde.$$, 9, '2025-11-02T17:52:05.651Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddzp00v4k4980y6zis5a', 'cmhi0ddyx00uik4989pa0o3sv', $$Thorax et respiration$$, $$[{"text":"La fréquence respiratoire n’a pas d’importance.","isCorrect":false,"justification":"Indice vital essentiel."},{"text":"Les tirages sous-costaux sont signe de détresse.","isCorrect":true},{"text":"L’auscultation recherche des râles, sibilants, ronchi.","isCorrect":true},{"text":"Le thorax en tonneau est typique de l’asthme chronique.","isCorrect":true},{"text":"L’asymétrie respiratoire est normale.","isCorrect":false,"justification":"Pathologique."}]$$::jsonb, $$Thorax = symétrie, FR, tirage, bruits respiratoires.$$, 10, '2025-11-02T17:52:05.653Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddzr00v6k498pv7d0w6l', 'cmhi0ddyx00uik4989pa0o3sv', $$Cœur et circulation$$, $$[{"text":"Les souffles systoliques sont toujours pathologiques.","isCorrect":false,"justification":"Parfois fonctionnels."},{"text":"Les pouls périphériques doivent être palpés.","isCorrect":true},{"text":"La fréquence cardiaque normale varie avec l’âge.","isCorrect":true},{"text":"Les cyanoses périphériques n’ont aucun intérêt.","isCorrect":false,"justification":"Souvent signe d’hypoxie."},{"text":"Le rythme cardiaque du nourrisson est irrégulier.","isCorrect":true}]$$::jsonb, $$Cœur = souffles, pouls, cyanose, fréquence adaptée à l’âge.$$, 11, '2025-11-02T17:52:05.655Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddzt00v8k4983eidycw2', 'cmhi0ddyx00uik4989pa0o3sv', $$Abdomen$$, $$[{"text":"Le foie est toujours pathologique s’il dépasse le rebord costal.","isCorrect":false,"justification":"Peut dépasser de 1–2 cm normal."},{"text":"La rate est toujours palpable.","isCorrect":false,"justification":"Seulement si splénomégalie."},{"text":"Le bruit hydroaérique est absent normalement.","isCorrect":false,"justification":"Présent."},{"text":"L’inspection et la palpation sont essentielles.","isCorrect":true},{"text":"La hernie ombilicale du nourrisson est bénigne.","isCorrect":true}]$$::jsonb, $$Abdomen = inspection + palpation douce + surveillance hernies.$$, 12, '2025-11-02T17:52:05.658Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddzw00vak498y6773t1c', 'cmhi0ddyx00uik4989pa0o3sv', $$Organes génitaux$$, $$[{"text":"Le testicule non palpable après 6 mois nécessite surveillance.","isCorrect":true},{"text":"Le phimosis est toujours chirurgical.","isCorrect":false,"justification":"Souvent physiologique jusqu’à 3–4 ans."},{"text":"L’hydrocèle est toujours urgente.","isCorrect":false,"justification":"Fréquente, bénigne avant 2 ans."},{"text":"Chez la fille, l’hymen doit être décrit.","isCorrect":true},{"text":"L’absence d’orifice anal est bénigne.","isCorrect":false,"justification":"Urgence chirurgicale."}]$$::jsonb, $$Examen génital = testicules, orifices, anomalies congénitales.$$, 13, '2025-11-02T17:52:05.660Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0ddzy00vck498in06a5mx', 'cmhi0ddyx00uik4989pa0o3sv', $$Appareil locomoteur$$, $$[{"text":"L’examen articulaire est inutile chez l’enfant.","isCorrect":false,"justification":"Essentiel."},{"text":"La marche doit être observée.","isCorrect":true},{"text":"Les anomalies de hanches doivent être recherchées.","isCorrect":true},{"text":"L’hypotonie est un signe normal.","isCorrect":false,"justification":"Pathologique."},{"text":"Les réflexes ostéotendineux n’existent pas chez le nourrisson.","isCorrect":false,"justification":"Présents."}]$$::jsonb, $$Locomoteur = posture, marche, tonus, symétrie.$$, 14, '2025-11-02T17:52:05.662Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0de0000vek498vfg3vhhb', 'cmhi0ddyx00uik4989pa0o3sv', $$Système nerveux$$, $$[{"text":"L’état de conscience se juge sur le Glasgow adulte.","isCorrect":false,"justification":"Score adapté pédiatrique."},{"text":"Les réflexes archaïques sont normaux chez le nouveau-né.","isCorrect":true},{"text":"Les convulsions fébriles sont fréquentes.","isCorrect":true},{"text":"Le tonus passif est sans intérêt.","isCorrect":false,"justification":"Très utile."},{"text":"Les réflexes cutanés plantaires ne sont jamais observés.","isCorrect":false,"justification":"Présents, Babinski normal < 2 ans."}]$$::jsonb, $$SNC = réflexes, tonus, conscience, crises, archaïsmes.$$, 15, '2025-11-02T17:52:05.664Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0de0200vgk498oxzsd2y2', 'cmhi0ddyx00uik4989pa0o3sv', $$Squelette et croissance osseuse$$, $$[{"text":"Les courbures rachidiennes sont fixes dès la naissance.","isCorrect":false,"justification":"Se développent avec la marche."},{"text":"Le rachitisme est lié à la carence en vitamine D.","isCorrect":true},{"text":"Les déformations des membres doivent être recherchées.","isCorrect":true},{"text":"La fermeture des cartilages de croissance à 2 ans est normale.","isCorrect":false,"justification":"Tardive (adolescence)."},{"text":"La taille doit être suivie régulièrement.","isCorrect":true}]$$::jsonb, $$Croissance osseuse = vitamine D, déformations, suivi longitudinal.$$, 16, '2025-11-02T17:52:05.666Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0de0400vik498fz6nwodi', 'cmhi0ddyx00uik4989pa0o3sv', $$Température et tension$$, $$[{"text":"La fièvre est définie par T° > 38°C.","isCorrect":true},{"text":"L’hypothermie est toujours bénigne.","isCorrect":false,"justification":"Grave chez nourrisson."},{"text":"La tension se mesure dès la naissance.","isCorrect":true},{"text":"L’hypotension est normale chez l’enfant.","isCorrect":false,"justification":"À interpréter selon âge."},{"text":"L’élévation tensionnelle est rare.","isCorrect":false,"justification":"Possible (néphro, endocrino)."}]$$::jsonb, $$Surveillance T° et PA adaptée à l’âge.$$, 17, '2025-11-02T17:52:05.668Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0de0500vkk498frjlixsx', 'cmhi0ddyx00uik4989pa0o3sv', $$Évaluation de la douleur$$, $$[{"text":"La douleur de l’enfant est subjective et négligeable.","isCorrect":false,"justification":"Objectivable et importante."},{"text":"L’échelle des visages (Wong-Baker) s’utilise dès 3 ans.","isCorrect":true},{"text":"Le comportement (pleurs, agitation) guide l’évaluation.","isCorrect":true},{"text":"Le traitement antalgique est différé.","isCorrect":false,"justification":"Immédiat."},{"text":"L’absence de pleurs exclut la douleur.","isCorrect":false,"justification":"Faux."}]$$::jsonb, $$La douleur s’évalue par âge et comportement → traiter vite.$$, 18, '2025-11-02T17:52:05.670Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0de0700vmk498q7se6t2f', 'cmhi0ddyx00uik4989pa0o3sv', $$Conclusion générale$$, $$[{"text":"L’examen pédiatrique est rapide et standardisé.","isCorrect":false,"justification":"Lent, individualisé."},{"text":"Il combine observation, mesure et palpation.","isCorrect":true},{"text":"Il tient compte de l’âge, du développement et du comportement.","isCorrect":true},{"text":"Il peut se faire sans les parents.","isCorrect":false,"justification":"Toujours avec eux."},{"text":"Il néglige l’environnement psychologique.","isCorrect":false,"justification":"Essentiel."}]$$::jsonb, $$L’examen pédiatrique = global, doux, individualisé et évolutif.$$, 19, '2025-11-02T17:52:05.671Z');
INSERT INTO "Chapter" (id, "subjectId", title, description, "orderIndex", "pdfUrl", "createdAt") VALUES ('cmhi0de0a00vok4982o5k0ha1', 'cmhi0ddxt00tkk498snuvbsi9', $$Chapitre 3 – Examens complémentaires en pédiatrie$$, NULL, 3, NULL, '2025-11-02T17:52:05.674Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0de0c00vqk498ab9w2qq8', 'cmhi0de0a00vok4982o5k0ha1', $$Indication des examens complémentaires$$, $$[{"text":"Les examens doivent précéder l’examen clinique.","isCorrect":false,"justification":"Toujours après l’examen."},{"text":"Leur prescription doit être justifiée par une hypothèse diagnostique.","isCorrect":true},{"text":"Ils remplacent l’observation et l’interrogatoire.","isCorrect":false,"justification":"Ils complètent la clinique."},{"text":"Ils sont systématiques chez l’enfant malade.","isCorrect":false,"justification":"Individualisés selon le cas."},{"text":"Leur interprétation dépend de l’âge.","isCorrect":true}]$$::jsonb, $$L’examen complémentaire est guidé par la clinique et interprété selon l’âge.$$, 0, '2025-11-02T17:52:05.676Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0de0e00vsk4982n4zj52o', 'cmhi0de0a00vok4982o5k0ha1', $$Examens biologiques courants$$, $$[{"text":"L’hémogramme est inutile en pédiatrie.","isCorrect":false,"justification":"Fondamental pour infections, anémies."},{"text":"L’ionogramme explore le bilan hydrique.","isCorrect":true},{"text":"La CRP évalue l’inflammation.","isCorrect":true},{"text":"La glycémie est sans intérêt.","isCorrect":false,"justification":"Indispensable (hypo/hyperglycémie)."},{"text":"La bilirubine ne se dose que chez l’adulte.","isCorrect":false,"justification":"Très utile chez le nouveau-né."}]$$::jsonb, $$Les examens biologiques sont simples, orientés, et d’interprétation pédiatrique.$$, 1, '2025-11-02T17:52:05.679Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0de0g00vuk4989thllkzw', 'cmhi0de0a00vok4982o5k0ha1', $$Prélèvements sanguins$$, $$[{"text":"Toujours réalisés sur veine périphérique.","isCorrect":false,"justification":"Capillaire ou veineux selon besoin."},{"text":"Nécessitent du matériel adapté à l’enfant.","isCorrect":true},{"text":"La douleur du geste n’est pas à anticiper.","isCorrect":false,"justification":"Préparer et soulager."},{"text":"Les prélèvements sont souvent inutiles.","isCorrect":false,"justification":"Ciblés mais essentiels."},{"text":"L’étiquetage rigoureux évite les erreurs.","isCorrect":true}]$$::jsonb, $$Les prélèvements exigent adaptation, rigueur et analgésie.$$, 2, '2025-11-02T17:52:05.681Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0de0i00vwk498ovln8fyu', 'cmhi0de0a00vok4982o5k0ha1', $$Urines et selles$$, $$[{"text":"Le recueil urinaire est identique à celui de l’adulte.","isCorrect":false,"justification":"Poche adhésive ou ponction."},{"text":"L’analyse d’urine peut détecter une infection.","isCorrect":true},{"text":"La bandelette urinaire est souvent utilisée.","isCorrect":true},{"text":"Les selles n’ont aucun intérêt.","isCorrect":false,"justification":"Recherche infection, malabsorption."},{"text":"L’examen parasitologique est rare.","isCorrect":false,"justification":"Fréquent selon contexte."}]$$::jsonb, $$Urines/selles = infections, digestions, parasitoses.$$, 3, '2025-11-02T17:52:05.683Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0de0k00vyk4981w2qj984', 'cmhi0de0a00vok4982o5k0ha1', $$Bilan inflammatoire$$, $$[{"text":"La VS et la CRP sont inutiles chez l’enfant.","isCorrect":false,"justification":"Indispensables en pédiatrie."},{"text":"La CRP s’élève précocement.","isCorrect":true},{"text":"La VS varie avec l’âge.","isCorrect":true},{"text":"Une CRP basse exclut toute infection.","isCorrect":false,"justification":"Pas absolu."},{"text":"La procalcitonine aide au diagnostic bactérien.","isCorrect":true}]$$::jsonb, $$VS/CRP/PCT = tri infectieux bactérien vs viral.$$, 4, '2025-11-02T17:52:05.685Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0de0n00w0k4981q0s7swt', 'cmhi0de0a00vok4982o5k0ha1', $$Bilan infectieux$$, $$[{"text":"L’hémoculture n’a pas d’intérêt chez l’enfant.","isCorrect":false,"justification":"Vitale en septicémie."},{"text":"Le prélèvement doit être stérile.","isCorrect":true},{"text":"La numération leucocytaire oriente le diagnostic.","isCorrect":true},{"text":"La culture urinaire n’est jamais indiquée.","isCorrect":false,"justification":"Fréquente."},{"text":"La ponction lombaire ne se fait jamais.","isCorrect":false,"justification":"Indiquée si méningite suspectée."}]$$::jsonb, $$Bilan infectieux = NFS + hémoculture + PL + urines selon contexte.$$, 5, '2025-11-02T17:52:05.687Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0de0p00w2k4985d689atg', 'cmhi0de0a00vok4982o5k0ha1', $$Ponction lombaire$$, $$[{"text":"Contre-indiquée si hypertension intracrânienne.","isCorrect":true},{"text":"Permet le diagnostic des méningites.","isCorrect":true},{"text":"Réalisée sans asepsie stricte.","isCorrect":false,"justification":"Asepsie rigoureuse obligatoire."},{"text":"Peut être faite chez tout nourrisson fébrile sans raison.","isCorrect":false,"justification":"Selon signes neurologiques."},{"text":"Nécessite surveillance post-geste.","isCorrect":true}]$$::jsonb, $$PL = outil diagnostique majeur, avec précautions strictes.$$, 6, '2025-11-02T17:52:05.689Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0de0r00w4k498iz1wm6rm', 'cmhi0de0a00vok4982o5k0ha1', $$Examens d’imagerie$$, $$[{"text":"La radiographie thoracique est proscrite.","isCorrect":false,"justification":"Indiquée si suspicion respiratoire."},{"text":"L’échographie est non invasive.","isCorrect":true},{"text":"Le scanner est première intention pour tout traumatisme crânien.","isCorrect":false,"justification":"Selon gravité."},{"text":"L’IRM est privilégiée pour les tissus mous et le cerveau.","isCorrect":true},{"text":"La scintigraphie osseuse est un examen de routine.","isCorrect":false,"justification":"Spécialisée."}]$$::jsonb, $$Imagerie = Rx, Echo, IRM selon âge et indication.$$, 7, '2025-11-02T17:52:05.692Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0de0u00w6k498tjugk2xp', 'cmhi0de0a00vok4982o5k0ha1', $$Radioprotection$$, $$[{"text":"L’enfant est plus sensible aux rayonnements.","isCorrect":true},{"text":"Le port de protection plombée est facultatif.","isCorrect":false,"justification":"Obligatoire."},{"text":"Les examens doivent être limités au strict nécessaire.","isCorrect":true},{"text":"Le scanner peut être répété sans risque.","isCorrect":false,"justification":"Accumulation dangereuse."},{"text":"L’échographie ne comporte aucun risque.","isCorrect":true}]$$::jsonb, $$Radioprotection = minimiser dose et fréquence d’exposition.$$, 8, '2025-11-02T17:52:05.694Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0de0x00w8k498uf9yjh7d', 'cmhi0de0a00vok4982o5k0ha1', $$ECG et cœur de l’enfant$$, $$[{"text":"L’ECG est identique à celui de l’adulte.","isCorrect":false,"justification":"Axes et fréquences différents."},{"text":"Permet d’évaluer rythme, conduction, hypertrophie.","isCorrect":true},{"text":"Les arythmies sinusales sont physiologiques.","isCorrect":true},{"text":"Ne doit pas être utilisé avant 10 ans.","isCorrect":false,"justification":"Dès la naissance si indication."},{"text":"L’ECG ne sert pas au dépistage sportif.","isCorrect":false,"justification":"Recommandé."}]$$::jsonb, $$ECG pédiatrique = rythme, conduction, hypertrophie selon l’âge.$$, 9, '2025-11-02T17:52:05.697Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0de0z00wak4986op594ma', 'cmhi0de0a00vok4982o5k0ha1', $$EEG pédiatrique$$, $$[{"text":"Utile pour suspicion d’épilepsie.","isCorrect":true},{"text":"Toujours pathologique chez le nouveau-né.","isCorrect":false,"justification":"Physiologique variable."},{"text":"Nécessite parfois sédation légère.","isCorrect":true},{"text":"Permet d’explorer le sommeil.","isCorrect":true},{"text":"Remplace l’imagerie cérébrale.","isCorrect":false,"justification":"Complémentaire."}]$$::jsonb, $$EEG = épilepsie, troubles du sommeil, encéphalopathies.$$, 10, '2025-11-02T17:52:05.699Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0de1100wck498739nzfr9', 'cmhi0de0a00vok4982o5k0ha1', $$Examens auditifs et visuels$$, $$[{"text":"Réservés à l’âge scolaire.","isCorrect":false,"justification":"Dépistage néonatal obligatoire."},{"text":"Audiogramme = test de l’audition.","isCorrect":true},{"text":"Le test du réflexe rouge dépiste la cataracte congénitale.","isCorrect":true},{"text":"Le tympanogramme mesure la vision.","isCorrect":false,"justification":"Mobilité du tympan."},{"text":"Les troubles visuels précoces sont bénins.","isCorrect":false,"justification":"Urgence parfois."}]$$::jsonb, $$Dépistage auditif et visuel = prévention du retard de développement.$$, 11, '2025-11-02T17:52:05.702Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0de1400wek4988elzii78', 'cmhi0de0a00vok4982o5k0ha1', $$Examens digestifs$$, $$[{"text":"L’échographie abdominale est le 1er examen.","isCorrect":true},{"text":"Le lavement baryté est sans risque.","isCorrect":false,"justification":"Contre-indiqué si suspicion perforation."},{"text":"Le test de Graham explore l’intestin grêle.","isCorrect":false,"justification":"Ancien, peu utilisé."},{"text":"La fibroscopie est toujours réalisée sans anesthésie.","isCorrect":false,"justification":"Sous anesthésie générale."},{"text":"Les examens digestifs doivent être justifiés.","isCorrect":true}]$$::jsonb, $$Examens digestifs = imagerie douce, anesthésie, justification clinique.$$, 12, '2025-11-02T17:52:05.704Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0de1600wgk498vr6wzgp2', 'cmhi0de0a00vok4982o5k0ha1', $$Examens urinaires$$, $$[{"text":"L’échographie rénale est systématique.","isCorrect":false,"justification":"Selon troubles urinaires."},{"text":"Le cystogramme rétrograde explore reflux vésico-urétéral.","isCorrect":true},{"text":"La bandelette détecte leucocytes et nitrites.","isCorrect":true},{"text":"L’ECBU ne s’indique jamais avant 5 ans.","isCorrect":false,"justification":"Dès la naissance si fièvre inexpliquée."},{"text":"Les anomalies urinaires ne concernent que les garçons.","isCorrect":false,"justification":"Les deux sexes."}]$$::jsonb, $$Explorations urinaires = infection, reflux, malformations.$$, 13, '2025-11-02T17:52:05.707Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0de1900wik498v12n9vam', 'cmhi0de0a00vok4982o5k0ha1', $$Synthèse générale$$, $$[{"text":"Les examens complètent la clinique.","isCorrect":true},{"text":"Ils sont toujours systématiques.","isCorrect":false,"justification":"Guidés par suspicion."},{"text":"Leur interprétation dépend de l’âge et du contexte.","isCorrect":true},{"text":"Ils remplacent l’observation.","isCorrect":false,"justification":"Jamais."},{"text":"Leur répétition n’a aucun risque.","isCorrect":false,"justification":"À limiter."}]$$::jsonb, $$Examens complémentaires = outils précis et adaptés à chaque enfant.$$, 14, '2025-11-02T17:52:05.709Z');
INSERT INTO "Chapter" (id, "subjectId", title, description, "orderIndex", "pdfUrl", "createdAt") VALUES ('cmhi0de1d00wkk498c6ui2ed7', 'cmhi0ddxt00tkk498snuvbsi9', $$Chapitre 4 – Examen du nouveau-né$$, NULL, 4, NULL, '2025-11-02T17:52:05.713Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0de1f00wmk4989s0sal2x', 'cmhi0de1d00wkk498c6ui2ed7', $$Objectifs de l’examen néonatal$$, $$[{"text":"Détecter uniquement les maladies infectieuses.","isCorrect":false,"justification":"Cible toutes les anomalies congénitales et fonctionnelles."},{"text":"Évaluer la vitalité, la croissance et la maturité.","isCorrect":true},{"text":"Se limite à l’aspect externe.","isCorrect":false,"justification":"Inclut examen interne et réflexes."},{"text":"Permet d’établir un lien mère–enfant.","isCorrect":true},{"text":"N’a aucune valeur pour la prévention.","isCorrect":false,"justification":"Crucial pour dépistage précoce."}]$$::jsonb, $$Examen néonatal = bilan global vitalité + croissance + anomalies.$$, 0, '2025-11-02T17:52:05.715Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0de1h00wok4984saw6mx8', 'cmhi0de1d00wkk498c6ui2ed7', $$Score d’Apgar$$, $$[{"text":"Se calcule à 1 minute et 5 minutes.","isCorrect":true},{"text":"Évalue fréquence cardiaque, respiration, tonus, couleur, réactivité.","isCorrect":true},{"text":"Un score de 10 traduit une asphyxie sévère.","isCorrect":false,"justification":"Score bas traduit détresse."},{"text":"Se base sur un prélèvement sanguin.","isCorrect":false,"justification":"Observation clinique."},{"text":"Un score < 7 impose une réanimation.","isCorrect":true}]$$::jsonb, $$Apgar = évaluation rapide de l’adaptation néonatale.$$, 1, '2025-11-02T17:52:05.718Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0de1j00wqk4986r7n3wxn', 'cmhi0de1d00wkk498c6ui2ed7', $$Poids et taille$$, $$[{"text":"Poids moyen à terme ≈ 2,5 à 3,5 kg.","isCorrect":true},{"text":"Taille moyenne ≈ 50 cm.","isCorrect":true},{"text":"Poids < 2 kg définit la macrosomie.","isCorrect":false,"justification":"Hypotrophie."},{"text":"Un poids > 4 kg évoque diabète maternel.","isCorrect":true},{"text":"La taille ne varie pas selon le sexe.","isCorrect":false,"justification":"Légère différence."}]$$::jsonb, $$Données de croissance = référence de maturité et de nutrition.$$, 2, '2025-11-02T17:52:05.720Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0de1l00wsk498o5kuyo7t', 'cmhi0de1d00wkk498c6ui2ed7', $$Aspect général du nouveau-né$$, $$[{"text":"La posture en flexion est normale.","isCorrect":true},{"text":"Les mouvements spontanés sont rares.","isCorrect":false,"justification":"Présents et symétriques."},{"text":"La coloration cutanée doit être rosée.","isCorrect":true},{"text":"Les acrocyanoses sont toujours pathologiques.","isCorrect":false,"justification":"Physiologiques transitoires."},{"text":"Les malformations visibles sont négligeables.","isCorrect":false,"justification":"À rechercher systématiquement."}]$$::jsonb, $$Observation initiale = posture, tonus, coloration, symétrie.$$, 3, '2025-11-02T17:52:05.722Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0de1o00wuk498twnsc1yu', 'cmhi0de1d00wkk498c6ui2ed7', $$Peau et annexes$$, $$[{"text":"Le vernix caseosa est pathologique.","isCorrect":false,"justification":"Physiologique, protecteur."},{"text":"Le lanugo disparaît progressivement.","isCorrect":true},{"text":"L’ictère précoce (avant 24h) est bénin.","isCorrect":false,"justification":"Toujours pathologique."},{"text":"L’érythème toxique du nouveau-né est bénin.","isCorrect":true},{"text":"Les angiomes plans sont sans gravité.","isCorrect":true}]$$::jsonb, $$Peau = indice de maturité et d’adaptation cutanée.$$, 4, '2025-11-02T17:52:05.724Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0de1q00wwk4982ddclc8w', 'cmhi0de1d00wkk498c6ui2ed7', $$Tête et crâne$$, $$[{"text":"La fontanelle antérieure ferme à 6 mois.","isCorrect":false,"justification":"12–18 mois."},{"text":"La postérieure ferme avant 2 mois.","isCorrect":true},{"text":"L’asymétrie du crâne (moulage) est physiologique.","isCorrect":true},{"text":"Une bosse sérosanguine croise les sutures.","isCorrect":false,"justification":"Non (caput succédanéum oui)."},{"text":"Le périmètre crânien est inutile.","isCorrect":false,"justification":"Essentiel pour croissance."}]$$::jsonb, $$Crâne = fontanelles, moulage, périmètre, symétrie.$$, 5, '2025-11-02T17:52:05.727Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0de1t00wyk4982cbfba5h', 'cmhi0de1d00wkk498c6ui2ed7', $$Yeux$$, $$[{"text":"Le réflexe photomoteur doit être présent.","isCorrect":true},{"text":"Le réflexe rouge est recherché systématiquement.","isCorrect":true},{"text":"Le strabisme permanent est normal.","isCorrect":false,"justification":"Transitoire possible seulement."},{"text":"L’écoulement purulent est fréquent et bénin.","isCorrect":false,"justification":"Peut évoquer conjonctivite néonatale."},{"text":"La vision est absente à la naissance.","isCorrect":false,"justification":"Perçoit formes et lumières."}]$$::jsonb, $$Œil = réflexes + dépistage cataracte et infection.$$, 6, '2025-11-02T17:52:05.729Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0de1v00x0k498dlifbuos', 'cmhi0de1d00wkk498c6ui2ed7', $$Oreilles et audition$$, $$[{"text":"L’implantation basse peut évoquer une malformation.","isCorrect":true},{"text":"Le dépistage auditif est obligatoire à la maternité.","isCorrect":true},{"text":"L’otite néonatale est bénigne.","isCorrect":false,"justification":"Grave, à traiter."},{"text":"Les réflexes auditifs ne se testent qu’à 1 an.","isCorrect":false,"justification":"Dès la naissance."},{"text":"Le tympan est toujours opaque.","isCorrect":false,"justification":"Translucide normalement."}]$$::jsonb, $$Oreilles = forme, implantation, audition précoce.$$, 7, '2025-11-02T17:52:05.731Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0de1x00x2k4989thscrnf', 'cmhi0de1d00wkk498c6ui2ed7', $$Nez et bouche$$, $$[{"text":"Le nouveau-né respire par la bouche.","isCorrect":false,"justification":"Respiration nasale exclusive."},{"text":"L’obstruction des choanes est une urgence.","isCorrect":true},{"text":"Le palais doit être palpé systématiquement.","isCorrect":true},{"text":"Le frein de langue n’a aucun intérêt.","isCorrect":false,"justification":"Recherche d’ankyloglossie."},{"text":"Les dents néonatales sont toujours pathologiques.","isCorrect":false,"justification":"Parfois physiologiques."}]$$::jsonb, $$Bouche/nez = perméabilité, succion, anomalies palatines.$$, 8, '2025-11-02T17:52:05.734Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0de2000x4k498ckzfsn0z', 'cmhi0de1d00wkk498c6ui2ed7', $$Cou et thorax$$, $$[{"text":"Le cou doit être mobile et libre.","isCorrect":true},{"text":"Un goitre néonatal est bénin.","isCorrect":false,"justification":"Souvent pathologique (thyroïde maternelle)."},{"text":"Les seins hypertrophiés peuvent être normaux.","isCorrect":true},{"text":"Les tirages respiratoires sont sans gravité.","isCorrect":false,"justification":"Signes de détresse."},{"text":"La clavicule doit être palpée à la recherche de fracture.","isCorrect":true}]$$::jsonb, $$Cou/thorax = mobilité, respiration, fractures obstétricales.$$, 9, '2025-11-02T17:52:05.736Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0de2200x6k4981rws3hv0', 'cmhi0de1d00wkk498c6ui2ed7', $$Cœur et circulation$$, $$[{"text":"Le souffle cardiaque est toujours pathologique.","isCorrect":false,"justification":"Souvent fonctionnel au début."},{"text":"La fréquence cardiaque normale ≈ 120–160 bpm.","isCorrect":true},{"text":"Les extrémités froides traduisent souvent un choc.","isCorrect":true},{"text":"La cyanose centrale est toujours normale.","isCorrect":false,"justification":"Signale cardiopathie ou détresse."},{"text":"La palpation du pouls fémoral est inutile.","isCorrect":false,"justification":"Dépiste coarctation."}]$$::jsonb, $$Cœur = souffle, FC, pouls, coloration.$$, 10, '2025-11-02T17:52:05.738Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0de2400x8k498er35wdw7', 'cmhi0de1d00wkk498c6ui2ed7', $$Abdomen$$, $$[{"text":"Le foie est souvent palpable.","isCorrect":true},{"text":"L’ombilic infecté (omphalite) est bénin.","isCorrect":false,"justification":"Grave chez nouveau-né."},{"text":"Les reins ne sont jamais palpables.","isCorrect":false,"justification":"Parfois si hypertrophiés."},{"text":"Les vomissements bilieux sont banals.","isCorrect":false,"justification":"Toujours pathologiques."},{"text":"L’abdomen distendu peut révéler occlusion.","isCorrect":true}]$$::jsonb, $$Abdomen = foie palpable, vigilance ombilicale et bilieuse.$$, 11, '2025-11-02T17:52:05.740Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0de2700xak498fyx5cn0n', 'cmhi0de1d00wkk498c6ui2ed7', $$Organes génitaux$$, $$[{"text":"Chez le garçon, testicules palpables en bourse.","isCorrect":true},{"text":"L’hydrocèle est toujours urgente.","isCorrect":false,"justification":"Bénigne la 1ʳᵉ année."},{"text":"Chez la fille, leucorrhées physiologiques possibles.","isCorrect":true},{"text":"L’hymen imperforé n’a pas de conséquence.","isCorrect":false,"justification":"À surveiller."},{"text":"L’ambiguïté sexuelle impose exploration.","isCorrect":true}]$$::jsonb, $$Génitaux = testicules, hydrocèle, ambiguïté, leucorrhée.$$, 12, '2025-11-02T17:52:05.743Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0de2900xck498cve381ou', 'cmhi0de1d00wkk498c6ui2ed7', $$Membres et hanches$$, $$[{"text":"La symétrie des plis fessiers est à vérifier.","isCorrect":true},{"text":"Le signe d’Ortolani dépiste la luxation congénitale.","isCorrect":true},{"text":"La limitation d’abduction des hanches est normale.","isCorrect":false,"justification":"Signe de luxation."},{"text":"Le réflexe de marche automatique n’a aucun intérêt.","isCorrect":false,"justification":"Évalue intégrité motrice."},{"text":"Le tonus doit être flasque.","isCorrect":false,"justification":"Tonus flexion."}]$$::jsonb, $$Hanches = symétrie, abduction, réflexe moteur.$$, 13, '2025-11-02T17:52:05.745Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0de2b00xek498musy3ccq', 'cmhi0de1d00wkk498c6ui2ed7', $$Membres supérieurs$$, $$[{"text":"Les mains doivent être observées ouvertes.","isCorrect":true},{"text":"La préhension réflexe doit être présente.","isCorrect":true},{"text":"Les mouvements asymétriques sont normaux.","isCorrect":false,"justification":"Pathologiques."},{"text":"Les doigts surnuméraires sont fréquents et bénins.","isCorrect":false,"justification":"Anomalies congénitales."},{"text":"Les cyanoses unilatérales sont normales.","isCorrect":false,"justification":"Trouble circulatoire."}]$$::jsonb, $$Mains = préhension, symétrie, anomalies digitales.$$, 14, '2025-11-02T17:52:05.747Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0de2d00xgk498jfsqkvfv', 'cmhi0de1d00wkk498c6ui2ed7', $$Système nerveux$$, $$[{"text":"Le réflexe de Moro est un signe pathologique.","isCorrect":false,"justification":"Physiologique (intégrité motrice)."},{"text":"Le réflexe de succion doit être présent.","isCorrect":true},{"text":"Le Babinski est toujours anormal.","isCorrect":false,"justification":"Normal < 2 ans."},{"text":"Le tonus axial et périphérique doit être noté.","isCorrect":true},{"text":"Le réflexe de redressement est absent à la naissance.","isCorrect":true}]$$::jsonb, $$SNC = réflexes archaïques et tonus global.$$, 15, '2025-11-02T17:52:05.750Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0de2f00xik4986a4vep5w', 'cmhi0de1d00wkk498c6ui2ed7', $$Dos et colonne$$, $$[{"text":"L’inspection du rachis est inutile.","isCorrect":false,"justification":"Recherche spina bifida."},{"text":"La symétrie des épaules est à vérifier.","isCorrect":true},{"text":"Le sacral dimple profond est bénin.","isCorrect":false,"justification":"Explorer (IRM possible)."},{"text":"Les mouvements de flexion/extension sont limités.","isCorrect":false,"justification":"Souples normalement."},{"text":"La palpation des épineuses est possible.","isCorrect":true}]$$::jsonb, $$Rachis = intégrité, symétrie, malformations.$$, 16, '2025-11-02T17:52:05.752Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0de2h00xkk498zol44zr5', 'cmhi0de1d00wkk498c6ui2ed7', $$Système ostéo-articulaire$$, $$[{"text":"Les fractures obstétricales sont rares.","isCorrect":false,"justification":"Assez fréquentes (clavicule, humérus)."},{"text":"La mobilité articulaire doit être testée doucement.","isCorrect":true},{"text":"Les déformations congénitales (pieds bots) sont à dépister.","isCorrect":true},{"text":"Les luxations traumatiques sont courantes.","isCorrect":false,"justification":"Exceptionnelles."},{"text":"Le tonus articulaire est sans intérêt.","isCorrect":false,"justification":"Renseigne sur hypotonie/hypertonie."}]$$::jsonb, $$Ostéo-articulaire = mobilité, fractures, anomalies congénitales.$$, 17, '2025-11-02T17:52:05.754Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0de2j00xmk498a0ju05y0', 'cmhi0de1d00wkk498c6ui2ed7', $$Examen digestif et anal$$, $$[{"text":"L’anus doit être visible et perméable.","isCorrect":true},{"text":"La recherche du méconium est secondaire.","isCorrect":false,"justification":"Essentielle dès 24 h."},{"text":"L’absence d’orifice anal est rare et bénigne.","isCorrect":false,"justification":"Urgence chirurgicale."},{"text":"La distension abdominale est anodine.","isCorrect":false,"justification":"Peut traduire occlusion."},{"text":"Le toucher rectal n’a jamais d’indication.","isCorrect":false,"justification":"Parfois nécessaire."}]$$::jsonb, $$Examen anal = perméabilité, méconium, anomalies.$$, 18, '2025-11-02T17:52:05.755Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0de2l00xok498z3v2hmjx', 'cmhi0de1d00wkk498c6ui2ed7', $$Système respiratoire$$, $$[{"text":"Le nouveau-né respire par la bouche.","isCorrect":false,"justification":"Respiration nasale."},{"text":"Le rythme respiratoire normal ≈ 30–60/min.","isCorrect":true},{"text":"Les pauses respiratoires prolongées sont normales.","isCorrect":false,"justification":"Apnées pathologiques."},{"text":"Le tirage intercostal est un signe de détresse.","isCorrect":true},{"text":"Les bruits laryngés ne nécessitent jamais d’évaluation.","isCorrect":false,"justification":"À explorer."}]$$::jsonb, $$Respiration = fréquence, régularité, tirage, apnées.$$, 19, '2025-11-02T17:52:05.757Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0de2n00xqk498quueb3ub', 'cmhi0de1d00wkk498c6ui2ed7', $$Examen neurologique de la vigilance$$, $$[{"text":"L’état de veille est absent à la naissance.","isCorrect":false,"justification":"Alternance veille-sommeil."},{"text":"La réactivité aux stimulations est à noter.","isCorrect":true},{"text":"Les cris doivent être évalués.","isCorrect":true},{"text":"Les mouvements spontanés n’ont pas de valeur.","isCorrect":false,"justification":"Renseignent sur intégrité motrice."},{"text":"Le sommeil profond empêche tout examen.","isCorrect":false,"justification":"Observation utile."}]$$::jsonb, $$Vigilance = réactivité + cris + motricité spontanée.$$, 20, '2025-11-02T17:52:05.760Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0de2p00xsk4983tsjmogw', 'cmhi0de1d00wkk498c6ui2ed7', $$Malformations à dépister$$, $$[{"text":"L’absence de choanes est une urgence.","isCorrect":true},{"text":"La fente palatine passe souvent inaperçue.","isCorrect":true},{"text":"L’hydrocéphalie se repère au périmètre crânien.","isCorrect":true},{"text":"Les cardiopathies congénitales n’ont pas de signes précoces.","isCorrect":false,"justification":"Cyanose, souffle."},{"text":"Les anomalies des membres sont rares.","isCorrect":false,"justification":"Fréquentes."}]$$::jsonb, $$Dépistage = anomalies craniofaciales, cardiaques, digestives,$$, 21, '2025-11-02T17:52:05.762Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0de2s00xuk498dmczj3o1', 'cmhi0de1d00wkk498c6ui2ed7', $$Réflexes archaïques$$, $$[{"text":"Le grasping (préhen­sion) disparaît vers 3 mois.","isCorrect":true},{"text":"Le réflexe de succion persiste jusqu’à 2 ans.","isCorrect":false,"justification":"Disparaît vers 3–4 mois."},{"text":"Le réflexe de marche automatique persiste toute la vie.","isCorrect":false,"justification":"Disparaît à 2 mois."},{"text":"Le réflexe tonique du cou disparaît vers 6 mois.","isCorrect":true},{"text":"L’absence de réflexes n’a pas de valeur.","isCorrect":false,"justification":"Peut indiquer lésion neurologique."}]$$::jsonb, $$Les réflexes archaïques = signes de maturation neurologique et dépistage précoce.$$, 22, '2025-11-02T17:52:05.764Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0de2u00xwk498liwtng3z', 'cmhi0de1d00wkk498c6ui2ed7', $$Maturité et vitalité$$, $$[{"text":"La peau ridée et les ongles longs traduisent une prématurité.","isCorrect":false,"justification":"Signes de postmaturité."},{"text":"L’hypotonie globale indique souvent une souffrance néonatale.","isCorrect":true},{"text":"Le cri fort et spontané est un bon signe de vitalité.","isCorrect":true},{"text":"La cyanose généralisée est normale.","isCorrect":false,"justification":"Pathologique."},{"text":"L’hypothermie est rare à la naissance.","isCorrect":false,"justification":"Fréquente si mauvaise adaptation."}]$$::jsonb, $$Maturité = tonus, cri, thermorégulation, coloration.$$, 23, '2025-11-02T17:52:05.766Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0de2w00xyk49823p3zpor', 'cmhi0de1d00wkk498c6ui2ed7', $$Synthèse de l’examen du nouveau-né$$, $$[{"text":"L’examen néonatal vise seulement à rechercher les anomalies génétiques.","isCorrect":false,"justification":"Il évalue tout l’état général."},{"text":"Il comprend la vérification des réflexes et de la vitalité.","isCorrect":true},{"text":"Il doit être réalisé immédiatement après la naissance et répété.","isCorrect":true},{"text":"L’anamnèse maternelle n’a aucune importance.","isCorrect":false,"justification":"Essentielle (pathologies, médicaments)."},{"text":"La surveillance du poids est accessoire.","isCorrect":false,"justification":"Indispensable."}]$$::jsonb, $$L’examen néonatal = clé de dépistage précoce et de suivi postnatal.$$, 24, '2025-11-02T17:52:05.768Z');
INSERT INTO "Chapter" (id, "subjectId", title, description, "orderIndex", "pdfUrl", "createdAt") VALUES ('cmhi0de2z00y0k4980r45otfa', 'cmhi0ddxt00tkk498snuvbsi9', $$Chapitre 5 – Examens complémentaires spécifiques du nouveau-né$$, NULL, 5, NULL, '2025-11-02T17:52:05.771Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0de3100y2k498q6nl5n4q', 'cmhi0de2z00y0k4980r45otfa', $$Principes généraux$$, $$[{"text":"Les examens complémentaires sont systématiques chez tout nouveau-né.","isCorrect":false,"justification":"Réalisés selon le contexte."},{"text":"Ils visent à confirmer les anomalies détectées à l’examen clinique.","isCorrect":true},{"text":"Ils remplacent l’examen physique.","isCorrect":false,"justification":"Le complètent."},{"text":"Ils ne concernent que les nouveau-nés prématurés.","isCorrect":false,"justification":"Tous peuvent être concernés."},{"text":"Leur interprétation dépend de l’âge gestationnel.","isCorrect":true}]$$::jsonb, $$Les examens du nouveau-né sont ciblés, interprétés selon âge et contexte.$$, 0, '2025-11-02T17:52:05.773Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0de3400y4k4981i0aixjl', 'cmhi0de2z00y0k4980r45otfa', $$Hémogramme néonatal$$, $$[{"text":"Le taux d’hémoglobine est identique à l’adulte.","isCorrect":false,"justification":"Plus élevé à la naissance."},{"text":"Les polynucléaires neutrophiles sont prédominants.","isCorrect":true},{"text":"L’hématocrite normal ≈ 50–60 %.","isCorrect":true},{"text":"L’hémoglobine fœtale disparaît à 24 h.","isCorrect":false,"justification":"Persiste plusieurs mois."},{"text":"Une polyglobulie traduit toujours une infection.","isCorrect":false,"justification":"Peut être adaptation ou diabète maternel."}]$$::jsonb, $$Hémogramme = Hb élevée, HbF présente, N neutrophilie physiologique.$$, 1, '2025-11-02T17:52:05.776Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0de3600y6k4980o215zuv', 'cmhi0de2z00y0k4980r45otfa', $$Numération plaquettaire$$, $$[{"text":"Le seuil normal est > 150 000/mm³.","isCorrect":true},{"text":"Une thrombopénie peut indiquer une infection materno-fœtale.","isCorrect":true},{"text":"Les plaquettes varient peu dans la première semaine.","isCorrect":true},{"text":"Une thrombocytose traduit toujours un saignement.","isCorrect":false,"justification":"Réactionnelle souvent."},{"text":"Les plaquettes ne jouent aucun rôle dans l’hémostase.","isCorrect":false,"justification":"Essentielles."}]$$::jsonb, $$Plaquettes = 150–400k, anomalies → infection, asphyxie, CIVD.$$, 2, '2025-11-02T17:52:05.779Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0de3800y8k498xddd2mwf', 'cmhi0de2z00y0k4980r45otfa', $$Glycémie du nouveau-né$$, $$[{"text":"La glycémie normale ≥ 0,80 g/L dès la naissance.","isCorrect":false,"justification":"Normale ≈ 0,40–0,70 g/L."},{"text":"L’hypoglycémie < 0,40 g/L est fréquente.","isCorrect":true},{"text":"Survient surtout chez les enfants de mères diabétiques.","isCorrect":true},{"text":"Est sans conséquence neurologique.","isCorrect":false,"justification":"Peut causer convulsions, coma."},{"text":"Nécessite une surveillance précoce.","isCorrect":true}]$$::jsonb, $$Hypoglycémie = urgence neurologique néonatale fréquente.$$, 3, '2025-11-02T17:52:05.781Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0de3c00yak498g4255xf2', 'cmhi0de2z00y0k4980r45otfa', $$Ionogramme sanguin$$, $$[{"text":"Le sodium néonatal normal ≈ 135–145 mmol/L.","isCorrect":true},{"text":"Le potassium < 3,0 mmol/L est physiologique.","isCorrect":false,"justification":"Hypokaliémie pathologique."},{"text":"La déshydratation hypernatrémique est rare.","isCorrect":false,"justification":"Fréquente si allaitement inadéquat."},{"text":"La calcémie doit être surveillée chez les prématurés.","isCorrect":true},{"text":"L’hypocalcémie peut provoquer des tremblements.","isCorrect":true}]$$::jsonb, $$Ionogramme = surveillance hydrique et électrolytique rigoureuse.$$, 4, '2025-11-02T17:52:05.784Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0de3e00yck498thii21t4', 'cmhi0de2z00y0k4980r45otfa', $$Bilan hépatique$$, $$[{"text":"L’ictère physiologique débute avant 12 h.","isCorrect":false,"justification":"Toujours pathologique si précoce."},{"text":"La bilirubine indirecte est majoritaire dans l’ictère physiologique.","isCorrect":true},{"text":"L’ictère nucléaire survient pour bilirubine > 25 mg/dL.","isCorrect":true},{"text":"L’élévation des transaminases est sans signification.","isCorrect":false,"justification":"Peut traduire hépatite néonatale."},{"text":"Le dosage de bilirubine est inutile.","isCorrect":false,"justification":"Indispensable."}]$$::jsonb, $$Bilan hépatique = prévention ictère pathologique et encéphalopathie.$$, 5, '2025-11-02T17:52:05.786Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0de3g00yek4983e50tkle', 'cmhi0de2z00y0k4980r45otfa', $$Examens infectieux$$, $$[{"text":"Le prélèvement du liquide gastrique recherche une infection maternelle.","isCorrect":true},{"text":"Les hémocultures sont inutiles en néonatalogie.","isCorrect":false,"justification":"Essentielles en suspicion de sepsis."},{"text":"La CRP se négative lentement.","isCorrect":false,"justification":"Baisse rapide si traitement efficace."},{"text":"Les ponctions lombaires sont contre-indiquées.","isCorrect":false,"justification":"Indiquées si méningite suspectée."},{"text":"Le prélèvement d’urine peut se faire par ponction sus-pubienne.","isCorrect":true}]$$::jsonb, $$Infection néonatale = PL + hémoculture + CRP.$$, 6, '2025-11-02T17:52:05.788Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0de3i00ygk498p3e3h79z', 'cmhi0de2z00y0k4980r45otfa', $$Examens d’imagerie$$, $$[{"text":"L’échographie cérébrale est possible par la fontanelle.","isCorrect":true},{"text":"Le scanner est examen de première intention.","isCorrect":false,"justification":"Échographie préférée."},{"text":"L’échographie abdominale dépiste les malformations viscérales.","isCorrect":true},{"text":"La radiographie thoracique est inutile.","isCorrect":false,"justification":"Indiquée pour détresse respiratoire."},{"text":"L’échographie cardiaque détecte les cardiopathies congénitales.","isCorrect":true}]$$::jsonb, $$Imagerie néonatale = écho, Rx, fontanelle ouverte = fenêtre diagnostique.$$, 7, '2025-11-02T17:52:05.790Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0de3l00yik498lxt5r17c', 'cmhi0de2z00y0k4980r45otfa', $$Bilan neurologique$$, $$[{"text":"L’électroencéphalogramme (EEG) est inutile chez le nouveau-né.","isCorrect":false,"justification":"Utile en encéphalopathie ou convulsions."},{"text":"L’échographie transfontanellaire évalue les ventricules.","isCorrect":true},{"text":"L’IRM est réalisée en première intention.","isCorrect":false,"justification":"Après échographie."},{"text":"Le tonus, la posture et les réflexes suffisent pour juger la maturation.","isCorrect":true},{"text":"Le réflexe de Moro indique un trouble cérébelleux.","isCorrect":false,"justification":"Rachidien."}]$$::jsonb, $$Bilan neuro = EEG + écho + examen clinique.$$, 8, '2025-11-02T17:52:05.793Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0de3n00ykk498z1keufef', 'cmhi0de2z00y0k4980r45otfa', $$Examens métaboliques$$, $$[{"text":"Le dépistage néonatal est réalisé à 1 semaine.","isCorrect":false,"justification":"J2–J3."},{"text":"Le test de Guthrie recherche plusieurs maladies métaboliques.","isCorrect":true},{"text":"Il se fait sur sang capillaire au talon.","isCorrect":true},{"text":"Il détecte uniquement la phénylcétonurie.","isCorrect":false,"justification":"Aussi hypothyroïdie, drépanocytose, MCAD, etc."},{"text":"Il est optionnel selon les régions.","isCorrect":false,"justification":"Obligatoire."}]$$::jsonb, $$Test de Guthrie = dépistage précoce obligatoire maladies métaboliques.$$, 9, '2025-11-02T17:52:05.795Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0de3p00ymk498f4sdsorv', 'cmhi0de2z00y0k4980r45otfa', $$Exploration cardio-pulmonaire$$, $$[{"text":"La saturation en O₂ est inutile.","isCorrect":false,"justification":"Vitale à la naissance."},{"text":"Le dépistage des cardiopathies se fait par oxymétrie.","isCorrect":true},{"text":"L’ECG n’a pas de valeur chez le nouveau-né.","isCorrect":false,"justification":"Interprétation spécifique."},{"text":"L’échocardiographie évalue les shunts et malformations.","isCorrect":true},{"text":"L’auscultation seule suffit.","isCorrect":false,"justification":"Toujours combinée à l’imagerie."}]$$::jsonb, $$Cœur/poumon = oxymétrie + ECG + écho pour dépistage.$$, 10, '2025-11-02T17:52:05.798Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0de3r00yok498hum2h67u', 'cmhi0de2z00y0k4980r45otfa', $$Examens urinaires et rénaux$$, $$[{"text":"L’échographie rénale est systématique chez tout nouveau-né.","isCorrect":false,"justification":"Réservée si anomalie anténatale."},{"text":"La créatinine reflète la fonction rénale maternelle au début.","isCorrect":true},{"text":"L’oligurie < 1 mL/kg/h est anormale.","isCorrect":true},{"text":"Le dosage d’urée est inutile.","isCorrect":false,"justification":"Utile dans IR."},{"text":"La diurèse se calcule sans peser les couches.","isCorrect":false,"justification":"Pesée nécessaire."}]$$::jsonb, $$Rein = diurèse, créatinine, échographie selon contexte.$$, 11, '2025-11-02T17:52:05.800Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0de3t00yqk498qqfc0zhj', 'cmhi0de2z00y0k4980r45otfa', $$Examens digestifs$$, $$[{"text":"L’échographie abdominale détecte les occlusions.","isCorrect":true},{"text":"Le lavement opaque est indiqué systématiquement.","isCorrect":false,"justification":"Selon indication (atrésie, Hirschsprung)."},{"text":"Le transit baryté se fait sans préparation.","isCorrect":false,"justification":"Sous surveillance."},{"text":"La recherche d’air sous diaphragme se fait à la radio.","isCorrect":true},{"text":"Le test de Graham est utilisé couramment.","isCorrect":false,"justification":"Obsolète."}]$$::jsonb, $$Imagerie digestive = écho + Rx ciblées selon signes.$$, 12, '2025-11-02T17:52:05.802Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0de3w00ysk498lscxkkjp', 'cmhi0de2z00y0k4980r45otfa', $$Examens endocriniens$$, $$[{"text":"Le dosage de TSH détecte l’hypothyroïdie congénitale.","isCorrect":true},{"text":"Ce dépistage fait partie du test de Guthrie.","isCorrect":true},{"text":"L’insuline est dosée en routine.","isCorrect":false,"justification":"Non systématique."},{"text":"Le cortisol est toujours bas à la naissance.","isCorrect":false,"justification":"Variable selon stress."},{"text":"L’hypoglycémie peut être endocrine.","isCorrect":true}]$$::jsonb, $$Endocrino = TSH obligatoire, autres dosages selon contexte.$$, 13, '2025-11-02T17:52:05.804Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0de3y00yuk498vw6poq4g', 'cmhi0de2z00y0k4980r45otfa', $$Radiologie osseuse$$, $$[{"text":"Permet d’évaluer la maturation squelettique.","isCorrect":true},{"text":"L’âge osseux du poignet est utile après 1 an.","isCorrect":true},{"text":"Les radiographies répétées sont sans danger.","isCorrect":false,"justification":"À limiter."},{"text":"Les fractures néonatales sont exceptionnelles.","isCorrect":false,"justification":"Fréquentes (clavicule, humérus)."},{"text":"Les malformations osseuses sont visibles à l’échographie.","isCorrect":true}]$$::jsonb, $$Os = âge osseux, fractures, anomalies détectées tôt.$$, 14, '2025-11-02T17:52:05.806Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0de4000ywk498lzco79hd', 'cmhi0de2z00y0k4980r45otfa', $$Examens hémostatiques$$, $$[{"text":"Le temps de Quick et le TCA évaluent la coagulation.","isCorrect":true},{"text":"Les troubles de la coagulation sont rares chez le nouveau-né.","isCorrect":false,"justification":"Relativement fréquents."},{"text":"La carence en vitamine K est une cause d’hémorragie.","isCorrect":true},{"text":"L’administration de vitamine K est optionnelle.","isCorrect":false,"justification":"Systématique."},{"text":"Les hémorragies du nouveau-né sont toujours traumatiques.","isCorrect":false,"justification":"Parfois métaboliques."}]$$::jsonb, $$Coagulation = Quick/TCA + vitamine K obligatoire.$$, 15, '2025-11-02T17:52:05.809Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0de4300yyk498h47wkai9', 'cmhi0de2z00y0k4980r45otfa', $$Électrolytes et bilan hydrique$$, $$[{"text":"L’hypokaliémie entraîne des convulsions.","isCorrect":false,"justification":"Troubles du rythme cardiaque."},{"text":"L’hypernatrémie est liée à une perte d’eau.","isCorrect":true},{"text":"La surveillance du poids reflète l’état hydrique.","isCorrect":true},{"text":"La perte physiologique de poids > 15 % est normale.","isCorrect":false,"justification":"> 10 % = pathologique."},{"text":"L’hyperhydratation est fréquente en maternité.","isCorrect":false,"justification":"Déshydratation plus courante."}]$$::jsonb, $$Bilan hydrique = Na, K, poids quotidien à surveiller.$$, 16, '2025-11-02T17:52:05.811Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0de4500z0k498hiy7vw67', 'cmhi0de2z00y0k4980r45otfa', $$Dépistages néonataux obligatoires$$, $$[{"text":"Réalisés à J7.","isCorrect":false,"justification":"J2–J3."},{"text":"Incluent phénylcétonurie, hypothyroïdie, drépanocytose.","isCorrect":true},{"text":"L’échantillon se fait sur sang capillaire.","isCorrect":true},{"text":"L’absence de test n’a pas de conséquence.","isCorrect":false,"justification":"Retard de diagnostic grave."},{"text":"Tous les pays ont les mêmes dépistages.","isCorrect":false,"justification":"Varie selon législation."}]$$::jsonb, $$Dépistage = phénylcétonurie, TSH, drépanocytose, test obligatoire à J2–3.$$, 17, '2025-11-02T17:52:05.813Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0de4700z2k4987fr52d3j', 'cmhi0de2z00y0k4980r45otfa', $$Surveillance post-natale$$, $$[{"text":"Le suivi biologique est inutile après sortie.","isCorrect":false,"justification":"Surveillance étroite les 1ers jours."},{"text":"La bilirubine et la glycémie sont contrôlées.","isCorrect":true},{"text":"Le poids doit être vérifié quotidiennement.","isCorrect":true},{"text":"La diurèse et les selles n’ont pas d’importance.","isCorrect":false,"justification":"Indicateurs vitaux."},{"text":"La température doit être stable > 36,5 °C.","isCorrect":true}]$$::jsonb, $$Surveillance post-natale = poids, glycémie, bilirubine, température.$$, 18, '2025-11-02T17:52:05.815Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0de4900z4k4988ro2wozi', 'cmhi0de2z00y0k4980r45otfa', $$Synthèse finale$$, $$[{"text":"Les examens néonataux sont systématiques et identiques pour tous.","isCorrect":false,"justification":"Adaptés à chaque cas."},{"text":"Ils permettent un dépistage précoce des anomalies.","isCorrect":true},{"text":"Leur interprétation nécessite de connaître l’âge gestationnel.","isCorrect":true},{"text":"Ils remplacent l’examen clinique.","isCorrect":false,"justification":"Complémentaires."},{"text":"Leur répétition quotidienne est toujours indiquée.","isCorrect":false,"justification":"Selon évolution."}]$$::jsonb, $$Examens néonataux spécifiques = surveillance ciblée + dépistage vital précoce.$$, 19, '2025-11-02T17:52:05.817Z');
INSERT INTO "Subject" (id, title, description, semester, tags, "totalQCM", "createdAt", views) VALUES ('cmhi0de4f00z5k498w3hbsriy', $$Sémiologie Rénale et Urologique$$, $$Cours de Sémiologie Rénale et Urologique$$, 'DCEM1', ARRAY[$$DCEM1$$, $$Sémiologie$$, $$Sémiologie Rénale et Urologique$$]::text[], 70, '2025-11-02T17:52:05.823Z', 0);
INSERT INTO "Chapter" (id, "subjectId", title, description, "orderIndex", "pdfUrl", "createdAt") VALUES ('cmhi0de4k00z7k4983p6biee5', 'cmhi0de4f00z5k498w3hbsriy', $$CHAPITRE 3 – INSUFFISANCE RÉNALE$$, NULL, 1, NULL, '2025-11-02T17:52:05.828Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0de5a00zvk498v5ha776l', 'cmhi0de4k00z7k4983p6biee5', $$Manifestations cliniques$$, $$[{"text":"Asthénie et anorexie.","isCorrect":true},{"text":"Crampes, insomnie.","isCorrect":true},{"text":"HTA fréquente.","isCorrect":true},{"text":"Œdèmes inflammatoires.","isCorrect":false,"justification":"Faux : œdèmes blancs, mous, non inflammatoires."},{"text":"Troubles neuro (encéphalopathie).","isCorrect":true}]$$::jsonb, $$IRC = asthénie + HTA + encéphalopathie urémique.$$, 11, '2025-11-02T17:52:05.854Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0de5c00zxk498zcieuk46', 'cmhi0de4k00z7k4983p6biee5', $$Manifestations cutanées$$, $$[{"text":"Peau pâle et sèche.","isCorrect":true},{"text":"Prurit intense.","isCorrect":true},{"text":"Givre urémique.","isCorrect":true},{"text":"Érythème facial.","isCorrect":false,"justification":"Faux : non typique de l’IRC."},{"text":"Ulcérations douloureuses.","isCorrect":false,"justification":"Faux : rares et tardives."}]$$::jsonb, $$Peau = pâleur + prurit + dépôts urémiques.$$, 12, '2025-11-02T17:52:05.856Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0de5e00zzk498ec0fuyhz', 'cmhi0de4k00z7k4983p6biee5', $$Manifestations digestives$$, $$[{"text":"Nausées et vomissements.","isCorrect":true},{"text":"Haleine ammoniacale.","isCorrect":true},{"text":"Diarrhée urémique.","isCorrect":true},{"text":"Saignements digestifs fréquents.","isCorrect":true},{"text":"Appétit conservé.","isCorrect":false,"justification":"Faux : anorexie fréquente."}]$$::jsonb, $$Digestif = anorexie + vomissements + haleine ammoniacale.$$, 13, '2025-11-02T17:52:05.859Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0de5h0101k498tdd7pvsc', 'cmhi0de4k00z7k4983p6biee5', $$Manifestations cardio-pulmonaires$$, $$[{"text":"HTA constante.","isCorrect":true},{"text":"OAP possible.","isCorrect":true},{"text":"Péricardite urémique.","isCorrect":true},{"text":"Hypotension.","isCorrect":false,"justification":"Faux : c’est l’hypertension qui prédomine."},{"text":"Tachycardie sinusale.","isCorrect":true}]$$::jsonb, $$IRC = HTA + OAP + péricardite.$$, 14, '2025-11-02T17:52:05.861Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0de5j0103k4986o1jdrhm', 'cmhi0de4k00z7k4983p6biee5', $$Troubles métaboliques$$, $$[{"text":"Acidose métabolique.","isCorrect":true},{"text":"Hyperkaliémie.","isCorrect":true},{"text":"Hypocalcémie.","isCorrect":true},{"text":"Hyperphosphorémie.","isCorrect":true},{"text":"Hypoprotéinémie.","isCorrect":true}]$$::jsonb, $$Troubles métaboliques = acidose, hyperK, Ca↓, P↑, protéines↓.$$, 15, '2025-11-02T17:52:05.863Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0de4m00z9k4989654cj6f', 'cmhi0de4k00z7k4983p6biee5', $$Définition générale$$, $$[{"text":"Diminution du débit de filtration glomérulaire (DFG).","isCorrect":true},{"text":"Altération aiguë ou chronique de la fonction rénale.","isCorrect":true},{"text":"Anurie dans tous les cas.","isCorrect":false,"justification":"Certaines formes sont oliguriques ou non oligurique (NTA, IRC débutante)."},{"text":"Réversible dans l’IRA.","isCorrect":true},{"text":"Toujours irréversible.","isCorrect":false,"justification":"Faux : seule l’IRC est irréversible, pas l’IRA."}]$$::jsonb, $$Insuffisance rénale = baisse du DFG, réversible (IRA) ou non (IRC).$$, 0, '2025-11-02T17:52:05.830Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0de4o00zbk4989rbaj4ax', 'cmhi0de4k00z7k4983p6biee5', $$Insuffisance rénale aiguë (IRA)$$, $$[{"text":"Installation rapide (heures à jours).","isCorrect":true},{"text":"Élévation d’urée et créatinine.","isCorrect":true},{"text":"État stable pendant des années.","isCorrect":false,"justification":"Faux : l’IRA évolue en quelques heures/jours, pas chronique."},{"text":"Réversible si cause corrigée.","isCorrect":true},{"text":"Touche surtout les glomérules.","isCorrect":false,"justification":"Faux : le plus souvent tubulaire (nécrose tubulaire)."}]$$::jsonb, $$IRA = atteinte réversible, rapide, souvent tubulaire.$$, 1, '2025-11-02T17:52:05.832Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0de4q00zdk498iinb0we6', 'cmhi0de4k00z7k4983p6biee5', $$Insuffisance rénale chronique (IRC)$$, $$[{"text":"DFG < 60 mL/min depuis > 3 mois.","isCorrect":true},{"text":"Progression lente.","isCorrect":true},{"text":"Réversible en 24 h.","isCorrect":false,"justification":"Faux : l’IRC est irréversible et progressive."},{"text":"Complications métaboliques multiples.","isCorrect":true},{"text":"Se manifeste souvent par des œdèmes aigus.","isCorrect":false,"justification":"Faux : œdèmes chroniques et progressifs."}]$$::jsonb, $$IRC = irréversible, lente, souvent silencieuse longtemps.$$, 2, '2025-11-02T17:52:05.834Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0de4s00zfk49828y9bu81', 'cmhi0de4k00z7k4983p6biee5', $$Étiologies de l’IRA$$, $$[{"text":"Prérénales : hypovolémie, choc, déshydratation.","isCorrect":true},{"text":"Rénales : nécrose tubulaire aiguë.","isCorrect":true},{"text":"Postrénales : obstacle urinaire.","isCorrect":true},{"text":"Congénitales.","isCorrect":false,"justification":"Faux : les causes congénitales donnent IRC, pas IRA."},{"text":"Toxiques : AINS, aminosides.","isCorrect":true}]$$::jsonb, $$IRA = prérénale, rénale, postrénale ou toxique.$$, 3, '2025-11-02T17:52:05.836Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0de4u00zhk498bmw8n2y8', 'cmhi0de4k00z7k4983p6biee5', $$IRA prérénale$$, $$[{"text":"DFG effondré par hypoperfusion.","isCorrect":true},{"text":"Réversible à la réhydratation.","isCorrect":true},{"text":"Sodium urinaire > 40 mmol/L.","isCorrect":false,"justification":"Faux : dans l’IRA prérénale, Na⁺ < 20 mmol/L (tubule fonctionnel)."},{"text":"Osmolarité urinaire élevée.","isCorrect":true},{"text":"Cylindres granuleux abondants.","isCorrect":false,"justification":"Faux : typiques d’une nécrose tubulaire aiguë."}]$$::jsonb, $$Prérénale = hypoperfusion sans atteinte tubulaire.$$, 4, '2025-11-02T17:52:05.838Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0de4w00zjk498pxkhx3lx', 'cmhi0de4k00z7k4983p6biee5', $$IRA rénale$$, $$[{"text":"Nécrose tubulaire fréquente.","isCorrect":true},{"text":"Cylindres granuleux présents.","isCorrect":true},{"text":"Sodium urinaire bas.","isCorrect":false,"justification":"Faux : Na⁺ > 40 mmol/L (perte de contrôle tubulaire)."},{"text":"Urée/Créatinine < 20.","isCorrect":true},{"text":"Réversible sans traitement.","isCorrect":false,"justification":"Faux : nécessite prise en charge rapide."}]$$::jsonb, $$Rénale = lésion du parenchyme tubulaire.$$, 5, '2025-11-02T17:52:05.841Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0de4z00zlk4989b4y9lwa', 'cmhi0de4k00z7k4983p6biee5', $$IRA postrénale$$, $$[{"text":"Due à obstacle urinaire.","isCorrect":true},{"text":"Globe vésical présent.","isCorrect":true},{"text":"Dilatation pyélocalicielle à l’écho.","isCorrect":true},{"text":"Polyurie initiale.","isCorrect":false,"justification":"Faux : phase initiale oligurique, polyurie après désobstacle."},{"text":"Réversible après levée d’obstacle.","isCorrect":true}]$$::jsonb, $$Postrénale = obstruction → levée = guérison.$$, 6, '2025-11-02T17:52:05.843Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0de5100znk498d7aku1v1', 'cmhi0de4k00z7k4983p6biee5', $$Signes biologiques de l’IRA$$, $$[{"text":"Urée et créatinine augmentées.","isCorrect":true},{"text":"Hyperkaliémie.","isCorrect":true},{"text":"Acidose métabolique.","isCorrect":true},{"text":"Hyponatrémie de dilution.","isCorrect":true},{"text":"Hypocalcémie rare.","isCorrect":false,"justification":"Faux : fréquente (rétention phosphate)."}]$$::jsonb, $$IRA = urée↑, K⁺↑, acide, Na⁺↓, Ca²⁺↓.$$, 7, '2025-11-02T17:52:05.845Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0de5300zpk498xmic27ut', 'cmhi0de4k00z7k4983p6biee5', $$Complications aiguës$$, $$[{"text":"Hyperkaliémie.","isCorrect":true},{"text":"Œdème aigu du poumon (OAP).","isCorrect":true},{"text":"Convulsions urémiques.","isCorrect":true},{"text":"Hypotension sévère.","isCorrect":true},{"text":"Hypothermie fréquente.","isCorrect":false,"justification":"Faux : l’IRA ne s’accompagne pas d’hypothermie."}]$$::jsonb, $$IRA sévère = hyperK + OAP + encéphalopathie.$$, 8, '2025-11-02T17:52:05.847Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0de5500zrk498mkz25gch', 'cmhi0de4k00z7k4983p6biee5', $$Insuffisance rénale chronique (IRC)$$, $$[{"text":"DFG < 60 mL/min durable.","isCorrect":true},{"text":"Rétention d’urée et créatinine.","isCorrect":true},{"text":"Polyurie initiale.","isCorrect":true},{"text":"Peau sèche, prurit, haleine urémique.","isCorrect":true},{"text":"Réversible par réhydratation.","isCorrect":false,"justification":"Faux : non réversible."}]$$::jsonb, $$IRC = altération lente, irréversible, symptômes cutanés et métaboliques.$$, 9, '2025-11-02T17:52:05.850Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0de5800ztk498vronzhg7', 'cmhi0de4k00z7k4983p6biee5', $$Causes principales de l’IRC$$, $$[{"text":"Glomérulonéphrites chroniques.","isCorrect":true},{"text":"Néphropathie diabétique.","isCorrect":true},{"text":"Polykystose rénale.","isCorrect":true},{"text":"Lithiase simple.","isCorrect":false,"justification":"Faux : rarement cause d’IRC seule."},{"text":"Pyélonéphrite chronique.","isCorrect":true}]$$::jsonb, $$IRC = lésions glomérulaires, infectieuses, métaboliques.$$, 10, '2025-11-02T17:52:05.852Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0de5l0105k49841nw7a2r', 'cmhi0de4k00z7k4983p6biee5', $$Anémie rénale$$, $$[{"text":"Normochrome normocytaire.","isCorrect":true},{"text":"Secondaire à déficit en EPO.","isCorrect":true},{"text":"Régénérative.","isCorrect":false,"justification":"Faux : arégénérative (moelle non stimulée)."},{"text":"Aggravée par carence martiale.","isCorrect":true},{"text":"Corrigée par EPO.","isCorrect":true}]$$::jsonb, $$Anémie de l’IRC = défaut d’EPO, arégénérative, corrigée par EPO.$$, 16, '2025-11-02T17:52:05.865Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0de5o0107k498jy2fwq7x', 'cmhi0de4k00z7k4983p6biee5', $$Troubles phosphocalciques$$, $$[{"text":"Hypocalcémie.","isCorrect":true},{"text":"Hyperphosphorémie.","isCorrect":true},{"text":"Hyperparathyroïdie secondaire.","isCorrect":true},{"text":"Ostéomalacie.","isCorrect":true},{"text":"Calcification vasculaire.","isCorrect":true}]$$::jsonb, $$IRC = hyperP, hypoCa → hyperparathyroïdie.$$, 17, '2025-11-02T17:52:05.868Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0de5q0109k498phsswdc1', 'cmhi0de4k00z7k4983p6biee5', $$Syndrome urémique$$, $$[{"text":"Troubles digestifs.","isCorrect":true},{"text":"Neuropathie périphérique.","isCorrect":true},{"text":"Encéphalopathie.","isCorrect":true},{"text":"Nausées, vomissements.","isCorrect":true},{"text":"Rémission spontanée.","isCorrect":false,"justification":"Faux : sans dialyse, évolution fatale."}]$$::jsonb, $$Urémie = intoxication métabolique terminale.$$, 18, '2025-11-02T17:52:05.870Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0de5s010bk498rgjb6oox', 'cmhi0de4k00z7k4983p6biee5', $$Critères de gravité de l’IRA$$, $$[{"text":"Oligurie/anurie.","isCorrect":true},{"text":"Hyperkaliémie > 6,5 mmol/L.","isCorrect":true},{"text":"Acidose sévère.","isCorrect":true},{"text":"OAP ou œdème cérébral.","isCorrect":true},{"text":"Coma urémique.","isCorrect":true}]$$::jsonb, $$Gravité = troubles hydroélectrolytiques majeurs.$$, 19, '2025-11-02T17:52:05.872Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0de5u010dk498ogdgf505', 'cmhi0de4k00z7k4983p6biee5', $$Traitement de l’IRA$$, $$[{"text":"Corriger la cause.","isCorrect":true},{"text":"Réhydratation si hypovolémie.","isCorrect":true},{"text":"Dialyse selon gravité.","isCorrect":true},{"text":"Antibiothérapie systématique.","isCorrect":false,"justification":"Faux : seulement si infection suspectée."},{"text":"Surveillance ionique continue.","isCorrect":true}]$$::jsonb, $$IRA = cause + correction métabolique + dialyse si besoin.$$, 20, '2025-11-02T17:52:05.874Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0de5x010fk49859gl19vh', 'cmhi0de4k00z7k4983p6biee5', $$Traitement de l’IRC$$, $$[{"text":"Contrôle de la TA.","isCorrect":true},{"text":"Régime pauvre en protéines et sel.","isCorrect":true},{"text":"Correction anémie par EPO.","isCorrect":true},{"text":"Restriction hydrique stricte.","isCorrect":false,"justification":"Faux : dépend de la diurèse."},{"text":"Supplémentation en vitamine D.","isCorrect":true}]$$::jsonb, $$IRC = traitement conservateur + correction complications.$$, 21, '2025-11-02T17:52:05.877Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0de5y010hk498zb750vid', 'cmhi0de4k00z7k4983p6biee5', $$Indications de la dialyse$$, $$[{"text":"Hyperkaliémie réfractaire.","isCorrect":true},{"text":"Acidose sévère.","isCorrect":true},{"text":"OAP.","isCorrect":true},{"text":"Nausées simples.","isCorrect":false,"justification":"Faux : symptômes mineurs ≠ indication."},{"text":"Coma urémique.","isCorrect":true}]$$::jsonb, $$Dialyse = indiquée pour troubles vitaux.$$, 22, '2025-11-02T17:52:05.879Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0de60010jk498hsxnu6gc', 'cmhi0de4k00z7k4983p6biee5', $$Techniques d’épuration$$, $$[{"text":"Hémodialyse.","isCorrect":true},{"text":"Dialyse péritonéale.","isCorrect":true},{"text":"Hémofiltration.","isCorrect":true},{"text":"Ultrafiltration isolée.","isCorrect":true},{"text":"Transplantation hépatique.","isCorrect":false,"justification":"Faux : rénale, pas hépatique."}]$$::jsonb, $$Épuration = hemodialyse ou DP selon patient.$$, 23, '2025-11-02T17:52:05.880Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0de61010lk498ea4g9744', 'cmhi0de4k00z7k4983p6biee5', $$Synthèse finale$$, $$[{"text":"L’IRA est toujours irréversible.","isCorrect":false,"justification":"Faux : souvent réversible."},{"text":"L’IRC guérit spontanément.","isCorrect":false,"justification":"Faux : irréversible."},{"text":"L’urémie n’a aucun danger vital.","isCorrect":false,"justification":"Faux : menace vitale."},{"text":"La dialyse corrige la cause.","isCorrect":false,"justification":"Faux : corrige les effets, pas la cause."},{"text":"Le DFG augmente en IRC.","isCorrect":false,"justification":"Faux : diminue progressivement."}]$$::jsonb, $$Aucun vrai → piège pour vérifier la rigueur de raisonnement.$$, 24, '2025-11-02T17:52:05.882Z');
INSERT INTO "Chapter" (id, "subjectId", title, description, "orderIndex", "pdfUrl", "createdAt") VALUES ('cmhi0de64010nk498n0v5ajqf', 'cmhi0de4f00z5k498w3hbsriy', $$Chapitre 1 – Examen clinique du malade en néphrologie$$, NULL, 2, NULL, '2025-11-02T17:52:05.884Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0de65010pk498yuw54z59', 'cmhi0de64010nk498n0v5ajqf', $$Objectif de l’examen néphrologique$$, $$[{"text":"Rechercher les signes d’insuffisance rénale.","isCorrect":true},{"text":"Identifier les anomalies urinaires.","isCorrect":true},{"text":"Se limiter à l’examen abdominal.","isCorrect":false,"justification":"Faux : l’évaluation néphrologique est générale (cardio-vasculaire, œdèmes, état général)."},{"text":"Évaluer le retentissement général.","isCorrect":true},{"text":"Ignorer les antécédents médicaux.","isCorrect":false,"justification":"Faux : ils orientent souvent le diagnostic (cardiopathie, diabète, AINS…)."}]$$::jsonb, $$Objectif = déterminer l’origine et le retentissement des troubles rénaux.$$, 0, '2025-11-02T17:52:05.886Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0de67010rk498gb9ixhmo', 'cmhi0de64010nk498n0v5ajqf', $$Interrogatoire néphrologique$$, $$[{"text":"Recherche les douleurs lombaires et troubles urinaires.","isCorrect":true},{"text":"Néglige les antécédents obstétricaux.","isCorrect":false,"justification":"Faux : grossesses et accouchements peuvent laisser des séquelles urinaires."},{"text":"Précise le mode d’installation et la chronologie.","isCorrect":true},{"text":"Écarte la notion de fièvre.","isCorrect":false,"justification":"Faux : la fièvre oriente vers une infection urinaire ou pyélonéphrite."},{"text":"Doit aborder les habitudes médicamenteuses.","isCorrect":true}]$$::jsonb, $$Interrogatoire = douleurs, diurèse, antécédents, fièvre et toxicité médicamenteuse.$$, 1, '2025-11-02T17:52:05.888Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0de69010tk498djidq8c5', 'cmhi0de64010nk498n0v5ajqf', $$Douleurs lombaires$$, $$[{"text":"Toujours bilatérales.","isCorrect":false,"justification":"Faux : souvent unilatérales (colique néphrétique)."},{"text":"Coliques néphrétiques = irradiations vers organes génitaux.","isCorrect":true},{"text":"Dorsalgies inflammatoires = pyélonéphrite.","isCorrect":true},{"text":"Absence de douleur = rein normal.","isCorrect":false,"justification":"Faux : beaucoup de néphropathies sont indolores."},{"text":"Douleur fixe + fièvre = infection rénale.","isCorrect":true}]$$::jsonb, $$Lombalgie = douleur colique (calcul) ou fixe (infection).$$, 2, '2025-11-02T17:52:05.889Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0de6b010vk498ljroid3f', 'cmhi0de64010nk498n0v5ajqf', $$Troubles urinaires$$, $$[{"text":"Oligurie < 400 mL/24 h.","isCorrect":true},{"text":"Polyurie > 3 L/24 h.","isCorrect":true},{"text":"Hématurie = sang dans les urines.","isCorrect":true},{"text":"Pyurie = glucose urinaire.","isCorrect":false,"justification":"Faux : pyurie = leucocytes dans les urines."},{"text":"Dysurie = jet accéléré.","isCorrect":false,"justification":"Faux : c’est un jet ralenti et difficile."}]$$::jsonb, $$Troubles urinaires = variations de débit, couleur, et signes inflammatoires.$$, 3, '2025-11-02T17:52:05.891Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0de6d010xk498aehxm47b', 'cmhi0de64010nk498n0v5ajqf', $$Œdèmes rénaux$$, $$[{"text":"Blancs, mous, déclives.","isCorrect":true},{"text":"Indolores.","isCorrect":true},{"text":"Durs et inflammatoires.","isCorrect":false,"justification":"Faux : ils sont souples et non inflammatoires."},{"text":"Matinaux au visage.","isCorrect":true},{"text":"Accompagnés de protéinurie.","isCorrect":true}]$$::jsonb, $$Œdème rénal = blanc, mou, matinal → protéinurie.$$, 4, '2025-11-02T17:52:05.894Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0de6f010zk498fclquohu', 'cmhi0de64010nk498n0v5ajqf', $$Examen de la région lombaire$$, $$[{"text":"Voussure lombaire.","isCorrect":true},{"text":"Palpation rénale toujours possible.","isCorrect":false,"justification":"Faux : rein souvent impalpable chez l’adulte obèse."},{"text":"Ballottement rénal.","isCorrect":true},{"text":"Douleur provoquée = infection.","isCorrect":true},{"text":"Percussion douloureuse : colique ou pyélonéphrite.","isCorrect":true}]$$::jsonb, $$Région lombaire = mobilité et douleur orientent le diagnostic.$$, 5, '2025-11-02T17:52:05.896Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0de6h0111k498fzinr42u', 'cmhi0de64010nk498n0v5ajqf', $$Palpation rénale$$, $$[{"text":"En position assise.","isCorrect":false,"justification":"Faux : en décubitus dorsal."},{"text":"Bimanuelle : main postérieure et antérieure.","isCorrect":true},{"text":"Rein normal palpable.","isCorrect":false,"justification":"Faux : seulement chez sujets minces."},{"text":"Masse ferme = rein pathologique.","isCorrect":true},{"text":"Mobilité respiratoire.","isCorrect":true}]$$::jsonb, $$Palpation bi-manuelle = recherche de masse respirante et ferme.$$, 6, '2025-11-02T17:52:05.898Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0de6j0113k498cm8p44d6', 'cmhi0de64010nk498n0v5ajqf', $$Globe vésical$$, $$[{"text":"Masse sus-pubienne dure.","isCorrect":true},{"text":"Matité convexe vers le haut.","isCorrect":true},{"text":"Ballottement hydrique.","isCorrect":true},{"text":"Disparaît après miction.","isCorrect":true},{"text":"Visible uniquement latéral.","isCorrect":false,"justification":"Faux : visible en décubitus dorsal."}]$$::jsonb, $$Globe vésical = rétention urinaire → urgence urologique.$$, 7, '2025-11-02T17:52:05.900Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0de6l0115k4985bm0wfy7', 'cmhi0de64010nk498n0v5ajqf', $$Toucher pelvien$$, $$[{"text":"TR : prostate, douleur, volume.","isCorrect":true},{"text":"TV : masse ou douleur pelvienne.","isCorrect":true},{"text":"Permet d’évaluer la vessie.","isCorrect":true},{"text":"Strictement contre-indiqué.","isCorrect":false,"justification":"Faux : utile dans le bilan urologique."},{"text":"Oriente le siège de l’obstacle.","isCorrect":true}]$$::jsonb, $$Touchers pelviens = évaluation basse appareil urinaire.$$, 8, '2025-11-02T17:52:05.901Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0de6n0117k498able3wa3', 'cmhi0de64010nk498n0v5ajqf', $$Signes fonctionnels urinaires (5 justes)$$, $$[{"text":"Pollakiurie diurne/nocturne.","isCorrect":true},{"text":"Brûlures mictionnelles.","isCorrect":true},{"text":"Hématurie macroscopique.","isCorrect":true},{"text":"Pyurie et urines troubles.","isCorrect":true},{"text":"Jet faible et intermittent.","isCorrect":true}]$$::jsonb, $$Tous ces signes = syndrome urinaire typique.$$, 9, '2025-11-02T17:52:05.903Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0de6o0119k498b5wacluu', 'cmhi0de64010nk498n0v5ajqf', $$Interprétation de la diurèse$$, $$[{"text":"Oligurie = IR fonctionnelle.","isCorrect":true},{"text":"Polyurie = atteinte tubulaire.","isCorrect":true},{"text":"Anurie = obstacle ou nécrose tubulaire.","isCorrect":true},{"text":"Diurèse normale ≈ 1-2 L/24 h.","isCorrect":true},{"text":"< 200 mL = polyurie.","isCorrect":false,"justification":"Faux : < 200 mL = anurie."}]$$::jsonb, $$La diurèse oriente vers le type d’atteinte rénale.$$, 10, '2025-11-02T17:52:05.905Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0de6q011bk498t9a64226', 'cmhi0de64010nk498n0v5ajqf', $$Examen des urines$$, $$[{"text":"Couleur, aspect, densité, pH, PU, HU.","isCorrect":true},{"text":"Bandelette = dépistage rapide.","isCorrect":true},{"text":"ECBU = infection.","isCorrect":true},{"text":"Microscopie = cylindres/cristaux.","isCorrect":true},{"text":"Indépendant du contexte.","isCorrect":false,"justification":"Faux : résultats à interpréter selon clinique."}]$$::jsonb, $$Analyse urinaire = clé du diagnostic néphrologique.$$, 11, '2025-11-02T17:52:05.907Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0de6s011dk498u4e8ivc7', 'cmhi0de64010nk498n0v5ajqf', $$Examens biologiques$$, $$[{"text":"Urée/créatinine = filtration.","isCorrect":true},{"text":"Ionogramme (Na, K, Cl, HCO₃).","isCorrect":true},{"text":"PU > 3 g/L = néphrotique.","isCorrect":true},{"text":"Urée basse = IRC.","isCorrect":false,"justification":"Faux : urée élevée dans l’IRC."},{"text":"Hb ↑ en IRC.","isCorrect":false,"justification":"Faux : anémie normochrome."}]$$::jsonb, $$Biologie = fonction rénale et troubles ioniques.$$, 12, '2025-11-02T17:52:05.908Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0de6v011fk498sjapsmrg', 'cmhi0de64010nk498n0v5ajqf', $$Imagerie rénale$$, $$[{"text":"Échographie = première intention.","isCorrect":true},{"text":"Scanner = lithiase/masses.","isCorrect":true},{"text":"IRM = exploration vasculaire.","isCorrect":true},{"text":"UIV courante.","isCorrect":false,"justification":"Faux : examen désuet."},{"text":"Radiographie simple = diagnostic complet.","isCorrect":false,"justification":"Faux : ne montre pas les reins."}]$$::jsonb, $$Imagerie = écho en 1er, scanner/IRM selon cas.$$, 13, '2025-11-02T17:52:05.911Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0de6x011hk498rhh9pd1s', 'cmhi0de64010nk498n0v5ajqf', $$Synthèse globale$$, $$[{"text":"Examen clinique secondaire à la biologie.","isCorrect":false,"justification":"Faux : c’est l’inverse."},{"text":"Urines inutiles.","isCorrect":false,"justification":"Faux : élément majeur."},{"text":"Rein jamais palpable.","isCorrect":false,"justification":"Faux : parfois palpable."},{"text":"ECBU inutile.","isCorrect":false,"justification":"Faux : test de base."},{"text":"Palpation lombaire contre-indiquée.","isCorrect":false,"justification":"Faux : au contraire, essentielle."}]$$::jsonb, $$Aucun item vrai → rappel du rôle central de l’examen clinique.$$, 14, '2025-11-02T17:52:05.914Z');
INSERT INTO "Chapter" (id, "subjectId", title, description, "orderIndex", "pdfUrl", "createdAt") VALUES ('cmhi0de72011jk498lqf7opk2', 'cmhi0de4f00z5k498w3hbsriy', $$Chapitre 2 – Les principaux syndromes en néphrologie$$, NULL, 3, NULL, '2025-11-02T17:52:05.918Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0de84012hk49879rj9m92', 'cmhi0de72011jk498lqf7opk2', $$Glomérulonéphrite post-streptococcique$$, $$[{"text":"Survient après angine ou impétigo.","isCorrect":true},{"text":"Œdèmes matinaux.","isCorrect":true},{"text":"Hématurie macroscopique.","isCorrect":true},{"text":"Complément C3 bas.","isCorrect":true},{"text":"Évolution favorable en 2–3 semaines.","isCorrect":true}]$$::jsonb, $$Post-strepto = HU, œdèmes, HTA, guérison complète.$$, 16, '2025-11-02T17:52:05.956Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0de86012jk49866zs6pfa', 'cmhi0de72011jk498lqf7opk2', $$Causes médicamenteuses d’atteinte rénale$$, $$[{"text":"AINS.","isCorrect":true},{"text":"Aminosides.","isCorrect":true},{"text":"Diurétiques de l’anse.","isCorrect":true},{"text":"IEC.","isCorrect":true},{"text":"Paracétamol.","isCorrect":false,"justification":"Rarement néphrotoxique."}]$$::jsonb, $$Médicaments = AINS, aminosides, IEC.$$, 17, '2025-11-02T17:52:05.958Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0de89012lk4984uiz7qf3', 'cmhi0de72011jk498lqf7opk2', $$Manifestations extrarénales$$, $$[{"text":"Œdèmes.","isCorrect":true},{"text":"HTA.","isCorrect":true},{"text":"Anémie.","isCorrect":true},{"text":"Acidose métabolique.","isCorrect":true},{"text":"Hyperglycémie.","isCorrect":false,"justification":"Non spécifique."}]$$::jsonb, $$Extrarenales = œdèmes, HTA, anémie, acidose.$$, 18, '2025-11-02T17:52:05.961Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0de8b012nk498danidc3d', 'cmhi0de72011jk498lqf7opk2', $$Syndrome néphrotique de l’enfant$$, $$[{"text":"Idiopathique (lésions minimes).","isCorrect":true},{"text":"Corticosensible.","isCorrect":true},{"text":"Œdèmes massifs et ascite.","isCorrect":true},{"text":"Protéinurie < 1 g/L.","isCorrect":false,"justification":"> 3 g/L."},{"text":"Hypoalbuminémie.","isCorrect":true}]$$::jsonb, $$Enfant = syndrome à lésions minimes, corticosensible.$$, 19, '2025-11-02T17:52:05.964Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0de8d012pk498gab9g90a', 'cmhi0de72011jk498lqf7opk2', $$Syndrome de Fanconi$$, $$[{"text":"Perte urinaire de glucose, phosphates, acides aminés.","isCorrect":true},{"text":"Acidose métabolique.","isCorrect":true},{"text":"PU massive.","isCorrect":false,"justification":"Faible."},{"text":"Hypokaliémie.","isCorrect":true},{"text":"Ostéomalacie secondaire.","isCorrect":true}]$$::jsonb, $$Fanconi = trouble tubulaire global.$$, 20, '2025-11-02T17:52:05.966Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0de8g012rk498n9kel1am', 'cmhi0de72011jk498lqf7opk2', $$Néphropathie diabétique$$, $$[{"text":"Microalbuminurie initiale.","isCorrect":true},{"text":"Évolution vers PU massive.","isCorrect":true},{"text":"HTA associée.","isCorrect":true},{"text":"Glucose urinaire absent.","isCorrect":false,"justification":"Présent."},{"text":"Traitement par contrôle glycémique et IEC.","isCorrect":true}]$$::jsonb, $$Diabétique = microalbuminurie → IR.$$, 21, '2025-11-02T17:52:05.968Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0de8i012tk498uuyz7v9j', 'cmhi0de72011jk498lqf7opk2', $$Amylose rénale$$, $$[{"text":"PU massive.","isCorrect":true},{"text":"Hypoalbuminémie.","isCorrect":true},{"text":"HTA absente.","isCorrect":true},{"text":"Macroglossie.","isCorrect":true},{"text":"Anurie précoce.","isCorrect":false,"justification":"Progressive."}]$$::jsonb, $$Amylose = syndrome néphrotique sans HTA.$$, 22, '2025-11-02T17:52:05.970Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0de8k012vk4985bhus5ea', 'cmhi0de72011jk498lqf7opk2', $$Lupus érythémateux disséminé$$, $$[{"text":"Protéinurie.","isCorrect":true},{"text":"Hématurie.","isCorrect":true},{"text":"Cylindres hématiques.","isCorrect":true},{"text":"Complément normal.","isCorrect":false,"justification":"Diminué."},{"text":"Atteinte glomérulaire fréquente.","isCorrect":true}]$$::jsonb, $$Lupus = néphrite avec PU, HU, C3↓.$$, 23, '2025-11-02T17:52:05.972Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0de8m012xk4983db2qyiy', 'cmhi0de72011jk498lqf7opk2', $$Syndrome néphrotique secondaire$$, $$[{"text":"Diabète.","isCorrect":true},{"text":"Amylose.","isCorrect":true},{"text":"Lupus.","isCorrect":true},{"text":"VIH.","isCorrect":true},{"text":"Médicaments (AINS).","isCorrect":true}]$$::jsonb, $$Secondaire = maladie systémique ou toxique.$$, 24, '2025-11-02T17:52:05.974Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0de8o012zk4981kclfluw', 'cmhi0de72011jk498lqf7opk2', $$Syndrome vasculaire rénal$$, $$[{"text":"HTA sévère.","isCorrect":true},{"text":"Bruit abdominal systolique.","isCorrect":true},{"text":"Atrophie rénale unilatérale.","isCorrect":true},{"text":"Hyperkaliémie.","isCorrect":false,"justification":"Faux : l’hypoperfusion rénale stimule la rénine, ce qui augmente l’aldostérone et provoque une hypokaliémie, pas une hyperkaliémie."},{"text":"Aggravation sous IEC.","isCorrect":true}]$$::jsonb, $$Le syndrome vasculaire rénal se manifeste par HTA, souffle lombaire, rein atrophique, et aggravation sous IEC.$$, 25, '2025-11-02T17:52:05.977Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0de8r0131k4982th0cno6', 'cmhi0de72011jk498lqf7opk2', $$Néphropathie interstitielle chronique$$, $$[{"text":"Polyurie.","isCorrect":true},{"text":"Protéinurie faible.","isCorrect":true},{"text":"Acidose métabolique.","isCorrect":true},{"text":"HTA modérée.","isCorrect":true},{"text":"Œdèmes importants.","isCorrect":false,"justification":"Faux : les œdèmes sont rares car la protéinurie est faible et la rétention sodée limitée."}]$$::jsonb, $$La néphropathie interstitielle chronique se traduit par polyurie, PU faible, acidose métabolique et HTA modérée.$$, 26, '2025-11-02T17:52:05.979Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0de76011nk4982atdtnyo', 'cmhi0de72011jk498lqf7opk2', $$Syndrome néphrotique impur$$, $$[{"text":"Présence d’hématurie.","isCorrect":true},{"text":"HTA fréquente.","isCorrect":true},{"text":"Insuffisance rénale associée.","isCorrect":true},{"text":"Pas d’œdèmes.","isCorrect":false,"justification":"Présents."},{"text":"Hypoalbuminémie absente.","isCorrect":false,"justification":"Présente."}]$$::jsonb, $$Impur = syndrome néphrotique + HU/HTA/IR.$$, 1, '2025-11-02T17:52:05.923Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0de74011lk498rj2scf37', 'cmhi0de72011jk498lqf7opk2', $$Syndrome néphrotique pur$$, $$[{"text":"Protéinurie > 3 g/24 h.","isCorrect":true},{"text":"Hypoalbuminémie < 30 g/L.","isCorrect":true},{"text":"Œdèmes mous, blancs, indolores.","isCorrect":true},{"text":"Hématurie macroscopique.","isCorrect":false,"justification":"Absente dans le pur."},{"text":"Hyperlipidémie associée.","isCorrect":true}]$$::jsonb, $$Néphrotique pur = PU + hypoalbuminémie + œdèmes + hyperlipidémie.$$, 0, '2025-11-02T17:52:05.921Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0de79011pk498u8fxe4d9', 'cmhi0de72011jk498lqf7opk2', $$Caractéristiques biochimiques$$, $$[{"text":"Protéinurie sélective (albumine).","isCorrect":true},{"text":"Hypoalbuminémie < 30 g/L.","isCorrect":true},{"text":"Cholestérolémie > 4 g/L.","isCorrect":true},{"text":"Urée diminuée.","isCorrect":false,"justification":"Normale ou élevée."},{"text":"Lipides urinaires rares.","isCorrect":false,"justification":"Présents (corps gras ovales)."}]$$::jsonb, $$Néphrotique = PU massive + hypoalbuminémie + hyperlipidémie.$$, 2, '2025-11-02T17:52:05.925Z');
