INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0de8v0135k498i7h2pjvt', 'cmhi0de72011jk498lqf7opk2', $$Faux syndrome néphrotique$$, $$[{"text":"Protéinurie < 1 g/24 h.","isCorrect":false,"justification":"Faux : une vraie protéinurie néphrotique est > 3 g/24 h."},{"text":"Hématurie importante.","isCorrect":false,"justification":"Faux : dans le syndrome néphrotique pur, l’hématurie est absente ou minime."},{"text":"Hypoalbuminémie absente.","isCorrect":false,"justification":"Faux : elle est constante dans le syndrome néphrotique."},{"text":"Œdèmes inflammatoires.","isCorrect":false,"justification":"Faux : les œdèmes sont blancs, mous et indolores, jamais inflammatoires."},{"text":"HTA sévère sans protéinurie.","isCorrect":false,"justification":"Faux : HTA sans PU évoque plutôt syndrome vasculaire, pas néphrotique."}]$$::jsonb, $$Aucun item vrai : ce QCM est un piège classique permettant de distinguer le syndrome néphrotique pur des autres.$$, 28, '2025-11-02T17:52:05.983Z');
INSERT INTO "Question" (id, "chapterId", "questionText", options, explanation, "orderIndex", "createdAt") VALUES ('cmhi0de8x0137k498okq7gwjw', 'cmhi0de72011jk498lqf7opk2', $$Synthèse générale$$, $$[{"text":"Syndromes glomérulaires = Hématurie + Protéinurie + HTA.","isCorrect":true},{"text":"Syndromes tubulaires = Acidose + PU faible.","isCorrect":true},{"text":"Syndromes interstitiels = Infection ascendante.","isCorrect":true},{"text":"Syndromes vasculaires = HTA rénovasculaire.","isCorrect":true},{"text":"Tous nécessitent une biopsie rénale immédiate.","isCorrect":false,"justification":"Faux : la biopsie est réservée aux cas où la cause n’est pas identifiée ou quand le traitement dépend du type histologique."}]$$::jsonb, $$La néphrologie clinique repose sur la reconnaissance syndromique : glomérulaire, tubulaire, interstitielle ou vasculaire.$$, 29, '2025-11-02T17:52:05.985Z');
